var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_alone_1"></a><div><a id="6504937708254a64972bf4d4ccdfcc9d_alone"></a><span class="entry"><span class="entryhead"><span class="hwd">alone</span><span class="hyphenation frequent">a<span class="hs0"></span>lone</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">əˈləʊn</span><span class="amevarpron"><span class="neutral"> $ </span>əˈloʊn</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S2</span> <span class="freq">W1</span><span class="pos"> adjective</span><span class="gram"> [not before noun]</span><span class="pos"><span class="neutral">, </span>adverb</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_1_alone0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_d_alone').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">alone</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1200-1300</span> <span class="etymorigin">all one</span> <span class="etymtran"> ‘wholly one’</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>Dorothy lives <span class="nodeword">alone</span>.</li><li>He felt terribly <span class="nodeword">alone</span> when June left.</li><li>It's an expensive place - lunch <span class="nodeword">alone</span> was $20.</li><li>Kim traveled <span class="nodeword">alone</span> through Europe.</li><li>Stevenson <span class="nodeword">alone</span> is to blame.</li><li>The price <span class="nodeword">alone</span> was enough to make me change my mind.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">on your own/by yourself</span></span><div class="content"><span class="def"> without help from anyone else</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I can’t move the bed on my own.</span><span class="example"><span class="neutral">· </span>See if you can work out the answer by yourself.</span><span class="thespropform">all on your own/all by yourself<span class="gloss"><span class="neutral"> (=</span>used when you feel impressed or feel sympathy for someone<span class="neutral">)</span></span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Did you paint this picture all on your own?</span><span class="example"><span class="neutral">· </span>It’s not fair that she does everything all by herself.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">alone</span></span><div class="content"><span class="def"> without any help from anyone else. <span class="defbold">Alone</span> is more formal and less common than <span class="defbold">on your own</span> or <span class="defbold">by yourself</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police believe the killer acted alone.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">independently</span></span><div class="content"><span class="def"> without asking for help or advice from anyone</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Students are taught to work independently.</span><span class="example"><span class="neutral">· </span>One day she will have to live independently without the support of her family.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">unaided</span></span><div class="content"><span class="def"> without the help of anyone or anything – used especially about people who are very weak, ill etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He is unable to breathe unaided.</span><span class="example"><span class="neutral">· </span>After treatment, he was able to go up and down stairs unaided.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">single-handedly</span></span><div class="content"><span class="def"> doing something difficult or impressive without help from anyone else</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She single-handedly reformed the entire system.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">solo</span></span><div class="content"><span class="def"> by one person, not a group</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Lindbergh’s solo flight across the Atlantic Ocean</span><span class="example"><span class="neutral">· </span>She is planning to release a solo album.</span><span class="example"><span class="neutral">· </span>He sang and played the song solo.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">lone</span></span><div class="content"><span class="pos"> adjective</span><span class="gram"> [only before noun]</span><span class="def"> doing something alone – used especially in the following phrases</span><span class="thespropform"><span class="neutral">: </span>a lone gunman</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police say the attack was carried out by a <span class="colloinexa">lone gunman</span>.</span><span class="example"><span class="neutral">· </span>The government should do more to help <span class="colloinexa">lone parents</span> <span class="gloss"><span class="neutral"> (=</span>who raise a child alone<span class="neutral">)</span></span>.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">self-made</span></span><div class="content"><span class="pos"> adjective</span><span class="def"> someone who has become very rich or successful without help from anyone else</span><span class="thespropform"><span class="neutral">: </span>a self-made man/millionaire/businessman etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Like most self-made men, he was extremely self-confident.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">when there are no other people with you</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">alone/on your own/by yourself</span></span><div class="content"><span class="def"> if you are alone, on your own, or by yourself, you are in a place and no-one else is there with you </span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She was sitting alone on a park bench.</span><span class="example"><span class="neutral">· </span>I don't really like walking home on my own at night.</span><span class="example"><span class="neutral">· </span>Do you share the apartment, or do you live by yourself?</span><span class="thespropform">all alone/on your own/by yourself</span><span class="gloss"><span class="neutral"> (=</span>completely alone<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Wendy was frightened, all alone in that big old house.</span><span class="thespropform">leave somebody alone/on their own/by themselves</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The first time his parents left him alone in the house, he set fire to the kitchen.</span><span class="example"><span class="neutral">· </span>Mark's not well. I can't go out and leave him on his own.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">unaccompanied</span></span><div class="content"><span class="def"> if you go somewhere unaccompanied, you go there alone, especially when it is more usual to be with someone else</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Children flying unaccompanied are looked after by the cabin crew.</span><span class="example"><span class="neutral">· </span>Some parts of town are not safe for an unaccompanied woman.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">solitude</span></span><div class="content"><span class="def"> when you are alone, especially when you want to be alone because this gives you time to think, work etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I need solitude in order to paint my pictures.</span><span class="example"><span class="neutral">· </span>Ella loved the quiet solitude of her weekends.</span><span class="thespropform">in solitude</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He spent his free time in solitude, reading or walking in the hills.</span></div></span></span><span class="section"><span class="secheading">when you do something without help from someone else</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">on your own/by yourself</span></span><div class="content"><span class="def"> if you do something on your own or by yourself , you do it without anyone helping you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I managed to fix the car on my own.</span><span class="example"><span class="neutral">· </span>He's old enough to get dressed by himself, isn't he?</span><span class="thespropform">all on your own/all by yourself</span><span class="gloss"><span class="neutral"> (=</span>use this when it is surprising that someone has done something without anyone's help<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>How did you manage to prepare so much food all by yourself?</span><span class="thespropform">you're on your own</span><span class="gloss"><span class="neutral"> (=</span>use this to tell someone that you will not help them<span class="neutral">)</span></span><span class="neutral"> </span><span class="registerlab">spoken</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I can get an application for you, but after that you're on your own.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">unaided</span></span><div class="content"><span class="def"> if you do something difficult unaided you do it without the help of anyone or anything</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>After his illness he was unable to walk unaided.</span><span class="example"><span class="neutral">· </span>With no one else in the office I had to deal with the problem unaided.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">single-handedly/single-handed</span></span><div class="content"><span class="def"> if you do something very difficult or very impressive single-handedly or single-handed , you succeed in doing it without the help of anyone else</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In 1992, he rowed across the Atlantic single-handed.</span><span class="thespropform">almost single-handedly/single-handed</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sanger almost single-handedly founded the birth control movement in the early 1900s.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">solo</span></span><div class="content"><span class="def"> if you do something solo, especially a sports or musical activity, you do it alone</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>By the end of the course, all students will fly solo.</span><span class="thespropform">go solo</span><span class="gloss"><span class="neutral"> (=</span>start doing something on your own instead of in a group<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>John played with the band for five years before going solo.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">self-made</span></span><div class="content"><span class="def"> someone who started without much money but has become rich and successful simply through their own efforts and work</span><span class="thespropform"><span class="neutral">: </span>self-made man/businesswoman/millionaire etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>C.J. Walker became one of black America's first self-made millionaires.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">self-starter</span></span><div class="content"><span class="def"> someone who is able to do things on their own without being told what to do by other people, especially in their job</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We're looking for creative self-starters with at least three years' experience.</span></div></span></span><span class="section"><span class="secheading">someone who spends a lot of time alone</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">solitary</span></span><div class="content"><span class="def"> a solitary person spends a lot of time alone, especially because they like being alone</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She was a very solitary woman who didn't make friends easily.</span><span class="example"><span class="neutral">· </span>Ed enjoys the solitary life of a rancher.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">loner</span></span><div class="content"><span class="def"> someone who prefers to do things alone and has few friends</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I had always been a loner, and I hated sharing an apartment when I went to college.</span><span class="example"><span class="neutral">· </span>Joe is one of our best workers but he's too much of a loner to be a good leader.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recluse</span></span><div class="content"><span class="def"> someone who lives alone and avoids meeting other people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Old Mr Grimes was a bad-tempered recluse, rarely seen in the town.</span><span class="example"><span class="neutral">· </span>If you don't get out more, you're going to turn into a recluse.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">prefer your own company</span></span><div class="content"><span class="def"> someone who prefers their own company prefers to be alone rather than being with other people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We asked him to come and eat with us, but he said he preferred his own company.</span></div></span></span><span class="section"><span class="secheading">alone and unhappy</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">lonely</span></span><div class="content"><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">lonesome</span><span class="neutral"> </span><span class="registerlab">American</span></span><span class="def"> unhappy because of being alone or without friends</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Tammy felt very lonely when she first arrived in New York.</span><span class="example"><span class="neutral">· </span>Our neighbor George is a very lonely man.</span><span class="example"><span class="neutral">· </span>I get so lonesome here with no-one to talk to.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">isolated</span></span><div class="content"><span class="def"> feeling that there is no one you can talk to or have as a friend, because your situation makes it difficult for you to meet people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Young, single parents often feel isolated and unhappy.</span><span class="thespropform">isolated from</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Children of very rich parents can grow up isolated from the rest of society.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">alienated</span></span><div class="content"><span class="def"> feeling alone and as if you are not wanted or understood by other people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We're making a special effort to help alienated members feel more part of the group.</span><span class="thespropform">alienated from</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In high school she felt somehow different and alienated from other students.</span></div></span></span><span class="section"><span class="secheading">to feel lonely because someone that you love is not there</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">miss</span></span><div class="content"><span class="def"> to feel lonely because someone that you like very much is not with you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>When are you coming home? I miss you.</span><span class="example"><span class="neutral">· </span>It was great living in Prague, but I really missed all my friends.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be pining (away) for</span></span><div class="content"><span class="def"> to feel unhappy because you cannot be with someone you love - often used humorously</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"What's wrong with Dan?" "I think he's pining for his girlfriend."</span></div></span></span><span class="section"><span class="secheading">to live in an independent way</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be independent/lead an independent life</span></span><div class="content"><span class="def"> to live in an independent way, without other people helping you or telling you what to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Alice was glad to be independent and making a life of her own at last.</span><span class="example"><span class="neutral">· </span>What strategies does a growing child use to become independent?</span><span class="example"><span class="neutral">· </span>Our main objective is to help disabled people lead independent lives within the community. </span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">take care of yourself</span></span><div class="content"><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">look after yourself</span></span><span class="neutral"> </span><span class="registerlab">especially British</span><span class="def"> to cook your own food, wash your own clothes, and do other basic things that are necessary to live</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Grandpa can't take care of himself any more so he's coming to live with us.</span><span class="example"><span class="neutral">· </span>Many youngsters who've been brought up in care are often incapable of looking after themselves when they leave.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">stand on your own two feet</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> to live your life independently without any help from your family or the government</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She'll never learn to stand on her own feet if you keep giving her whatever she wants.</span><span class="example"><span class="neutral">· </span>A year abroad gives students the chance to stand on their own two feet.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">go it alone</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> to start working or living on your own, especially after working or living with other people in a family, organization etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The response to our proposal was lukewarm, so we felt we had to go it alone.</span><span class="example"><span class="neutral">· </span>After years of working for a big company, she decided to go it alone and set up her own business.</span><span class="example"><span class="neutral">· </span>When it comes to parenthood, more and more women are deciding to go it alone.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">do your own thing</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> to live in an independent way and do what you want to do, without being influenced by what other people think</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He has a couple of roommates but they kind of all do their own thing.</span><span class="example"><span class="neutral">· </span>He's given up his job and is living in northern California, just doing his own thing.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fend for yourself</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">written</span><span class="def"> to look after yourself, when you are used to being taken care of by someone else, or when being independent is very difficult</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The mother died before the cubs were old enough to fend for themselves.</span><span class="example"><span class="neutral">· </span>Dad always wanted me to be able to fend for myself from a very early age.</span><span class="example"><span class="neutral">· </span>The children were left to fend for themselves on the streets.</span></div></span></span><span class="section"><span class="secheading">to not interfere</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">mind your own business/it's none of your business</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you want someone to stop interfering or asking questions about something that is private</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Whitney, you just mind your own business. He can do what he wants.</span><span class="example"><span class="neutral">· </span>He wanted a copy of the will but I told him it was none of his business.</span><span class="example"><span class="neutral">· </span>I didn't ask, because it's none of my business really, but I was sure curious.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">stay/keep out of it</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken informal</span><span class="def"> to not get involved in a fight or an argument between other people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It isn't your business, you stay out of it and let her handle it.</span><span class="example"><span class="neutral">· </span>I know what I'm doing - you keep out of it.</span><span class="thespropform">stay/keep well out of it</span><span class="gloss"><span class="neutral"> (=</span>not interfere at all<span class="neutral">)</span></span><span class="neutral"> </span><span class="registerlab">British</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They're always arguing about money, but I try to stay well out of it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be/have nothing to do with</span></span><div class="content"><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">not have anything to do with somebody</span></span><span class="def"> if a situation has nothing to do with someone, it is very personal and private, and that person has no right to interfere in it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I really shouldn't have told her about it - it had nothing to do with her, really.</span><span class="example"><span class="neutral">· </span>Back off and let me handle it. It's nothing to do with you.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">leave/let well alone</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British</span><span class="variant"> <span class="lexvar">/leave/let well enough alone</span><span class="neutral"> </span><span class="registerlab">American</span></span><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> to not interfere or try to change a situation, because you might make it worse than before</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I should have left well enough alone, and never told my parents what had happened at all.</span><span class="example"><span class="neutral">· </span>Once you've finished a project, leave well alone and go on to the next one.</span></div></span></span><span class="section last"><span class="secheading">what you say to tell someone not to touch something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">don't touch</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this when you are telling someone, especially a child, not to touch something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Don't touch! That's daddy's work.</span><span class="example"><span class="neutral">· </span>Those glasses were very expensive. You can look but don't touch.</span><span class="example"><span class="neutral">· </span>Don't touch the iron - it's hot.</span><span class="example"><span class="neutral">· </span>How many times do I have to tell you - don't touch my things!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">leave something alone</span></span><div class="content"><span class="def"> use this when you are telling someone not to touch something they are already touching</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>That's a very delicate piece of equipment - please leave it alone.</span><span class="example"><span class="neutral">· </span>Our neighbor would have cut the tree down if I hadn't told him to leave it alone.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">hands off</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are angrily telling someone not to touch something, especially when they are trying to take it away</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Hands off my coat!</span><span class="thespropform">get/take/keep your hands off something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Get your hands off my car!</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">lives alone</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-000937203').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She <span class="colloinexa">lives alone</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">leave ... alone</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001630417').play();"><img src="LDOCE6_img_spkr_g.png"/></a> You shouldn’t <span class="colloinexa">leave</span> a child <span class="colloinexa">alone</span> in the house.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">alone together</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-000937207').play();"><img src="LDOCE6_img_spkr_g.png"/></a> My wife and I like to spend time <span class="colloinexa">alone together</span> away from the kids.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">all alone</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000937214').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It was scary being <span class="colloinexa">all alone</span> in a strange city.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">all alone in the world</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001989770').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She was <span class="colloinexa">all alone in the world</span><span class="collgloss"><span class="neutral"> (=</span>she had no family or friends to help her or look after her<span class="neutral">)</span></span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">felt so alone</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001630430').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I cried like a child because I <span class="colloinexa">felt so alone</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">leave well (enough) alone</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>not change something that is satisfactory<span class="neutral">)</span></span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001630463').play();"><img src="LDOCE6_img_spkr_g.png"/></a> In economic matters, they should leave well alone.</span></div></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">die alone</span></span><div class="content"><span class="example"><span class="neutral">· </span>He had a fear of dying alone.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">living alone</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001796295').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’m quite happy <span class="colloinexa">living alone</span>.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section last"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">here</span></span><div class="content"><span class="example"><span class="neutral">· </span>For nearly a year I have been <span class="colloinexa">here alone</span>.</span><span class="example"><span class="neutral">· </span>I think they must feel sorry for me, <span class="colloinexa">here alone</span>.</span><span class="example"><span class="neutral">· </span>The lawyers had told me that the cottage was let to a girl, who was <span class="colloinexa">here alone</span>.</span><span class="example"><span class="neutral">· </span>I have often come out <span class="colloinexa">here alone</span> after dark to breathe it in.</span><span class="example"><span class="neutral">· </span>All the same it was a long way down and she hoped that Ana did not ever come <span class="colloinexa">here alone</span>.</span><span class="example"><span class="neutral">· </span>Right now all I want to do is sit <span class="colloinexa">here alone</span> for a while.</span><span class="example"><span class="neutral">· </span>Then I was <span class="colloinexa">here alone</span> for no more than a few seconds.</span><span class="example"><span class="neutral">· </span>I must be guilty of whatever he says I have done, I should not be <span class="colloinexa">here alone</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">home</span></span><div class="content"><span class="example"><span class="neutral">· </span>He had got under her skin, and after half an hour she went <span class="colloinexa">home alone</span>, not content with second-best.</span><span class="example"><span class="neutral">· </span>The guide contains commonsense recommendations covering situations such as meetings in strangers' offices and <span class="colloinexa">homes</span>, walking home <span class="colloinexa">alone</span> and car breakdowns.</span><span class="example"><span class="neutral">· </span>For this too was part of the tradition: the daughters came <span class="colloinexa">home alone</span>.</span><span class="example"><span class="neutral">· </span>Mr Popple, upon returning <span class="colloinexa">home alone</span>, found the spider floating in the toilet-bowl.</span><span class="example"><span class="neutral">· </span>At <span class="colloinexa">home alone</span> with Eulah Mae in the stately old house soaked with memories, Miss Tish cried.</span><span class="example"><span class="neutral">· </span>No, this technique is only to be used when travelling <span class="colloinexa">home alone</span>, late at night, through fairly deserted streets.</span><span class="example"><span class="neutral">· </span>Williams told police she went <span class="colloinexa">home alone</span> at midnight, an hour and a half before the shooting.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">out</span></span><div class="content"><span class="example"><span class="neutral">· </span>Parents should, however, be advised that repeat treatment may be required, particularly when insertion is carried <span class="colloinexa">out alone</span>.</span><span class="example"><span class="neutral">· </span>Disappointed customers and forsaken employees pretty much had to tough it <span class="colloinexa">out alone</span>.</span><span class="example"><span class="neutral">· </span>Connie Fraser would have to work it <span class="colloinexa">out alone</span>, and I hoped she'd make it.</span><span class="example"><span class="neutral">· </span>It was evident that the sensible decision had been reached to let the two most concerned fight it <span class="colloinexa">out alone</span>.</span><span class="example"><span class="neutral">· </span>That 90 percent of women are afraid to go <span class="colloinexa">out alone</span> at night represents enormous numbers of journeys avoided and trips foregone.</span><span class="example"><span class="neutral">· </span>Left <span class="colloinexa">alone</span> in the dairy for a moment, he asks her whether she ever walks <span class="colloinexa">out</span> alone.</span><span class="example"><span class="neutral">· </span>I regularly see them sent <span class="colloinexa">out alone</span> on shopping errands.</span><span class="example"><span class="neutral">· </span>The farmer gave him a pair of shoes and sent him <span class="colloinexa">out alone</span> on the road.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">well</span></span><div class="content"><span class="example"><span class="neutral">· </span>Thankfully it's proved far easier to leave <span class="colloinexa">well alone</span>.</span><span class="example"><span class="neutral">· </span>The infatuation will pass: <span class="colloinexa">better</span> to let well <span class="colloinexa">alone</span>.</span><span class="example"><span class="neutral">· </span>If you had left <span class="colloinexa">well alone</span> and let me carry on my business I wouldn't be here.</span><span class="example"><span class="neutral">· </span>If he had been able to shout, Nicholas would have told John to let <span class="colloinexa">well alone</span>.</span><span class="example"><span class="neutral">· </span>And the policy of leaving things <span class="colloinexa">well alone</span> is working so sweetly.</span><span class="example"><span class="neutral">· </span>You leave <span class="colloinexa">well alone</span>, you've done enough damage.</span><span class="example"><span class="neutral">· </span>All in all, I think we'd be <span class="colloinexa">well</span> advised to leave well <span class="colloinexa">alone</span>.</span><span class="example"><span class="neutral">· </span>We were an autonomous unit, left <span class="colloinexa">well alone</span> by the military hierarchy out there.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">leave somebody alone</span></span><div class="content"><ul class="exas"><li>Go away and leave me <span class="nodeword">alone</span>.</li><li>Just leave me <span class="nodeword">alone</span> and stop asking me questions.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">leave something alone</span></span><div class="content"><ul class="exas"><li>Leave it <span class="nodeword">alone</span> or you'll break it.</li><li>Our neighbor would have cut the tree down if I hadn't told him to leave it <span class="nodeword">alone</span>.</li><li>That's a very delicate piece of equipment - please leave it <span class="nodeword">alone</span>.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">be yours/hers/his etc alone</span></span><div class="content"><ul class="exas"><li>Even when your growing brain shared my blood, your dreams were yours <span class="nodeword">alone</span>.</li><li>She had never helped with any of the bills, so he felt that the house was his <span class="nodeword">alone</span>.</li><li>The next job was his <span class="nodeword">alone</span>.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">not be alone in (doing) something</span></span><div class="content"><ul class="exas"><li>But this market has not yet developed, and when it does Pippin will not be <span class="nodeword">alone</span> in it.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">stand alone</span></span><div class="content"><ul class="exas"><li>For talent, popularity, and style, Muhammad Ali stands <span class="nodeword">alone</span>.</li><li>I stood <span class="nodeword">alone</span> on the shore of the lake.</li><li>The house stood <span class="nodeword">alone</span> at the end of the road.</li><li>The vote on the ban was 14-1, with Britain standing <span class="nodeword">alone</span>.</li><li>Again, standing <span class="nodeword">alone</span> this evidence is not probative of any discrimination in the local construction industry...</li><li>Bedford stood <span class="nodeword">alone</span> and put his drink down on the table.</li><li>But Boston stands <span class="nodeword">alone</span> in saying officially and unequivocally that no such condition exists here.</li><li>Clasper stood <span class="nodeword">alone</span> on the box, clasping a hand microphone to his ranting mouth.</li><li>Frequently the theatre-in-education group is associated with a historical site, although their workshops can stand <span class="nodeword">alone</span> and take place in school.</li><li>He stood <span class="nodeword">alone</span> for a minute, until she was out of sight.</li><li>It is often said of him that he stood <span class="nodeword">alone</span>.</li><li>They stand <span class="nodeword">alone</span> - and fall alone.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">you/he etc alone</span></span><div class="content"><ul class="exas"><li>And he <span class="nodeword">alone</span> guarantees their keeping.</li><li>Do not place the blame anywhere but on yourself, because you <span class="nodeword">alone</span> have chosen that path.</li><li>Furthermore, Ishmael is alive because he <span class="nodeword">alone</span> did not assign specific meanings to events.</li><li>His control in these cases is such that he <span class="nodeword">alone</span> decides whether or not to sample, whether or not to demand remedial action.</li><li>It may not be necessary to back up your words with actions - he or she may leave you <span class="nodeword">alone</span> after that.</li><li>Obviously seriously wounded, he had keyed the set so he and he <span class="nodeword">alone</span> could speak, and it was not coherent.</li><li>Usu-ally the people leave you <span class="nodeword">alone</span> after that.</li><li>What you tell the fuzz to get them to leave you <span class="nodeword">alone</span>?</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">go it alone</span></span><div class="content"><ul class="exas"><li>After years of working for a big company, I decided to go it <span class="nodeword">alone</span>.</li><li>Sayles hasn't regretted his decision to go it <span class="nodeword">alone</span> as a filmmaker.</li><li>The response to our proposal was lukewarm, so we felt we had to go it <span class="nodeword">alone</span>.</li><li>When it comes to parenthood, more and more women are deciding to go it <span class="nodeword">alone</span>.</li><li>As much as he can, he tries to go it <span class="nodeword">alone</span>.</li><li>But County Auctions, a big operation with centres at Wooler and Newcastle, was always likely to go it <span class="nodeword">alone</span>.</li><li>Do not try to go it <span class="nodeword">alone</span> - everything you do will be enhanced by the company of another.</li><li>He knew that each brought something important to the relationship, but that neither could go it <span class="nodeword">alone</span>.</li><li>If we would not be better off, it might be better to go it <span class="nodeword">alone</span>.</li><li>Many of them do not have the capital or a big enough infrastructure to go it <span class="nodeword">alone</span>, he said.</li><li>No single community could go it <span class="nodeword">alone</span>.</li><li>That was when Brian decided to go it <span class="nodeword">alone</span>, sourcing the units and adapting them himself.</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">leave somebody/something alone</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">let alone</span></span><div class="content"><ul class="exas"><li>I wouldn't work with my mom, <span class="nodeword">let</span> alone my whole family.</li><li>But these images were not arbitrary, <span class="nodeword">let</span> alone trivial.</li><li>Hardly an inspirational notion, <span class="nodeword">let</span> alone a formula for improved customer relations.</li><li>I doubt he would have given one of his grandparents a point that morning, <span class="nodeword">let</span> alone me.</li><li>If your income drops or ends, how will you pay them off, <span class="nodeword">let</span> alone keep up with the accumulating interest?</li><li>It's disappointing that Butman doesn't ask these questions, <span class="nodeword">let</span> alone provide answers.</li><li>It's unwise to <span class="nodeword">let</span> policy be influenced, let alone jeopardized, by outraged personal pride.</li><li>Keeper Judge never touched him <span class="nodeword">let</span> alone trip him.</li><li>Many thousands of children had never even seen, <span class="nodeword">let</span> alone owned a pair of shoes.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">in name only/alone</span></span><div class="content"><ul class="exas"><li>Don't be fooled by his title: he remains Health Secretary in <span class="nodeword">name</span> only.</li><li>Lewis had been the ward boss in <span class="nodeword">name</span> only, because white precinct captains ran the organization, including him.</li><li>Many of these local mills remain in <span class="nodeword">name</span> alone, having fallen into disuse and demolition.</li><li>Others converted in <span class="nodeword">name</span> only, out of fear, while others stuck to their faith.</li><li>The strong mayor system employed from 1881 to 1914 was strong in <span class="nodeword">name</span> only.</li><li>Their homes are mobile in <span class="nodeword">name</span> only.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">stand alone</span></span><div class="content"><ul class="exas"><li>Again, <span class="nodeword">standing</span> alone this evidence is not probative of any discrimination in the local construction industry...</li><li>Bedford <span class="nodeword">stood</span> alone and put his drink down on the table.</li><li>But Boston <span class="nodeword">stands</span> alone in saying officially and unequivocally that no such condition exists here.</li><li>Clasper <span class="nodeword">stood</span> alone on the box, clasping a hand microphone to his ranting mouth.</li><li>Frequently the theatre-in-education group is associated with a historical site, although their workshops can <span class="nodeword">stand</span> alone and take place in school.</li><li>He <span class="nodeword">stood</span> alone for a minute, until she was out of sight.</li><li>It is often said of him that he <span class="nodeword">stood</span> alone.</li><li>They <span class="nodeword">stand</span> alone - and fall alone.</li></ul></div></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">if you are alone in a place, there is no one with you</span><span class="syn"> <span class="synopp">SYN</span>  by yourself</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-000937203"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-000937203').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She <span class="colloinexa">lives alone</span>.</span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-001630417"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001630417').play();"><img src="LDOCE6_img_spkr_g.png"/></a> You shouldn’t <span class="colloinexa">leave</span> a child <span class="colloinexa">alone</span> in the house.</span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-000937207"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-000937207').play();"><img src="LDOCE6_img_spkr_g.png"/></a> My wife and I like to spend time <span class="colloinexa">alone together</span> away from the kids.</span><span class="f2nbox"><span class="heading">Register</span><span class="expl last">In everyday English, people often say <span class="expr">by yourself</span> or <span class="expr">on your own</span> rather than <span class="expr">alone</span>:<span class="example"><span class="neutral">· </span>She lives <span class="colloinexa">by herself/on her own</span>.</span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="def">without any friends or people you know</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-000937214"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000937214').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It was scary being <span class="colloinexa">all alone</span> in a strange city.</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001989770"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001989770').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She was <span class="colloinexa">all alone in the world</span><span class="gloss"><span class="neutral"> (=</span>she had no family or friends to help her or look after her<span class="neutral">)</span></span>.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s3"></a><span class="sense newline"><span class="sensenum">3</span><span class="def">feeling unhappy and lonely</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001630430"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001630430').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I cried like a child because I <span class="colloinexa">felt so alone</span>.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s4"></a><span class="sense newline"><span class="sensenum">4</span><span class="def">without any help from other people</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-001630437"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001630437').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He was left to raise their two children alone.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s5"></a><span class="sense newline"><span class="sensenum">5</span><span class="def">without including anything else</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-000937229"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-000937229').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The case will cost thousands of pounds in legal fees alone.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s6"></a><span class="sense newline"><span class="sensenum">6</span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s7"></a><span class="lexunit">you/he etc alone</span><span class="def"> used to emphasize that there is only one person who knows, can do something etc</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-000937224"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000937224').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Julie alone knew the truth.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s8"></a><span class="sense newline"><span class="sensenum">7</span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s9"></a><span class="lexunit">go it alone</span><span class="def"> to start working or living on your own, especially after working or living with other people</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001630442"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001630442').play();"><img src="LDOCE6_img_spkr_g.png"/></a> After years of working for a big company I decided to go it alone.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s10"></a><span class="sense newline"><span class="sensenum">8</span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s11"></a><span class="lexunit">leave somebody alone</span><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> let somebody alone</span><span class="registerlab"> old-fashioned</span><span class="neutral">)</span></span><span class="def"> to stop annoying or interrupting someone</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001630450"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001630450').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘Leave me alone!’ she screamed.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s12"></a><span class="sense newline"><span class="sensenum">9</span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s13"></a><span class="lexunit">leave something alone</span><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> let something alone</span><span class="registerlab"> old-fashioned</span><span class="neutral">)</span></span><span class="def"> to stop touching an object or changing something</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_d_p008-001630458"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-001630458').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Leave those cakes alone. They’re for the guests.</span><span class="colloexa"><span class="collo">leave well (enough) alone</span><span class="gloss"><span class="neutral"> (=</span>not change something that is satisfactory<span class="neutral">)</span></span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-001630463"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001630463').play();"><img src="LDOCE6_img_spkr_g.png"/></a> In economic matters, they should leave well alone.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s14"></a><span class="sense newline"><span class="sensenum">10</span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s15"></a><span class="lexunit">be yours/hers/his etc alone</span><span class="def"> used to emphasize that something belongs to someone</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-001630468"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-001630468').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The responsibility is yours and yours alone.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s16"></a><span class="sense newline"><span class="sensenum">11</span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s17"></a><span class="lexunit">not be alone in (doing) something</span><span class="def"> to not be the only person to do something</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-000937234"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-000937234').play();"><img src="LDOCE6_img_spkr_g.png"/></a> You’re not alone in feeling upset, believe me.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s18"></a><span class="sense newline"><span class="sensenum">12</span><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s19"></a><span class="lexunit">stand alone</span><span class="subsense"> <span class="sensenum">a)</span><span class="def">to be strong and independent</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001630474"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001630474').play();"><img src="LDOCE6_img_spkr_g.png"/></a> the courage to stand alone</span></span><span class="subsense"> <span class="sensenum">b)</span><span class="def">to be at a distance from other objects or buildings</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001630477"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001630477').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The house stood alone at the end of the road.</span></span></span><span class="tail"><a id="6504937708254a64972bf4d4ccdfcc9d_alone_s20"></a><span class="thesbox"><span class="heading">THESAURUS</span><span class="heading">– Meaning 4</span><span class="section last"><span class="exponent"><span class="exp display">on your own/by yourself</span><span class="def"> without help from anyone else</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I can’t move the bed on my own.</span><span class="example"><span class="neutral">· </span>See if you can work out the answer by yourself.</span><span class="thespropform">all on your own/all by yourself<span class="gloss"><span class="neutral"> (=</span>used when you feel impressed or feel sympathy for someone<span class="neutral">)</span></span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Did you paint this picture all on your own?</span><span class="example"><span class="neutral">· </span>It’s not fair that she does everything all by herself.</span></span><span class="exponent"><span class="exp display">alone</span><span class="def"> without any help from anyone else. <span class="defbold">Alone</span> is more formal and less common than <span class="defbold">on your own</span> or <span class="defbold">by yourself</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police believe the killer acted alone.</span></span><span class="exponent"><span class="exp display">independently</span><span class="def"> without asking for help or advice from anyone</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Students are taught to work independently.</span><span class="example"><span class="neutral">· </span>One day she will have to live independently without the support of her family.</span></span><span class="exponent"><span class="exp display">unaided</span><span class="def"> without the help of anyone or anything – used especially about people who are very weak, ill etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He is unable to breathe unaided.</span><span class="example"><span class="neutral">· </span>After treatment, he was able to go up and down stairs unaided.</span></span><span class="exponent"><span class="exp display">single-handedly</span><span class="def"> doing something difficult or impressive without help from anyone else</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She single-handedly reformed the entire system.</span></span><span class="exponent"><span class="exp display">solo</span><span class="def"> by one person, not a group</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Lindbergh’s solo flight across the Atlantic Ocean</span><span class="example"><span class="neutral">· </span>She is planning to release a solo album.</span><span class="example"><span class="neutral">· </span>He sang and played the song solo.</span></span><span class="exponent"><span class="exp display">lone</span><span class="pos"> adjective</span><span class="gram"> [only before noun]</span><span class="def"> doing something alone – used especially in the following phrases</span><span class="thespropform"><span class="neutral">: </span>a lone gunman</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police say the attack was carried out by a <span class="colloinexa">lone gunman</span>.</span><span class="example"><span class="neutral">· </span>The government should do more to help <span class="colloinexa">lone parents</span> <span class="gloss"><span class="neutral"> (=</span>who raise a child alone<span class="neutral">)</span></span>.</span></span><span class="exponent"><span class="exp display">self-made</span><span class="pos"> adjective</span><span class="def"> someone who has become very rich or successful without help from anyone else</span><span class="thespropform"><span class="neutral">: </span>a self-made man/millionaire/businessman etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Like most self-made men, he was extremely self-confident.</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_1_alone0205" preload="none" src="LDOCE6_hwd_bre_1_alone0205.mp3"></audio><audio id="LDOCE6_hwd_ame_d_alone" preload="none" src="LDOCE6_hwd_ame_d_alone.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-000937203" preload="none" src="LDOCE6_exa_bre_0_p008-000937203.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001630417" preload="none" src="LDOCE6_exa_bre_0_p008-001630417.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-000937207" preload="none" src="LDOCE6_exa_bre_8_p008-000937207.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-000937214" preload="none" src="LDOCE6_exa_bre_a_p008-000937214.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001989770" preload="none" src="LDOCE6_exa_bre_1_p008-001989770.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001630430" preload="none" src="LDOCE6_exa_bre_f_p008-001630430.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001630463" preload="none" src="LDOCE6_exa_bre_0_p008-001630463.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001796295" preload="none" src="LDOCE6_exa_bre_3_p008-001796295.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001630437" preload="none" src="LDOCE6_exa_bre_3_p008-001630437.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-000937229" preload="none" src="LDOCE6_exa_bre_3_p008-000937229.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-000937224" preload="none" src="LDOCE6_exa_bre_f_p008-000937224.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001630442" preload="none" src="LDOCE6_exa_bre_9_p008-001630442.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001630450" preload="none" src="LDOCE6_exa_bre_5_p008-001630450.mp3"></audio><audio id="LDOCE6_exa_bre_d_p008-001630458" preload="none" src="LDOCE6_exa_bre_d_p008-001630458.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-001630468" preload="none" src="LDOCE6_exa_bre_a_p008-001630468.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-000937234" preload="none" src="LDOCE6_exa_bre_b_p008-000937234.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001630474" preload="none" src="LDOCE6_exa_bre_8_p008-001630474.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001630477" preload="none" src="LDOCE6_exa_bre_f_p008-001630477.mp3"></audio>`;
