var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_arrest_1"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_arrest_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_arrest_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_arrest_1"></a><span class="entry"><span class="entryhead"><span class="hwd">arrest</span><span class="hyphenation frequent">ar<span class="hs0"></span>rest</span><span class="homnum frequent">1</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">əˈrest</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S3</span> <span class="freq">W2</span><span class="pos"> verb</span><span class="gram"> [transitive]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_7_arrest_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_2_arrest1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">arrest</span><span class="homnum">1</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1300-1400</span> <span class="etymlang">Old French</span> <span class="etymorigin">arester</span> <span class="etymtran"> ‘to rest, arrest’</span>, from <span class="etymlang">Vulgar Latin</span> <span class="etymorigin">arrestare</span>, from <span class="etymlang">Latin</span> <span class="etymorigin">ad-</span> <span class="etymtran"> ‘to’</span> + <span class="etymorigin">restare</span> <span class="etymtran"> ‘to remain, rest’</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">arrest</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">arrest</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">arrests</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">arrested</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">arrested</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">arrested</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">arrested</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">arrest</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">arrested</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">arresting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">arresting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">arresting</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">arresting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">arresting</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">arresting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">arresting</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">arresting</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">arresting</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">arresting</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>Dwayne has been <span class="nodeword">arrested</span> for drunk driving again.</li><li>Nine men were <span class="nodeword">arrested</span> in drug raids Saturday.</li><li>Police <span class="nodeword">arrested</span> 26 demonstrators.</li><li>Powerful drugs are used to <span class="nodeword">arrest</span> the spread of the disease.</li><li>Smoking at an early age is thought to <span class="nodeword">arrest</span> growth in children.</li><li>Wallace was <span class="nodeword">arrested</span> for assault.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>A woman had been <span class="nodeword">arrested</span> for soliciting.</li><li>Accountant Freeman testified that Simpson has signed 19 contracts since he was <span class="nodeword">arrested</span> on double homicide charges.</li><li>At least 15 people were <span class="nodeword">arrested</span> as clashes developed in the Ravenscliffe and Holme Wood areas.</li><li>He had been <span class="nodeword">arrested</span> trying to steal lead from the roof of a church.</li><li>However, student demonstrations erupted at Bangkok's Ramkhamhaeng University on Feb. 25, and 15 people were <span class="nodeword">arrested</span> for illegal assembly.</li><li>On June 3 between six and eight members of left-wing parties were reported to have been <span class="nodeword">arrested</span>.</li><li>Reportedly, two Houston police officers <span class="nodeword">arrested</span> a black woman for being drunk and disorderly.</li><li>The suspects were <span class="nodeword">arrested</span> after questioning Thursday morning, the chief said, but have been released pending court appearances.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">catch</span></span><div class="content"><span class="def"> to stop someone who is trying to escape, especially by running after them and then holding them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He raced after her, but he couldn’t catch her.</span><span class="example"><span class="neutral">· </span>The police caught the bank robbers after a car chase through the city.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">arrest</span></span><div class="content"><span class="def"> if the police arrest someone, they take him or her to a police station because they think that person has done something illegal</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Wayne was arrested for dangerous driving.</span><span class="example"><span class="neutral">· </span>The police arrested him and charged him with murder.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">apprehend</span></span><div class="content"><span class="registerlab"> formal</span><span class="def"> if the police apprehend someone they think has done something illegal, they catch him or her</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The two men were later apprehended after they robbed another store.</span><span class="example"><span class="neutral">· </span>The killers were never apprehended.</span><span class="example"><span class="neutral">· </span>All of the kidnappers were apprehended and convicted.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">capture</span></span><div class="content"><span class="def"> to catch an enemy or a criminal in order to keep them as a prisoner</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The French king was captured by the English at the Battle of Poitiers in 1356.</span><span class="example"><span class="neutral">· </span>The gunmen were finally captured after a shoot-out with the police.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">take somebody prisoner</span></span><div class="content"><span class="def"> to catch someone, especially in a war, in order to keep them as a prisoner</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>350 soldiers were killed and another 300 taken prisoner.</span><span class="example"><span class="neutral">· </span>Ellison was taken prisoner by the Germans during the retreat to Dunkirk.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">trap</span></span><div class="content"><span class="def"> to make someone go to a place from which they cannot escape, especially by using your skill and intelligence</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police trapped the man inside a bar on the city’s southside.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">corner</span></span><div class="content"><span class="def"> to force someone into a place from which they cannot escape</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was cornered outside the school by three gang members.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section last"><span class="secheading">when the police catch a criminal</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">catch</span></span><div class="content"> <span class="example"><span class="neutral">· </span>Police say they are determined to catch the killer.</span><span class="example"><span class="neutral">· </span>A lot of thieves never get caught.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">arrest</span></span><div class="content"><span class="def"> if a police officer arrests someone they catch them, tell them officially that they have done something illegal, and take them away</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police arrested twenty-six demonstrators.</span><span class="example"><span class="neutral">· </span>Nine men were arrested in drug raids, Saturday.</span><span class="thespropform">arrest somebody for something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Wallace was arrested for assault.</span><span class="example"><span class="neutral">· </span>Dwayne has been arrested for drunk driving again.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">get</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially British</span><span class="def"> to catch and punish someone for something illegal they have done</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They still haven't got the man who did it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">nail</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> to catch someone and prove that they are guilty of a crime</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police use radar to nail speeding drivers.</span><span class="thespropform">nail somebody for something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Myers was nailed for selling marijuana.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">take somebody into custody</span></span><div class="content"><span class="def"> if the police take someone into custody , they take that person and put them in prison until they appear in court, because they think the person is guilty of a crime</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Officers took three suspects into custody Friday morning.</span><span class="example"><span class="neutral">· </span>As soon as the plane landed, the men were taken into custody by waiting FBI agents.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">got arrested</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001664299').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I <span class="colloinexa">got arrested</span> for careless driving.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">arrest somebody in connection with something</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001664305').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Five youths were arrested in connection with the attack.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">arrest somebody on charges/suspicion of (doing) something</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001664308').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He was arrested on suspicion of supplying drugs.</span></div></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">arbitrary arrests</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001676301').play();"><img src="LDOCE6_img_spkr_g.png"/></a> the <span class="colloinexa">arbitrary arrests</span> of political opponents</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">cardiac arrest/failure</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>when the heart stops working<span class="neutral">)</span></span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">made a citizen’s arrest</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001694061').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Brown <span class="colloinexa">made a citizen’s arrest</span> when a youth attempted to rob an elderly woman.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the police arrest somebody/make an arrest</span></span><div class="content"><span class="example"><span class="neutral">· </span>The police arrested Mr Fox as he tried to leave the country.</span><span class="example"><span class="neutral">· </span>Officer Singer said the police have made no arrests in the robbery.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">risk being seen/caught/arrested etc</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-000755429').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Workers who broke the strike risked being attacked when they left the factory.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">be arrested/imprisoned/shot etc as a spy</span></span><div class="content"><span class="example"><span class="neutral">· </span>Anyone caught working with the Resistance was shot as a spy.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">arrest/detain a suspect</span></span><div class="content"><span class="example"><span class="neutral">· </span>Detectives arrested the suspect after a five-day undercover operation.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section last"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">charge</span></span><div class="content"><span class="example"><span class="neutral">· </span>In the first few hours after the verdict, 60 people were <span class="colloinexa">arrested</span> on <span class="colloinexa">charges</span> of disorderly conduct.</span><span class="example"><span class="neutral">· </span>Goldsmith, for example, paroled in 1987, was <span class="colloinexa">arrested</span> on the firearms <span class="colloinexa">charge</span> in 1992 and convicted in 1993.</span><span class="example"><span class="neutral">· </span>The company's former vice-president, Sadamu Takagaki, was also <span class="colloinexa">arrested</span> on similar <span class="colloinexa">charges</span>.</span><span class="example"><span class="neutral">· </span>Accountant Freeman testified that Simpson has signed 19 contracts since he was <span class="colloinexa">arrested</span> on double homicide <span class="colloinexa">charges</span>.</span><span class="example"><span class="neutral">· </span>Soul was <span class="colloinexa">arrested</span> on a <span class="colloinexa">charge</span> of assault and spousal abuse and led away in handcuffs.</span><span class="example"><span class="neutral">· </span>Pappas had been prepared to testify against Turner after he was <span class="colloinexa">arrested</span> on drug <span class="colloinexa">charges</span>.</span><span class="example"><span class="neutral">· </span>That report showed there were more suspects <span class="colloinexa">arrested</span> in 1999 on <span class="colloinexa">charges</span> involving marijuana than for powder or crack cocaine.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">connection</span></span><div class="content"><span class="example"><span class="neutral">· </span>Profeto was <span class="colloinexa">arrested</span> in <span class="colloinexa">connection</span> only with the second killing but detectives said a link would soon be made.</span><span class="example"><span class="neutral">· </span>At least 42 people were <span class="colloinexa">arrested</span> in <span class="colloinexa">connection</span> with the caches, said Gen.</span><span class="example"><span class="neutral">· </span>A 40-year-old Liverpool man was <span class="colloinexa">arrested</span> in <span class="colloinexa">connection</span> with the assault.</span><span class="example"><span class="neutral">· </span>Some 40 people have since been <span class="colloinexa">arrested</span> in <span class="colloinexa">connection</span> with the case, and they include police and lower-level state officials.</span><span class="example"><span class="neutral">· </span>Six men have been <span class="colloinexa">arrested</span> in <span class="colloinexa">connection</span> with the violence.</span><span class="example"><span class="neutral">· </span>Davis was <span class="colloinexa">arrested</span> in <span class="colloinexa">connection</span> with the crime on Nov. 30, 1993.</span><span class="example"><span class="neutral">· </span>Eleven teenagers were <span class="colloinexa">arrested</span> in <span class="colloinexa">connection</span> with the fatal stabbing of 10-year-old Damilola Taylor.</span><span class="example"><span class="neutral">· </span>Elmer Lee Nance, a 63-year-old drifter, was <span class="colloinexa">arrested</span> last fall in <span class="colloinexa">connection</span> with the murder.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">crime</span></span><div class="content"><span class="example"><span class="neutral">· </span>He said a number of people had been <span class="colloinexa">arrested</span> in connection with the <span class="colloinexa">crime</span> but were later released without charge.</span><span class="example"><span class="neutral">· </span>She picked Graham out of a police lineup after he was <span class="colloinexa">arrested</span> for unrelated <span class="colloinexa">crimes</span>.</span><span class="example"><span class="neutral">· </span>In the end we were both <span class="colloinexa">arrested</span> for several <span class="colloinexa">crimes</span>.</span><span class="example"><span class="neutral">· </span>A disgruntled Gulf War veteran was <span class="colloinexa">arrested</span> for the <span class="colloinexa">crime</span>, which killed 169 people.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">drug</span></span><div class="content"><span class="example"><span class="neutral">· </span>Michael Stein has been <span class="colloinexa">arrested</span> for possession of <span class="colloinexa">drugs</span>.</span><span class="example"><span class="neutral">· </span>Pappas had been prepared to testify against Turner after he was <span class="colloinexa">arrested</span> on <span class="colloinexa">drug</span> charges.</span><span class="example"><span class="neutral">· </span>March 6: Four people <span class="colloinexa">arrested</span> and £1,000-worth of <span class="colloinexa">drugs</span> seized after a bus is stopped on the Holywood Road in Belfast.</span><span class="example"><span class="neutral">· </span>In August 1988, he was <span class="colloinexa">arrested</span> for possessing illegal <span class="colloinexa">drug</span> paraphernalia - syringes.</span><span class="example"><span class="neutral">· </span>Todd Bridges BABY-faced actor Todd Bridges was <span class="colloinexa">arrested</span> yesterday on <span class="colloinexa">drugs</span> and firearms charges.</span><span class="example"><span class="neutral">· </span>Meanwhile in San Diego, a society wife faces ruin when her millionaire husband is <span class="colloinexa">arrested</span> as a suspected <span class="colloinexa">drug</span> trafficker.</span><span class="example"><span class="neutral">· </span>Janet Janet was thirty-one when she was <span class="colloinexa">arrested</span> for possession of <span class="colloinexa">drugs</span>.</span><span class="example"><span class="neutral">· </span>Mohibullah was <span class="colloinexa">arrested</span> at Heathrow after <span class="colloinexa">drugs</span> were found smuggled in his suitcase.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">murder</span></span><div class="content"><span class="example"><span class="neutral">· </span>But if you were <span class="colloinexa">arrested</span> for <span class="colloinexa">murder</span> they would have to be very careful not to blow their cover.</span><span class="example"><span class="neutral">· </span>How else would we account for black men being disproportionately <span class="colloinexa">arrested</span> for robbery and <span class="colloinexa">murder</span>?</span><span class="example"><span class="neutral">· </span>Sacco, a factory worker, and Vanzetti, a fish pedlar, were <span class="colloinexa">arrested</span> for the <span class="colloinexa">murder</span>.</span><span class="example"><span class="neutral">· </span>He wanted to know whether Wickham had <span class="colloinexa">arrested</span> anyone for the <span class="colloinexa">murder</span> because until then he could not face going to work.</span><span class="example"><span class="neutral">· </span>But he realised they needed much more evidence before they could <span class="colloinexa">arrest</span> him for <span class="colloinexa">murder</span>.</span><span class="example"><span class="neutral">· </span>When Creed called, Jed was watching a news report about a vulture who'd just been <span class="colloinexa">arrested</span> on a <span class="colloinexa">murder</span> charge.</span><span class="example"><span class="neutral">· </span>Man <span class="colloinexa">arrested</span> over schoolgirl <span class="colloinexa">murder</span> A man has been arrested in connection with the murder of the schoolgirl from Kent, Claire Tiltman.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">police</span></span><div class="content"><span class="example"><span class="neutral">· </span>And it was Justine that the <span class="colloinexa">police</span> had <span class="colloinexa">arrested</span>.</span><span class="example"><span class="neutral">· </span>Last week, <span class="colloinexa">police arrested</span> a motorist in Houston for driving a car with Republic of Texas license plates.</span><span class="example"><span class="neutral">· </span>It's a serious attack on a defenceless child. <span class="colloinexa">Police arrested</span> the baby's father at the scene.</span><span class="example"><span class="neutral">· </span>The local <span class="colloinexa">police</span> frequently <span class="colloinexa">arrested</span> students for exceeding the speed limit or other minor infractions of the law.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">police arrested</span> the offender who was tried and convicted of assault.</span><span class="example"><span class="neutral">· </span>When officers approached the car, both males fled. <span class="colloinexa">Police arrested</span> a male juvenile a short time later.</span><span class="example"><span class="neutral">· </span>The 2 had been watched by <span class="colloinexa">police</span> and were <span class="colloinexa">arrested</span> shortly afterwards at their homes in Gloucester and Ruscombe near Stroud.</span><span class="example"><span class="neutral">· </span>Brown wants state and federal officials to help <span class="colloinexa">police</span> by <span class="colloinexa">arresting</span> parole violators who are in the park.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">suspect</span></span><div class="content"><span class="example"><span class="neutral">· </span>This point is proved by the officer who <span class="colloinexa">arrests</span> the <span class="colloinexa">suspect</span> stating that the defendant in court is the person involved.</span><span class="example"><span class="neutral">· </span>It took nearly 18 years, thousands of hours and great expense for authorities to <span class="colloinexa">arrest</span> a <span class="colloinexa">suspect</span> in the Unabomber case.</span><span class="example"><span class="neutral">· </span>No group claimed responsibility but the police were reported to have <span class="colloinexa">arrested</span> three <span class="colloinexa">suspects</span>.</span><span class="example"><span class="neutral">· </span>Police recognized Moll and <span class="colloinexa">arrested</span> him as a <span class="colloinexa">suspect</span> in two grocery store robberies, also involving getaway cabs.</span><span class="example"><span class="neutral">· </span>Detectives <span class="colloinexa">arrested</span> the <span class="colloinexa">suspect</span> at his home in Toxteth after a lengthy cat-and-mouse game involving surveillance experts.</span><span class="example"><span class="neutral">· </span>Nor do they have the power to <span class="colloinexa">arrest</span> or detain <span class="colloinexa">suspects</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">suspicion</span></span><div class="content"><span class="example"><span class="neutral">· </span>Three men were <span class="colloinexa">arrested</span> on <span class="colloinexa">suspicion</span> of causing violent disorder and were released on police bail.</span><span class="example"><span class="neutral">· </span>A man found at the house, Ronald Lerma, 29, was <span class="colloinexa">arrested</span> on <span class="colloinexa">suspicion</span> of possessing a crack cocaine pipe.</span><span class="example"><span class="neutral">· </span>On Nov. 29 a 19-year-old skinhead was <span class="colloinexa">arrested</span> under <span class="colloinexa">suspicion</span> of arson in connection with the Mölln attack.</span><span class="example"><span class="neutral">· </span>Lombardy was stopped and <span class="colloinexa">arrested</span> on <span class="colloinexa">suspicion</span> of rape by force; rape with a foreign object and false imprisonment.</span><span class="example"><span class="neutral">· </span>Kedie was initially <span class="colloinexa">arrested</span> on <span class="colloinexa">suspicion</span> of supplying drugs when police discovered £2,000 in cash in his house.</span><span class="example"><span class="neutral">· </span>In August 1984, Benicia police <span class="colloinexa">arrested</span> him on <span class="colloinexa">suspicion</span> of molesting a 10-year-old girl.</span><span class="example"><span class="neutral">· </span>The following day Gary Burn was <span class="colloinexa">arrested</span> by police on <span class="colloinexa">suspicion</span> of murder.</span><span class="example"><span class="neutral">· </span>When police <span class="colloinexa">arrested</span> Nestor Padron on <span class="colloinexa">suspicion</span> of skimming meter receipts, he was carrying $ 850 in cash, Maher said.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">false imprisonment/arrest</span></span><div class="content"><ul class="exas"><li>An action of damages may be brought for <span class="nodeword">false</span> imprisonment. 3.</li><li>But she was spared from that ordeal when Anthony Bourgois pleaded guilty to charges of <span class="nodeword">false</span> imprisonment and carrying a knife.</li><li>He denies <span class="nodeword">false</span> imprisonment, assault and blackmail.</li><li>If anyone prevents you from doing so, you can sue for <span class="nodeword">false</span> imprisonment.</li><li>Lombardy was stopped and arrested on suspicion of rape by force; rape with a foreign object and <span class="nodeword">false</span> imprisonment.</li><li>The boy admitted <span class="nodeword">false</span> imprisonment and wounding.</li><li>The rest included allegations of wrongful arrest, <span class="nodeword">false</span> imprisonment and loss of property.</li><li>Was recently awarded £30,000 damages against Thames Valley Police for wrongful arrest and <span class="nodeword">false</span> imprisonment.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">be under house arrest</span></span><div class="content"><ul class="exas"><li>He, he was under <span class="nodeword">house</span> arrest.</li><li>The editor of the party newspaper is under <span class="nodeword">house</span> arrest for printing a report about tanks being moved out of Tirana.</li><li>We are not in darkest prison like our brothers and sisters in the flesh, but we are under <span class="nodeword">house</span> arrest.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">wrongful arrest/conviction/imprisonment/dismissal etc</span></span><div class="content"><ul class="exas"><li>All claims by the employee, whether they be for unfair dismissal, <span class="nodeword">wrongful</span> dismissal or redundancy are claims against the vendor.</li><li>Every <span class="nodeword">wrongful</span> imprisonment could lead to a civil lawsuit against the city.</li><li>He issued a writ claiming damages for <span class="nodeword">wrongful</span> dismissal.</li><li>Now he is being tried for kidnapping, <span class="nodeword">wrongful</span> imprisonment and bodily injury.</li><li>One point to clear up immediately is the widespread confusion between <span class="nodeword">wrongful</span> dismissal and unfair dismissal.</li><li>So the trial led to the <span class="nodeword">wrongful</span> conviction of Al-Megrahi and the final betrayal of the bereaved families.</li><li>The city is facing many claims for <span class="nodeword">wrongful</span> arrest, totalling millions of dollars.</li><li>What do you stand to gain in a <span class="nodeword">wrongful</span> dismissal case?</li></ul></div></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_arrest_1_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="imgholder"><img onclick="d8018d6852bc49e3b3e655364cf1439c.toggleImg(this)" src="LDOCE6_img_arrest.jpg"/></span><span class="def">if the police arrest someone, the person is taken to a <span class="nondv"><span class="refhwd">police station</span></span> because the police think they have done something illegal</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-000989142"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-000989142').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He was arrested and charged with murder.</span><span class="gramexa"><span class="propform">arrest somebody for something</span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-000989145"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000989145').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Her father was arrested for fraud.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001664299"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001664299').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I <span class="colloinexa">got arrested</span> for careless driving.</span><span class="colloexa"><span class="collo">arrest somebody in connection with something</span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001664305"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001664305').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Five youths were arrested in connection with the attack.</span></span><span class="colloexa"><span class="collo">arrest somebody on charges/suspicion of (doing) something</span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001664308"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001664308').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He was arrested on suspicion of supplying drugs.</span></span><span class="thesref"><span class="thesaurus">►</span> see <span class="thesaurus">thesaurus</span> at <a><span class="refhwd">catch</span><span class="refhomnum"></span></a></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_arrest_1_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="registerlab">formal</span><span class="def"> to stop something happening or to make it happen more slowly</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_ame_d_p032-000074883"><a onclick="document.getElementById('LDOCE6_exa_ame_d_p032-000074883').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The drug is used to arrest the spread of the disease.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_arrest_1_s3"></a><span class="sense newline"><span class="sensenum">3</span><span class="registerlab">literary</span><span class="def"> if something arrests you or arrests your attention, you notice it because it is interesting or unusual</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001664309"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001664309').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The mountains are the most arresting feature of the glen.</span></span><span class="tail"><span class="grambox"><span class="heading">GRAMMAR: Comparison</span><span class="compareword"><span class="exp display">arrest</span><span class="expl"><span class="neutral">• </span>The police <span class="expr">arrest</span> someone: <span class="example"><span class="neutral">· </span>The police arrested a man as he fled the scene.</span></span><span class="expl"><span class="neutral">• </span>The police <span class="expr">arrest</span> someone <span class="expr">for</span> a crime: <span class="example"><span class="neutral">· </span>He was arrested for robbery.</span></span><span class="expl"><span class="neutral">• </span>The police <span class="expr">arrest</span> someone <span class="expr">for doing</span> something: <span class="example"><span class="neutral">· </span>They arrested him for possessing an illegal weapon.</span></span></span><span class="compareword last"><span class="exp display">catch</span><span class="expl"><span class="neutral">• </span>The police <span class="expr">catch</span> someone: <span class="example"><span class="neutral">· </span>The police believe they have caught the killer.</span></span><span class="expl"><span class="neutral">• </span>You <span class="expr">catch</span> someone <span class="expr">doing</span> something: <span class="example"><span class="neutral">· </span>I caught him stealing money from my purse.</span></span></span></span></span></span></div><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_arrest_2"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_arrest_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_arrest_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_arrest_2"></a><span class="entry"><span class="entryhead"><span class="hwd">arrest</span><span class="hyphenation frequent">arrest</span><span class="homnum frequent">2</span><span class="level tooltip"> ●●●</span> <span class="freq">S3</span> <span class="freq">W2</span><span class="pos"> noun</span><span class="gram"> [countable, uncountable]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_7_arrest_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_2_arrest1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>Guzman, twenty-five, was placed under <span class="nodeword">arrest</span> at his parents' home Friday.</li><li>Kramer's confession led to the <span class="nodeword">arrests</span> of six others.</li><li>Police made several <span class="nodeword">arrests</span> over the weekend in connection with last year's courthouse bombing.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>Bonnie Hanssen has been in seclusion since the <span class="nodeword">arrest</span> of her husband.</li><li>In 1993 Operation Gangbusters led to 43 <span class="nodeword">arrests</span>.</li><li>Last year, <span class="nodeword">arrests</span> jumped to 51, 000.</li><li>Mr Emery reopened his store one day after his <span class="nodeword">arrest</span>, and said he will sell marijuana seeds by mail order.</li><li>Police have issued an <span class="nodeword">arrest</span> warrant for Mr de Michelis's former secretary, Barbara Ceolin.</li><li>Suppose it offers a large reward for some one's <span class="nodeword">arrest</span>.</li><li>The police later claimed that he had resisted <span class="nodeword">arrest</span>.</li><li>Toplis was eventually cornered by police, I believe in Cumberland, and the murderer was shot dead whilst resisting <span class="nodeword">arrest</span>.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">when you are forced to stay somewhere</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be in custody</span></span><div class="content"><span class="def"> if someone who the police think is guilty of a crime is in custody , they are kept in prison until it is time for them to be judged in a court</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Parry was in custody for a month before being released.</span><span class="thespropform">be in police custody</span><span class="gloss"><span class="neutral"> (=</span>in a police station<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The activist died under suspicious circumstances while he was in police custody.</span><span class="thespropform">be remanded in custody</span><span class="gloss"><span class="neutral"> (=</span>be sent back to prison from a court until your trial<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Three men and a woman have been remanded in custody on fraud charges.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be under arrest</span></span><div class="content"><span class="def"> if someone is under arrest , the police are keeping them guarded because they think they are guilty of a crime</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He's under arrest and may only be seen by his lawyer.</span><span class="thespropform">be under house arrest</span><span class="gloss"><span class="neutral"> (=</span>not be allowed to leave your home<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The opposition leader has been under house arrest for the past few months.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">captivity</span></span><div class="content"><span class="def"> when someone is being forced to stay in a place where they do not want to be - use this when this situation is illegal or wrong</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In his autobiography, Mandela describes his life during captivity.</span><span class="thespropform">in captivity</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The hostages are now entering their fourth week in captivity.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">imprisonment</span></span><div class="content"><span class="def"> when someone is being kept as a prisoner, especially as a punishment for a crime</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Johnson was sentenced to three years' imprisonment for causing a riot.</span><span class="example"><span class="neutral">· </span>The offence is punishable by either a fine or imprisonment.</span><span class="thespropform">life imprisonment</span><span class="gloss"><span class="neutral"> (=</span>for the rest of someone's life<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Garrison faces life imprisonment for his role in the killings.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">detention</span></span><div class="content"><span class="def"> when someone is being kept somewhere by the police, usually because they think that person has done something illegal and they want to ask them questions</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>By the 1920s the average period of detention for new immigrants lasted two weeks.</span><span class="example"><span class="neutral">· </span>A dissident, recently released from detention, gave a press conference in the capital today.</span><span class="thespropform">in detention</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>About a dozen people remain in detention without trial.</span><span class="thespropform">take somebody into detention</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They were taken into detention two weeks ago and still are not allowed visitors.</span><span class="thespropform">detention center</span><span class="gloss"><span class="neutral"> (=</span>a place where someone is kept by the police<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There was another riot at the men's detention center yesterday.</span><span class="thespropform">juvenile detention</span><span class="gloss"><span class="neutral"> (=</span>a place that is like a prison for young people<span class="neutral">)</span></span><span class="neutral"> </span><span class="registerlab">American</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was in and out of juvenile detention for drugs charges as a teenager.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">confinement</span></span><div class="content"><span class="def"> when someone is being kept in a room, prison etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was sentenced to 5 months of home confinement for the crime.</span><span class="thespropform">solitary confinement</span><span class="gloss"><span class="neutral"> (=</span>the state of being kept completely alone<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Prisoners are punished by being put in solitary confinement.</span></div></span></span><span class="section last"><span class="secheading">to be kept in a place by the police</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be in custody</span></span><div class="content"><span class="def"> if someone who the police think is guilty of a crime is in custody , they are kept in prison until it is time for them to be judged in a law court</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The twenty-seven militants now in custody were arrested in raids last month.</span><span class="thespropform">be in police custody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>An inquiry has been launched following the death of a man in police custody.</span><span class="thespropform">be held in custody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A woman is being held in custody in connection with the murder.</span><span class="thespropform">be remanded in custody</span><span class="neutral"> </span><span class="registerlab">British</span><span class="gloss"><span class="neutral"> (=</span>be sent back to prison from a court until your trial<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Naylor was remanded in custody by Huyton magistrates until June 17th.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be under arrest</span></span><div class="content"><span class="def"> if someone is under arrest , the police are keeping them guarded because they think they are guilty of a crime</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police confirmed last night that Mr Joshi is under arrest.</span><span class="thespropform">be under arrest for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm afraid your son is under arrest for theft.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be detained</span></span><div class="content"><span class="def"> to be kept somewhere by the police or army so that you cannot leave, and especially so that they can ask you questions</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>On Tuesday last week, Finnegan was detained and questioned by fraud squad officers.</span><span class="thespropform">be detained for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was detained for questioning about the terrorist attacks.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">made ... arrests</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-001664065').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The police <span class="colloinexa">made</span> several <span class="colloinexa">arrests</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">under arrest</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001664070').play();"><img src="LDOCE6_img_spkr_g.png"/></a> A man is <span class="colloinexa">under arrest</span> <span class="collgloss"><span class="neutral"> (=</span>the police are guarding him<span class="neutral">)</span></span> following the suspicious death of his wife.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">place/put somebody under arrest</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>arrest someone<span class="neutral">)</span></span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">wrongful arrest</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001664075').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He sued the police for <span class="colloinexa">wrongful arrest</span> <span class="collgloss"><span class="neutral"> (=</span>when someone who is not guilty is arrested<span class="neutral">)</span></span>.</span></div></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">arbitrary arrests</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001676301').play();"><img src="LDOCE6_img_spkr_g.png"/></a> the <span class="colloinexa">arbitrary arrests</span> of political opponents</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">cardiac arrest/failure</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>when the heart stops working<span class="neutral">)</span></span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">made a citizen’s arrest</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001694061').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Brown <span class="colloinexa">made a citizen’s arrest</span> when a youth attempted to rob an elderly woman.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the police arrest somebody/make an arrest</span></span><div class="content"><span class="example"><span class="neutral">· </span>The police arrested Mr Fox as he tried to leave the country.</span><span class="example"><span class="neutral">· </span>Officer Singer said the police have made no arrests in the robbery.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">risk being seen/caught/arrested etc</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-000755429').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Workers who broke the strike risked being attacked when they left the factory.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">be arrested/imprisoned/shot etc as a spy</span></span><div class="content"><span class="example"><span class="neutral">· </span>Anyone caught working with the Resistance was shot as a spy.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">arrest/detain a suspect</span></span><div class="content"><span class="example"><span class="neutral">· </span>Detectives arrested the suspect after a five-day undercover operation.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADJECTIVE</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">cardiac</span></span><div class="content"><span class="example"><span class="neutral">· </span>About 20% of patients recruited for this study were <span class="colloinexa">cardiac arrest</span> survivors.</span><span class="example"><span class="neutral">· </span>He went into <span class="colloinexa">cardiac arrest</span> and was brought back to life immediately after the initial shock.</span><span class="example"><span class="neutral">· </span>Hospital staff were puzzled after children collapsed with <span class="colloinexa">cardiac arrests</span> and respiratory attacks.</span><span class="example"><span class="neutral">· </span>He went into <span class="colloinexa">cardiac arrest</span> again in the ambulance and once more at the hospital.</span><span class="example"><span class="neutral">· </span>She then suffered a <span class="colloinexa">cardiac arrest</span> 32 hours after admission.</span><span class="example"><span class="neutral">· </span>She was by now in constant crisis; she was suffering repeated <span class="colloinexa">cardiac arrests</span>.</span><span class="example"><span class="neutral">· </span>Defibrillation Early defibrillation remains the most important determinant of survival in <span class="colloinexa">cardiac arrest</span> victims.</span><span class="example"><span class="neutral">· </span>According to legend, anyone who ever fired the weapon died of a broken heart or <span class="colloinexa">cardiac arrest</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">wrongful</span></span><div class="content"><span class="example"><span class="neutral">· </span>The rest included allegations of <span class="colloinexa">wrongful arrest</span>, false imprisonment and loss of property.</span><span class="example"><span class="neutral">· </span>The city is facing many claims for <span class="colloinexa">wrongful arrest</span>, totalling millions of dollars.</span><span class="example"><span class="neutral">· </span>Was recently awarded £30,000 damages against Thames Valley Police for <span class="colloinexa">wrongful arrest</span> and false imprisonment.</span><span class="example"><span class="neutral">· </span>Pending a successful appeal, Downing could claim for <span class="colloinexa">wrongful arrest</span> and 27 years unlawful detention.</span></div></span></span><span class="section"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">house</span></span><div class="content"><span class="example"><span class="neutral">· </span>He died under <span class="colloinexa">house arrest</span> in Salamanca, uncompromising to the end.</span><span class="example"><span class="neutral">· </span>Tried in Hanoi on charges of sedition, he died under <span class="colloinexa">house arrest</span> in Hue fifteen years later.</span><span class="example"><span class="neutral">· </span>They would be kept under <span class="colloinexa">house arrest</span> within a fort until the yasak payment season came round.</span><span class="example"><span class="neutral">· </span>He has grown reclusive in recent years, seemingly unaware that he is no longer under <span class="colloinexa">house arrest</span>.</span><span class="example"><span class="neutral">· </span>He was described as neither fit for questioning nor detention, but remains under <span class="colloinexa">house arrest</span>.</span><span class="example"><span class="neutral">· </span>Park then placed him under <span class="colloinexa">house arrest</span>, while his captors went free, and later imprisoned him for sedition.</span><span class="example"><span class="neutral">· </span>As punishment he received thirty days <span class="colloinexa">house arrest</span> and a two-ducat fine!</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">warrant</span></span><div class="content"><span class="example"><span class="neutral">· </span>Police have issued an <span class="colloinexa">arrest warrant</span> for Mr de Michelis's former secretary, Barbara Ceolin.</span><span class="example"><span class="neutral">· </span>Hall said, but an <span class="colloinexa">arrest warrant</span> was issued after Hall failed to appear in court on the charge.</span><span class="example"><span class="neutral">· </span>After an investigation, police put out an <span class="colloinexa">arrest warrant</span> on Mesa.</span><span class="example"><span class="neutral">· </span>Two suspected murderers have had international <span class="colloinexa">arrest warrants</span> issued against them and the Government's intention is to pursue those arrest warrants.</span><span class="example"><span class="neutral">· </span>The object is to gather enough evidence and sift through all the forensics information needed to write an acceptable <span class="colloinexa">arrest warrant</span>.</span><span class="example"><span class="neutral">· </span>They also helped uncover a drug manufacturing lab and handled an <span class="colloinexa">arrest warrant</span>, according to the State Attorney General's Office.</span></div></span></span><span class="section last"><span class="secheading">VERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">make</span></span><div class="content"><span class="example"><span class="neutral">· </span>Despite limitations on their deployment, they kept <span class="colloinexa">making arrests</span>.</span><span class="example"><span class="neutral">· </span>Something had to be done, and Daley gave the orders: the police were told to start <span class="colloinexa">making arrests</span>.</span><span class="example"><span class="neutral">· </span>He hadn't <span class="colloinexa">made</span> the <span class="colloinexa">arrest</span> himself, but had been called in as back up.</span><span class="example"><span class="neutral">· </span>I gulped at the prospect of <span class="colloinexa">making</span> an <span class="colloinexa">arrest</span>.</span><span class="example"><span class="neutral">· </span>Nobody wants to fight and the police only <span class="colloinexa">make arrests</span> if we telephone them ourselves to report trouble.</span><span class="example"><span class="neutral">· </span>Attorney General Brewster <span class="colloinexa">made</span> an attempt to punish the violators by ordering the local district attorney to make <span class="colloinexa">arrests</span>.</span><span class="example"><span class="neutral">· </span>We will introduce a new police power to <span class="colloinexa">make</span> an <span class="colloinexa">arrest</span> for breach of police bail.</span><span class="example"><span class="neutral">· </span>Not only do the police fail to <span class="colloinexa">make arrests</span>, sometimes they are the ones committing the crimes.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">resist</span></span><div class="content"><span class="example"><span class="neutral">· </span>Is it a crime to <span class="colloinexa">resist</span> such an <span class="colloinexa">arrest</span>?</span><span class="example"><span class="neutral">· </span>He was killed by a shotgun blast while supposedly <span class="colloinexa">resisting arrest</span>.</span><span class="example"><span class="neutral">· </span>The accused was charged with one form of s.18, wounding with intent to <span class="colloinexa">resist arrest</span>.</span><span class="example"><span class="neutral">· </span>Twenty of the protesters were also cited for <span class="colloinexa">resisting arrest</span>.</span><span class="example"><span class="neutral">· </span>Other charges included <span class="colloinexa">resisting arrest</span> and loitering.</span><span class="example"><span class="neutral">· </span>Quoting a local source, Reuter said that the suspect and his family <span class="colloinexa">resisted arrest</span>.</span><span class="example"><span class="neutral">· </span>However, by then feelings were running so high Mr Pennell <span class="colloinexa">resisted arrest</span>.</span><span class="example"><span class="neutral">· </span>The incident began early Sunday when San Jose police began chasing the man for <span class="colloinexa">resisting arrest</span> and drug use.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">false imprisonment/arrest</span></span><div class="content"><ul class="exas"><li>An action of damages may be brought for <span class="nodeword">false</span> imprisonment. 3.</li><li>But she was spared from that ordeal when Anthony Bourgois pleaded guilty to charges of <span class="nodeword">false</span> imprisonment and carrying a knife.</li><li>He denies <span class="nodeword">false</span> imprisonment, assault and blackmail.</li><li>If anyone prevents you from doing so, you can sue for <span class="nodeword">false</span> imprisonment.</li><li>Lombardy was stopped and arrested on suspicion of rape by force; rape with a foreign object and <span class="nodeword">false</span> imprisonment.</li><li>The boy admitted <span class="nodeword">false</span> imprisonment and wounding.</li><li>The rest included allegations of wrongful arrest, <span class="nodeword">false</span> imprisonment and loss of property.</li><li>Was recently awarded £30,000 damages against Thames Valley Police for wrongful arrest and <span class="nodeword">false</span> imprisonment.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">be under house arrest</span></span><div class="content"><ul class="exas"><li>He, he was under <span class="nodeword">house</span> arrest.</li><li>The editor of the party newspaper is under <span class="nodeword">house</span> arrest for printing a report about tanks being moved out of Tirana.</li><li>We are not in darkest prison like our brothers and sisters in the flesh, but we are under <span class="nodeword">house</span> arrest.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">wrongful arrest/conviction/imprisonment/dismissal etc</span></span><div class="content"><ul class="exas"><li>All claims by the employee, whether they be for unfair dismissal, <span class="nodeword">wrongful</span> dismissal or redundancy are claims against the vendor.</li><li>Every <span class="nodeword">wrongful</span> imprisonment could lead to a civil lawsuit against the city.</li><li>He issued a writ claiming damages for <span class="nodeword">wrongful</span> dismissal.</li><li>Now he is being tried for kidnapping, <span class="nodeword">wrongful</span> imprisonment and bodily injury.</li><li>One point to clear up immediately is the widespread confusion between <span class="nodeword">wrongful</span> dismissal and unfair dismissal.</li><li>So the trial led to the <span class="nodeword">wrongful</span> conviction of Al-Megrahi and the final betrayal of the bereaved families.</li><li>The city is facing many claims for <span class="nodeword">wrongful</span> arrest, totalling millions of dollars.</li><li>What do you stand to gain in a <span class="nodeword">wrongful</span> dismissal case?</li></ul></div></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_arrest_2_s1"></a><span class="sense"> <span class="def">when the police take someone away and guard them because they may have done something illegal</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-001664065"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-001664065').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The police <span class="colloinexa">made</span> several <span class="colloinexa">arrests</span>.</span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-001664070"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001664070').play();"><img src="LDOCE6_img_spkr_g.png"/></a> A man is <span class="colloinexa">under arrest</span> <span class="gloss"><span class="neutral"> (=</span>the police are guarding him<span class="neutral">)</span></span> following the suspicious death of his wife.</span><span class="colloexa"><span class="collo">place/put somebody under arrest</span><span class="gloss"><span class="neutral"> (=</span>arrest someone<span class="neutral">)</span></span></span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-001664075"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001664075').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He sued the police for <span class="colloinexa">wrongful arrest</span> <span class="gloss"><span class="neutral"> (=</span>when someone who is not guilty is arrested<span class="neutral">)</span></span>.</span></span><span class="tail"><span class="crossref"><span class="neutral"> →</span><a> <span class="refhwd">house arrest</span></a><span class="reflex"><span class="neutral">, → </span>cardiac arrest</span><a> <span class="refhwd"><span class="neutral">at </span>cardiac</span></a></span></span></span></div><audio id="LDOCE6_hwd_bre_7_arrest_n0205" preload="none" src="LDOCE6_hwd_bre_7_arrest_n0205.mp3"></audio><audio id="LDOCE6_hwd_ame_2_arrest1" preload="none" src="LDOCE6_hwd_ame_2_arrest1.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001664299" preload="none" src="LDOCE6_exa_bre_4_p008-001664299.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001664305" preload="none" src="LDOCE6_exa_bre_2_p008-001664305.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001664308" preload="none" src="LDOCE6_exa_bre_2_p008-001664308.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001676301" preload="none" src="LDOCE6_exa_bre_f_p008-001676301.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001694061" preload="none" src="LDOCE6_exa_bre_0_p008-001694061.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-000755429" preload="none" src="LDOCE6_exa_bre_3_p008-000755429.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-000989142" preload="none" src="LDOCE6_exa_bre_7_p008-000989142.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-000989145" preload="none" src="LDOCE6_exa_bre_f_p008-000989145.mp3"></audio><audio id="LDOCE6_exa_ame_d_p032-000074883" preload="none" src="LDOCE6_exa_ame_d_p032-000074883.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001664309" preload="none" src="LDOCE6_exa_bre_2_p008-001664309.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-001664065" preload="none" src="LDOCE6_exa_bre_6_p008-001664065.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001664070" preload="none" src="LDOCE6_exa_bre_0_p008-001664070.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001664075" preload="none" src="LDOCE6_exa_bre_e_p008-001664075.mp3"></audio>`;
