var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_bet_1"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_bet_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_bet_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1"></a><span class="entry"><span class="entryhead"><span class="hwd">bet</span><span class="hyphenation frequent">bet</span><span class="homnum frequent">1</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">bet</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S1</span><span class="pos"> verb</span><span class="inflections"><span class="neutral"> (</span><span class="ptandpp"><span class="infllab">past tense and past participle </span>bet</span><span class="prespart"><span class="neutral">, </span><span class="infllab">present participle </span>betting</span><span class="neutral">)</span></span> <a onclick="document.getElementById('LDOCE6_hwd_bre_4_bet_v0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_4_l3bet').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">bet</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">bet</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">bets</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">bet</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">bet</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">bet</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">bet</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">bet</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">bet</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">betting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">betting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">betting</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">betting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">betting</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">betting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">betting</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">betting</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">betting</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">betting</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>I'll <span class="nodeword">bet</span> that made her mad!</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>Albert bowed over the girl's hand - Caro would <span class="nodeword">bet</span> it wasn't his wife.</li><li>Crumwallis - I would <span class="nodeword">bet</span> - was sexually attracted, whether he understood that himself, or not.</li><li>Fun and exciting, you <span class="nodeword">bet</span>, but is this the way to the Super Bowl?</li><li>I <span class="nodeword">bet</span> he had the fish cakes.</li><li>I <span class="nodeword">bet</span> you taste real good Ram.</li><li>It became an offence for anyone in charge of children to allow them to <span class="nodeword">bet</span> in public places or to enter brothels.</li><li>Should your management <span class="nodeword">bet</span> the company on a high-risk business strategy?</li><li>Yeah, he says, I <span class="nodeword">bet</span> you a dollar.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">gamble</span></span><div class="content"><span class="def"> to try to win money, for example by playing cards or guessing which horse will win a race</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Eddie loved to gamble, and would spend most evenings at the roulette table.</span><span class="example"><span class="neutral">· </span>A growing number of people are gambling online.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">bet/have a bet</span></span><div class="content"><span class="def"> to try to win money by guessing who will win a race or game</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He liked to smoke, drink, and bet on horses.</span><span class="example"><span class="neutral">· </span>Jerry bet $1000 on the game.</span><span class="example"><span class="neutral">· </span>We had a bet on the Irish team to win.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put £10/$20 etc on something</span></span><div class="content"><span class="def"> to gamble £10, $20 etc on the horse or team that you think will win a race or competition</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I put $100 on the Cowboys to win.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">have a flutter</span></span><div class="content"><span class="geo"> British English</span><span class="registerlab"> informal</span><span class="def"> to gamble a small amount of money, especially on the result of a horse race – used especially when someone does not gamble very often</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I’m not a heavy gambler, but I like to have a flutter from time to time.</span><span class="example"><span class="neutral">· </span>I had a little flutter on the Grand National and won £5.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">play for money</span></span><div class="content"><span class="def"> to gamble money on the result of a game which you are playing</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>‘Are we playing for money here, gentlemen?’ he said as he approached the pool table.</span><span class="example"><span class="neutral">· </span>You’re allowed to play cards in the bar, but not for money.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">back</span></span><div class="content"><span class="def"> to gamble money on a particular horse, team etc that you think will win a race or competition</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>When I back a horse, it always seems to finish second.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">what you say when you guess something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">my guess is</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are telling someone what you think has probably happened or will probably happen</span><span class="thespropform"><span class="neutral">: </span>my guess is (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My guess is that they've been delayed in a traffic jam.</span><span class="example"><span class="neutral">· </span>My guess is she'll move back to the States after the divorce.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I bet</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are fairly sure that something is true or something is happening, although you cannot prove this</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He'll be really pleased to see you, I bet!</span><span class="thespropform">I bet (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Come and sit down, I bet you're exhausted.</span><span class="example"><span class="neutral">· </span>I bet that the meeting will be cancelled again.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">off the top of my head</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are guessing a number or amount and you have not checked it exactly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Off the top of my head, I think that the figure is about 25%.</span><span class="example"><span class="neutral">· </span>"What's the painting worth?" "Three million or so," he said, off the top of his head.</span></div></span></span><span class="section last"><span class="secheading">when you feel sure about something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">sure</span></span><div class="content"><span class="def"> if you are sure about something, you believe that it is definitely true or correct</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>'The car was a BMW' 'Are you sure?'</span><span class="thespropform">sure (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm surprised she isn't here - I was sure she would come.</span><span class="example"><span class="neutral">· </span>Are you quite sure that he understood your instructions?</span><span class="thespropform">sure about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I think children are influenced by these films, but it's impossible to be sure about this.</span><span class="thespropform">sure of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You need to be sure of your facts before making any accusations.</span><span class="thespropform">pretty sure</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm pretty sure Barbara still works here.</span><span class="thespropform">absolutely sure</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We do not believe this is the body of the missing girl, but we have to check to be absolutely sure.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">certain</span></span><div class="content"><span class="def"> completely sure that something is true</span><span class="thespropform"><span class="neutral">: </span>certain (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Are you certain you didn't leave your keys at home?</span><span class="thespropform">certain about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was certain about one thing - she would come back one day.</span><span class="thespropform">certain of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She won't let you borrow the car - I'm certain of that.</span><span class="thespropform">absolutely certain</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Don't stop looking until you're absolutely certain you've found the place you want.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">positive</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> completely sure that something is true - use this especially when other people are saying it might not be true</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Are you sure you locked the door?" "Yes, I'm positive."</span><span class="thespropform">positive (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She said she was positive the exam was next Tuesday.</span><span class="thespropform">absolutely positive</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm absolutely positive I haven't made a mistake.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">convinced</span></span><div class="content"><span class="def"> sure that something is true, even when you cannot prove it</span><span class="thespropform"><span class="neutral">: </span>convinced (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We've had no news of him, but we're convinced he's still alive.</span><span class="example"><span class="neutral">· </span>She became convinced that her boyfriend was seeing someone else.</span><span class="thespropform">convinced of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Brown's wife was convinced of his innocence.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">have no doubt</span></span><div class="content"><span class="def"> to be so certain about something that there are no doubts in your mind</span><span class="thespropform"><span class="neutral">: </span>have no doubt (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I've no doubt that his story is true.</span><span class="thespropform">have no doubt about/of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My boss told me he had no doubt about my abilities as a salesman.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">know</span></span><div class="content"><span class="def"> to have a strong feeling that something is right or true, although there may be nothing to prove</span><span class="thespropform"><span class="neutral">: </span>know (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sally knew that she and Carl would be the best of friends.</span><span class="example"><span class="neutral">· </span>I just know your mother will love this necklace.</span><span class="example"><span class="neutral">· </span>As soon as the phone rang, we knew something terrible must have happened.</span><span class="thespropform">know it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She's going to pass -- I just know it!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">satisfied</span></span><div class="content"><span class="def"> sure that you know the truth about something that has happened, because you have enough information</span><span class="thespropform"><span class="neutral">: </span>satisfied (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police are now satisfied that her death was an accident.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">confident</span></span><div class="content"><span class="def"> sure that something good will happen, or that you will be able to achieve what you want</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>''Do you think you'll win tomorrow's game?'' - ''Well, we're all feeling pretty confident''.</span><span class="thespropform">confident (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Doctors are confident that he'll make a full recovery.</span><span class="thespropform">confident of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A spokesman said the government was confident of winning the vote and would not discuss the possibility of defeat.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I bet</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken informal</span><span class="def"> say this when you feel sure that something is true</span><span class="thespropform"><span class="neutral">: </span>I bet (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I bet you're tired after such a long journey.</span><span class="example"><span class="neutral">· </span>I bet she hasn't told her parents about this.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">must</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> if you say that something must be true or must have happened, you are sure about it, because of information you have or things you have noticed that make it seem very likely</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You must remember Sally Newton. She was in our French class.</span><span class="thespropform">must have</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Kim didn't answer when I called - she must have gone to bed.</span><span class="example"><span class="neutral">· </span>She didn't get into movies just because her mother was a famous actress, but it must have helped.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">can't</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> if you say that something can't be true or can't have happened, you are sure that it is not true or has not happened</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He says he's 21 but he can't be older than 18.</span><span class="thespropform">can't have</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She can't have gone to bed yet - it's only 8 o'clock.</span><span class="example"><span class="neutral">· </span>If you failed that exam you can't have worked very hard.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I'd put money on it</span></span><div class="content"><span class="def"> you say I'd put money on it when you are so sure of something that you would be willing to take the chance of losing money if you were wrong</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Do you really think the president will win again?'' "I'd put money on it.''</span></div></span></span></span><span class="entry"><span class="popheader popthes">WORD SETS</span><span class="category"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="ws-head">Gambling</span></span><div class="content"><span class="wswd">back, <span class="pos">verb</span></span><span class="wswd">bank, <span class="pos">noun</span></span><span class="wswd">banker, <span class="pos">noun</span></span><span class="wswd">bet, <span class="pos">verb</span></span><span class="wswd">bet, <span class="pos">noun</span></span><span class="wswd">bid, <span class="pos">verb</span></span><span class="wswd">bingo, <span class="pos">noun</span></span><span class="wswd">bookie, <span class="pos">noun</span></span><span class="wswd">bookmaker, <span class="pos">noun</span></span><span class="wswd">casino, <span class="pos">noun</span></span><span class="wswd">chip, <span class="pos">noun</span></span><span class="wswd">croupier, <span class="pos">noun</span></span><span class="wswd">dividend, <span class="pos">noun</span></span><span class="wswd">draw, <span class="pos">noun</span></span><span class="wswd">each way, <span class="pos">adverb</span></span><span class="wswd">fruit machine, <span class="pos">noun</span></span><span class="wswd">gamble, <span class="pos">verb</span></span><span class="wswd">gaming, <span class="pos">noun</span></span><span class="wswd">lottery, <span class="pos">noun</span></span><span class="wswd">odds, <span class="pos">noun</span></span><span class="wswd">offtrack, <span class="pos">adjective</span></span><span class="wswd">one-armed bandit, <span class="pos">noun</span></span><span class="wswd">parimutuel, <span class="pos">noun</span></span><span class="wswd">place, <span class="pos">verb</span></span><span class="wswd">raffle, <span class="pos">noun</span></span><span class="wswd">raffle, <span class="pos">verb</span></span><span class="wswd">roulette, <span class="pos">noun</span></span><span class="wswd">slot machine, <span class="pos">noun</span></span><span class="wswd">stakeholder, <span class="pos">noun</span></span><span class="wswd">sweepstake, <span class="pos">noun</span></span><span class="wswd">tombola, <span class="pos">noun</span></span><span class="wswd">tout, <span class="pos">verb</span></span><span class="wswd">wager, <span class="pos">noun</span></span><span class="wswd">wager, <span class="pos">verb</span></span></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">offtrack betting</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000261315').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Few states allow <span class="colloinexa">offtrack betting</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">place ... bet</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001784489').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I had no idea which horse I should <span class="colloinexa">place</span> a <span class="colloinexa">bet</span> on.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section last"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">dollar</span></span><div class="content"><span class="example"><span class="neutral">· </span>You know, I <span class="colloinexa">bet</span> you a hundred <span class="colloinexa">dollars</span> you won't sleep with me.</span><span class="example"><span class="neutral">· </span>Yeah, he says, I <span class="colloinexa">bet</span> you a <span class="colloinexa">dollar</span>.</span><span class="example"><span class="neutral">· </span>You can <span class="colloinexa">bet</span> your bottom <span class="colloinexa">dollar</span> on that!</span><span class="example"><span class="neutral">· </span>Investors and traders who had <span class="colloinexa">bet</span> the <span class="colloinexa">dollar</span> would fall had placed such orders to limit their losses.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">investor</span></span><div class="content"><span class="example"><span class="neutral">· </span>Soros made his fortune by setting up pools of wealthy <span class="colloinexa">investors</span> who <span class="colloinexa">bet</span> huge sums on global markets.</span><span class="example"><span class="neutral">· </span>With a minimal investment, <span class="colloinexa">investors</span> can <span class="colloinexa">bet</span> whether almost the entire market will rise or fall.</span><span class="example"><span class="neutral">· </span>Many <span class="colloinexa">investors</span> had <span class="colloinexa">bet</span> heavily in recent months that a balanced-budget agreement would have already been passed by now.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">life</span></span><div class="content"><span class="example"><span class="neutral">· </span>While Cooley <span class="colloinexa">bets</span> his intellectual <span class="colloinexa">life</span> upon inquiry that depends upon such methods, these strategies for learning may fall away.</span><span class="example"><span class="neutral">· </span>You <span class="colloinexa">bet</span> your <span class="colloinexa">life</span> she was.</span><span class="example"><span class="neutral">· </span>Laybourne essentially <span class="colloinexa">bet</span> the young <span class="colloinexa">life</span> of Oxygen on this decision.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">money</span></span><div class="content"><span class="example"><span class="neutral">· </span>I would have <span class="colloinexa">bet money</span> she was high on something.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">you (can) bet your life/your bottom dollar</span></span></span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">bet the farm/ranch</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">I bet/I’ll bet</span></span><div class="content"><ul class="exas"><li>Come and sit down, I <span class="nodeword">bet</span> you're exhausted.</li><li>He'll be really pleased to see you, I <span class="nodeword">bet</span>!</li><li>I <span class="nodeword">bet</span> she hasn't told her parents about this.</li><li>I <span class="nodeword">bet</span> that the meeting will be cancelled again.</li><li>I <span class="nodeword">bet</span> you're tired after such a long journey.</li><li>Ever since he got ill - for nearly a week, I <span class="nodeword">bet</span>.</li><li>Jealous of her. I <span class="nodeword">bet</span> it really got him going seeing my little pink dimples bobbing up and down there.</li><li>My dentist would laugh at me, I <span class="nodeword">bet</span>.</li><li>P.S. I <span class="nodeword">bet</span> you won't print this.</li><li>Why, I <span class="nodeword">bet</span> you never had the slightest intention of following through last night, did you?</li><li>Yes, there was something in there and I <span class="nodeword">bet</span> myself it would be Jo's emerald pendant.</li></ul></div></span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">you bet!</span></span></span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">(do you) want to bet?/wanna bet?</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">don’t bet on it/I wouldn’t bet on it</span></span><div class="content"><ul class="exas"><li>Maybe he's really a nice guy, but I wouldn't <span class="nodeword">bet</span> on it.</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">hedge your bets</span></span><div class="content"><ul class="exas"><li>It's a good idea to <span class="nodeword">hedge</span> your bets by applying to more than one college.</li><li>Dealers on the foreign exchange markets were also <span class="nodeword">hedging</span> their bets and the pound was also on ice.</li><li>However, that has not stopped the cable companies from <span class="nodeword">hedging</span> their bets by getting into the satellite business, too.</li><li>I made a decision, or rather I <span class="nodeword">hedged</span> my bets.</li><li>Maybe, I tell myself, I was really <span class="nodeword">hedging</span> my bets.</li><li>Not surprisingly, Whitehall has been <span class="nodeword">hedging</span> its bets with officials preparing briefs to cover a variety of eventualities.</li><li>Now, what will we have to <span class="nodeword">hedge</span> our bets on?</li><li>Pat Hayes, a 10-year plant veteran at the age of 30, has already begun to <span class="nodeword">hedge</span> her bets.</li><li>Still, it always is wise to <span class="nodeword">hedge</span> our bets about the future.</li></ul></div></span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">put/bet/stake your shirt on something</span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="gram">[intransitive, transitive]</span><span class="def"> to risk money on the result of a race, game, competition, or other future event</span><span class="relatedwd"><span class="neutral"> → </span>gamble</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-001654035"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001654035').play();"><img src="LDOCE6_img_spkr_g.png"/></a> How much do you want to bet?</span><span class="gramexa"><span class="propform">bet (somebody) that</span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-000915477"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-000915477').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He bet me £10 that I wouldn’t do it.</span></span><span class="gramexa"><span class="propform">bet (something) on something</span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-000915480"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000915480').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She bet all her money on a horse that came last.</span></span><span class="gramexa"><span class="propformprep">bet against</span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-001654042"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001654042').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I wouldn’t bet against him winning the championship this year.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s2"></a><span class="sense newline"><span class="sensenum">2</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s3"></a><span class="lexunit">I bet/I’ll bet</span><span class="registerlab"> spoken</span><span class="subsense"> <span class="sensenum">a)</span><span class="def">used to say that you are fairly sure that something is true, something is happening etc, although you cannot prove this</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-000915488"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-000915488').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Bet you wish you’d arrived earlier.</span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-000915489"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-000915489').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I bet you she won’t come.</span></span><span class="subsense"> <span class="sensenum">b)</span><span class="def">used to show that you understand or can imagine the situation that someone has just told you about</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-000915494"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000915494').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘God, I was so angry.’ ‘I bet you were.’</span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-001654046"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-001654046').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘It makes things much easier.’ ‘Yeah, I’ll bet it does.’</span></span><span class="subsense"> <span class="sensenum">c)</span><span class="def">used to show that you do not believe what someone has just told you</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-000915502"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-000915502').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘I’m definitely going to give up smoking this time.’ ‘Yeah, I bet!’</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s4"></a><span class="sense newline"><span class="sensenum">3</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s5"></a><span class="lexunit">you bet!</span><span class="registerlab"> spoken</span><span class="def"> used to emphasize that you agree with someone or are keen to do what they suggest</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-000915508"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-000915508').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘Going to the party on Saturday?’ ‘You bet!’</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s6"></a><span class="sense newline"><span class="sensenum">4</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s7"></a><span class="lexunit">(do you) want to bet?/wanna bet?</span><span class="registerlab"> spoken</span><span class="def"> used to say that you think something that someone has just said is not true or not likely to happen</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001987260"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001987260').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘I’m sure Tom’ll be here soon.’ ‘Wanna bet?’</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s8"></a><span class="sense newline"><span class="sensenum">5</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s9"></a><span class="lexunit">don’t bet on it/I wouldn’t bet on it</span><span class="registerlab"> spoken</span><span class="def"> used to say that you do not think something is likely to happen</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001654053"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001654053').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He said he’d finish by tomorrow, but I wouldn’t bet on it.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s10"></a><span class="sense newline"><span class="sensenum">6</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s11"></a><span class="lexunit">you (can) bet your life/your bottom dollar</span><span class="registerlab"> spoken</span><span class="def"> used when you are sure that you know what someone will do or what will happen</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_d_p008-000915513"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-000915513').play();"><img src="LDOCE6_img_spkr_g.png"/></a> You can bet your bottom dollar he won’t be back.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s12"></a><span class="sense newline"><span class="sensenum">7</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_1_s13"></a><span class="lexunit">bet the farm/ranch</span><span class="geo"> American English</span><span class="registerlab"> informal</span><span class="def"> to risk everything that you own</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_3_ldoce5_exa000164"><a onclick="document.getElementById('LDOCE6_exa_bre_3_ldoce5_exa000164').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Do we really want to bet the ranch on this deal?</span></span></span></div><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_bet_2"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_bet_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_bet_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2"></a><span class="entry"><span class="entryhead"><span class="hwd">bet</span><span class="hyphenation frequent">bet</span><span class="homnum frequent">2</span><span class="level tooltip"> ●●●</span> <span class="freq">S3</span><span class="pos"> noun</span><span class="gram"> [countable]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_c_bet_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_f_l3bet2').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">bet</span><span class="homnum">2</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1500-1600</span> Perhaps from <span class="etymorigin">abet</span> <span class="etymtran"> ‘support, help’</span> <span class="etymcentury">(14-16 centuries)</span>, from <span class="etymorigin">abet</span> <span class="etymtran"> ‘to help’</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>a $50 <span class="nodeword">bet</span></li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>A: Your best <span class="nodeword">bet</span> is to change your behavior and wait it out.</li><li>Balvinder jumped up and down, punched the air, then promptly confronted the man with whom he had made the <span class="nodeword">bet</span>.</li><li>Digges argued that the Watch was way off, as was the island, and offered to lay odds on the <span class="nodeword">bet</span>.</li><li>Each of us invested ten shillings and we met to decide our <span class="nodeword">bets</span>.</li><li>He blamed an October snowstorm in Denver for losing a $ 500 <span class="nodeword">bet</span> on the Broncos.</li><li>Ira Sanchez offered a <span class="nodeword">bet</span> and the bookie took it on the nod.</li><li>The highest yields are from a risky <span class="nodeword">bet</span>, while security means a lower rate of interest.</li><li>With our last two food dollars he placed still another <span class="nodeword">bet</span> and lost.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">certain to win or succeed</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be destined to</span></span><div class="content"><span class="def"> if someone is destined to do or become something, they will certainly do it or become it, especially something that will make them famous or admired</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She was convinced that her little boy was destined to become President.</span><span class="example"><span class="neutral">· </span>Clara worked in a factory and sang in clubs at weekends - but even then you could tell that she was destined to become a star.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be assured of</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> to be certain to get something good or to be successful</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>After the success of its recent single, the band is now assured of a contract with a major record company.</span><span class="example"><span class="neutral">· </span>Our clients are assured of comfortable accommodation and the attention of our trained staff.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">somebody/something is a dead cert</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken informal</span><span class="def"> use this to say that someone or something will definitely win or succeed</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm betting on Sceptre. He's a dead cert to win the Gold Cup.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">can't go wrong</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to say that something or someone will definitely be successful</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We've been through the plan a dozen times. It can't go wrong.</span><span class="example"><span class="neutral">· </span>Just follow the instructions and you can't go wrong.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">it's in the bag</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken informal</span><span class="def"> use this to say that you will certainly get something you want, or something you want will certainly happen, because you have almost got it or it has almost happened already</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You're bound to get Bill's job when he leaves - it's in the bag.</span><span class="thespropform">be in the bag</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If we win the next game, the championship's in the bag.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">it's a safe bet/it's a sure bet/it's a sure thing</span></span><div class="content"><span class="def"> use this to say that it is almost certain something will happen because of what you know of the situation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I told my friends it was a sure thing, and they bet every last penny on that horse of yours.</span><span class="thespropform">it's a safe bet/it's a sure bet/it's a sure thing (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If the President supports him it's a sure bet that he'll get a seat in Congress.</span><span class="example"><span class="neutral">· </span>As it was the first concert of the season it was a safe bet all the critics would be there.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">foolproof</span></span><div class="content"><span class="def"> a foolproof plan or method is one that is certain to work and be successful</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span> A pasta book is a foolproof gift for anyone who cooks.</span><span class="thespropform">foolproof method/system/plan etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The new speech recognition system is not absolutely foolproof, but it is a huge breakthrough.</span><span class="example"><span class="neutral">· </span>The gang hit on a seemingly foolproof way of getting drugs into the country.</span></div></span></span><span class="section"><span class="secheading">a particular way of dealing with something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">course of action/course</span></span><div class="content"><span class="def"> an action or several actions which could be taken in order to deal with a particular situation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The best course of action would be to speak to her and tell her the whole story.</span><span class="example"><span class="neutral">· </span>One possible course of action is to increase taxes on alcohol and tobacco.</span><span class="example"><span class="neutral">· </span>Jim didn't want to start an argument, so agreeing seemed to be the safest course.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">approach</span></span><div class="content"><span class="def"> a particular way of dealing with a problem, difficult situation, or job</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The company needs to adopt a much more radical approach.</span><span class="thespropform">approach to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Each of the delegates suggested a different approach to the problem.</span><span class="example"><span class="neutral">· </span>the government's aggressive approach to the question of homelessness</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">option</span></span><div class="content"><span class="def"> one of several ways that you could choose in order to deal with a problem or a difficult situation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Working full-time may not be your best option.</span><span class="example"><span class="neutral">· </span>What other options do I have?</span><span class="thespropform">only option</span><span class="gloss"><span class="neutral"> (=</span>the only thing you can do<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Our only option now is to contact the police.</span><span class="thespropform">no option</span><span class="gloss"><span class="neutral"> (=</span>no other way of dealing with something<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>These people have no option but to take low-paid unattractive work.</span><span class="thespropform">environmental/nuclear/political etc option</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>As for replacement fuels, many people do not like to contemplate the nuclear option.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">your best bet</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> the best way of dealing with something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>For getting around the city centre, a bicycle's your best bet.</span><span class="thespropform">somebody's best bet is to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We decided that our best bet was to leave him where he was and go and get help.</span></div></span></span><span class="section"><span class="secheading">what you say when you expect something to happen</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I expect</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially British</span><span class="variant"> <span class="lexvar">/I guess</span></span><span class="neutral"> </span><span class="registerlab">especially American</span> <span class="example"><span class="neutral">· </span>Hasn't Tony arrived yet? He'll be here soon, I expect.</span><span class="thespropform">I expect (that)</span> <span class="example"><span class="neutral">· </span>I expect your mother will be overjoyed when she hears you're having a baby.</span><span class="thespropform">I expect/guess so</span> <span class="example"><span class="neutral">· </span>"Is Alan going to drive tonight?" "Yeah, I guess so."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I bet/my bet is</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> use this to introduce something that you expect to happen because of what you know about someone or about the way things usually happen. I bet is more common than my bet is</span><span class="thespropform"><span class="neutral">: </span>I bet/my bet is (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I bet you'll miss your boyfriend when you go away for college.</span><span class="example"><span class="neutral">· </span>Sandra says she's never going to have any children, but my bet is she has at least three.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I suspect</span></span><div class="content"><span class="def"> use this when you have a feeling that something is going to happen, especially something bad or unpleasant</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You'll find Rick's parents rather hard to talk to, I suspect.</span><span class="thespropform">I suspect (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I suspect that 10 years after the book is published, nobody will even remember the name of the author.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I would think</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially British</span><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">I should think</span><span class="neutral"> </span><span class="registerlab">British</span></span><span class="def"> use this when you think something is likely to happen although you are not really sure</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It'll be cooler down by the lake, I would think.</span><span class="thespropform">I would think (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I should think Sarah and Greg will be going to the party.</span><span class="thespropform">I would/should think so</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Will you have finished work by 6.30?'' "Oh yes, I should think so.''</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I wouldn't be surprised</span></span><div class="content"><span class="def"> say this when you think something may happen, even though other people think it is unlikely</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Do you think they'll get married?" "I wouldn't be surprised."</span><span class="thespropform">I wouldn't be surprised if</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You know, I wouldn't be surprised if some of the top executives lose their jobs.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">my expectation is</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> use this to introduce something that you expect to happen because of information that you have</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My expectation is that the two companies will eventually come to an agreement.</span></div></span></span><span class="section last"><span class="secheading">to gamble</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">gamble</span></span><div class="content"><span class="def"> to try to win money, for example by playing cards or guessing which horse will win a race</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Eddie loved to gamble, and would spend most evenings at the roulette table.</span><span class="thespropform">gamble away something/gamble something away</span><span class="gloss"><span class="neutral"> (=</span>waste a lot of money by gambling<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Roger gambled away all his money in a Las Vegas casino.</span><span class="example"><span class="neutral">· </span>She inherited $50,000 but gambled it away.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">gambling</span></span><div class="content"><span class="def"> when you try to win money, for example by playing cards or guessing which horse will win a race</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Is gambling legal here?</span><span class="example"><span class="neutral">· </span>Your Uncle Maury has a gambling problem.</span><span class="example"><span class="neutral">· </span>Crane admits that he is addicted to gambling.</span><span class="example"><span class="neutral">· </span>He was against the introduction of a National Lottery as he thought it might encourage gambling.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">bet/have a bet</span></span><div class="content"><span class="def"> to try to win money by guessing who will win a race or game</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I don't bet very often.</span><span class="thespropform">bet on something/have a bet on something</span><span class="gloss"><span class="neutral"> (=</span>gamble money on the result of a race or game<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We usually have a bet on the Grand Prix.</span><span class="example"><span class="neutral">· </span>Rogers was not much of a gambler. When he bet on the horses, he almost always lost.</span><span class="thespropform">bet £10/$100 etc on something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Jerry bet $1000 on the game.</span><span class="thespropform">place a bet</span><span class="gloss"><span class="neutral"> (=</span>say which horse, team, competition etc you want to gamble on and pay the money that you want to gamble<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I've placed a bet on a horse in the next race.</span><span class="example"><span class="neutral">· </span>All bets must be placed before the start of the race.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put £10/$20 etc on</span></span><div class="content"><span class="def"> to gamble £10, $20 etc on the horse or team that you think will win a race or competition</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I put $20 on the Cowboys to win.</span><span class="example"><span class="neutral">· </span>He put £50 on Middlesborough to beat Manchester United at odds of 3-1.</span><span class="thespropform">put a bet on something</span><span class="gloss"><span class="neutral"> (=</span>gamble on a horse, game etc<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I think I'll put a bet on the next race.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">back</span></span><div class="content"><span class="def"> to gamble money on a particular horse, team, or person that you think will win a particular competition</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We backed a horse named Travelling Light that finished first at 10-1.</span><span class="thespropform">back something/somebody to win something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Uncle Barry backed Arsenal to win the FA Cup.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">play for money</span></span><div class="content"><span class="def"> to gamble money on the result of a game which you are playing, for example cards</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Are we playing for money here, gentlemen?" he said as he approached the pool table.</span><span class="example"><span class="neutral">· </span>You're allowed to play cards in the bar, but not for money.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">have a flutter</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British informal</span><span class="def"> to gamble a small amount of money, especially on the result of a horse race - use this especially about someone who does not gamble very often</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not a heavy gambler, but I like to have a flutter from time to time.</span><span class="thespropform">have a flutter on</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I had a little flutter on the Grand National and won £5.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">lose money on</span></span><div class="content"><span class="def"> to lose money by not guessing correctly the result of a game, race, or competition</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He claims that he lost the money on a dice game.</span><span class="example"><span class="neutral">· </span>I lost a lot of money on the dogs last night.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">win money on</span></span><div class="content"><span class="def"> to win money by correctly guessing the result of a game, race, or competition</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I heard that he won a lot of money on the Superbowl a few years ago.</span></div></span></span></span><span class="entry"><span class="popheader popthes">WORD SETS</span><span class="category"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="ws-head">Gambling</span></span><div class="content"><span class="wswd">back, <span class="pos">verb</span></span><span class="wswd">bank, <span class="pos">noun</span></span><span class="wswd">banker, <span class="pos">noun</span></span><span class="wswd">bet, <span class="pos">verb</span></span><span class="wswd">bet, <span class="pos">noun</span></span><span class="wswd">bid, <span class="pos">verb</span></span><span class="wswd">bingo, <span class="pos">noun</span></span><span class="wswd">bookie, <span class="pos">noun</span></span><span class="wswd">bookmaker, <span class="pos">noun</span></span><span class="wswd">casino, <span class="pos">noun</span></span><span class="wswd">chip, <span class="pos">noun</span></span><span class="wswd">croupier, <span class="pos">noun</span></span><span class="wswd">dividend, <span class="pos">noun</span></span><span class="wswd">draw, <span class="pos">noun</span></span><span class="wswd">each way, <span class="pos">adverb</span></span><span class="wswd">fruit machine, <span class="pos">noun</span></span><span class="wswd">gamble, <span class="pos">verb</span></span><span class="wswd">gaming, <span class="pos">noun</span></span><span class="wswd">lottery, <span class="pos">noun</span></span><span class="wswd">odds, <span class="pos">noun</span></span><span class="wswd">offtrack, <span class="pos">adjective</span></span><span class="wswd">one-armed bandit, <span class="pos">noun</span></span><span class="wswd">parimutuel, <span class="pos">noun</span></span><span class="wswd">place, <span class="pos">verb</span></span><span class="wswd">raffle, <span class="pos">noun</span></span><span class="wswd">raffle, <span class="pos">verb</span></span><span class="wswd">roulette, <span class="pos">noun</span></span><span class="wswd">slot machine, <span class="pos">noun</span></span><span class="wswd">stakeholder, <span class="pos">noun</span></span><span class="wswd">sweepstake, <span class="pos">noun</span></span><span class="wswd">tombola, <span class="pos">noun</span></span><span class="wswd">tout, <span class="pos">verb</span></span><span class="wswd">wager, <span class="pos">noun</span></span><span class="wswd">wager, <span class="pos">verb</span></span></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="section last"><span class="secheading">verbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">have a bet</span></span><div class="content"><span class="example"><span class="neutral">· </span>Are you going to have a bet on the race?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">place/put/lay a bet on something</span></span><div class="content"><span class="example"><span class="neutral">· </span>She placed a bet on a horse called Beethoven.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">take a bet</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>accept someone's money as part of a bet<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>They're taking bets on the result of the election now.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">win a bet</span></span><div class="content"><span class="example"><span class="neutral">· </span>France won the game and I won my bet.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">lose a bet</span></span><div class="content"><span class="example"><span class="neutral">· </span>If I have a bet, I always lose it.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">make a bet</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>risk money on the way a situation will develop in future<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>If you want to make money on the stock market, you have to start making bets.</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">offtrack betting</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000261315').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Few states allow <span class="colloinexa">offtrack betting</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">place ... bet</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001784489').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I had no idea which horse I should <span class="colloinexa">place</span> a <span class="colloinexa">bet</span> on.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADJECTIVE</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">big</span></span><div class="content"><span class="example"><span class="neutral">· </span>He. wanted to make a <span class="colloinexa">big bet</span> on it.</span><span class="example"><span class="neutral">· </span>Berghuis avoids making <span class="colloinexa">big bets</span> in a single industry, choosing instead to spread his risks.</span><span class="example"><span class="neutral">· </span>Many managers of similar funds are unwilling or not allowed to make very <span class="colloinexa">big bets</span> on a particular market sector.</span><span class="example"><span class="neutral">· </span>Accordingly, <span class="colloinexa">big bets</span> could be made.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">fair</span></span><div class="content"><span class="example"><span class="neutral">· </span>As soon as a board attempts to interfere with management tasks it's a <span class="colloinexa">fair bet</span> that profits will decline.</span><span class="example"><span class="neutral">· </span>And I got on at 50-1 immediately after that race, and had a <span class="colloinexa">fair bet</span>.</span><span class="example"><span class="neutral">· </span>Since they're not, it's a <span class="colloinexa">fair bet</span> that they show something she doesn't want you to know.</span><span class="example"><span class="neutral">· </span>It is a <span class="colloinexa">fair bet</span> that this suspicion is right.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">good</span></span><div class="content"><span class="example"><span class="neutral">· </span>Even if you spend more and risk recurring sticker shock, the place is a <span class="colloinexa">good bet</span> to tranquilize you.</span><span class="example"><span class="neutral">· </span>Extreme surfer and skateboard styles are still a <span class="colloinexa">best bet</span>.</span><span class="example"><span class="neutral">· </span>Held's road-focused 2080 gloves recently won a recommendation from RiDE magazine, so may be a <span class="colloinexa">better bet</span>.</span><span class="example"><span class="neutral">· </span>Still, if a well-equipped sporty car is in your heart, the fifth-generation Prelude probably is a <span class="colloinexa">good bet</span>.</span><span class="example"><span class="neutral">· </span>Udal could emerge as the <span class="colloinexa">best bet</span> of all.</span><span class="example"><span class="neutral">· </span>A <span class="colloinexa">better bet</span> for performance is the smaller 190, with the optional 2.5-litre diesel instead of the usual 2-litre version.</span><span class="example"><span class="neutral">· </span>I spotted a carton that seemed to be a <span class="colloinexa">good bet</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">safe</span></span><div class="content"><span class="example"><span class="neutral">· </span>Above all, remember that there is no such thing as a <span class="colloinexa">safe bet</span>.</span><span class="example"><span class="neutral">· </span>If you are determined to cultivate fruits, then the <span class="colloinexa">safest bets</span> are berry bushes and nut trees.</span><span class="example"><span class="neutral">· </span>The supermodels are a <span class="colloinexa">safe bet</span> and, in times of recession, that is what matters.</span><span class="example"><span class="neutral">· </span>Or is Paul Parker a <span class="colloinexa">safer bet</span>?</span><span class="example"><span class="neutral">· </span>It was always a <span class="colloinexa">safe bet</span>, of course, that Hillsborough would be just about the least dangerous place in Britain yesterday.</span><span class="example"><span class="neutral">· </span>You can't have a <span class="colloinexa">safer bet</span> than that!</span><span class="example"><span class="neutral">· </span>But Nintendo looks a <span class="colloinexa">safer bet</span>.</span><span class="example"><span class="neutral">· </span>He may not fancy it, but it's a <span class="colloinexa">safe bet</span> that he would be the first man to do it.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">sure</span></span><div class="content"><span class="example"><span class="neutral">· </span>As Patrick O'Brien proved, maritime adventures are always a <span class="colloinexa">sure bet</span> for landlubber readers.</span><span class="example"><span class="neutral">· </span>A bill to let workers carry health insurance coverage from one employer to another should be a <span class="colloinexa">sure bet</span> to become law.</span><span class="example"><span class="neutral">· </span>At 38, he seemed a <span class="colloinexa">sure bet</span> to become a towering figure of popular music.</span></div></span></span><span class="section last"><span class="secheading">VERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">hedge</span></span><div class="content"><span class="example"><span class="neutral">· </span>Not surprisingly, Whitehall has been <span class="colloinexa">hedging</span> its <span class="colloinexa">bets</span> with officials preparing briefs to cover a variety of eventualities.</span><span class="example"><span class="neutral">· </span>However, that has not stopped the cable companies from <span class="colloinexa">hedging</span> their <span class="colloinexa">bets</span> by getting into the satellite business, too.</span><span class="example"><span class="neutral">· </span>Dealers on the foreign exchange markets were also <span class="colloinexa">hedging</span> their <span class="colloinexa">bets</span> and the pound was also on ice.</span><span class="example"><span class="neutral">· </span>Now, what will we have to <span class="colloinexa">hedge</span> our <span class="colloinexa">bets</span> on?</span><span class="example"><span class="neutral">· </span>I made a decision, or rather I <span class="colloinexa">hedged</span> my <span class="colloinexa">bets</span>.</span><span class="example"><span class="neutral">· </span>Still, it always is wise to <span class="colloinexa">hedge</span> our <span class="colloinexa">bets</span> about the future.</span><span class="example"><span class="neutral">· </span>Maybe, I tell myself, I was really <span class="colloinexa">hedging</span> my <span class="colloinexa">bets</span>.</span><span class="example"><span class="neutral">· </span>Pat Hayes, a 10-year plant veteran at the age of 30, has already begun to <span class="colloinexa">hedge</span> her <span class="colloinexa">bets</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">lay</span></span><div class="content"><span class="example"><span class="neutral">· </span>One of the orderlies, in defiance of the Prophet, began to <span class="colloinexa">lay bets</span>.</span><span class="example"><span class="neutral">· </span>Digges argued that the Watch was way off, as was the island, and offered to <span class="colloinexa">lay</span> odds on the <span class="colloinexa">bet</span>.</span><span class="example"><span class="neutral">· </span>And millions of pounds are <span class="colloinexa">laid</span> in <span class="colloinexa">bets</span> over the three days of the most colourful event in the racing calendar.</span><span class="example"><span class="neutral">· </span>People read the New York Post and <span class="colloinexa">laid bets</span> on whatever game was to be played that evening.</span><span class="example"><span class="neutral">· </span>Nicholas Young <span class="colloinexa">laid</span> over £10m-worth of <span class="colloinexa">bets</span> in five years - at least a third of it with other people's money.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">laid</span> a small <span class="colloinexa">bet</span> with myself that he couldn't get beyond level two of Super Mario Brothers.</span><span class="example"><span class="neutral">· </span>He <span class="colloinexa">laid</span> a small <span class="colloinexa">bet</span> with himself that Fishbane had been responsible for giving her this photo-opportunity.</span><span class="example"><span class="neutral">· </span>A crowd had come then to stare and cheer and <span class="colloinexa">lay bets</span> - with the man Randall.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">look</span></span><div class="content"><span class="example"><span class="neutral">· </span>Son-shine boy <span class="colloinexa">looks</span> a real Bonny <span class="colloinexa">bet</span>!</span><span class="example"><span class="neutral">· </span>He now <span class="colloinexa">looks</span> a good <span class="colloinexa">bet</span> to go through to the next stage of the competition tomorrow.</span><span class="example"><span class="neutral">· </span>Despite the presence of the unbeaten Daarik, Montendre <span class="colloinexa">looks</span> the best <span class="colloinexa">bet</span> at York.</span><span class="example"><span class="neutral">· </span>But Nintendo <span class="colloinexa">looks</span> a safer <span class="colloinexa">bet</span>.</span><span class="example"><span class="neutral">· </span>This race gave McCallen one of his five wins last year, and <span class="colloinexa">looks</span> his best <span class="colloinexa">bet</span> for an action replay victory.</span><span class="example"><span class="neutral">· </span>I was chided for implying in last month's notes that Somerset <span class="colloinexa">looked</span> rather a better <span class="colloinexa">bet</span> than Gloucestershire.</span><span class="example"><span class="neutral">· </span>In town, this becomes tiresome and the £359 optional power assistance <span class="colloinexa">looks</span> a good <span class="colloinexa">bet</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">lose</span></span><div class="content"><span class="example"><span class="neutral">· </span>But if they were gambling on becoming a regional force, they seem to have <span class="colloinexa">lost</span> the <span class="colloinexa">bet</span>.</span><span class="example"><span class="neutral">· </span>He blamed an October snowstorm in Denver for <span class="colloinexa">losing</span> a $ 500 <span class="colloinexa">bet</span> on the Broncos.</span><span class="example"><span class="neutral">· </span>Steve Jobs <span class="colloinexa">lost</span> his <span class="colloinexa">bet</span> that the Macintosh would be finished before Lisa.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">make</span></span><div class="content"><span class="example"><span class="neutral">· </span>Balvinder jumped up and down, punched the air, then promptly confronted the man with whom he had <span class="colloinexa">made</span> the <span class="colloinexa">bet</span>.</span><span class="example"><span class="neutral">· </span>Thousands of others raced back at halftime to <span class="colloinexa">make</span> additional proposition <span class="colloinexa">bets</span>.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">made</span> some <span class="colloinexa">bets</span> and they came good.</span><span class="example"><span class="neutral">· </span>But when the game was over, we found out that the guy who <span class="colloinexa">made</span> the <span class="colloinexa">bet</span> put his money on Cincinnati.</span><span class="example"><span class="neutral">· </span>That <span class="colloinexa">makes</span> them a good <span class="colloinexa">bet</span> if you travel with a laptop computer.</span><span class="example"><span class="neutral">· </span>He. wanted to <span class="colloinexa">make</span> a big <span class="colloinexa">bet</span> on it.</span><span class="example"><span class="neutral">· </span>There are lots of ways to <span class="colloinexa">make</span> the same <span class="colloinexa">bet</span>.</span><span class="example"><span class="neutral">· </span>And behind him, a hundred people lamented and laughed and <span class="colloinexa">made bets</span> and dreamed.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">place</span></span><div class="content"><span class="example"><span class="neutral">· </span>He <span class="colloinexa">placed</span> the <span class="colloinexa">bet</span> by messenger.</span><span class="example"><span class="neutral">· </span>About half the normal amount of trades took <span class="colloinexa">place</span> as traders were reluctant to place <span class="colloinexa">bets</span> ahead of the long weekend.</span><span class="example"><span class="neutral">· </span>Sam would <span class="colloinexa">place bets</span> on the horses for him, as Dad was unable to write a slip, nor comprehend form.</span><span class="example"><span class="neutral">· </span>A trader <span class="colloinexa">placed bets</span> in the markets on behalf of Salomon Brothers.</span><span class="example"><span class="neutral">· </span>The only way you can <span class="colloinexa">place</span> a cash <span class="colloinexa">bet</span> is with a bookmaker on Saturday.</span><span class="example"><span class="neutral">· </span>With our last two food dollars he <span class="colloinexa">placed</span> still another <span class="colloinexa">bet</span> and lost.</span><span class="example"><span class="neutral">· </span>Like every shrewd politician, Michael Heseltine has <span class="colloinexa">placed</span> a two-way <span class="colloinexa">bet</span>.</span><span class="example"><span class="neutral">· </span>He dialed what I gathered to be his bookie and <span class="colloinexa">placed</span> two <span class="colloinexa">bets</span> of five pounds each on horses racing that day.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">seem</span></span><div class="content"><span class="example"><span class="neutral">· </span>Incredibly, it <span class="colloinexa">seems</span> they stopped taking <span class="colloinexa">bets</span> on Whaddon finishing bottom way before Christmas.</span><span class="example"><span class="neutral">· </span>At 38, he <span class="colloinexa">seemed</span> a sure <span class="colloinexa">bet</span> to become a towering figure of popular music.</span><span class="example"><span class="neutral">· </span>Our subjective judgement of what <span class="colloinexa">seems</span> like a good <span class="colloinexa">bet</span> is irrelevant to what is actually a good bet.</span><span class="example"><span class="neutral">· </span>Solar desalination plants, though requiring a great emphasis on cash rather than labour, <span class="colloinexa">seem</span> the best long-term <span class="colloinexa">bet</span> here.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">take</span></span><div class="content"><span class="example"><span class="neutral">· </span>The time has not yet come to <span class="colloinexa">take bets</span> on which of our neighbours will swallow us up.</span><span class="example"><span class="neutral">· </span>He wanted to <span class="colloinexa">take</span> back the <span class="colloinexa">bet</span> just to keep from getting entangled.</span><span class="example"><span class="neutral">· </span>Sometimes they <span class="colloinexa">took bets</span> on the heights or depths of things and the location of things and the number of things.</span><span class="example"><span class="neutral">· </span>Equilibrium is everything to sportsbook operators, who look to make money by <span class="colloinexa">taking</span> a commission on <span class="colloinexa">bets</span>.</span><span class="example"><span class="neutral">· </span>I would <span class="colloinexa">take</span> another <span class="colloinexa">bet</span> that the response of the core countries would be protectionist.</span><span class="example"><span class="neutral">· </span>I would <span class="colloinexa">take</span> a sizable <span class="colloinexa">bet</span> that the coming months are going to be most interesting ones.</span><span class="example"><span class="neutral">· </span>Incredibly, it seems they stopped <span class="colloinexa">taking bets</span> on Whaddon finishing bottom way before Christmas.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">win</span></span><div class="content"><span class="example"><span class="neutral">· </span>He has slept in graveyards since being a boy and six years ago slept in a coffin to <span class="colloinexa">win</span> a <span class="colloinexa">bet</span>.</span><span class="example"><span class="neutral">· </span>San Francisco <span class="colloinexa">won</span> the game, but he won the <span class="colloinexa">bet</span> because the Bengals covered with the points.</span><span class="example"><span class="neutral">· </span>He recalls with obvious pleasure <span class="colloinexa">winning</span> a <span class="colloinexa">bet</span> by eating 11 raw eggs.</span><span class="example"><span class="neutral">· </span>Anyone who has noticed recent elections knows that Alan <span class="colloinexa">won</span> that <span class="colloinexa">bet</span>.</span><span class="example"><span class="neutral">· </span>But beware playing this with Really Nasty addicts - they will probably take advantage of your Christmas hangover to <span class="colloinexa">win</span> huge <span class="colloinexa">bets</span>.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">do something for a bet</span></span><div class="content"><ul class="exas"><li>It was this book that Rincewind had once opened for a <span class="nodeword">bet</span>.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">your best bet</span></span><div class="content"><ul class="exas"><li>For getting around the city centre, a bicycle's your best <span class="nodeword">bet</span>.</li><li>We decided that our best <span class="nodeword">bet</span> was to leave him where he was and go and get help.</li><li>Well, your best <span class="nodeword">bet</span> would be to go back to Highway 218 and turn left.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">a good/safe bet</span></span><div class="content"><ul class="exas"><li>The earrings seemed like a good <span class="nodeword">bet</span> for a birthday present.</li><li>Cohen says companies like PictureTel have improved transmission quality, making video conferencing a better <span class="nodeword">bet</span> than in years past.</li><li>Even if you spend more and risk recurring sticker shock, the place is a good <span class="nodeword">bet</span> to tranquilize you.</li><li>It was always a safe <span class="nodeword">bet</span>, of course, that Hillsborough would be just about the least dangerous place in Britain yesterday.</li><li>Only a few months ago he had looked like a good <span class="nodeword">bet</span>.</li><li>Still, if a well-equipped sporty car is in your heart, the fifth-generation Prelude probably is a good <span class="nodeword">bet</span>.</li><li>That makes them a good <span class="nodeword">bet</span> if you travel with a laptop computer.</li><li>The supermodels are a safe <span class="nodeword">bet</span> and, in times of recession, that is what matters.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">it’s a safe/sure/fair bet (that)</span></span><div class="content"><ul class="exas"><li>As soon as a board attempts to interfere with management tasks it's a fair <span class="nodeword">bet</span> that profits will decline.</li><li>He may not fancy it, but it's a safe <span class="nodeword">bet</span> that he would be the first man to do it.</li><li>Since they're not, it's a fair <span class="nodeword">bet</span> that they show something she doesn't want you to know.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">my bet</span></span><div class="content"><ul class="exas"><li>I made a decision, or rather I hedged my <span class="nodeword">bets</span>.</li><li>Maybe, I tell myself, I was really hedging my <span class="nodeword">bets</span>.</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">hedge your bets</span></span><div class="content"><ul class="exas"><li>It's a good idea to <span class="nodeword">hedge</span> your bets by applying to more than one college.</li><li>Dealers on the foreign exchange markets were also <span class="nodeword">hedging</span> their bets and the pound was also on ice.</li><li>However, that has not stopped the cable companies from <span class="nodeword">hedging</span> their bets by getting into the satellite business, too.</li><li>I made a decision, or rather I <span class="nodeword">hedged</span> my bets.</li><li>Maybe, I tell myself, I was really <span class="nodeword">hedging</span> my bets.</li><li>Not surprisingly, Whitehall has been <span class="nodeword">hedging</span> its bets with officials preparing briefs to cover a variety of eventualities.</li><li>Now, what will we have to <span class="nodeword">hedge</span> our bets on?</li><li>Pat Hayes, a 10-year plant veteran at the age of 30, has already begun to <span class="nodeword">hedge</span> her bets.</li><li>Still, it always is wise to <span class="nodeword">hedge</span> our bets about the future.</li></ul></div></span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">put/bet/stake your shirt on something</span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">an agreement to risk money on the result of a race, game etc or on something happening, or the money that you risk</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001654234"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001654234').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a £50 bet</span><span class="gramexa"><span class="propformprep">bet on</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001016324"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001016324').play();"><img src="LDOCE6_img_spkr_g.png"/></a> A few of us <span class="colloinexa">had</span> a <span class="colloinexa">bet</span> on who’d get married first.</span></span><span class="colloexa"><span class="collo">place/put/lay a bet</span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-001892031"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001892031').play();"><img src="LDOCE6_img_spkr_g.png"/></a> We placed bets on three horses.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-001654242"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-001654242').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Bookmakers are already <span class="colloinexa">taking bets</span> on the outcome.</span><span class="colloexa"><span class="collo">win/lose a bet</span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001892034"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001892034').play();"><img src="LDOCE6_img_spkr_g.png"/></a> If he scores now, I’ll win my bet.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s2"></a><span class="sense newline"><span class="sensenum">2</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s3"></a><span class="lexunit">your best bet</span><span class="registerlab"> spoken</span><span class="def"> used when advising someone what to do</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001654252"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001654252').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Your best bet is to put an advert in the local newspaper.</span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001654253"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001654253').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The train might be a better bet.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s4"></a><span class="sense newline"><span class="sensenum">3</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s5"></a><span class="lexunit">a good/safe bet</span><span class="def"> an action or situation that is likely to be successful or does not involve much risk</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-001016349"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001016349').play();"><img src="LDOCE6_img_spkr_g.png"/></a> If you’re looking for long-term growth, the government’s own saving certificates are a pretty good bet.</span><span class="crossref"><span class="neutral"> →</span><span class="reflex"> hedge your bets</span><a> <span class="refhwd"><span class="neutral">at </span>hedge</span><span class="refhomnum">2</span><span class="refsensenum">(2)</span></a></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s6"></a><span class="sense newline"><span class="sensenum">4</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s7"></a><span class="lexunit">it’s a safe/sure/fair bet (that)</span><span class="registerlab"> spoken</span><span class="def"> used to say that something seems almost certain</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001016356"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001016356').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I think it’s a pretty safe bet that he’ll get the job.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s8"></a><span class="sense newline"><span class="sensenum">5</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s9"></a><span class="lexunit">my bet</span><span class="registerlab"> spoken</span><span class="def"> used when saying what you expect to happen in the future</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001016344"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001016344').play();"><img src="LDOCE6_img_spkr_g.png"/></a> My bet is he’ll be back this time next week.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s10"></a><span class="sense newline"><span class="sensenum">6</span><a id="6504937708254a64972bf4d4ccdfcc9d_bet_2_s11"></a><span class="lexunit">do something for a bet</span><span class="def"> to do something stupid, dangerous etc to win money from someone or to prove that you can do it</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-001016361"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001016361').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He climbed the tree for a bet.</span></span><span class="tail"><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="section last"><span class="secheading">verbs</span><span class="collocate"><span class="colloc collo">have a bet</span><span class="example"><span class="neutral">· </span>Are you going to have a bet on the race?</span></span><span class="collocate"><span class="colloc collo">place/put/lay a bet on something</span><span class="example"><span class="neutral">· </span>She placed a bet on a horse called Beethoven.</span></span><span class="collocate"><span class="colloc collo">take a bet</span><span class="collgloss"><span class="neutral"> (=</span>accept someone's money as part of a bet<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>They're taking bets on the result of the election now.</span></span><span class="collocate"><span class="colloc collo">win a bet</span><span class="example"><span class="neutral">· </span>France won the game and I won my bet.</span></span><span class="collocate"><span class="colloc collo">lose a bet</span><span class="example"><span class="neutral">· </span>If I have a bet, I always lose it.</span></span><span class="collocate"><span class="colloc collo">make a bet</span><span class="collgloss"><span class="neutral"> (=</span>risk money on the way a situation will develop in future<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>If you want to make money on the stock market, you have to start making bets.</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_4_bet_v0205" preload="none" src="LDOCE6_hwd_bre_4_bet_v0205.mp3"></audio><audio id="LDOCE6_hwd_ame_4_l3bet" preload="none" src="LDOCE6_hwd_ame_4_l3bet.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-000261315" preload="none" src="LDOCE6_exa_bre_a_p008-000261315.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001784489" preload="none" src="LDOCE6_exa_bre_e_p008-001784489.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001654035" preload="none" src="LDOCE6_exa_bre_e_p008-001654035.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-000915477" preload="none" src="LDOCE6_exa_bre_0_p008-000915477.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-000915480" preload="none" src="LDOCE6_exa_bre_a_p008-000915480.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001654042" preload="none" src="LDOCE6_exa_bre_3_p008-001654042.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-000915488" preload="none" src="LDOCE6_exa_bre_9_p008-000915488.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-000915489" preload="none" src="LDOCE6_exa_bre_6_p008-000915489.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-000915494" preload="none" src="LDOCE6_exa_bre_2_p008-000915494.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-001654046" preload="none" src="LDOCE6_exa_bre_b_p008-001654046.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-000915502" preload="none" src="LDOCE6_exa_bre_8_p008-000915502.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-000915508" preload="none" src="LDOCE6_exa_bre_5_p008-000915508.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001987260" preload="none" src="LDOCE6_exa_bre_f_p008-001987260.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001654053" preload="none" src="LDOCE6_exa_bre_5_p008-001654053.mp3"></audio><audio id="LDOCE6_exa_bre_d_p008-000915513" preload="none" src="LDOCE6_exa_bre_d_p008-000915513.mp3"></audio><audio id="LDOCE6_exa_bre_3_ldoce5_exa000164" preload="none" src="LDOCE6_exa_bre_3_ldoce5_exa000164.mp3"></audio><audio id="LDOCE6_hwd_bre_c_bet_n0205" preload="none" src="LDOCE6_hwd_bre_c_bet_n0205.mp3"></audio><audio id="LDOCE6_hwd_ame_f_l3bet2" preload="none" src="LDOCE6_hwd_ame_f_l3bet2.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001654234" preload="none" src="LDOCE6_exa_bre_2_p008-001654234.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001016324" preload="none" src="LDOCE6_exa_bre_1_p008-001016324.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001892031" preload="none" src="LDOCE6_exa_bre_e_p008-001892031.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-001654242" preload="none" src="LDOCE6_exa_bre_a_p008-001654242.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001892034" preload="none" src="LDOCE6_exa_bre_8_p008-001892034.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001654252" preload="none" src="LDOCE6_exa_bre_9_p008-001654252.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001654253" preload="none" src="LDOCE6_exa_bre_5_p008-001654253.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001016349" preload="none" src="LDOCE6_exa_bre_e_p008-001016349.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001016356" preload="none" src="LDOCE6_exa_bre_5_p008-001016356.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001016344" preload="none" src="LDOCE6_exa_bre_4_p008-001016344.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001016361" preload="none" src="LDOCE6_exa_bre_0_p008-001016361.mp3"></audio>`;
