var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_clash_1"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_clash_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_clash_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_clash_1"></a><span class="entry"><span class="entryhead"><span class="hwd">clash</span><span class="hyphenation frequent">clash</span><span class="homnum frequent">1</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">klæʃ</span><span class="neutral">/</span></span><span class="level tooltip"> ●●○</span><span class="pos"> verb</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_3_clash_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_8_clash1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">clash</span><span class="homnum">1</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1500-1600</span> From the sound of sharp blows</span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">clash</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">clash</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">clashes</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">clashed</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">clashed</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">clashed</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">clashed</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">clash</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">clashed</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">clashing</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">clashing</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">clashing</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">clashing</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">clashing</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">clashing</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">clashing</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">clashing</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">clashing</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">clashing</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>"Are you watching Family Fortunes tonight?" "No, it <span class="nodeword">clashes</span> with the Tina Turner interview on Channel 3."</li><li>Animal rights activists and fox-hunters <span class="nodeword">clashed</span> at the annual Boxing Day hunt.</li><li>Choose bright colors, but make sure they don't <span class="nodeword">clash</span>.</li><li>Democrats <span class="nodeword">clashed</span> with Republicans last night in a heated debate about unemployment.</li><li>France and Britain are likely to <span class="nodeword">clash</span> over the proposed space programme.</li><li>I can't wear red - it <span class="nodeword">clashes</span> with my hair.</li><li>Iranian and Iraqi troops <span class="nodeword">clashed</span> on the border.</li><li>Police <span class="nodeword">clashed</span> with demonstrators for the second time in a week.</li><li>That scarf <span class="nodeword">clashes</span> terribly with her green coat.</li><li>US planes <span class="nodeword">clashed</span> with enemy fighter aircraft again today.</li><li>We've rescheduled the next meeting -- it <span class="nodeword">clashed</span> with a conference that most of us will be attending.</li><li>We can go to both classes if they don't <span class="nodeword">clash</span>.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>And though they regularly <span class="nodeword">clashed</span>, they were undoubtedly in love.</li><li>I've a worrying feeling that the Medau Annual Reunion may <span class="nodeword">clash</span> - I do hope it doesn't.</li><li>Premier John Major could <span class="nodeword">clash</span> with him on five big issues.</li><li>The colors <span class="nodeword">clash</span>, the furniture is ugly, and nobody is happy about this.</li><li>The couple have <span class="nodeword">clashed</span> with the Archbishop of York over their wish.</li><li>The two have often <span class="nodeword">clashed</span> on these and other issues.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to argue</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">argue</span></span><div class="content"><span class="def"> if people argue , they speak angrily to each other because they disagree about something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Jim and Beth seem to spend all their time arguing.</span><span class="thespropform">argue with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Don't argue with me, John. Just do what I tell you.</span><span class="thespropform">argue about/over</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The two men at the bar were arguing about politics.</span><span class="example"><span class="neutral">· </span>My kids spend more time arguing over the rules than they do playing the game.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">quarrel</span></span><div class="content"><span class="def"> if two people quarrel , they argue angrily and may stop being friends with each other</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Whenever my sisters meet they always end up quarrelling.</span><span class="thespropform">quarrel with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She left home after quarrelling with her parents.</span><span class="thespropform">quarrel about/over</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The two brothers had quarreled over ownership of the farm.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fight</span></span><div class="content"><span class="def"> to argue in an angry and violent way, especially with someone you know well</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Kerry's parents are always fighting -- I'm not surprised she left home.</span><span class="thespropform">fight over/about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Two men fighting over a parking space were arrested earlier today.</span><span class="example"><span class="neutral">· </span>If you two don't stop fighting about what to watch I'm going to send you to your room.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">have a fight</span></span><div class="content"><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">have a row</span><span class="neutral"> </span><span class="registerlab">British</span></span><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> if two people have a fight or have a row , they argue very angrily and noisily</span><span class="thespropform"><span class="neutral">: </span>have a fight with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>April had a fight with her boyfriend and doesn't want to come out of her room.</span><span class="thespropform">have a fight about/over</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Kelvin and his wife have endless rows over money.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fall out with</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British</span><span class="def"> to stop having a friendly relationship with someone, because you have disagreed with them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I think she's fallen out with her boyfriend.</span><span class="thespropform">fall out with about/over</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Murray left the company after he fell out with the chairman over his salary.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be at each other's throats</span></span><div class="content"><span class="def"> if two people or groups are at each other's throats , they are always arguing in a very angry way because they cannot agree about something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Congress and the President have been at each other's throats for so long that it's a wonder they can agree on anything.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">clash</span></span><div class="content"><span class="def"> if two groups of people clash , they argue publicly with each other about a particular subject -- used in news reports</span><span class="thespropform"><span class="neutral">: </span>clash with somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Democrats clashed with Republicans last night in a heated debate about unemployment.</span><span class="thespropform">clash over something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>France and Britain are likely to clash over the proposed space programme.</span></div></span></span><span class="section"><span class="secheading">when people fight each other</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fight</span></span><div class="content"><span class="def"> if people fight , or if one person fights another, they hit or kick each other in order to hurt each other</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Two men were fighting in the street outside.</span><span class="example"><span class="neutral">· </span>He said he'd fight anyone who tried to stop him entering.</span><span class="thespropform">fight with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Billy had been fighting with some kids from another school.</span><span class="thespropform">fight over/about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Two men in the bar began fighting over a game of cards.</span><span class="example"><span class="neutral">· </span>As kids, we fought about everything, but now we're pretty good friends.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">have a fight</span></span><div class="content"><span class="def"> to fight with another person</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The two girls had a fist fight in the school cafeteria.</span><span class="thespropform">have a fight with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He ran away from school after he had a fight with a bigger boy.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">struggle</span></span><div class="content"><span class="def"> to fight someone who is attacking you or holding you, especially so that you can escape from them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She tried to struggle but he put his hand over her mouth.</span><span class="thespropform">struggle to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Vince struggled to free himself from the policeman's grip.</span><span class="thespropform">struggle with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It seems that he struggled with the robber and got quite seriously hurt.</span><span class="thespropform">struggle against</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The victim had obviously struggled furiously against her attacker.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">wrestle</span></span><div class="content"><span class="def"> to fight someone by holding, pulling, or pushing them rather than hitting them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The two boys wrestled for a while then gave up, tired.</span><span class="thespropform">wrestle with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His jaw was broken while he tried to wrestle with a drunken bus driver.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">come to blows</span></span><div class="content"><span class="def"> if two people come to blows , they start fighting after an argument or when both people are very angry</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police say they don't know what the two were arguing about, only that it came to blows.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">clash</span></span><div class="content"><span class="def"> if two groups of people, especially people with opposing opinions or aims, clash , they fight for a short time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Animal rights activists and fox-hunters clashed at the annual Boxing Day hunt.</span><span class="example"><span class="neutral">· </span>Police clashed with demonstrators for the second time in a week.</span></div></span></span><span class="section"><span class="secheading">to not look good with something else</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not match</span></span><div class="content"><span class="def"> if two things do not match , they are not the same colour or style and so do not look attractive together</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>That tie doesn't match your shirt.</span><span class="example"><span class="neutral">· </span>I felt slightly out of place, and was conscious that my jacket and trousers didn't quite match.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not go with/not go together</span></span><div class="content"><span class="def"> if one thing does not go with another, or if two things do not go together , they do not look attractive next to each other</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>That shirt doesn't go with your blue trousers.</span><span class="example"><span class="neutral">· </span>Pink and purple don't usually go very well together.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">clash</span></span><div class="content"><span class="def"> if two things clash , they look very bad when they are worn or seen together because they are completely different in style, colour etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Choose bright colors, but make sure they don't clash.</span><span class="thespropform">clash with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>That scarf clashes terribly with her green coat.</span><span class="example"><span class="neutral">· </span>I can't wear red - it clashes with my hair.</span></div></span></span><span class="section"><span class="secheading">to happen or do things at the same time</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">coincide</span></span><div class="content"><span class="def"> if something coincides with something else, or if two things coincide , they happen at the same time as each other, usually by chance</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>When our vacations coincided, we often holidayed together.</span><span class="thespropform">coincide with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His speech coincided with the release of a report on the New England economy.</span><span class="example"><span class="neutral">· </span>I had to cancel our lunch date, as it coincided with my hospital appointment.</span><span class="thespropform">timed/arranged to coincide</span><span class="gloss"><span class="neutral"> (=</span>arranged so that something coincides<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The exhibition was timed to coincide with the anniversary celebrations.</span><span class="example"><span class="neutral">· </span>They have arranged the launch to coincide with the start of the college term. </span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">clash</span></span><div class="content"><span class="def"> if one event clashes with another, or if two events clash , they are arranged to happen at the same time, and this usually causes problems or is inconvenient</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We can go to both classes if they don't clash.</span><span class="thespropform">clash with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We've rescheduled the next meeting -- it clashed with a conference that most of us will be attending.</span><span class="example"><span class="neutral">· </span>"Are you watching Family Fortunes tonight?" "No, it clashes with the Tina Turner interview on Channel 3."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tie in with</span></span><div class="content"><span class="def"> to arrange an event so that it happens at the same time as something else, because this helps you in some way</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His publishers have tied the release in with his new television series.</span><span class="example"><span class="neutral">· </span>AIDS education can be tied in with existing health education programs.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">juggle</span></span><div class="content"><span class="def"> to try to do two or more things at the same time, even though this is difficult and you are very busy</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The film is about a maintenance man who juggles three jobs to provide for his family.</span><span class="example"><span class="neutral">· </span>suburban working mothers who juggle careers, families, and after-school sports</span><span class="thespropform">juggle something and something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I don't think any man can ever understand the difficulties of juggling motherhood and politics.</span><span class="thespropform">juggle something with something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>With school starting, Anna will have to juggle her love of swimming with her homework.</span></div></span></span><span class="section last"><span class="secheading">to fight in a war or be in a war</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fight</span></span><div class="content"><span class="def"> to take part in a war or battle</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His grandfather fought on the Republican side in the Spanish Civil War.</span><span class="example"><span class="neutral">· </span>The Boers were fighting the British at this time.</span><span class="thespropform">fight for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Most of these young soldiers don't even know what they're fighting for.</span><span class="thespropform">fight in</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My grandfather fought in World War II.</span><span class="thespropform">fight a war/battle</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They were fighting a war of independence against a powerful enemy.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">clash</span></span><div class="content"><span class="def"> if two armies or groups clash , they suddenly start fighting each other, especially for a short time - used especially in news reports:</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Iranian and Iraqi troops clashed on the border.</span><span class="thespropform">clash with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>US planes clashed with enemy fighter aircraft again today.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be at war</span></span><div class="content"><span class="def"> if two countries are at war with each other, they are fighting a war against each other</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Europe had been at war for nearly two years.</span><span class="example"><span class="neutral">· </span>He could not remember a time when his country had not been at war.</span><span class="thespropform">be at war with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In 1792 England was at war with America.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">wage war</span></span><div class="content"><span class="def"> to start and continue a war, especially for a long period</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In his speech he promised full support to wars of independence waged by colonial peoples.</span><span class="thespropform">wage war on/against</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Many Americans now question whether the US should have waged war on Vietnam.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">make war</span></span><div class="content"><span class="def"> to fight a war, especially by suddenly attacking a country that does not threaten your country</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He believes that men make war because they are by nature aggressive.</span><span class="thespropform">make war on</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Throughout the nineteenth century the colonial powers made war on poorer countries in order to gain territory.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">warring</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>warring factions/nations/tribes/groups etc</span><span class="def"> nations etc that are fighting against each other</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Fighting between the various warring factions was destroying the country.</span><span class="example"><span class="neutral">· </span>All attempts to reconcile the two warring groups have failed.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a colours clashes (with something)</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>is different from something in a way that is unattractive<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Do you think the colour of this tie clashes with my shirt?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a personality clash</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>when people cannot work together because they are so different<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The band eventually split because of personality clashes.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a violent clash</span></span><div class="content"><span class="example"><span class="neutral">· </span>There were violent clashes between police and protesters.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a battle/clash/test of wills</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>when two determined people oppose each other<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Even the smallest decision could become an exhausting battle of wills.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">over</span></span><div class="content"><span class="example"><span class="neutral">· </span>Protestors welcomed the official objection although some were concerned that different government departments had <span class="colloinexa">clashed over</span> the matter.</span><span class="example"><span class="neutral">· </span>Election candidates and doctors <span class="colloinexa">clashed over</span> claims that waiting times have increased in the town.</span></div></span></span><span class="section last"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">police</span></span><div class="content"><span class="example"><span class="neutral">· </span>Street fighting broke out after hundreds of demonstrators <span class="colloinexa">clashed</span> with the <span class="colloinexa">police</span>.</span><span class="example"><span class="neutral">· </span>Disturbances in Zurich On July 8, 1989, about 50 demonstrators protesting against lack of housing in Zurich <span class="colloinexa">clashed</span> with <span class="colloinexa">police</span>.</span><span class="example"><span class="neutral">· </span>On May 8 Chamlong led another large rally at Democracy Monument, where some demonstrators came close to <span class="colloinexa">clashing</span> with riot <span class="colloinexa">police</span>.</span></div></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_1_s1"></a><span class="sense"><span class="sensenum">1</span><span class="gram">[intransitive]</span><span class="def"> if two armies, groups etc clash, they start fighting – used in news reports</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-000888225"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-000888225').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Troops clashed near the border.</span><span class="gramexa"><span class="propformprep">clash with</span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-000888228"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-000888228').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Police have clashed with demonstrators again today.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_1_s2"></a><span class="sense"><span class="sensenum">2</span><span class="gram">[intransitive]</span><span class="def"> if two people or groups clash, they argue because they have very different beliefs and opinions – used in news reports</span><span class="gramexa"><span class="propformprep">clash with</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001662946"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001662946').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Democrats clashed with Republicans in a heated debate.</span></span><span class="gramexa"><span class="propformprep">clash over/on</span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-001662949"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001662949').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The two men have clashed over the report’s conclusions.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_1_s3"></a><span class="sense"><span class="sensenum">3</span><span class="gram">[intransitive]</span><span class="def"> if two colours or designs clash, they look very bad together</span><span class="gramexa"><span class="propformprep">clash with</span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-000888242"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-000888242').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I can’t wear red – it clashes with my hair.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_1_s4"></a><span class="sense"><span class="sensenum">4</span><span class="gram">[intransitive]</span><span class="geo"> especially British English</span><span class="def"> if two events clash, they happen at the same time in a way that is inconvenient</span><span class="amequiv"> <span class="synopp">SYN</span> conflict<span class="geo"> American English</span></span><span class="gramexa"><span class="propformprep">clash with</span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-000888249"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-000888249').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The announcement has been delayed to avoid clashing with the prime minister’s speech.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_1_s5"></a><span class="sense"><span class="sensenum">5</span><span class="gram">[intransitive, transitive]</span><span class="def"> if two pieces of metal clash, or if you clash them, they make a loud ringing sound</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-000888254"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-000888254').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The cymbals clashed.</span></span></span></div><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_clash_2"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_clash_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_clash_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_clash_2"></a><span class="entry"><span class="entryhead"><span class="hwd">clash</span><span class="hyphenation frequent">clash</span><span class="homnum frequent">2</span><span class="level tooltip"> ●●○</span><span class="pos"> noun</span><span class="gram"> [countable]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_3_clash_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_8_clash1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>border <span class="nodeword">clashes</span> between India and Pakistan</li><li>In the last two months, there have been numerous border <span class="nodeword">clashes</span>.</li><li>The <span class="nodeword">clashes</span> came as farmers blockaded roads.</li><li>There were <span class="nodeword">clashes</span> last night between local residents and young people attending the rock festival.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>And with Leonard ruled out of that Wembley <span class="nodeword">clash</span>, Hynes could claim his place today.</li><li>Combs said he did not see a man throwing money at him, an incident that the prosecution says sparked the <span class="nodeword">clash</span>.</li><li>Minor <span class="nodeword">clashes</span> were reported to have taken place between police and students at Lima's San Marcos University.</li><li>More than 75 people died and hundreds were wounded in those <span class="nodeword">clashes</span>.</li><li>No one should be too surprised at these <span class="nodeword">clashes</span>.</li><li>Problems arise where there is a <span class="nodeword">clash</span> of cultures.</li><li>The two co-exist as memories of their cultures, with a healthy <span class="nodeword">clash</span> where the two collide in the entrance foyer.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section last"><span class="secheading">a fight involving a large number of people</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fighting</span></span><div class="content"><span class="def"> when a lot of people fight each other in a public place</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There was fighting on the streets of Paris yesterday when police and demonstrators clashed.</span><span class="thespropform">fighting between</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Fighting between rival gangs resulted in the death of a teenage boy.</span><span class="thespropform">fighting breaks out</span><span class="gloss"><span class="neutral"> (=</span>starts<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Fighting broke out between English and Dutch football fans after the game.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">riot</span></span><div class="content"><span class="def"> an uncontrolled violent fight in a public place that involves a large number of people and usually damage to property, cars etc, especially among people who are protesting about something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There were riots in several cities after it was announced that the price of bread would rise by 200%.</span><span class="example"><span class="neutral">· </span>Four days of unrest and anti-government riots left at least three people dead.</span><span class="thespropform">a riot breaks out</span><span class="gloss"><span class="neutral"> (=</span>starts<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A riot broke out after a police shooting of a local man.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">battle</span></span><div class="content"><span class="def"> a fight between two large groups of people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was killed in a street battle in 1998.</span><span class="thespropform">battle between</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a battle between rival gangs</span><span class="thespropform">pitched battle</span><span class="gloss"><span class="neutral"> (=</span>a very violent fight<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>About 200 students fought pitched battles with police during the demonstrations.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">clash</span></span><div class="content"><span class="def"> a short fight between two groups of people who have opposing opinions or aims</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The clashes came as farmers blockaded roads.</span><span class="thespropform">clash between</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There were clashes last night between local residents and young people attending the rock festival.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">confrontation</span></span><div class="content"><span class="def"> a situation in which there is a lot of angry disagreement between two groups, which may develop into a fight</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The police were obviously anticipating a confrontation, as they were heavily armed.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">free-for-all</span></span><div class="content"><span class="def"> a fight that a lot of people in a crowd join, especially when they have no particular reason to be fighting each other</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>After Mathews was attacked, a free-for-all broke out in the audience. </span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="section"><span class="secheading">adjectives</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a violent clash</span></span><div class="content"><span class="example"><span class="neutral">· </span>Four people were killed during violent clashes with the army.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a fierce/bitter clash</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>involving violence and strong feelings<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Fierce clashes swept across Bosnia and Croatia.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a serious clash</span></span><div class="content"><span class="example"><span class="neutral">· </span>This was one of the most serious clashes since the two countries agreed to a ceasefire.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a minor clash</span></span><div class="content"><span class="example"><span class="neutral">· </span>Minor clashes were reported to have taken place between police and students.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">an armed clash</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>involving the use of weapons<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The violence could soon become armed clashes and even a war.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">an ethnic clash</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>between people of different races<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>20 people died in ethnic clashes before Christmas.</span></div></span></span><span class="section last"><span class="secheading">verbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">lead to a clash</span></span><div class="content"><span class="example"><span class="neutral">· </span>The anti-war protests led to violent clashes between police and demonstrators.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a clash occurs</span></span><div class="content"><span class="example"><span class="neutral">· </span>A number of clashes occurred throughout the south between these opposing groups.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a clash breaks out</span></span><div class="content"><span class="example"><span class="neutral">· </span>Armed clashes broke out on Tuesday between the military and the rebels.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a clash takes place</span></span><div class="content"><span class="example"><span class="neutral">· </span>Fierce clashes took place with local police.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">be killed/injured in a clash</span></span><div class="content"><span class="example"><span class="neutral">· </span>Eight people were killed in clashes between the army and police.</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a colours clashes (with something)</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>is different from something in a way that is unattractive<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Do you think the colour of this tie clashes with my shirt?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a personality clash</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>when people cannot work together because they are so different<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The band eventually split because of personality clashes.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a violent clash</span></span><div class="content"><span class="example"><span class="neutral">· </span>There were violent clashes between police and protesters.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a battle/clash/test of wills</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>when two determined people oppose each other<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Even the smallest decision could become an exhausting battle of wills.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADJECTIVE</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">violent</span></span><div class="content"><span class="example"><span class="neutral">· </span>In the Forest of Dean poaching was rife, and there were frequent <span class="colloinexa">violent clashes</span> between keepers and poachers.</span><span class="example"><span class="neutral">· </span>There were <span class="colloinexa">violent clashes</span> between police and pickets, notably at the Orgreave coking depot in Rotherham in the summer of 1984.</span><span class="example"><span class="neutral">· </span>Borja ordered the armed forces to intervene after <span class="colloinexa">violent clashes</span>, including gunfire, between the security forces and the demonstrators.</span><span class="example"><span class="neutral">· </span>In Dresden, witnesses reported <span class="colloinexa">violent clashes</span> between police and would-be emigrants desperate to board trains to the West.</span><span class="example"><span class="neutral">· </span>Four people were reportedly killed during <span class="colloinexa">violent clashes</span> with the Army in the capital, Santo Domingo.</span><span class="example"><span class="neutral">· </span>Weekend celebrations to mark the end of military rule had led to <span class="colloinexa">violent clashes</span> between police and demonstrators.</span><span class="example"><span class="neutral">· </span>Protests continue nightly in Vienna and other cities, peaceful affairs now unlike the <span class="colloinexa">violent clashes</span> of recent weeks.</span></div></span></span><span class="section"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">culture</span></span><div class="content"><span class="example"><span class="neutral">· </span>In the resultant <span class="colloinexa">culture clash</span> Guinness drowns, heroism subverted by duty, reality distorted by the military mindset.</span><span class="example"><span class="neutral">· </span>Despite the wry observations about the differences between working-class Hispanics and upper-class whites, this is not a story about <span class="colloinexa">culture clashes</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">cup</span></span><div class="content"><span class="example"><span class="neutral">· </span>Their last Challenge <span class="colloinexa">Cup clash</span> was at the first-round stage at the Willows in 1983 and Salford won 12-5.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">personality</span></span><div class="content"><span class="example"><span class="neutral">· </span>But it is not just this <span class="colloinexa">personality clash</span> that is driving Solidarity-as-we-know-it to extinction.</span><span class="example"><span class="neutral">· </span>There is talk of <span class="colloinexa">personality clashes</span>, and sniping against the campaign chairman, Tony Coelho.</span><span class="example"><span class="neutral">· </span>Blaming a <span class="colloinexa">personality clash</span> within the department for her ultimatum, she said she would resume work when her back was better.</span><span class="example"><span class="neutral">· </span>Where there is a <span class="colloinexa">personality clash</span>, the learner should be re-allocated to another clinical teacher.</span><span class="example"><span class="neutral">· </span>They turned out to be a good power-rock band doomed by <span class="colloinexa">personality clashes</span> and their own audacity.</span></div></span></span><span class="section last"><span class="secheading">VERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">avoid</span></span><div class="content"><span class="example"><span class="neutral">· </span>Pre-booking is essential to <span class="colloinexa">avoid clashes</span> with other groups.</span><span class="example"><span class="neutral">· </span>Instead of escalation, Clinton's preferred tactic had been to scale back the patrols to <span class="colloinexa">avoid clashes</span>.</span><span class="example"><span class="neutral">· </span>The new date will <span class="colloinexa">avoid</span> a <span class="colloinexa">clash</span> in Edinburgh with the ScotlandFrance Rugby Union international the previous day.</span><span class="example"><span class="neutral">· </span>Presumably preliminary consultation by conservation agencies and institutional involvement of local people in plan formulation and implementation would <span class="colloinexa">avoid</span> these <span class="colloinexa">clashes</span>.</span><span class="example"><span class="neutral">· </span>If Congress and the administration are to <span class="colloinexa">avoid</span> a head-on <span class="colloinexa">clash</span>, and a presidential veto, a compromise must be struck.</span><span class="example"><span class="neutral">· </span>Discussion covered procedures for <span class="colloinexa">avoiding</span> accidental <span class="colloinexa">clashes</span>, disclosing the location of mines, and exchanging information about those missing in action.</span><span class="example"><span class="neutral">· </span>The aim is to <span class="colloinexa">avoid</span> any <span class="colloinexa">clash</span> between good forestry and the claims of amenity.</span><span class="example"><span class="neutral">· </span>When reception class children fight back, schools take drastic measures to <span class="colloinexa">avoid clashes</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">die</span></span><div class="content"><span class="example"><span class="neutral">· </span>Nevertheless, some 15 people <span class="colloinexa">died</span> in campaign-related <span class="colloinexa">clashes</span>.</span><span class="example"><span class="neutral">· </span>The townspeople are hoping the fair will quietly fade away and <span class="colloinexa">die</span> because of the usual <span class="colloinexa">clashes</span> between the locals and the travellers.</span><span class="example"><span class="neutral">· </span>At least five people <span class="colloinexa">died</span> in <span class="colloinexa">clashes</span> in Ivory Coast's main city, Abidjan.</span><span class="example"><span class="neutral">· </span>By the end of July up to 180 people had <span class="colloinexa">died</span> in the <span class="colloinexa">clashes</span> and over 500 were wounded.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">injure</span></span><div class="content"><span class="example"><span class="neutral">· </span>A policeman was killed and four people were <span class="colloinexa">injured</span> in a <span class="colloinexa">clash</span> in Brsadin at the same time.</span><span class="example"><span class="neutral">· </span>The shopping centre was burned down, riot police were brought in and about 30 people were <span class="colloinexa">injured</span> in <span class="colloinexa">clashes</span>.</span><span class="example"><span class="neutral">· </span>Sixteen policemen were <span class="colloinexa">injured</span> in <span class="colloinexa">clashes</span>.</span><span class="example"><span class="neutral">· </span>Sixty-one police officers were reported to have been <span class="colloinexa">injured</span> in <span class="colloinexa">clashes</span>, as well as 31 delegates and 65 demonstrators.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">kill</span></span><div class="content"><span class="example"><span class="neutral">· </span>Between January and April it was estimated that 563 government soldiers had been <span class="colloinexa">killed</span> in <span class="colloinexa">clashes</span> with the guerrillas.</span><span class="example"><span class="neutral">· </span>He was picked up by police while covering a funeral of two leftists <span class="colloinexa">killed</span> in a prison <span class="colloinexa">clash</span>, according to colleagues.</span><span class="example"><span class="neutral">· </span>Supporters of the death penalty gathered at the funerals in Ankaraof soldiers who were <span class="colloinexa">killed</span> in a <span class="colloinexa">clash</span> with rebels this month.</span><span class="example"><span class="neutral">· </span>Thirty-four soldiers and at least 150 rebels were <span class="colloinexa">killed</span> in the <span class="colloinexa">clash</span>, military spokesman Kumara Dewage said.</span><span class="example"><span class="neutral">· </span>Three students were <span class="colloinexa">killed</span> in <span class="colloinexa">clashes</span> with the police on Nov. 22, and several members of the strike committee were arrested.</span><span class="example"><span class="neutral">· </span>According to an April 22 official report, 15 rebels and seven others were <span class="colloinexa">killed</span> in <span class="colloinexa">clashes</span> between rebels and security forces.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">lead</span></span><div class="content"><span class="example"><span class="neutral">· </span>This has <span class="colloinexa">led</span> to constant <span class="colloinexa">clashes</span> with everybody from health ministers to bishops and police officers.</span><span class="example"><span class="neutral">· </span>Weekend celebrations to mark the end of military rule had <span class="colloinexa">led</span> to violent <span class="colloinexa">clashes</span> between police and demonstrators.</span><span class="example"><span class="neutral">· </span>The review may <span class="colloinexa">lead</span> to another <span class="colloinexa">clash</span> of scientific opinion.</span></div></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_2_s1"></a><span class="sense"><span class="sensenum">1</span><span class="def">a short fight between two armies or groups – used in news reports</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001789821"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001789821').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">armed clashes</span> along the border</span><span class="gramexa"><span class="propformprep">clash between/with</span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-001662954"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001662954').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">violent clashes</span> between police and demonstrators</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_2_s2"></a><span class="sense"><span class="sensenum">2</span><span class="def">an argument between two people or groups because they have very different beliefs or opinions – used in news reports</span><span class="gramexa"><span class="propformprep">clash between/with</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-000888272"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-000888272').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The plans put oil companies in a <span class="colloinexa">head-on clash</span> with environmentalists.</span></span><span class="colloexa"><span class="collo">personality/culture clash</span><span class="gloss"><span class="neutral"> (=</span>a situation in which two people or groups do not like each other<span class="neutral">)</span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_2_s3"></a><span class="sense"><span class="sensenum">3</span><span class="def">a sports match between two teams, players etc that is expected to be very exciting – used in sports reports</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001662961"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001662961').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The heavyweight clash goes ahead in Las Vegas on 8 May.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_2_s4"></a><span class="sense"><span class="sensenum">4</span><span class="def">a situation in which two events happen at the same time in a way that is inconvenient</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-000888286"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000888286').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a scheduling clash on TV</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_2_s5"></a><span class="sense"><span class="sensenum">5</span><span class="def">a loud sound made by two metal objects hitting each other</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-000888279"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-000888279').play();"><img src="LDOCE6_img_spkr_g.png"/></a> the clash of swords</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_clash_2_s6"></a><span class="sense"><span class="sensenum">6</span><span class="def">a combination of two colours, designs etc that look bad together</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-001662962"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-001662962').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a colour clash</span></span><span class="tail"><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="section"><span class="secheading">adjectives</span><span class="collocate"><span class="colloc collo">a violent clash</span><span class="example"><span class="neutral">· </span>Four people were killed during violent clashes with the army.</span></span><span class="collocate"><span class="colloc collo">a fierce/bitter clash</span><span class="collgloss"><span class="neutral"> (=</span>involving violence and strong feelings<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Fierce clashes swept across Bosnia and Croatia.</span></span><span class="collocate"><span class="colloc collo">a serious clash</span><span class="example"><span class="neutral">· </span>This was one of the most serious clashes since the two countries agreed to a ceasefire.</span></span><span class="collocate"><span class="colloc collo">a minor clash</span><span class="example"><span class="neutral">· </span>Minor clashes were reported to have taken place between police and students.</span></span><span class="collocate"><span class="colloc collo">an armed clash</span><span class="collgloss"><span class="neutral"> (=</span>involving the use of weapons<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The violence could soon become armed clashes and even a war.</span></span><span class="collocate"><span class="colloc collo">an ethnic clash</span><span class="collgloss"><span class="neutral"> (=</span>between people of different races<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>20 people died in ethnic clashes before Christmas.</span></span></span><span class="section last"><span class="secheading">verbs</span><span class="collocate"><span class="colloc collo">lead to a clash</span><span class="example"><span class="neutral">· </span>The anti-war protests led to violent clashes between police and demonstrators.</span></span><span class="collocate"><span class="colloc collo">a clash occurs</span><span class="example"><span class="neutral">· </span>A number of clashes occurred throughout the south between these opposing groups.</span></span><span class="collocate"><span class="colloc collo">a clash breaks out</span><span class="example"><span class="neutral">· </span>Armed clashes broke out on Tuesday between the military and the rebels.</span></span><span class="collocate"><span class="colloc collo">a clash takes place</span><span class="example"><span class="neutral">· </span>Fierce clashes took place with local police.</span></span><span class="collocate"><span class="colloc collo">be killed/injured in a clash</span><span class="example"><span class="neutral">· </span>Eight people were killed in clashes between the army and police.</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_3_clash_n0205" preload="none" src="LDOCE6_hwd_bre_3_clash_n0205.mp3"></audio><audio id="LDOCE6_hwd_ame_8_clash1" preload="none" src="LDOCE6_hwd_ame_8_clash1.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-000888225" preload="none" src="LDOCE6_exa_bre_b_p008-000888225.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-000888228" preload="none" src="LDOCE6_exa_bre_4_p008-000888228.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001662946" preload="none" src="LDOCE6_exa_bre_1_p008-001662946.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001662949" preload="none" src="LDOCE6_exa_bre_e_p008-001662949.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-000888242" preload="none" src="LDOCE6_exa_bre_3_p008-000888242.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-000888249" preload="none" src="LDOCE6_exa_bre_9_p008-000888249.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-000888254" preload="none" src="LDOCE6_exa_bre_1_p008-000888254.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001789821" preload="none" src="LDOCE6_exa_bre_1_p008-001789821.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001662954" preload="none" src="LDOCE6_exa_bre_3_p008-001662954.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-000888272" preload="none" src="LDOCE6_exa_bre_1_p008-000888272.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001662961" preload="none" src="LDOCE6_exa_bre_8_p008-001662961.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-000888286" preload="none" src="LDOCE6_exa_bre_2_p008-000888286.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-000888279" preload="none" src="LDOCE6_exa_bre_b_p008-000888279.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-001662962" preload="none" src="LDOCE6_exa_bre_a_p008-001662962.mp3"></audio>`;
