var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_expect_1"></a><div><a id="6504937708254a64972bf4d4ccdfcc9d_expect"></a><span class="entry"><span class="entryhead"><span class="hwd">expect</span><span class="hyphenation frequent">ex<span class="hs0"></span>pect</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">ɪkˈspekt</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S1</span> <span class="freq">W1</span><span class="pos"> verb</span><span class="gram"> [transitive]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_4_expect0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_b_expect').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Entry menu</span><div class="at-link"><span class="entry"><span class="popheader pope_menu">MENU FOR expect</span><span class="entrymenu"><span class="hwd">expect</span><span class="menuitem"><span class="sensenum">1 </span><span class="signpost">think something will happen</span></span><span class="menuitem"><span class="sensenum">2 </span><span class="signpost">demand</span></span><span class="menuitem"><span class="sensenum">3 </span><span class="signpost">think somebody/something will arrive</span></span><span class="menuitem"><span class="sensenum">4 </span><span class="signpost">think</span></span><span class="menuitem"><span class="sensenum">5 </span><span class="lexunit">be expecting (a baby)</span></span><span class="menuitem"><span class="sensenum">6 </span><span class="lexunit">what can/do you expect?</span></span><span class="menuitem"><span class="sensenum">7 </span><span class="lexunit">how do/can you expect ...?</span></span><span class="menuitem"><span class="sensenum">8 </span><span class="lexunit">I expect</span></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">expect</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1500-1600</span> <span class="etymlang">Latin</span> <span class="etymorigin">exspectare</span> <span class="etymtran"> ‘to look forward to’</span>, from <span class="etymorigin">spectare</span> <span class="etymtran"> ‘to look at’</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">expect</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">expect</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">expects</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">expected</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">expected</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">expected</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">expected</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">expect</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">expected</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">expecting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">expecting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">expecting</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">expecting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">expecting</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">expecting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">expecting</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">expecting</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">expecting</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">expecting</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>At these prices, I <span class="nodeword">expect</span> better service.</li><li>Drivers should <span class="nodeword">expect</span> long delays on all roads out of town today.</li><li>Economists <span class="nodeword">expect</span> the economy to grow by 5% next year.</li><li>Forecasters <span class="nodeword">expect</span> snow in the mountains.</li><li>How many people are you <span class="nodeword">expecting</span>?</li><li>I'm <span class="nodeword">expecting</span> a call from him soon.</li><li>I'm <span class="nodeword">expecting</span> a fax from Korea. Has anything arrived yet?</li><li>I <span class="nodeword">expected</span> to find him in the bar, but he wasn't there.</li><li>Light rain is <span class="nodeword">expected</span> today in the Bay Area.</li><li>Perkins fully <span class="nodeword">expects</span> to be back in Boston by July 1.</li><li>She's shorter than I <span class="nodeword">expected</span>.</li><li>We all <span class="nodeword">expected</span> she'd get the job - it was a real shock when she didn't.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>But just don, t <span class="nodeword">expect</span> me not to whinge about it.</li><li>I hope you.won't <span class="nodeword">expect</span> me to discuss it.</li><li>It might be <span class="nodeword">expected</span> that the percentage thinking that health had improved since their parents' time would increase with age.</li><li>People <span class="nodeword">expect</span> lawyers to look this way.</li><li>The Hindu of May 20 reported that the death toll in the coastal districts had reached 817 and was <span class="nodeword">expected</span> to rise.</li><li>This week the company released new drilling results showing that nickel reserves are twice as large as <span class="nodeword">expected</span>.</li><li>When he heard the heavy doors open, the loud voices, he knew what to <span class="nodeword">expect</span>.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to expect something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">expect</span></span><div class="content"><span class="def"> if you expect something to happen, you think it probably will</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm expecting a fax from Korea. Has anything arrived yet?</span><span class="example"><span class="neutral">· </span>Drivers should expect long delays on all roads out of town today.</span><span class="thespropform">expect to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I expected to find him in the bar, but he wasn't there.</span><span class="thespropform">expect (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We all expected she'd get the job - it was a real shock when she didn't.</span><span class="thespropform">expect somebody/something to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Economists expect the economy to grow by 5% next year.</span><span class="thespropform">fully expect</span><span class="gloss"><span class="neutral"> (=</span>confidently expect that something will definitely happen<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Perkins fully expects to be back in Boston by July 1.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">think</span></span><div class="content"><span class="def"> to believe that something is likely to happen</span><span class="thespropform"><span class="neutral">: </span>think (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Do you think they'll come to the party?</span><span class="example"><span class="neutral">· </span>I never thought her business would be so successful.</span><span class="thespropform">think something is likely</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The builders said the job would be finished tomorrow, but I don't think that's likely.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">anticipate</span></span><div class="content"><span class="def"> to expect that something will happen, and be prepared for it - use this especially to say that something was different from what you had expected</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The journey took a lot longer than we had anticipated.</span><span class="thespropform">anticipate that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We had anticipated that interest rates would have fallen further by now.</span><span class="thespropform">anticipate doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I think we've fixed everything, and I don't anticipate finding any more problems.</span></div></span></span><span class="section"><span class="secheading">what you say when you expect something to happen</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I expect</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially British</span><span class="variant"> <span class="lexvar">/I guess</span></span><span class="neutral"> </span><span class="registerlab">especially American</span> <span class="example"><span class="neutral">· </span>Hasn't Tony arrived yet? He'll be here soon, I expect.</span><span class="thespropform">I expect (that)</span> <span class="example"><span class="neutral">· </span>I expect your mother will be overjoyed when she hears you're having a baby.</span><span class="thespropform">I expect/guess so</span> <span class="example"><span class="neutral">· </span>"Is Alan going to drive tonight?" "Yeah, I guess so."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I bet/my bet is</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> use this to introduce something that you expect to happen because of what you know about someone or about the way things usually happen. I bet is more common than my bet is</span><span class="thespropform"><span class="neutral">: </span>I bet/my bet is (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I bet you'll miss your boyfriend when you go away for college.</span><span class="example"><span class="neutral">· </span>Sandra says she's never going to have any children, but my bet is she has at least three.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I suspect</span></span><div class="content"><span class="def"> use this when you have a feeling that something is going to happen, especially something bad or unpleasant</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You'll find Rick's parents rather hard to talk to, I suspect.</span><span class="thespropform">I suspect (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I suspect that 10 years after the book is published, nobody will even remember the name of the author.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I would think</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially British</span><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">I should think</span><span class="neutral"> </span><span class="registerlab">British</span></span><span class="def"> use this when you think something is likely to happen although you are not really sure</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It'll be cooler down by the lake, I would think.</span><span class="thespropform">I would think (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I should think Sarah and Greg will be going to the party.</span><span class="thespropform">I would/should think so</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Will you have finished work by 6.30?'' "Oh yes, I should think so.''</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I wouldn't be surprised</span></span><div class="content"><span class="def"> say this when you think something may happen, even though other people think it is unlikely</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Do you think they'll get married?" "I wouldn't be surprised."</span><span class="thespropform">I wouldn't be surprised if</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You know, I wouldn't be surprised if some of the top executives lose their jobs.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">my expectation is</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> use this to introduce something that you expect to happen because of information that you have</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My expectation is that the two companies will eventually come to an agreement.</span></div></span></span><span class="section"><span class="secheading">to do something because you expect something to happen</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">figure on/reckon on</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> to expect something to happen, so that you make plans or take actions that depend on it happening</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We had expected it to take about an hour to get home, but we hadn't reckoned on the traffic.</span><span class="thespropform">reckon/figure on doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Visitors to the city should figure on spending about $150 a day for food and lodging.</span><span class="thespropform">reckon/figure on somebody doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We can reckon on about 100 people coming to the meeting.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">count on</span></span><div class="content"><span class="def"> to expect something so much that your plans depend on it happening or you are completely prepared for it when it happens</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We're counting on good weather for the picnic - if it rains, we'll have to cancel.</span><span class="thespropform">count on doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The automaker is planning on earning large profits with this new model.</span><span class="thespropform">count on somebody doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Don't count on Congress passing the bill anytime soon.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">in expectation/anticipation of</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially written</span><span class="def"> if you make plans or take actions in expectation or anticipation of something happening, you do this because you expect it to happen and you want to be prepared</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The workers have called off their strike in expectation of a pay settlement.</span><span class="example"><span class="neutral">· </span>Residents are buying supplies and stacking wood in anticipation of the coming storms.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">don't count your chickens (before they're hatched)</span></span><div class="content"><span class="def"> use this to tell someone not to be too sure that what they are hoping for will happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If you want to go on a date sometime, you can ask me. But don't count your chickens.</span><span class="example"><span class="neutral">· </span>Getting an Oscar would be wonderful, but I think it's too early to count my chickens.</span></div></span></span><span class="section"><span class="secheading">to make someone expect something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">lead somebody to expect</span></span><div class="content"><span class="def"> to encourage someone to expect something, especially something that does not actually happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The hotel was horrible - not at all what we'd been led to expect.</span><span class="example"><span class="neutral">· </span>I think his campaign promises led us all to expect he'd be a much more honest politician.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">raise/arouse expectations</span></span><div class="content"><span class="def"> if something that someone says raises or arouses expectations , it makes people begin to expect something good or interesting</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A good manager raises expectations among employees.</span><span class="thespropform">raise/arouse expectations of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The prime minister's remarks aroused expectations of tax cuts.</span></div></span></span><span class="section"><span class="secheading">expecting something good to happen</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">optimistic</span></span><div class="content"><span class="def"> someone who is optimistic expects good things to happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In spite of all her problems she manages to remain optimistic.</span><span class="example"><span class="neutral">· </span>an optimistic economic forecast</span><span class="thespropform">optimistic about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm pretty optimistic about our chances of winning here today.</span><span class="thespropform">optimistic that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Are you still optimistic that the climbers can be rescued?</span><span class="thespropform">cautiously/guardedly optimistic</span><span class="gloss"><span class="neutral"> (=</span>expecting good things to happen but knowing that they might not happen<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We are cautiously optimistic that the trade deal will go through.</span><span class="thespropform">overly optimistic</span><span class="gloss"><span class="neutral"> (=</span>more optimistic than you should be<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The company was overly optimistic in its sale projections.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">optimist</span></span><div class="content"><span class="def"> someone who always expects good things to happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Optimists still believe we can resolve the problem without going to war.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">upbeat</span></span><div class="content"><span class="def"> expressing a cheerful attitude and showing that you expect to succeed, even though the situation may not be encouraging</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The senator took an upbeat view of the Republicans' chances in the next election.</span><span class="thespropform">upbeat about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Analysts are more upbeat about the long-term outlook for the economy.</span></div></span></span><span class="section"><span class="secheading">expecting something bad to happen</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pessimistic</span></span><div class="content"><span class="def"> someone who is pessimistic always expects bad things to happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Don't be too pessimistic - we may still win the game.</span><span class="thespropform">pessimistic about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He's quite pessimistic about his chances of getting another job.</span><span class="thespropform">pessimistic that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Beron is pessimistic that a peaceful solution can be found.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pessimist</span></span><div class="content"><span class="def"> someone who always expects bad things to happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Don't be such a pessimist - I'm sure you'll pass your driving test!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">downbeat</span></span><div class="content"><span class="def"> expressing an attitude that is not at all hopeful and showing that you do not expect success</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The latest economic surveys are more downbeat as a result of the recent decline in world trade.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">gloomy</span></span><div class="content"><span class="def"> not having or offering much hope for the future</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A year ago it seemed that a peace treaty looked possible, but now the outlook is much gloomier.</span><span class="example"><span class="neutral">· </span>The world's largest chip maker gave a gloomy forecast for the first quarter.</span><span class="thespropform">gloomy about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Most political analysts are gloomy about the country's future.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fear the worst</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> to expect a situation to have the worst possible result because you know how bad the situation could be</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>After I hadn't heard from him for several hours, I began to fear the worst.</span><span class="example"><span class="neutral">· </span>Fearing the worst, police have called in reinforcements to help control the crowds.</span></div></span></span><span class="section"><span class="secheading">when things happens in the way you expected</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">as expected</span></span><div class="content"><span class="def"> if something happens as expected , it happens exactly in the way that people expected it to happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>As expected, the three men were sentenced to life imprisonment.</span><span class="example"><span class="neutral">· </span>Tulsa beat New Mexico as expected in last night's game.</span><span class="thespropform">as was/had been expected</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Yesterday, as had been expected, the government announced its intention to launch a public enquiry.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be no surprise/come as no surprise</span></span><div class="content"><span class="def"> if something that happens is no surprise or comes as no surprise , it is exactly as you expected, so you are not surprised by it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>After a series of scandals, Fleischman's resignation comes as no surprise.</span><span class="thespropform">be no surprise/come as no surprise to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Tina was shocked when she heard she was not being promoted, but it came as no surprise to the rest of us.</span><span class="thespropform">be/come as no great surprise</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The fact that Brown has decided to run for reelection comes as no great surprise.</span><span class="thespropform">it is/comes as no surprise that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's no surprise that Jeff and his wife are getting divorced.</span><span class="thespropform">be no surprise/come as no surprise to hear/discover/find etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was no surprise to hear that Joel had messed the whole thing up again.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">predictable</span></span><div class="content"><span class="def"> happening as you expect - use this especially about someone's behaviour, when you think they are boring or stupid because they always do exactly what you expect</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My dad's so predictable - every evening he comes home, has two beers, and falls asleep in front of the TV.</span><span class="example"><span class="neutral">· </span>The movie was completely predictable - I couldn't wait for it to end.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be only to be expected</span></span><div class="content"><span class="def"> if something, especially something bad or unpleasant, is only to be expected , it is what you should expect in that situation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A few mistakes were only to be expected when you're cooking something for the first time.</span><span class="thespropform">it is only to be expected that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>When you're over 60, it's only to be expected that you can't do as much as you used to.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be par for the course</span></span><div class="content"><span class="def"> to be what you would normally expect to happen in a particular situation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If you want to be a politician, a little criticism is par for the course.</span><span class="example"><span class="neutral">· </span>It seems in some of those countries that political torture and assassination are par for the course.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I'm not surprised</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when something happens that you expected to happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not surprised she left him - look at the way he treated her!</span><span class="example"><span class="neutral">· </span>Of course I'm disappointed that we lost, but I'm not really surprised.</span></div></span></span><span class="section"><span class="secheading">when something happens that you did not expect</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">unexpected</span></span><div class="content"><span class="def"> something that is unexpected surprises you because you did not expect it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There have been unexpected delays on the freeway because of an accident.</span><span class="thespropform">completely/totally unexpected</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Bobby's decision to leave the band was totally unexpected.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">unforeseen</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>unforeseen circumstances/problems/changes etc</span><span class="def"> situations, problems, changes etc that you did not expect or prepare for, and which usually cause you difficulty</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We had to cancel our visit to Egypt because of unforeseen problems.</span><span class="example"><span class="neutral">· </span>Once you have started the training you will not be allowed to leave, unless unforeseen circumstances arise.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be a surprise/come as a surprise</span></span><div class="content"><span class="def"> if something that happens is a surprise or comes as a surprise , you did not expect it to happen, and so you are surprised by it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Winning the award was a total surprise.</span><span class="thespropform">be a surprise/come as a surprise to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Fazio's announcement came as a surprise to most political observers.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">out of the blue</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> if something happens out of the blue , you did not expect it, and you are very surprised or shocked by it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Out of the blue, he asked me to come with him to Europe.</span><span class="example"><span class="neutral">· </span>One evening, Angela phoned me out of the blue and said she was in some kind of trouble.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">the last person/thing/place (that) you would expect</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> one that you did not expect, so that you are very surprised</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mary's the last person you'd expect to be stopped for drunk driving</span><span class="example"><span class="neutral">· </span>It's such a quiet little village - it's the last place you'd expect something like this to happen.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">catch somebody off guard/catch somebody unawares</span></span><div class="content"><span class="def"> to happen or do something when someone is not expecting it and not ready to deal with it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I was caught unawares by the hug and the kiss he gave me.</span><span class="example"><span class="neutral">· </span>Her rude comments really caught me off guard.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">contrary to expectations</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> if something happens contrary to expectations , it is the opposite of what people expected to happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Contrary to expectations, the play was a big success.</span><span class="thespropform">contrary to somebody's expectations</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Michael won the competition, contrary to everyone's expectations.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">unannounced</span></span><div class="content"><span class="def"> happening unexpectedly, because no one was told about it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Investigators from the health department made unannounced visits to the hospital in March.</span><span class="thespropform">arrive unannounced/show up unannounced</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My brother is famous for showing up at our houses unannounced, usually around dinner time.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">in your wildest dreams</span></span><div class="content"><span class="def"> if something happens that you did not expect in your wildest dreams , it is so good that you never thought that there was any possibility that it could happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In our wildest dreams, we could not have expected how successful this program would be.</span><span class="thespropform">never in somebody's wildest dreams</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Never in her wildest dreams had she imagined she would win the gold medal.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">more than you (had) bargained for</span></span><div class="content"><span class="def"> if you get more than you had bargained for , you have more problems or difficulties while doing something than you had expected or prepared for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Their summer vacation turned out to be much more expensive than they'd bargained for.</span><span class="example"><span class="neutral">· </span>We got more than we bargained for when we bought the house. There's so much work to be done!</span></div></span></span><span class="section"><span class="secheading">when someone or something is expected</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">expected</span></span><div class="content"> <span class="example"><span class="neutral">· </span>An expected crowd of 200,000 will take part in the march.</span><span class="example"><span class="neutral">· </span>The expected increase in interest rates has already had an effect on stock prices.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">long-awaited</span></span><div class="content"><span class="def"> a long-awaited event, book, film etc is one that people have been expecting for a long time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Band members were celebrating the long-awaited release of their first album.</span><span class="example"><span class="neutral">· </span>The court's long-awaited decision was unpopular with groups on both sides of the issue.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be due</span></span><div class="content"><span class="def"> to be expected to happen, arrive, or be finished at a particular time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>When is your baby due?</span><span class="thespropform">be due at</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The flight from New York is due at 10:30.</span><span class="thespropform">be due in</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I've ruined the meal and our guests are due in half an hour!</span><span class="thespropform">be due on</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The report is due on Monday, so we'll have to work over the weekend.</span><span class="thespropform">be due to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Federal employees are due to return to work on January 8.</span></div></span></span><span class="section"><span class="secheading">having a baby developing in your body</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pregnant</span></span><div class="content"> <span class="example"><span class="neutral">· </span>The health centre provides milk and vitamins for pregnant women.</span><span class="example"><span class="neutral">· </span>When our cat was pregnant she looked like a round, furry ball.</span><span class="thespropform">get pregnant</span> <span class="example"><span class="neutral">· </span>We can't stop teens from having sex, but we can help them to avoid getting pregnant.</span><span class="thespropform">pregnant with somebody</span> <span class="example"><span class="neutral">· </span>When I was pregnant with Mandy, I felt fat and unattractive.</span><span class="thespropform">twenty weeks/three months etc pregnant</span> <span class="example"><span class="neutral">· </span>When Janette was three months pregnant, she caught flu.</span><span class="thespropform">heavily pregnant</span><span class="neutral"> </span><span class="registerlab">British</span><span class="gloss"><span class="neutral"> (=</span>when the baby is almost ready to be born<span class="neutral">)</span></span> <span class="example"><span class="neutral">· </span>By this time I was heavily pregnant and could hardly get into any of my clothes.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be going to have a baby/be having a baby</span></span><div class="content"><span class="def"> to have a baby developing in your body - used especially when you are telling someone that you or another person is going to have a baby</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Isn't it wonderful that Susie's going to have a baby?</span><span class="example"><span class="neutral">· </span>My boss is having a baby in March, but she's only taking two months maternity leave.</span><span class="example"><span class="neutral">· </span>The doctor told Ellie that the tests were positive and she was going to have a baby.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be expecting</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken informal</span><span class="def"> to have a baby developing inside you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They'd only been married a couple of months and already Rebecca was expecting.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">expectant mother</span></span><div class="content"><span class="def"> a woman who is going to have a baby</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Expectant mothers are entitled to free healthcare.</span><span class="example"><span class="neutral">· </span>Expectant mothers sometimes have cravings for unusual foods.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">mother-to-be</span></span><div class="content"><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">mum-to-be</span><span class="neutral"> </span><span class="registerlab">British informal</span></span><span class="def"> a woman who is going to have a baby</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I hear you are now a mother-to-be.</span><span class="example"><span class="neutral">· </span>For first time mothers-to-be, the importance of regular check-ups cannot be stressed enough.</span></div></span></span><span class="section last"><span class="secheading">typical behaviour or qualities</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">typical</span></span><div class="content"><span class="def"> behaviour or actions that are typical of a person are just what you usually expect them to do, especially when this is something bad or annoying</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Dad forgot to mail the letter." "That's just typical!"</span><span class="example"><span class="neutral">· </span>Mrs Quilley greeted the guests with typical charm and confidence.</span><span class="example"><span class="neutral">· </span>This is typical adolescent behaviour - part of the process of becoming independent from your parents.</span><span class="thespropform">it is typical of somebody to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's typical of Ramon to waste time when he knows we're already late.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">characteristic</span></span><div class="content"><span class="def"> very typical of a particular type of thing, or of someone's character or usual behaviour</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Each species of bird has its own characteristic song.</span><span class="example"><span class="neutral">· </span>Larry, with characteristic generosity, invited everyone back to his house.</span><span class="thespropform">characteristic of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>This pattern is characteristic of the local architecture.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">just what you'd expect</span></span><div class="content"><span class="def"> if you say that something is just what you'd expect , you mean that it is exactly what your idea of someone makes you expect them to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Jenny's house is very clean and neat, just what you'd expect.</span><span class="example"><span class="neutral">· </span>He was annoyed, but he soon got over it -- just what you'd expect, in fact.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be just like</span></span><div class="content"><span class="def"> if you say that someone's action or behaviour is just like them, you mean that it is typical of them in a way that annoys you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You have left everything to the last minute. That's just like you.</span><span class="thespropform">it is just like somebody to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's just like Uncle Roy to invite us all to lunch and then forget to tell Aunt Sarah.</span><span class="example"><span class="neutral">· </span>It would be just like my son to get the measles twice.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">that's somebody all over</span></span><div class="content"><span class="def"> used for saying that you are not surprised that someone has done something or behaved in a particular way, because it's the kind of thing they often do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was late, of course, but that's Tim all over.</span><span class="example"><span class="neutral">· </span>"That's Dora all over," interrupted Rose with a sniff. "Once she gets an idea into her head, nothing will stop her."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">true to form</span></span><div class="content"><span class="def"> if you say that someone does something true to form you mean it is very typical of them, especially when it is something annoying</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>True to form, Oliver turned up late and drunk.</span><span class="example"><span class="neutral">· </span>They promised to pay by Friday and yes, true to form, the money didn't arrive till Wednesday.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">would</span></span><div class="content"><span class="def"> you say someone would do something when they do something that is typical of them and you want to criticize them for it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Janice is going to be late for the meeting." "She would be!"</span><span class="example"><span class="neutral">· </span>"And then Harry got drunk." "He would do, wouldn't he!"</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">the inevitable</span></span><div class="content"><span class="def"> usual and expected for a particular person or type of person or a situation - you can often use this in humorous contexts</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was a typical student's room with the inevitable Van Gogh print on the wall.</span><span class="example"><span class="neutral">· </span>Dinner started with the inevitable chicken soup.</span><span class="example"><span class="neutral">· </span>In the subway I got cornered by the inevitable drunkard wanting to give me some advice.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be in character</span></span><div class="content"><span class="def"> an action or remark that is in character is typical of someone's character</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Julie said that? That doesn't seem to be in character at all.</span><span class="example"><span class="neutral">· </span>His reaction was quite in character. The man had no respect for education, and it was no surprise when he took his son out of school.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="section"><span class="secheading">adverbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">fully expect</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>completely<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>We fully expected to win.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">confidently expect</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>with a feeling of confidence<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He confidently expected to be elected again.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">half expect</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>partly, but not completely<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He walked slowly towards the box, half expecting it to explode.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">really expect</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>definitely<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I didn’t really expect her to come.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">honestly expect</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>really expect<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Do you honestly expect me to look after the kids while you go on holiday?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">hardly expect</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>almost not<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>You can hardly expect a child of three to know the difference between right and wrong.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">rightly expect</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>with good reason<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The public rightly expects government officials to be honest.</span></div></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">as expected</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>in the way that was planned or thought likely to happen<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Tickets have not been selling as well as expected.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">something is (only) to be expected</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>used to say that you are not surprised by something unpleasant<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>After all this rain, some flooding is only to be expected.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">something happens when you least expect it</span></span><div class="content"><span class="example"><span class="neutral">· </span>Bad luck tends to happen when you least expect it.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">somebody is entitled to expect something</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>have the right to think something will happen<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>You’re entitled to expect decent service at these prices.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">it is reasonable/unreasonable to expect something</span></span><div class="content"><span class="example"><span class="neutral">· </span>It’s unreasonable to expect a tenant to pay for repairs to the outside of the house.</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">be expecting a baby</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>be pregnant<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>My wife’s expecting a baby.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">expect a call</span></span><div class="content"><span class="example"><span class="neutral">· </span>She’s expecting a phone call from Matt.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">lead somebody to believe/expect/understand something</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001802583').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He had led everyone to believe that his family was very wealthy.</span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001802584').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The hotel was terrible, and not at all what we had been led to expect.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">don't expect miracles</span></span><div class="content"><span class="example"><span class="neutral">· </span>Don’t expect miracles. A hairdresser can't make a 50-year-old look like a 20-year-old.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">demand/expect obedience</span></span><div class="content"><span class="example"><span class="neutral">· </span>Parents should not demand unquestioning obedience from their children.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">expect sympathy</span></span><div class="content"><span class="example"><span class="neutral">· </span>I know I can’t expect any sympathy from her!</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">what to do/say/expect etc</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001814596').play();"><img src="LDOCE6_img_spkr_g.png"/></a> They’re discussing what to do next.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">fully</span></span><div class="content"><span class="example"><span class="neutral">· </span>Goldstein <span class="colloinexa">fully expects</span> an Architecture-Neutral Distribution Format product programme to be initiated during 1994.</span><span class="example"><span class="neutral">· </span>Any day now, we <span class="colloinexa">fully expect</span> to hear some guy playing Sousa marches in his armpit.</span><span class="example"><span class="neutral">· </span>Irene had <span class="colloinexa">fully expected</span> that Douglas's move to a new branch would lead to promotion, but it hadn't happened.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">fully expect</span> that Tony will end his career with the San Diego Padres.</span><span class="example"><span class="neutral">· </span>She had <span class="colloinexa">fully expected</span> to be dismissed the next morning, but nothing was said and she didn't ask.</span><span class="example"><span class="neutral">· </span>She <span class="colloinexa">fully expected</span> to be back in Boston July 1 for the opening of the vacation activities.</span><span class="example"><span class="neutral">· </span>He was waiting to see you and <span class="colloinexa">fully expecting</span> you would be there.</span><span class="example"><span class="neutral">· </span>She <span class="colloinexa">fully expected</span> to see her suitcases standing somewhere.</span></div></span></span><span class="section last"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">analyst</span></span><div class="content"><span class="example"><span class="neutral">· </span>Political <span class="colloinexa">analysts expect</span> the Labor government to call the poll some time in February, about five weeks before it is held.</span><span class="example"><span class="neutral">· </span>Heavy advertising has won candidate Forbes far more attention than political <span class="colloinexa">analysts expected</span> he would get.</span><span class="example"><span class="neutral">· </span>Henry Kaufman, the economist and investment <span class="colloinexa">analyst</span>, might be <span class="colloinexa">expected</span> to welcome the trend.</span><span class="example"><span class="neutral">· </span>Most <span class="colloinexa">analysts</span> had <span class="colloinexa">expected</span> a loss.</span><span class="example"><span class="neutral">· </span>But <span class="colloinexa">analysts expect</span> the bullish trend to continue.</span><span class="example"><span class="neutral">· </span>Cirrus had warned <span class="colloinexa">analysts</span> to <span class="colloinexa">expect</span> a significant shortfall.</span><span class="example"><span class="neutral">· </span>To complete the turnaround at the Glasgow-based group, <span class="colloinexa">analysts</span> are <span class="colloinexa">expecting</span> a call on investors with a rights issue.</span><span class="example"><span class="neutral">· </span>Many market <span class="colloinexa">analysts expect</span> the sector to continue to climb in 1996, benefiting in part from the colder weather this winter.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">how do/can you expect ...?</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">I expect</span></span><div class="content"><ul class="exas"><li>Hasn't Tony arrived yet? He'll be here soon, I <span class="nodeword">expect</span>.</li><li>I <span class="nodeword">expect</span> you're right.</li><li>I <span class="nodeword">expect</span> your mother will be overjoyed when she hears you're having a baby.</li><li>But I <span class="nodeword">expect</span> it is the effects of expatriation.</li><li>But this team surprises me. I <span class="nodeword">expect</span> us to go out and play well.</li><li>Gosh darn it, I <span class="nodeword">expected</span> it to be done.</li><li>I had not asked him for anything, nor had I <span class="nodeword">expected</span> anything.</li><li>We had a sort of poet in this house once. I <span class="nodeword">expect</span> you'd think nothing to her.</li><li>When I opened the frame I found, as I <span class="nodeword">expected</span>, that the drawing had been hinged with masking tape.</li><li>With these memories, I <span class="nodeword">expected</span> Galway City, my destination, to be a hopeless, broken-down kind of place.</li><li>Yes, I <span class="nodeword">expect</span> you did have to give a lot for your house.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">be expecting (a baby)</span></span><div class="content"><ul class="exas"><li>They're <span class="nodeword">expecting</span> their first child September 5.</li><li>Did I really have it before I was <span class="nodeword">expecting</span> Annie?</li><li>Having heard of his family's desire for an heir, she fantasised a little as to whether his wife might be <span class="nodeword">expecting</span> again.</li><li>However, Perot supporters filed new petitions and are <span class="nodeword">expecting</span> an answer soon from the Ohio secretary of state.</li><li>I was <span class="nodeword">expecting</span> them to go to three wide receivers..</li><li>Just as everyone was <span class="nodeword">expecting</span> a draw, however, Yusupov made a an unexpected sacrifice of a bishop.</li><li>One in five had received no training in the Act and most were <span class="nodeword">expecting</span> to receive none during the next six months.</li><li>USAir, based in Arlington, Va., is <span class="nodeword">expecting</span> similar problems today.</li><li>We are <span class="nodeword">expecting</span> a little action very soon.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">what can/do you expect?</span></span><div class="content"><ul class="exas"><li>Nothing spectacular but what do you <span class="nodeword">expect</span> from beginners?!!!</li><li>Sad about the Derwent but what can we <span class="nodeword">expect</span>?</li><li>Sure, what do you <span class="nodeword">expect</span> for that kind of money?</li><li>What can we <span class="nodeword">expect</span> in the future?</li><li>What can you <span class="nodeword">expect</span> from those young people in the small towns.</li><li>What do you <span class="nodeword">expect</span> from an also-ran business publication like Fortune?</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">England expects that every man will do his duty</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">the last thing somebody wants/expects/needs etc</span></span><div class="content"><ul class="exas"><li>I like going to bed with her when going to bed with me is the last <span class="nodeword">thing</span> she wants.</li><li>To be slipshod is to be hounded, which is the last <span class="nodeword">thing</span> he wants.</li><li>With household costs inevitably rising, the last <span class="nodeword">thing</span> he wants is a larger mortgage than he can reasonably afford.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">expect/fear the worst</span></span><div class="content"><ul class="exas"><li>Distillery boss Billy Hamilton fears the <span class="nodeword">worst</span> after Heath was assisted off in the second-half with a torn calf muscle.</li><li>From what he has heard he fears the <span class="nodeword">worst</span> about the likelihood of a quick turnaround on the field.</li><li>I knew I was being irrational but I began to fear the <span class="nodeword">worst</span>.</li><li>Leading the mob assault into the fisherman's cabin, the pastor expects the <span class="nodeword">worst</span>.</li><li>Only then did we begin to fear the <span class="nodeword">worst</span>.</li><li>Quite frankly we expected the <span class="nodeword">worst</span>.</li></ul></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word family</span><div class="at-link"><span class="entry"><span class="popheader popwf">WORD FAMILY</span><span class="wf"><span class="group"><span class="pos">noun</span><span class="w"><span class="wfwd">expectancy</span></span><span class="w"><span class="wfwd">expectation</span></span></span><span class="group"><span class="pos">adjective</span><span class="w"><span class="wfwd">expectant</span></span><span class="w">expected ≠ <span class="opp"><span class="wfwd">unexpected</span></span></span></span><span class="group"><span class="pos">adverb</span><span class="w"><span class="wfwd">expectantly</span></span><span class="w"><span class="wfwd">unexpectedly</span></span></span><span class="group"><span class="pos">verb</span><span class="w"><span class="wfwd">expect</span></span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="signpost">think something will happen</span><span class="def"> to think that something will happen because it seems likely or has been planned</span><span class="gramexa"><span class="propform">expect to do something</span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001653652"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001653652').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I expect to be back within a week.</span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001653653"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001653653').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The company expects to complete work in April.</span></span><span class="gramexa"><span class="propform">expect somebody/something to do something</span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001231988"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001231988').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Emergency repairs were expected to take three weeks.</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001653656"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001653656').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I didn’t expect him to stay so long.</span></span><span class="gramexa"><span class="propform">expect (that)</span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-001231985"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001231985').play();"><img src="LDOCE6_img_spkr_g.png"/></a> There’s the doorbell – I expect it’ll be my mother.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-001653659"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-001653659').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He will be hard to beat. I <span class="colloinexa">fully expect</span><span class="gloss"><span class="neutral"> (=</span>am completely sure about<span class="neutral">)</span></span> that and I’m ready.</span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001653664"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001653664').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘Who are you?’ he murmured, only <span class="colloinexa">half expecting</span><span class="gloss"><span class="neutral"> (=</span>thinking it was possible, but not likely<span class="neutral">)</span></span> her to answer.</span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-001653669"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-001653669').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He didn’t get his expected pay rise.</span><span class="gramexa"><span class="propform">as expected</span><span class="gloss"><span class="neutral"> (=</span>in the way that was planned or thought likely to happen<span class="neutral">)</span></span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-001232003"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001232003').play();"><img src="LDOCE6_img_spkr_g.png"/></a> As expected, the whole family was shocked by the news.</span></span><span class="colloexa"><span class="collo">something is (only) to be expected</span><span class="gloss"><span class="neutral"> (=</span>used to say that you are not surprised by something, especially something unpleasant<span class="neutral">)</span></span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001232007"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001232007').play();"><img src="LDOCE6_img_spkr_g.png"/></a> A little nervousness is only to be expected when you are starting a new job.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="signpost">demand</span><span class="def"> to demand that someone does something because it is a duty or seems reasonable</span><span class="gramexa"><span class="propform">expect something from somebody</span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-001232018"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-001232018').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The officer expects complete obedience from his troops.</span></span><span class="gramexa"><span class="propform">expect somebody to do something</span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001232021"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001232021').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I can’t expect her to be on time if I’m late myself.</span></span><span class="colloexa"><span class="collo">expect a lot of somebody/expect too much of somebody</span><span class="gloss"><span class="neutral"> (=</span>think someone can do more than may be possible<span class="neutral">)</span></span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001232025"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001232025').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The school expects a lot of its students.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s3"></a><span class="sense newline"><span class="sensenum">3</span><span class="signpost">think somebody/something will arrive</span><span class="def"> to believe that someone or something is going to arrive</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001232012"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001232012').play();"><img src="LDOCE6_img_spkr_g.png"/></a> We’re expecting Alison home any minute now.</span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001232013"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001232013').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Snow is expected by the weekend.</span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001653672"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001653672').play();"><img src="LDOCE6_img_spkr_g.png"/></a> an expected crowd of 80,000 people</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s4"></a><span class="sense newline"><span class="sensenum">4</span><span class="signpost">think</span><span class="def"> to think that you will find that someone or something has a particular quality or does a particular thing</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001653676"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001653676').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I expected her to be taller than me, not shorter.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s5"></a><span class="sense newline"><span class="sensenum">5</span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s6"></a><span class="lexunit">be expecting (a baby)</span><span class="def"> if a woman is expecting, she is going to have a baby</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s7"></a><span class="sense newline"><span class="sensenum">6</span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s8"></a><span class="lexunit">what can/do you expect?</span><span class="registerlab"> spoken</span><span class="def"> used to say that you are not surprised by something unpleasant or disappointing</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001232041"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001232041').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He was late, but what do you expect?</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s9"></a><span class="sense newline"><span class="sensenum">7</span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s10"></a><span class="lexunit">how do/can you expect ...?</span><span class="registerlab"> spoken</span><span class="def"> used to say that it is unreasonable to think that something will happen or be true</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-001653681"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-001653681').play();"><img src="LDOCE6_img_spkr_g.png"/></a> If I can’t help her, how can you expect to?</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s11"></a><span class="sense newline"><span class="sensenum">8</span><a id="6504937708254a64972bf4d4ccdfcc9d_expect_s12"></a><span class="lexunit">I expect</span><span class="geo"> British English</span><span class="registerlab"> spoken</span><span class="def"> used to introduce or agree with a statement that you think is probably true</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-001232048"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-001232048').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I expect you’re right.</span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001653683"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001653683').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘Do you think they’re going to attack?’ ‘<span class="colloinexa">I expect so</span>.’</span></span><span class="tail"><span class="grambox"><span class="heading">GRAMMAR: Patterns with expect</span><span class="expl"><span class="neutral">• </span>You <span class="expr">expect to do</span> something: <span class="example"><span class="neutral">· </span>The team expects to win.</span></span><span class="expl"><span class="neutral">• </span>You <span class="expr">expect</span> someone <span class="expr">to do</span> something: <span class="example"><span class="neutral">· </span>We didn’t expect them to come.</span></span><span class="expl"><span class="neutral">• </span>You <span class="expr">expect that</span> something <span class="expr">will</span> happen: <span class="example"><span class="neutral">· </span>We expect that we will arrive at about midday.</span></span><span class="expl"><span class="neutral">• </span>You say that you <span class="expr">expected that</span> something <span class="expr">would</span> happen: <span class="example"><span class="neutral">· </span>They had not expected that there would be so much traffic.</span></span><span class="expl"><span class="neutral">• </span>You say that something <span class="expr">is expected to</span> happen: <span class="example"><span class="neutral">· </span>Sales are expected to reach $10 million.</span></span><span class="expl"><span class="neutral">• </span>In more formal English, you can also say <span class="expr">it is expected that</span> something <span class="expr">will</span> happen: <span class="example"><span class="neutral">· </span>It is expected that sales will reach $10 million.</span></span><span class="expl"><span class="neutral">• </span>You can say you <span class="expr">are expecting</span> something. <span class="expr">Expect</span> is often used in the progressive: <span class="example"><span class="neutral">· </span>I’m expecting a call from the hospital.</span></span><span class="expl last"><span class="neutral">• </span>You can use the phrases <span class="expr">I expect so</span> or <span class="expr">I don’t expect so</span>, usually to give a short answer: <span class="example"><span class="neutral">· </span>‘Were they surprised?’ ‘I expect so.’</span><span class="example"><span class="neutral">· </span>‘Will it take long?’ ‘I don’t expect so.’</span> <span class="cross"><span class="cross">✗</span></span>Don’t say: <span class="badexa">I expect it</span>.<span class="gramrefcont"><span>Grammar guide ‒ </span>VERBS</span></span></span><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="section"><span class="secheading">adverbs</span><span class="collocate"><span class="colloc collo">fully expect</span><span class="collgloss"><span class="neutral"> (=</span>completely<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>We fully expected to win.</span></span><span class="collocate"><span class="colloc collo">confidently expect</span><span class="collgloss"><span class="neutral"> (=</span>with a feeling of confidence<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He confidently expected to be elected again.</span></span><span class="collocate"><span class="colloc collo">half expect</span><span class="collgloss"><span class="neutral"> (=</span>partly, but not completely<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He walked slowly towards the box, half expecting it to explode.</span></span><span class="collocate"><span class="colloc collo">really expect</span><span class="collgloss"><span class="neutral"> (=</span>definitely<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I didn’t really expect her to come.</span></span><span class="collocate"><span class="colloc collo">honestly expect</span><span class="collgloss"><span class="neutral"> (=</span>really expect<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Do you honestly expect me to look after the kids while you go on holiday?</span></span><span class="collocate"><span class="colloc collo">hardly expect</span><span class="collgloss"><span class="neutral"> (=</span>almost not<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>You can hardly expect a child of three to know the difference between right and wrong.</span></span><span class="collocate"><span class="colloc collo">rightly expect</span><span class="collgloss"><span class="neutral"> (=</span>with good reason<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The public rightly expects government officials to be honest.</span></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="colloc collo">as expected</span><span class="collgloss"><span class="neutral"> (=</span>in the way that was planned or thought likely to happen<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Tickets have not been selling as well as expected.</span></span><span class="collocate"><span class="colloc collo">something is (only) to be expected</span><span class="collgloss"><span class="neutral"> (=</span>used to say that you are not surprised by something unpleasant<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>After all this rain, some flooding is only to be expected.</span></span><span class="collocate"><span class="colloc collo">something happens when you least expect it</span><span class="example"><span class="neutral">· </span>Bad luck tends to happen when you least expect it.</span></span><span class="collocate"><span class="colloc collo">somebody is entitled to expect something</span><span class="collgloss"><span class="neutral"> (=</span>have the right to think something will happen<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>You’re entitled to expect decent service at these prices.</span></span><span class="collocate"><span class="colloc collo">it is reasonable/unreasonable to expect something</span><span class="example"><span class="neutral">· </span>It’s unreasonable to expect a tenant to pay for repairs to the outside of the house.</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_4_expect0205" preload="none" src="LDOCE6_hwd_bre_4_expect0205.mp3"></audio><audio id="LDOCE6_hwd_ame_b_expect" preload="none" src="LDOCE6_hwd_ame_b_expect.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001802583" preload="none" src="LDOCE6_exa_bre_8_p008-001802583.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001802584" preload="none" src="LDOCE6_exa_bre_c_p008-001802584.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001814596" preload="none" src="LDOCE6_exa_bre_3_p008-001814596.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001653652" preload="none" src="LDOCE6_exa_bre_4_p008-001653652.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001653653" preload="none" src="LDOCE6_exa_bre_9_p008-001653653.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001231988" preload="none" src="LDOCE6_exa_bre_5_p008-001231988.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001653656" preload="none" src="LDOCE6_exa_bre_1_p008-001653656.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001231985" preload="none" src="LDOCE6_exa_bre_3_p008-001231985.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-001653659" preload="none" src="LDOCE6_exa_bre_a_p008-001653659.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001653664" preload="none" src="LDOCE6_exa_bre_8_p008-001653664.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-001653669" preload="none" src="LDOCE6_exa_bre_b_p008-001653669.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001232003" preload="none" src="LDOCE6_exa_bre_0_p008-001232003.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001232007" preload="none" src="LDOCE6_exa_bre_f_p008-001232007.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-001232018" preload="none" src="LDOCE6_exa_bre_b_p008-001232018.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001232021" preload="none" src="LDOCE6_exa_bre_4_p008-001232021.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001232025" preload="none" src="LDOCE6_exa_bre_c_p008-001232025.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001232012" preload="none" src="LDOCE6_exa_bre_7_p008-001232012.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001232013" preload="none" src="LDOCE6_exa_bre_7_p008-001232013.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001653672" preload="none" src="LDOCE6_exa_bre_5_p008-001653672.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001653676" preload="none" src="LDOCE6_exa_bre_2_p008-001653676.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001232041" preload="none" src="LDOCE6_exa_bre_c_p008-001232041.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-001653681" preload="none" src="LDOCE6_exa_bre_6_p008-001653681.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-001232048" preload="none" src="LDOCE6_exa_bre_6_p008-001232048.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001653683" preload="none" src="LDOCE6_exa_bre_4_p008-001653683.mp3"></audio>`;
