var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_explain_1"></a><div><a id="6504937708254a64972bf4d4ccdfcc9d_explain"></a><span class="entry"><span class="entryhead"><span class="hwd">explain</span><span class="hyphenation frequent">ex<span class="hs0"></span>plain</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">ɪkˈspleɪn</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S1</span> <span class="freq">W1</span><span class="pos"> verb</span><span class="gram"> [intransitive, transitive]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_d_explain0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_a_explain').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">explain</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1500-1600</span> <span class="etymlang">Latin</span> <span class="etymorigin">explanare</span> <span class="etymtran"> ‘to make level, unfold’</span>, from <span class="etymorigin">planus</span> <span class="etymtran"> ‘level, flat’</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">explain</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">explain</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">explains</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">explained</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">explained</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">explained</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">explained</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">explain</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">explained</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">explaining</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">explaining</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">explaining</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">explaining</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">explaining</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">explaining</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">explaining</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">explaining</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">explaining</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">explaining</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>Can you <span class="nodeword">explain</span> what the poem means?</li><li>Could you <span class="nodeword">explain</span> the rules of the game to me, please?</li><li>Doctors are unable to <span class="nodeword">explain</span> why the disease spread so quickly.</li><li>I don't have time to <span class="nodeword">explain</span> now - just come with me quickly!</li><li>If you don't get the joke, I'll <span class="nodeword">explain</span> it to you later.</li><li>It's not so complicated - let me <span class="nodeword">explain</span>.</li><li>It was difficult <span class="nodeword">explaining</span> to the children why their father was leaving home.</li><li>Let me show you - it's too difficult to <span class="nodeword">explain</span>.</li><li>Sarah <span class="nodeword">explained</span> that she hadn't been feeling well recently.</li><li>She couldn't sleep last night, which <span class="nodeword">explains</span> why she was in such a bad mood this morning.</li><li>She just doesn't like me. How else can you <span class="nodeword">explain</span> her behaviour?</li><li>The doctor <span class="nodeword">explained</span> how the clinic operates.</li><li>We listened carefully while Pam <span class="nodeword">explained</span> the process.</li><li>We were all puzzled: what could <span class="nodeword">explain</span> his sudden change of mind?</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>But the dynamics of his thinking have never been easy to <span class="nodeword">explain</span>.</li><li>I had nothing in my vocabulary of human behavior to <span class="nodeword">explain</span> him.</li><li>I would prefer the teacher to <span class="nodeword">explain</span> things rather than the computer.</li><li>Shakespeare took the time to <span class="nodeword">explain</span> this; director Jocelyn Moorhouse does not.</li><li>The problem may <span class="nodeword">explain</span> why Brady has not made more of an impact since exploding on the scene three years ago.</li><li>The reason this requires <span class="nodeword">explaining</span> is that young women are different from other human beings in this respect.</li><li>This <span class="nodeword">explains</span> why the extent of the use of crack is not necessarily indicated by the scale of seizures.</li><li>This may, inpart, <span class="nodeword">explain</span> why current approaches seem so distant from those applied to other periods of the past.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">explain</span></span><div class="content"><span class="def"> to give someone the information they need to understand something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She explained how to use the software.</span><span class="example"><span class="neutral">· </span>Doctors usually explain the risks of the treatment to patients.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tell</span></span><div class="content"><span class="def"> to explain something. People often use <span class="defbold">tell</span> instead of <span class="defbold">explain</span> in everyday conversation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He told me how to get to his house.</span><span class="example"><span class="neutral">· </span>The book tells you how to improve your health through diet.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">show</span></span><div class="content"><span class="def"> to explain to someone how to do something by doing it while they watch you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ellen showed me how to work the coffee maker.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">demonstrate</span></span><div class="content"><span class="def"> to explain to someone how to do something by doing it while they watch you, especially when it is part of your job</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The cabin crew demonstrated the safety procedures to the passengers.</span><span class="example"><span class="neutral">· </span>A qualified instructor will demonstrate how to use the equipment properly.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">go through something</span></span><div class="content"><span class="def"> to explain something carefully, especially one step at a time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mrs. Riddell went through the homework assignment.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">set out something</span></span><div class="content"><span class="def"> to explain a series of ideas, reasons or opinions in an organized way in writing or in a speech</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In his speech, he intended to set out the party’s policies on education.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to explain something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">explain</span></span><div class="content"><span class="def"> to give someone the information they need to understand something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's not so complicated - let me explain.</span><span class="example"><span class="neutral">· </span>We listened carefully while Pam explained the process.</span><span class="example"><span class="neutral">· </span>Let me show you - it's too difficult to explain.</span><span class="thespropform">explain something to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If you don't get the joke, I'll explain it to you later.</span><span class="example"><span class="neutral">· </span>Could you explain the rules of the game to me, please?</span><span class="thespropform">explain how/what/why etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you explain what the poem means?</span><span class="example"><span class="neutral">· </span>The doctor explained how the clinic operates. </span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tell</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> to explain to someone how something works or how to do something</span><span class="thespropform"><span class="neutral">: </span>tell somebody how/what/why etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you tell me how to log on to the Internet?</span><span class="example"><span class="neutral">· </span>The leaflet tells you what to do if you get malaria.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say what/why/where etc</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> to explain the reasons for something or give detailed information about something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He didn't say where he was going or who he was going with.</span><span class="example"><span class="neutral">· </span>Did Caroline say why she needed the tape recorder?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">show</span></span><div class="content"><span class="def"> to explain to someone how to do something by doing it while they watch you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How do you change the speed of the drill?" "Let me show you."</span><span class="thespropform">show somebody something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'll show you an easier way to get down from there.</span><span class="thespropform">show somebody how to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you show me how to use your camera?</span><span class="thespropform">show somebody what to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If you show him what to do, I'm sure he'll do a good job.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">demonstrate</span></span><div class="content"><span class="def"> to show someone how to do something by doing it while they watch you, especially when it is your job to show people how to do things</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The ski instructor began by demonstrating the correct way to turn.</span><span class="example"><span class="neutral">· </span>If you still don't understand, Marcia will be happy to demonstrate.</span><span class="thespropform">demonstrate how</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A trainer came in to demonstrate how the new computer system worked.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">go through</span></span><div class="content"><span class="def"> to explain all the details about something in the right order, to help someone understand it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'll go through the instructions once more in case you missed anything.</span><span class="example"><span class="neutral">· </span>If you stay after class, I'll go through the theory with you again.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">throw/shed light on</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">written</span><span class="def"> to provide new information which makes something easier to understand, especially something which has been studied, but which is still not well understood</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>These discoveries may shed light on the origins of the universe.</span><span class="example"><span class="neutral">· </span>Scientists working in the Gobi desert have thrown new light on the life of dinosaurs.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">set out</span></span><div class="content"><span class="def"> to explain facts, arguments, reasons etc by stating them clearly and in a carefully planned order</span><span class="thespropform"><span class="neutral">: </span>set out something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She set out the reasons for her resignation in a confidential letter to her boss.</span><span class="example"><span class="neutral">· </span>The Republicans' goals for the year are clearly set out in the party platform.</span><span class="thespropform">set something out</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The facts, as you have set them out, seem convincing enough.</span></div></span></span><span class="section"><span class="secheading">what you say when you are going to explain something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">(you) see</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are explaining something to someone, and you want to check that they are listening and that they understand you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>This fits on here, see, where the arrow is.</span><span class="example"><span class="neutral">· </span>Simon's car broke down, you see, and neither of us knew how to fix it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I mean</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are explaining something you have said or giving an example of something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ted seems kind of lazy. I mean, he never offers to help and he just lies in front of the TV.</span><span class="thespropform">what I mean (to say) is</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm afraid I can't help you. What I mean is that I'm not a detective, and I don't solve crimes.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">in other words</span></span><div class="content"><span class="def"> use this when you are saying something in a different way in order to explain it more clearly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>What we need is a more sustainable transport system, in other words, more buses and trains, and fewer cars.</span><span class="example"><span class="neutral">· </span>This is supposed to be a democracy - in other words, one person one vote.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">the thing is</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this when you are explaining a problem or the reason for something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I really don't want to leave yet. The thing is, I have an appointment in 15 minutes.</span><span class="example"><span class="neutral">· </span>I do have a computer, but the thing is, it's really old and I can't use it for email.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">that is</span></span><div class="content"><span class="def"> use this to explain the meaning of the previous word or phrase by giving more information about it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The fare is reduced for children, that is, anyone under 15 years old.</span><span class="example"><span class="neutral">· </span>All documents are printed in the two official languages - that is, English and French.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">let me explain</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you want to explain something to someone because you think they have not understood</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I can see you're getting confused. Let me explain.</span><span class="example"><span class="neutral">· </span>I know the plan seems a little crazy at first, but it's really not. Let me explain.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">to put it another way</span></span><div class="content"><span class="def"> used when you have explained something in one way and you are going to try to make it clearer by explaining it in a different way</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Money makes money. To put it another way, the more you invest, the greater your potential profit will be.</span><span class="example"><span class="neutral">· </span>The problem demands a global solution. To put it another way, local regulations will have very little effect.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put it like this/put it this way</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when someone is not sure what you mean and you are going to try to explain in a way that will help them to understand, especially by saying something humorous or direct</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Does he get many dates?" "Put it like this - you don't have to feel sorry for him."</span><span class="example"><span class="neutral">· </span>Put it this way, honey - what the boss doesn't know isn't going to bother him.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">let me rephrase that</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> used when you are going to use different words to say something again, because you have just said it in an unsuitable way and it may not have been understood correctly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm sorry, let me rephrase that. That wasn't what I meant to say at all.</span><span class="example"><span class="neutral">· </span>Most of the people there were incredibly old. Let me rephrase that - we were the youngest couple there.</span></div></span></span><span class="section"><span class="secheading">to explain something in a simpler way</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">simplify</span></span><div class="content"><span class="def"> to explain something difficult in simple language so that it is easier to understand</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We have done everything we can to simplify the procedure.</span><span class="example"><span class="neutral">· </span>She took a complex topic and simplified it in a way that we could all understand.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">demystify</span></span><div class="content"><span class="def"> to give a simple, clear explanation of an important but difficult subject so that it is easier for ordinary people to understand</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>This new book demystifies some of the computer language currently in use.</span><span class="example"><span class="neutral">· </span>The course seeks to demystify the loan application process for people buying a home for the first time.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">in plain English</span></span><div class="content"><span class="def"> if you explain something or tell someone something in plain English , you explain it simply and clearly, without using difficult words or technical language</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I just wish someone would explain to me in plain English what is wrong with my computer.</span><span class="example"><span class="neutral">· </span>It says "the children lack the ability to mobilize self and commit" - what does that mean in plain English?</span></div></span></span><span class="section"><span class="secheading">the words you write or say to explain something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">explanation</span></span><div class="content"><span class="def"> something that you say or write in order to make something clearer or to explain why something happened</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Each diagram is followed by a simple explanation.</span><span class="thespropform">explanation for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>What was their explanation for their decision?</span><span class="thespropform">explanation of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Our guide gave us a detailed explanation of the system of government.</span><span class="thespropform">give/offer (somebody) an explanation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you give us a quick explanation of how it works?</span><span class="example"><span class="neutral">· </span>She offered no explanation as to why she had left so suddenly.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">instructions</span></span><div class="content"><span class="def"> written or spoken information that explains exactly how to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Read the instructions carefully before using the machine.</span><span class="example"><span class="neutral">· </span>The cooking instructions are on the back of the box.</span><span class="thespropform">give (somebody) instructions</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They gave us detailed instructions explaining how to get to their house.</span><span class="thespropform">follow instructions</span><span class="gloss"><span class="neutral"> (=</span>do what they tell you<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>if you had followed my instructions, none of this would have happened.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">account</span></span><div class="content"><span class="def"> a detailed description of a process which also explains how it happens and what makes it possible</span><span class="thespropform"><span class="neutral">: </span>account of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>So far no linguist has given us a satisfactory account of how children learn language.</span><span class="example"><span class="neutral">· </span>Her account of the events of that day was wildly different from the first witness's.</span></div></span></span><span class="section"><span class="secheading">to tell someone the reason for something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say why/tell somebody why</span></span><div class="content"><span class="def"> to tell someone why something happened</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I knew she was annoyed, but she wouldn't say why.</span><span class="example"><span class="neutral">· </span>Can anyone tell us why there are no buses today?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">explain</span></span><div class="content"><span class="def"> to tell someone the reason for something, so that they understand the situation better</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She just doesn't like me. How else can you explain her behaviour?</span><span class="example"><span class="neutral">· </span>I don't have time to explain now - just come with me quickly!</span><span class="thespropform">explain why/how/what etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Doctors are unable to explain why the disease spread so quickly.</span><span class="thespropform">explain that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sarah explained that she hadn't been feeling well recently.</span><span class="thespropform">explain to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was difficult explaining to the children why their father was leaving home.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">give a reason</span></span><div class="content"><span class="def"> to tell someone why you are doing something, especially something surprising</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"He says he's not coming." "Oh, did he give a reason?"</span><span class="thespropform">give somebody a reason</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The landlord told us we had to go, but we were never given any reason.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">account for</span></span><div class="content"><span class="def"> to tell someone the reason for something that has happened, especially when people are looking for a reason to explain it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If you walked home, how do you account for the fact that a witness said he saw you driving your van?</span><span class="example"><span class="neutral">· </span>It is difficult to account for the big differences in their scores.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">provide an explanation/come up with an explanation</span></span><div class="content"><span class="def"> to tell someone the reason why something happened when they have asked you to explain. Provide an explanation is more formal than come up with an explanation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In an attempt to provide an explanation she said she thought I had given her permission to be absent.</span><span class="example"><span class="neutral">· </span>After failing to come up with an adequate explanation, Jones was arrested for robbery.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">justify</span></span><div class="content"><span class="def"> to tell someone why something that seems wrong is in fact right</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>How can you justify the expense?</span><span class="thespropform">justify doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>How can you possibly justify charging four pounds for a glass of beer?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">explain away</span></span><div class="content"><span class="def"> to tell someone the reason why you did something or why something happened, with the intention of making them think you have not done anything wrong or bad</span><span class="thespropform"><span class="neutral">: </span>explain away something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>George was trying to explain away his outburst, saying that he'd been under a lot of pressure.</span><span class="example"><span class="neutral">· </span>The government tried to explain away the police's unexpected brutality, but this only led to rioting.</span><span class="thespropform">explain something away</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>But there's broken glass all over the floor - how are we going to explain that away?</span></div></span></span><span class="section last"><span class="secheading">to be the reason why something happened or why someone does something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be the reason</span></span><div class="content"><span class="def"> to be the reason why something happened or why someone did something</span><span class="thespropform"><span class="neutral">: </span>be the reason for/behind</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Nick's teachers think that problems at home are the reason for his poor schoolwork.</span><span class="thespropform">be the reason why</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He borrowed too much money, and that's the reason why his business failed.</span><span class="thespropform">be the reason (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I knew that I had my weaknesses. Perhaps that is the reason that I was not more successful.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">explain</span></span><div class="content"><span class="def"> if a fact or situation explains something, it helps you to understand why it happened</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We were all puzzled: what could explain his sudden change of mind?</span><span class="thespropform">explain why/what/how</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She couldn't sleep last night, which explains why she was in such a bad mood this morning.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">account for</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> to be the reason that explains why something strange or surprising happened</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If it's true that he was taking drugs, that would account for his strange behaviour.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">motivate</span></span><div class="content"><span class="def"> to be someone's personal reason for doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Not all people are motivated by self-interest.</span><span class="thespropform">motivate somebody to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's frustration that motivates babies to learn to walk.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">lie behind</span></span><div class="content"><span class="def"> to be the secret or hidden reason for someone's action or behaviour</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I wonder what lies behind Arthur's sudden interest in golf?</span><span class="example"><span class="neutral">· </span>I think that behind Tania's aggression lay a deep insecurity about herself.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">describe/explain something in detail</span></span><div class="content"><span class="example"><span class="neutral">· </span>The doctor spent time describing the treatment in detail to me.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">explain your dilemma</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>talk about it<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He explained his dilemma to me.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">explain ... predicament</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001568184').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She went to the office to <span class="colloinexa">explain</span> her <span class="colloinexa">predicament</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">explain the presence of something/somebody</span></span><div class="content"><span class="example"><span class="neutral">· </span>How could he possibly explain the presence of so much money in his flat?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">explain the reasons for something</span></span><div class="content"><span class="example"><span class="neutral">· </span>Explain the reasons for your choice.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">away</span></span><div class="content"><span class="example"><span class="neutral">· </span>She had almost been tempted to <span class="colloinexa">explain away</span> André's remark, but her mind was working quickly.</span><span class="example"><span class="neutral">· </span>There are many others, but most can be <span class="colloinexa">explained away</span>, or are inconclusive.</span><span class="example"><span class="neutral">· </span>It is something that can not be <span class="colloinexa">explained away</span>.</span><span class="example"><span class="neutral">· </span>If the relationship between two variables disappears when a prior variable is controlled, the original effect has been <span class="colloinexa">explained away</span>.</span><span class="example"><span class="neutral">· </span>Those changes that were noticed were <span class="colloinexa">explained away</span> as disturbances in the upper atmosphere.</span><span class="example"><span class="neutral">· </span>This staggering figure could not easily be <span class="colloinexa">explained away</span>.</span><span class="example"><span class="neutral">· </span>There may be some justification for this if you are trying to <span class="colloinexa">explain away</span> your overdraft to the bank manager yet again.</span><span class="example"><span class="neutral">· </span>Recognising this paradox lends weight to the patriarchy thesis, <span class="colloinexa">explaining away</span> many apparent counter-examples.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">how</span></span><div class="content"><span class="example"><span class="neutral">· </span>He <span class="colloinexa">explains how</span> he felt a tingling sensation down his left-hand side but did not immediately call for help.</span><span class="example"><span class="neutral">· </span>Here's a few reasons that might <span class="colloinexa">explain how</span> Brown won.</span><span class="example"><span class="neutral">· </span>This sediment thickening indicates the role of sediment loading in sustaining basin subsidence but it does not <span class="colloinexa">explain how</span> basins originate.</span><span class="example"><span class="neutral">· </span>Can some one <span class="colloinexa">explain how</span> kids' birthday parties became mandatory and, frequently, icky?</span><span class="example"><span class="neutral">· </span>Can you <span class="colloinexa">explain how</span> you got the idea?</span><span class="example"><span class="neutral">· </span>Behe contends that evolutionists never try to <span class="colloinexa">explain how</span> complex systems might have arisen through incremental changes.</span><span class="example"><span class="neutral">· </span>Hilary <span class="colloinexa">explains how</span> she deals with on-air technical problems.</span><span class="example"><span class="neutral">· </span>He listens to an oldies radio station while <span class="colloinexa">explaining how</span> to parallel park.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">why</span></span><div class="content"><span class="example"><span class="neutral">· </span>This <span class="colloinexa">explains why</span> the extent of the use of crack is not necessarily indicated by the scale of seizures.</span><span class="example"><span class="neutral">· </span>He was brilliant, with a tendency to arrogance; perhaps that <span class="colloinexa">explained why</span> he got along so well with Lleland.</span><span class="example"><span class="neutral">· </span>This <span class="colloinexa">explains why</span> one finds contrary demands being made by groups in different places.</span><span class="example"><span class="neutral">· </span>This <span class="colloinexa">explains why</span> heads of governments, the police and the Establishment in general are such popular targets for the protesters' attacks.</span><span class="example"><span class="neutral">· </span>The lack of consensus <span class="colloinexa">explains why</span> area politicians have failed to reach an acceptable solution.</span><span class="example"><span class="neutral">· </span>In this section we <span class="colloinexa">explain why</span> they can, and list some of the information sources you might want to use.</span><span class="example"><span class="neutral">· </span>Here I should <span class="colloinexa">explain why</span> over a hundred thousand miners were striking against the federal courts.</span></div></span></span><span class="section"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">difference</span></span><div class="content"><span class="example"><span class="neutral">· </span>To <span class="colloinexa">explain</span> the <span class="colloinexa">difference</span> between the two structures, they are placed in a realistic context.</span><span class="example"><span class="neutral">· </span>This may <span class="colloinexa">explain</span> the major <span class="colloinexa">difference</span> between men and women when it comes to reading and reacting to others.</span><span class="example"><span class="neutral">· </span>Economists are more concerned to find similarities among firms than to <span class="colloinexa">explain</span> the <span class="colloinexa">differences</span>.</span><span class="example"><span class="neutral">· </span>Gordon <span class="colloinexa">explained</span> the <span class="colloinexa">difference</span> between mere literacy and critical reading.</span><span class="example"><span class="neutral">· </span>As a result, there has been a recent proliferation of studies seeking to <span class="colloinexa">explain</span> international <span class="colloinexa">differences</span> in strike activity.</span><span class="example"><span class="neutral">· </span>And these <span class="colloinexa">differences</span> in structure partially <span class="colloinexa">explain</span> differences in attitudes found among the nations.</span><span class="example"><span class="neutral">· </span>Other risk factors, not related to ethnicity, probably <span class="colloinexa">explain</span> remaining <span class="colloinexa">differences</span> between Maori and non-Maori children.</span><span class="example"><span class="neutral">· </span>Political socialization research attempts to <span class="colloinexa">explain</span> how such <span class="colloinexa">differences</span> in political beliefs can occur.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">fact</span></span><div class="content"><span class="example"><span class="neutral">· </span>Given the mobility constraints, is the cooperation of labour not better <span class="colloinexa">explained</span> by the <span class="colloinexa">fact</span> that employees have little alternative?</span><span class="example"><span class="neutral">· </span>The girls <span class="colloinexa">explain</span> the <span class="colloinexa">facts</span> of life to him.</span><span class="example"><span class="neutral">· </span>Sadly, as I shall <span class="colloinexa">explain</span>, the <span class="colloinexa">facts</span> do not bear out the interpretation given to them by the Foreign Secretary.</span><span class="example"><span class="neutral">· </span>In addition, it <span class="colloinexa">explains</span> various detailed observational <span class="colloinexa">facts</span> which are incompatible with the older Newtonian scheme.</span><span class="example"><span class="neutral">· </span>This phenomenon is <span class="colloinexa">explained</span> by the <span class="colloinexa">fact</span> that elements can be introduced into mental models without being explicitly mentioned.</span><span class="example"><span class="neutral">· </span>But this sort of secrecy can often produce more anxiety than if parents <span class="colloinexa">explain</span> the <span class="colloinexa">facts</span> clearly and simply.</span><span class="example"><span class="neutral">· </span>There's no other way to <span class="colloinexa">explain</span> the <span class="colloinexa">fact</span> that he's been allowed to buy that land so cheaply.</span><span class="example"><span class="neutral">· </span>To <span class="colloinexa">explain</span> the most important <span class="colloinexa">facts</span> about drugs. 2.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">reason</span></span><div class="content"><span class="example"><span class="neutral">· </span>He did not apparently find it necessary to <span class="colloinexa">explain</span> the <span class="colloinexa">reasons</span> for this sexist-sounding conclusion.</span><span class="example"><span class="neutral">· </span>The men of long ago wondered why this should be, and they told stories to <span class="colloinexa">explain</span> the <span class="colloinexa">reason</span>.</span><span class="example"><span class="neutral">· </span>There have been several attempts to <span class="colloinexa">explain</span> the <span class="colloinexa">reasons</span> for the strong seismic reflections from such zones.</span><span class="example"><span class="neutral">· </span>If this has not been the case, it will be up to the individuals to <span class="colloinexa">explain</span> the <span class="colloinexa">reasons</span> behind this.</span><span class="example"><span class="neutral">· </span>No need to <span class="colloinexa">explain</span> one's <span class="colloinexa">reasons</span> for suddenly looking out of the window.</span><span class="example"><span class="neutral">· </span>When asked by Geoffrey Fisher to <span class="colloinexa">explain</span> his <span class="colloinexa">reasons</span>, he found that he had difficulty in finding a reason.</span><span class="example"><span class="neutral">· </span>A centre wishing to offer any of these modules in the Session 1991/92 should write, <span class="colloinexa">explaining</span> the <span class="colloinexa">reasons</span>, to.</span><span class="example"><span class="neutral">· </span>If the doctor overspends, he or she may be asked to <span class="colloinexa">explain</span> the medical <span class="colloinexa">reasons</span> for this to another doctor.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">theory</span></span><div class="content"><span class="example"><span class="neutral">· </span>Life-history <span class="colloinexa">theory</span> could readily <span class="colloinexa">explain</span> dwarfing if juvenile, but not adult, male mortality were large.</span><span class="example"><span class="neutral">· </span>A major purpose of monetary <span class="colloinexa">theory</span> is to <span class="colloinexa">explain</span> the behavior of real income and prices.</span><span class="example"><span class="neutral">· </span>Generally, different <span class="colloinexa">theories</span> are used to <span class="colloinexa">explain</span> widely different phenomena, from the forces in the atom to gravity.</span><span class="example"><span class="neutral">· </span>Such a step allows development of <span class="colloinexa">theory</span> which can <span class="colloinexa">explain</span> social transformation in terms of unitary types of activity. 2.</span><span class="example"><span class="neutral">· </span>Many <span class="colloinexa">theories</span> purport to <span class="colloinexa">explain</span> growth in terms of a single cause, economic, political or organisational.</span><span class="example"><span class="neutral">· </span>This <span class="colloinexa">theory explains</span> our ability to refer using names in terms of our ability to refer using definite descriptions.</span><span class="example"><span class="neutral">· </span>Merton's model or <span class="colloinexa">theory</span> does not adequately <span class="colloinexa">explain</span> all types of criminal behaviour.</span><span class="example"><span class="neutral">· </span>There is a fashionable <span class="colloinexa">theory</span> to <span class="colloinexa">explain</span> this.</span></div></span></span><span class="section last"><span class="secheading">VERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">ask</span></span><div class="content"><span class="example"><span class="neutral">· </span>A teacher wrote to me recently <span class="colloinexa">asking</span> if I could <span class="colloinexa">explain</span> her inspection feedback.</span><span class="example"><span class="neutral">· </span>Ahab talks to this head and <span class="colloinexa">asks</span> it to <span class="colloinexa">explain</span> the mystery of the universe.</span><span class="example"><span class="neutral">· </span>Bob <span class="colloinexa">asks</span> the questions then <span class="colloinexa">explains</span> how the youngsters maintain his enthusiasm.</span><span class="example"><span class="neutral">· </span>More advanced students might be <span class="colloinexa">asked</span> to read and <span class="colloinexa">explain</span> directions.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">asked</span> him to <span class="colloinexa">explain</span> his pricing policy.</span><span class="example"><span class="neutral">· </span>Zall was also <span class="colloinexa">asked</span> to <span class="colloinexa">explain</span> why his group should be a party to the litigation in the first place.</span><span class="example"><span class="neutral">· </span>You should never be afraid to <span class="colloinexa">ask</span> the physiotherapist to <span class="colloinexa">explain</span> what she is doing.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">asked</span> him to <span class="colloinexa">explain</span> the voting rules again.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">attempt</span></span><div class="content"><span class="example"><span class="neutral">· </span>Be that as it may, I shall <span class="colloinexa">attempt</span> to <span class="colloinexa">explain</span> the spiritual aspect in my own terms.</span><span class="example"><span class="neutral">· </span>It is not deconstructive in the sense that it <span class="colloinexa">attempts</span> to <span class="colloinexa">explain</span> those contradictions via a coherent theory.</span><span class="example"><span class="neutral">· </span>Freud's theory is <span class="colloinexa">attempting</span> to <span class="colloinexa">explain</span> how religious rituals and beliefs started in human cultures in the first instance.</span><span class="example"><span class="neutral">· </span>Kass and his colleagues presented this important bit of information without <span class="colloinexa">attempting</span> to <span class="colloinexa">explain</span> it.</span><span class="example"><span class="neutral">· </span>The study not only describes changes in central-local relationships but it also <span class="colloinexa">attempts</span> to <span class="colloinexa">explain</span> them.</span><span class="example"><span class="neutral">· </span>Once again I <span class="colloinexa">attempted</span> to <span class="colloinexa">explain</span>.</span><span class="example"><span class="neutral">· </span>This is a clear example of <span class="colloinexa">attempting</span> to <span class="colloinexa">explain</span> an unknown phenomenon by means of models based on known phenomena.</span><span class="example"><span class="neutral">· </span>In such a climate, it is ambitious to <span class="colloinexa">attempt</span> to <span class="colloinexa">explain</span> scientific problems in simple terms.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">help</span></span><div class="content"><span class="example"><span class="neutral">· </span>Geography was important because it would <span class="colloinexa">help</span> the evolutionist to <span class="colloinexa">explain</span> how populations were able to migrate and adapt to new locations.</span><span class="example"><span class="neutral">· </span>That may <span class="colloinexa">help explain</span> why alternative keyboards remain something of a novelty in the workplace.</span><span class="example"><span class="neutral">· </span>This weaved-up mystery lies at the heart of the Metamorphoses and <span class="colloinexa">helps</span> to <span class="colloinexa">explain</span> its extraordinary continuing appeal.</span><span class="example"><span class="neutral">· </span>The drawings <span class="colloinexa">help explain</span> what to do. 11 Breeding flour beetles Flour beetles live in and feed on stored flour.</span><span class="example"><span class="neutral">· </span>Yet this relationship was deeply problematic, which <span class="colloinexa">helps</span> to <span class="colloinexa">explain</span> the diversity of opinion and the contradictions within the phenomenon.</span><span class="example"><span class="neutral">· </span>This ambiguity of language <span class="colloinexa">helps</span> to <span class="colloinexa">explain</span> why so many interpretations of Wordsworth's philosophy and religion were able to arise.</span><span class="example"><span class="neutral">· </span>The origins are important because they <span class="colloinexa">help explain</span> a number of otherwise curious features of the societies.</span><span class="example"><span class="neutral">· </span>The degree to which characteristics and local and national factors <span class="colloinexa">help explain</span> the differing degree of unemployment will be investigated.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">try</span></span><div class="content"><span class="example"><span class="neutral">· </span>I have <span class="colloinexa">tried</span> to <span class="colloinexa">explain</span> that in Claudia's case memory is particularly treacherous, but Letterman likes the idea of ambiguity.</span><span class="example"><span class="neutral">· </span>I remember my mom <span class="colloinexa">trying</span> to <span class="colloinexa">explain</span> it to me.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">try</span> to <span class="colloinexa">explain</span> that Rainbow Rosenbloom has almost nothing but a few genes in common with Anya's faithless Gittel.</span><span class="example"><span class="neutral">· </span>At last, but how no one ever <span class="colloinexa">tried</span> to <span class="colloinexa">explain</span>, two children were born to this shapeless nothingness.</span><span class="example"><span class="neutral">· </span>How does a man even <span class="colloinexa">try</span> to <span class="colloinexa">explain</span> that he is falling apart because he doesn't kill people any more?</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">try</span> to <span class="colloinexa">explain</span> this to Professor Sano, working hard to keep back the tears.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">tried</span> my hardest to <span class="colloinexa">explain</span> how it had come about.</span><span class="example"><span class="neutral">· </span>He <span class="colloinexa">tried</span> to <span class="colloinexa">explain</span> it to her.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">explain yourself</span></span><div class="content"><ul class="exas"><li>John doesn't make excuses or <span class="nodeword">explain</span> himself to anybody.</li><li>No, that's not what I meant. I guess I didn't <span class="nodeword">explain</span> myself very well.</li><li>Censure gives Mr Wahid three months to <span class="nodeword">explain</span> himself.</li><li>He waited for Ballater to <span class="nodeword">explain</span> himself or to state the reason for his visit.</li><li>I had <span class="nodeword">explained</span> myself, as I always did, but I could scarcely explain myself away.</li><li>I knew I could <span class="nodeword">explain</span> myself.</li><li>I regard this as part of my life, a duty to <span class="nodeword">explain</span> myself.</li><li>She tried to speak, to call for help, to <span class="nodeword">explain</span> herself, to scream, but no words would come.</li><li>When, eventually, he was admitted to the presence of the Chairman he would have perhaps fifteen minutes to <span class="nodeword">explain</span> himself.</li><li>Yet now, commanded to appear before elected officials, he had to <span class="nodeword">explain</span> himself, give an accounting of his views.</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">take a bit of doing/explaining etc</span></span><div class="content"><ul class="exas"><li>It took a <span class="nodeword">bit</span> of doing - for instance, the disposal site had to check out 100 percent.</li><li>It took a <span class="nodeword">bit</span> of explaining.</li><li>That's going to take a <span class="nodeword">bit</span> of explaining.</li></ul></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word family</span><div class="at-link"><span class="entry"><span class="popheader popwf">WORD FAMILY</span><span class="wf"><span class="group"><span class="pos">adjective</span><span class="w"><span class="wfwd">unexplained</span></span><span class="w"><span class="wfwd">explanatory</span></span><span class="w"><span class="wfwd">explicable</span> ≠ <span class="opp"><span class="wfwd">inexplicable</span></span></span></span><span class="group"><span class="pos">noun</span><span class="w">explaining</span><span class="w"><span class="wfwd">explanation</span></span></span><span class="group"><span class="pos">verb</span><span class="w"><span class="wfwd">explain</span></span></span><span class="group"><span class="pos">adverb</span><span class="w"><span class="wfwd">inexplicably</span></span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_explain_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">to tell someone about something in a way that is clear or easy to understand</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001196398"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001196398').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He carefully explained the procedure.</span><span class="gramexa"><span class="propform">explain (to somebody) why/how/what etc</span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001196401"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001196401').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The librarian will explain how to use the catalogue system.</span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001653577"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001653577').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Let me explain what I mean.</span></span><span class="gramexa"><span class="propform">explain that</span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-001196404"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001196404').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He explained that it had been a difficult film to make.</span></span><span class="gramexa"><span class="propform">explain something to somebody</span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001196407"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001196407').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I explained the situation to my bank manager.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_explain_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="def">to give a reason for something or to be a reason for something</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-001196411"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001196411').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Wait! I can explain everything.</span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001653582"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001653582').play();"><img src="LDOCE6_img_spkr_g.png"/></a> How can you explain that sort of behaviour?</span><span class="gramexa"><span class="propform">explain that</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001196414"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001196414').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She explained that she had been ill.</span></span><span class="gramexa"><span class="propform">explain why/how/what etc</span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001653583"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001653583').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’ll explain why I don’t believe your story.</span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001653585"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001653585').play();"><img src="LDOCE6_img_spkr_g.png"/></a> That still doesn’t explain how he was able to hide the body.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_explain_s3"></a><span class="sense newline"><span class="sensenum">3</span><a id="6504937708254a64972bf4d4ccdfcc9d_explain_s4"></a><span class="lexunit">explain yourself</span><span class="subsense"> <span class="sensenum">a)</span><span class="def">to tell someone who is angry or upset with you the reasons why you did something</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-001196423"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-001196423').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’m going to give you five seconds to explain yourself.</span></span><span class="subsense"> <span class="sensenum">b)</span><span class="def">to say clearly what you mean</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-001196427"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-001196427').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Sorry, I’m not explaining myself very well.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_explain_s5"></a><span class="phrvbentry"><span class="entryhead"><span class="phrvbhwd">explain<span class="object"> something ↔</span> away</span><span class="pos"> phrasal verb</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_explain_s6"></a><span class="sense"> <span class="def">to make something seem less important or bad by giving reasons for it</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-001196439"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-001196439').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The difference in the treatment they receive is hard to explain away.</span></span></span><span class="tail"><span class="grambox"><span class="heading">GRAMMAR</span><span class="expl last">You <span class="expr">explain</span> something <span class="expr">to</span> someone: <span class="example"><span class="neutral">· </span>Alan explained the system to me.</span> <span class="cross"><span class="cross">✗</span></span>Don’t say: <span class="badexa">Alan explained me the system.</span> <span class="neutral"> | </span><span class="badexa">Alan explained to me the system.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_explain_s7"></a><span class="thesbox"><span class="heading">THESAURUS</span><span class="section last"><span class="exponent"><span class="exp display">explain</span><span class="def"> to give someone the information they need to understand something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She explained how to use the software.</span><span class="example"><span class="neutral">· </span>Doctors usually explain the risks of the treatment to patients.</span></span><span class="exponent"><span class="exp display">tell</span><span class="def"> to explain something. People often use <span class="defbold">tell</span> instead of <span class="defbold">explain</span> in everyday conversation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He told me how to get to his house.</span><span class="example"><span class="neutral">· </span>The book tells you how to improve your health through diet.</span></span><span class="exponent"><span class="exp display">show</span><span class="def"> to explain to someone how to do something by doing it while they watch you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ellen showed me how to work the coffee maker.</span></span><span class="exponent"><span class="exp display">demonstrate</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">ˈdemənstreɪt</span><span class="neutral">/</span></span><span class="def"> to explain to someone how to do something by doing it while they watch you, especially when it is part of your job</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The cabin crew demonstrated the safety procedures to the passengers.</span><span class="example"><span class="neutral">· </span>A qualified instructor will demonstrate how to use the equipment properly.</span></span><span class="exponent"><span class="exp display">go through something</span><span class="def"> to explain something carefully, especially one step at a time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mrs. Riddell went through the homework assignment.</span></span><span class="exponent"><span class="exp display">set out something</span><span class="def"> to explain a series of ideas, reasons or opinions in an organized way in writing or in a speech</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In his speech, he intended to set out the party’s policies on education.</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_d_explain0205" preload="none" src="LDOCE6_hwd_bre_d_explain0205.mp3"></audio><audio id="LDOCE6_hwd_ame_a_explain" preload="none" src="LDOCE6_hwd_ame_a_explain.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001568184" preload="none" src="LDOCE6_exa_bre_e_p008-001568184.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001196398" preload="none" src="LDOCE6_exa_bre_5_p008-001196398.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001196401" preload="none" src="LDOCE6_exa_bre_8_p008-001196401.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001653577" preload="none" src="LDOCE6_exa_bre_c_p008-001653577.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001196404" preload="none" src="LDOCE6_exa_bre_0_p008-001196404.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001196407" preload="none" src="LDOCE6_exa_bre_5_p008-001196407.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001196411" preload="none" src="LDOCE6_exa_bre_3_p008-001196411.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001653582" preload="none" src="LDOCE6_exa_bre_f_p008-001653582.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001196414" preload="none" src="LDOCE6_exa_bre_1_p008-001196414.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001653583" preload="none" src="LDOCE6_exa_bre_5_p008-001653583.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001653585" preload="none" src="LDOCE6_exa_bre_4_p008-001653585.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-001196423" preload="none" src="LDOCE6_exa_bre_6_p008-001196423.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-001196427" preload="none" src="LDOCE6_exa_bre_a_p008-001196427.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-001196439" preload="none" src="LDOCE6_exa_bre_a_p008-001196439.mp3"></audio>`;
