var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_guess_1"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_guess_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_guess_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1"></a><span class="entry"><span class="entryhead"><span class="hwd">guess</span><span class="hyphenation frequent">guess</span><span class="homnum frequent">1</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">ɡes</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S1</span> <span class="freq">W3</span><span class="pos"> verb</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_2_guess_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_d_guess1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">guess</span><span class="homnum">1</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1200-1300</span> Probably from a <span class="etymlang">Scandinavian</span> language</span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">guess</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">guess</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">guesses</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">guessed</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">guessed</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">guessed</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">guessed</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">guess</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">guessed</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">guessing</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">guessing</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">guessing</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">guessing</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">guessing</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">guessing</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">guessing</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">guessing</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">guessing</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">guessing</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>"Are you Dan's brother?'' "Yes, how did you <span class="nodeword">guess</span>?''</li><li>"Don't tell me - you got the job." "How'd you <span class="nodeword">guess</span>?"</li><li><span class="nodeword">Guess</span> how much I paid for this watch!</li><li><span class="nodeword">Guess</span> who I saw in town yesterday.</li><li>Are you sure Linda's pregnant, or are you just <span class="nodeword">guessing</span>?</li><li>Detectives <span class="nodeword">guess</span> the attacker must be aged from 25 - 30.</li><li>From their behaviour it was easy to <span class="nodeword">guess</span> that they were married.</li><li>Guy looked at her face, and <span class="nodeword">guessed</span> what she was thinking.</li><li>He <span class="nodeword">guessed</span> she was about 30.</li><li>I'm only <span class="nodeword">guessing</span>, but I should think their house is worth over a million.</li><li>I didn't know all the answers so I just had to <span class="nodeword">guess</span> some of them.</li><li>Luckily, I <span class="nodeword">guessed</span> the right answer and won the prize!</li><li>Sally <span class="nodeword">guessed</span> that he had been drinking for most of the afternoon.</li><li>They had already <span class="nodeword">guessed</span> the truth about their son's disappearance.</li><li>We can only <span class="nodeword">guess</span> at the cause of the crash.</li><li>Which hand have I got the chocolate in? If you <span class="nodeword">guess</span> right you can have it.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>Already he's <span class="nodeword">guessing</span> the colour of her toothbrush.</li><li>And I <span class="nodeword">guess</span>, yeah, we could stand to read more.</li><li>I <span class="nodeword">guess</span> how each one I guess.</li><li>I <span class="nodeword">guess</span> I was doing the best I could with the knowledge I had.</li><li>No prizes either for <span class="nodeword">guessing</span> his four-legged friend is named after a famous Liverpool manager.</li><li>Perhaps you can <span class="nodeword">guess</span> exactly where I fell that night.</li><li>She might have <span class="nodeword">guessed</span> that as soon as she tried for a little peace and quiet the whole place would be inundated with callers.</li><li>You should have come to Ward or me the minute you suspected it, instead of fooling around <span class="nodeword">guessing</span>.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to guess something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guess</span></span><div class="content"><span class="def"> to give an answer or decide that something is probably true, when you do not know enough to be definitely sure</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Are you sure Linda's pregnant, or are you just guessing?</span><span class="example"><span class="neutral">· </span>I'm only guessing, but I should think their house is worth over a million.</span><span class="example"><span class="neutral">· </span>I didn't know all the answers so I just had to guess some of them.</span><span class="thespropform">guess who/what/how etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Guess who I saw in town yesterday.</span><span class="example"><span class="neutral">· </span>Guess how much I paid for this watch!</span><span class="thespropform">guess at</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We can only guess at the cause of the crash.</span><span class="thespropform">guess (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sally guessed that he had been drinking for most of the afternoon.</span><span class="example"><span class="neutral">· </span>Detectives guess the attacker must be aged from 25 - 30.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">make a guess</span></span><div class="content"><span class="def"> to guess something, especially when you are trying to answer a question or explain how something might have happened</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I've no idea where I lost the keys. I couldn't even make a guess.</span><span class="thespropform">make a guess at/about/as to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's possible to make a guess at who the woman in the story might be.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">have a guess</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British</span><span class="variant"> <span class="lexvar">/take a guess</span><span class="neutral"> </span><span class="registerlab">especially American</span></span><span class="def"> to guess an answer, amount, or number - use this especially when you are telling someone to do this</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How much rent do you pay for your apartment?" "Take a guess."</span><span class="thespropform">have a guess at</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Have a guess at the answer, then check it with your calculator.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">estimate</span></span><div class="content"><span class="def"> to say how much something will cost, how many of something there are etc, partly by calculating and partly by guessing</span><span class="thespropform"><span class="neutral">: </span>estimate (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police estimate that over 10,000 people took part in the demonstration.</span><span class="thespropform">estimate something at $350/£400 etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His personal fortune is estimated at £150 million.</span><span class="example"><span class="neutral">· </span>The mechanic estimated the cost of repairs at $350.</span><span class="thespropform">estimate how much/how many etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you estimate how much fabric you will need for the curtains?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put something at</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>put the cost/the value/somebody's age etc at something</span><span class="def"> to guess that the cost, value etc is a particular amount without trying to be very exact</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'd put her age at around 35.</span><span class="example"><span class="neutral">· </span>The cost of the war was put at more than $10 billion.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">speculate</span></span><div class="content"><span class="def"> to think about or discuss something in order to guess what its causes or results might be</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We don't know why the prehistoric stone circles were built. We can only speculate.</span><span class="thespropform">speculate about/on/as to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Edward began to speculate on what life would be like if he were single again.</span><span class="example"><span class="neutral">· </span>People have been speculating about interstellar flight for years.</span></div></span></span><span class="section"><span class="secheading">what you say when you guess something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">my guess is</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are telling someone what you think has probably happened or will probably happen</span><span class="thespropform"><span class="neutral">: </span>my guess is (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My guess is that they've been delayed in a traffic jam.</span><span class="example"><span class="neutral">· </span>My guess is she'll move back to the States after the divorce.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I bet</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are fairly sure that something is true or something is happening, although you cannot prove this</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He'll be really pleased to see you, I bet!</span><span class="thespropform">I bet (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Come and sit down, I bet you're exhausted.</span><span class="example"><span class="neutral">· </span>I bet that the meeting will be cancelled again.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">off the top of my head</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are guessing a number or amount and you have not checked it exactly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Off the top of my head, I think that the figure is about 25%.</span><span class="example"><span class="neutral">· </span>"What's the painting worth?" "Three million or so," he said, off the top of his head.</span></div></span></span><span class="section"><span class="secheading">to guess correctly</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guess</span></span><div class="content"><span class="def"> to guess correctly that something is true, or guess the right answer to something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Are you Dan's brother?'' "Yes, how did you guess?''</span><span class="example"><span class="neutral">· </span>Luckily, I guessed the right answer and won the prize!</span><span class="thespropform">guess what/who/where etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Guy looked at her face, and guessed what she was thinking.</span><span class="thespropform">guess (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>From their behaviour it was easy to guess that they were married.</span><span class="thespropform">guess the truth</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They had already guessed the truth about their son's disappearance.</span><span class="thespropform">guess right</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Which hand have I got the chocolate in? If you guess right you can have it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">you're getting warm</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when someone's guess about something is nearly correct</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Where are you going? Egypt, Morocco?" "You're getting warm!"</span></div></span></span><span class="section"><span class="secheading">to guess incorrectly</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guess wrong</span></span><div class="content"><span class="def"> to guess incorrectly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I guessed she was over sixteen, but I guessed wrong.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">overestimate</span></span><div class="content"><span class="def"> to guess wrongly, by thinking that the amount, level, or cost of something is bigger, more expensive etc than it really is</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>People overestimated the risk of catching the disease.</span><span class="thespropform">overestimate how much/how long etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We overestimated how long the journey would take, and arrived far too early.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">underestimate</span></span><div class="content"><span class="def"> to guess wrongly, by thinking that the amount, level, or cost of something is smaller, less expensive etc than it really is</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They underestimated the amount of time it would take to finish the work.</span><span class="thespropform">underestimate how much/ how long etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You must never underestimate how much I love you.</span><span class="thespropform">how difficult/ how important etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Don't underestimate how good the other team is, or how hard they're going to play.</span><span class="thespropform">badly/greatly underestimate</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They had badly underestimated how many lifeboats would be needed.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be wide of the mark</span></span><div class="content"><span class="def"> to guess incorrectly - use this when someone is not at all close to being correct</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The movie was expected to gross millions, but that forecast was wide of the mark.</span><span class="thespropform">be seriously/hopelessly wide of the mark</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>All the opinion polls turned out to be hopelessly wide of the mark.</span></div></span></span><span class="section"><span class="secheading">an attempt to guess something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guess</span></span><div class="content"><span class="def"> an attempt to guess something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>This is only a guess, but I think Barbara might have gone to Jan's house.</span><span class="example"><span class="neutral">· </span>I didn't really know the answer. It was just a lucky guess.</span><span class="thespropform">make a guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not sure why she left him, but I think I can make a guess.</span><span class="thespropform">good guess</span><span class="neutral"> </span><span class="registerlab">spoken</span><span class="gloss"><span class="neutral"> (=</span>say this when someone's guess is almost correct<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"When was the house built - about 1600?" "That's a good guess - it was 1624."</span><span class="thespropform">I'll give you two/three guesses</span><span class="neutral"> </span><span class="registerlab">spoken</span><span class="gloss"><span class="neutral"> (=</span>say this when you think the other person already knows the answer to the question<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Who's her new boyfriend then?" "I'll give you three guesses!''</span><span class="thespropform">an educated guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Did Cindy tell you that she's sold the business?" "No, it was just an educated guess."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">estimate</span></span><div class="content"><span class="def"> an opinion about the value, size, speed etc of something that is formed partly by calculating and partly by guessing</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>According to some estimates, almost two thirds of the city has been destroyed by the earthquake.</span><span class="thespropform">rough estimate</span><span class="gloss"><span class="neutral"> (=</span>an estimate that is not intended to be exact<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>These are the figures, but they're only a rough estimate.</span><span class="thespropform">at/as a rough estimate</span><span class="gloss"><span class="neutral"> (=</span>making a rough estimate<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>At a rough estimate, staff are recycling less than a quarter of the paper we buy.</span><span class="thespropform">a conservative estimate</span><span class="gloss"><span class="neutral"> (=</span>an estimate that is deliberately low<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We're predicting a 10% rise in oil prices -- and that's a conservative estimate.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guesswork</span></span><div class="content"><span class="def"> when you try to understand something or find the answer to something by guessing, because you do not have all the information you need</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's important to find out what consumers want to buy, rather than relying on guesswork.</span><span class="example"><span class="neutral">· </span>At the beginning, the police investigation was largely based on guesswork.</span><span class="thespropform">it was pure/sheer guesswork</span><span class="neutral"> </span><span class="registerlab">spoken</span><span class="gloss"><span class="neutral"> (=</span>use this to say that you found out something by guessing<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How did you know where she'd gone?" "It was pure guesswork."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">speculation</span></span><div class="content"><span class="def"> when a lot of people, especially in newspapers and on television, try to guess what is happening or what will happen because they do not have much definite information</span><span class="thespropform"><span class="neutral">: </span>speculation about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There has been a lot of speculation about the date of the next election.</span><span class="example"><span class="neutral">· </span>The success of the book was heightened by media speculation about who the characters were in real life.</span><span class="thespropform">speculation that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A further defeat for the government led to increasing speculation that the Prime Minister would resign.</span><span class="thespropform">amid speculation (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The investigation into the crash continued amid speculation that terrorists had destroyed the plane.</span><span class="thespropform">wild speculation</span><span class="gloss"><span class="neutral"> (=</span>guesses that are not sensible<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>the wild speculation that surrounded Princess Diana's death</span><span class="thespropform">pure speculation</span><span class="gloss"><span class="neutral"> (=</span> guesses that are not sensible based only on guessing, and not on informaion<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Any suggestion of an imminent crash in property prices is pure speculation.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">speculative</span></span><div class="content"><span class="def"> an opinion, explanation etc that is speculative is based on guessing and not on facts</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Theories of the origin of life are partly speculative.</span><span class="thespropform">highly speculative</span><span class="gloss"><span class="neutral"> (=</span>based almost completely on guessing and probably not correct<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Until further research has been done, any figures that I can give you are highly speculative.</span><span class="thespropform">entirely/purely speculative</span><span class="gloss"><span class="neutral"> (=</span>based completely on guessing<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The papers were full of talk of Lucan's whereabouts, all of it entirely speculative.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">conjecture</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> guesses that are based on information that is not complete</span><span class="thespropform"><span class="neutral">: </span>a matter for/of conjecture</span><span class="gloss"><span class="neutral"> (=</span>something that people can try to guess but cannot know<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's a matter for conjecture who wrote the original text in the fifteenth century.</span><span class="thespropform">pure conjecture</span><span class="gloss"><span class="neutral"> (=</span>based only on guessing, and not on facts<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The judge dismissed the evidence as pure conjecture.</span></div></span></span><span class="section"><span class="secheading">a guess that is based on very little information</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">rough guess</span></span><div class="content"><span class="def"> the answer you give when you are trying to guess a number or amount but are not able to be exact</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'd say Mrs Roberts was about 35, but that's only a rough guess.</span><span class="thespropform">make/take/have a rough guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's terribly difficult to calculate, but I could make a rough guess and say the cost will be about half a million.</span><span class="thespropform">at a rough guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How long will the journey take?" "At a rough guess, about six hours."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">hunch</span></span><div class="content"><span class="def"> a strange feeling that you know the answer to something, even though you have very little information to help you to guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's just a hunch, but it's possible the murderer may have been a woman.</span><span class="thespropform">hunch that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My hunch that he was lying turned out to be correct.</span><span class="thespropform">have a hunch</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I have a hunch that Jodie may be planning a surprise party.</span><span class="thespropform">hunch about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How did you know the answer?" "I just had a hunch about it."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">wild guess</span></span><div class="content"><span class="def"> a guess, especially an answer, that seems stupid or is very likely to be wrong</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>This is a wild guess, but is the answer Michael Jackson?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">a shot in the dark</span></span><div class="content"><span class="def"> a guess that you make when you have no information at all, so that it is almost certainly wrong</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Let's see if she's at Fiona's house. It's a shot in the dark, but we've got to start looking somewhere.</span></div></span></span><span class="section last"><span class="secheading">what you say when you think something is true, but you are not sure</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I think</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span> <span class="example"><span class="neutral">· </span>She'll be here about 9, I think.</span><span class="thespropform">I think (that)</span> <span class="example"><span class="neutral">· </span>I think the dog must have eaten it.</span><span class="thespropform">I think so</span> <span class="example"><span class="neutral">· </span>"Is Matthew still here?" "I think so -- I've just seen him."</span><span class="thespropform">I would think so</span> <span class="example"><span class="neutral">· </span>"Will Jenny be at the meeting?" "I would think so."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I suppose</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you think that something is probably true but you are not really sure</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Having a burglar alarm makes you feel safer, I suppose.</span><span class="thespropform">I suppose (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I suppose we can pay by credit card but we'd better check first.</span><span class="thespropform">I suppose so</span><span class="gloss"><span class="neutral"> (=</span>say this to answer a question<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Will the children be disappointed?" "Yes, I suppose so."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I guess</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially American, spoken</span><span class="def"> say this when you think that something is probably true but you are not really sure</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Rob just got tired of living with her, I guess.</span><span class="thespropform">I guess (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I guess this is the best way to do it.</span><span class="thespropform">I guess so</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Is the truck safe to drive now?" "I guess so." </span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">get the impression/feeling/idea</span></span><div class="content"><span class="def"> to start to think something is a fact because of various things that happen, because of the way someone is behaving etc</span><span class="thespropform"><span class="neutral">: </span>get the impression/feeling/idea (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I got the impression she was actually quite nervous about it.</span><span class="example"><span class="neutral">· </span>Walking down the main street, you get the feeling that nothing ever happens in this town.</span><span class="example"><span class="neutral">· </span>I don't want you to get the idea that I don't like him.</span><span class="thespropform">get this/that idea</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"He thinks you're angry with them." "Where on earth did he get that idea?"</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">as far as I know</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you think that something is true, although you realize that you may not know all the facts</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>As far as I know, Caroline's never been married.</span><span class="example"><span class="neutral">· </span>Alaska doesn't have any drilling sites off the coast, as far as I know.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I imagine</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you think that something is likely to be true, although what you say is only based on your opinion</span><span class="thespropform"><span class="neutral">: </span>I imagine (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I imagine she's stuck in a traffic jam or something.</span><span class="thespropform">I would imagine (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I would imagine that Libby could help you - she knows a lot about legal things.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">as far as I'm aware</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you want to make it clear that there may be things you do not know about a situation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>As far as I am aware, Mr Cusner and his family are still living in Cleveland, Ohio.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">to the best of my knowledge</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal spoken</span><span class="def"> say this when you want to make it clear that you are not completely sure about the statement you are making</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>To the best of my knowledge, this is the first time that anyone has translated these poems into English.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I take it</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you think that something is true and you are asking someone else to tell you that you are right</span><span class="thespropform"><span class="neutral">: </span>I take it (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I take it that you're Rob's sister?</span><span class="example"><span class="neutral">· </span>So can we take it that you'll be at the meeting?</span><span class="example"><span class="neutral">· </span>You've made plans for the future, I take it?</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">guess right/correctly/wrong</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001702557').play();"><img src="LDOCE6_img_spkr_g.png"/></a> If you guess correctly, you have another turn.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">let ... guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001702563').play();"><img src="LDOCE6_img_spkr_g.png"/></a> What star sign are you? No, <span class="colloinexa">let</span> me <span class="colloinexa">guess</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">difficult/hard/easy etc to guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001702569').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It’s hard to guess his age because he dyes his hair.</span></div></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">guess the answer</span></span><div class="content"><span class="example"><span class="neutral">· </span>If you don’t know the answer, try guessing it.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">make ... educated guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-001637614').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Investors must <span class="colloinexa">make</span> an <span class="colloinexa">educated guess</span> as to the company’s potential.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a good guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001722897').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’m not sure, but I could make <span class="colloinexa">a good guess</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">hazarding a guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001756565').play();"><img src="LDOCE6_img_spkr_g.png"/></a> $50,000? I don’t know. I’m only <span class="colloinexa">hazarding a guess</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">wild guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001740523').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’m just making a <span class="colloinexa">wild guess</span> here, so correct me if I’m wrong.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">correctly</span></span><div class="content"><span class="example"><span class="neutral">· </span>He wondered what was behind it and <span class="colloinexa">guessed correctly</span>.</span><span class="example"><span class="neutral">· </span>The first team to <span class="colloinexa">guess correctly</span> wins the round.</span><span class="example"><span class="neutral">· </span>Yesterday winners Jennie Edwards, 10, and Dawn Waters, 30, received their prize for <span class="colloinexa">guessing correctly</span>.</span><span class="example"><span class="neutral">· </span>Campbell, who once challenged Lathrop to identify tenors in the same format, <span class="colloinexa">correctly guesses</span> a handful of more than 20.</span><span class="example"><span class="neutral">· </span>You placed bets on what card was going to be drawn and you had to <span class="colloinexa">guess correctly</span> to get your money back.</span><span class="example"><span class="neutral">· </span>The player who finally <span class="colloinexa">guesses correctly</span> chooses the next card.</span><span class="example"><span class="neutral">· </span>Somehow my pursuer had <span class="colloinexa">guessed correctly</span> at every turn.</span><span class="example"><span class="neutral">· </span>Of the 102 symbols, 79 were <span class="colloinexa">correctly guessed</span> first time and only 8 symbols required more than 5 guesses.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">never</span></span><div class="content"><span class="example"><span class="neutral">· </span>You'd <span class="colloinexa">never guess</span> that the performances actually date from the early to mid-1960's!</span><span class="example"><span class="neutral">· </span>They had <span class="colloinexa">never</span> seen a landscape like this, never <span class="colloinexa">guessed</span> one could even exist.</span><span class="example"><span class="neutral">· </span>But he must <span class="colloinexa">never guess</span> the truth.</span><span class="example"><span class="neutral">· </span>But soon, she noticed that he <span class="colloinexa">never guessed</span> her name.</span><span class="example"><span class="neutral">· </span>But, encased in the false security of her new-found happiness, she had <span class="colloinexa">never guessed</span>.</span><span class="example"><span class="neutral">· </span>They <span class="colloinexa">never guessed</span> she found this distasteful.</span><span class="example"><span class="neutral">· </span>Looking at me, you'd <span class="colloinexa">never guess</span> I'd killed three people.</span><span class="example"><span class="neutral">· </span>A person strolling through the dusty bed of the Arkansas near Dodge City might <span class="colloinexa">never guess</span> at the river that was there.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">only</span></span><div class="content"><span class="example"><span class="neutral">· </span>For example, instead of typing try other that you can <span class="colloinexa">only guess</span>.</span><span class="example"><span class="neutral">· </span>I can <span class="colloinexa">only guess</span> at what I have done to provoke her.</span><span class="example"><span class="neutral">· </span>What had gone from the attic she could <span class="colloinexa">only guess</span>, for she did not remember half the things they stored there.</span><span class="example"><span class="neutral">· </span>Why this was so we can <span class="colloinexa">only guess</span>.</span><span class="example"><span class="neutral">· </span>Once again, we can never know, <span class="colloinexa">only guess</span>.</span><span class="example"><span class="neutral">· </span>What Baden Powell would say to a rap anthem, one can <span class="colloinexa">only guess</span> at.</span><span class="example"><span class="neutral">· </span>That little creature must have had a strength to survive that we can <span class="colloinexa">only guess</span> at.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">right</span></span><div class="content"><span class="example"><span class="neutral">· </span>Duncan had <span class="colloinexa">guessed right</span> about it being a Shogun, the smaller, two-door version.</span><span class="example"><span class="neutral">· </span>If we <span class="colloinexa">guess right</span>, then we confirm his suspicions.</span><span class="example"><span class="neutral">· </span>As it turns out, David <span class="colloinexa">guesses right</span>.</span><span class="example"><span class="neutral">· </span>She'd <span class="colloinexa">guessed right</span> the first time.</span><span class="example"><span class="neutral">· </span>And still Jinny did not <span class="colloinexa">guess right</span>.</span></div></span></span><span class="section last"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">prize</span></span><div class="content"><span class="example"><span class="neutral">· </span>No <span class="colloinexa">prizes</span> either for <span class="colloinexa">guessing</span> his four-legged friend is named after a famous Liverpool manager.</span><span class="example"><span class="neutral">· </span>There are no <span class="colloinexa">prizes</span> for <span class="colloinexa">guessing</span> why this should be.</span><span class="example"><span class="neutral">· </span>No <span class="colloinexa">prizes</span> for <span class="colloinexa">guessing</span> the names of the owners here.</span><span class="example"><span class="neutral">· </span>Enclosed his picture - no <span class="colloinexa">prizes</span> for <span class="colloinexa">guessing</span> his breed.</span><span class="example"><span class="neutral">· </span>Yesterday winners Jennie Edwards, 10, and Dawn Waters, 30, received their <span class="colloinexa">prize</span> for <span class="colloinexa">guessing</span> correctly.</span><span class="example"><span class="neutral">· </span>No <span class="colloinexa">prizes</span> for <span class="colloinexa">guessing</span> the answer to that question.</span><span class="example"><span class="neutral">· </span>No <span class="colloinexa">prizes</span> for <span class="colloinexa">guessing</span> what she was wearing.</span><span class="example"><span class="neutral">· </span>No <span class="colloinexa">prizes</span> for <span class="colloinexa">guessing</span> that Brahms is a major culprit.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">guess what/you’ll never guess who/what etc</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">keep somebody guessing</span></span><div class="content"><ul class="exas"><li>Our supervisor likes to keep everyone <span class="nodeword">guessing</span>.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">I guess</span></span><div class="content"><ul class="exas"><li>"Is the truck safe to drive now?" "I <span class="nodeword">guess</span> so."</li><li>His light's on, so I <span class="nodeword">guess</span> he's still up.</li><li>I <span class="nodeword">guess</span> his dad had to work two jobs when they were little.</li><li>I <span class="nodeword">guess</span> I'll stay home tonight.</li><li>I <span class="nodeword">guess</span> this is the best way to do it.</li><li>Rob just got tired of living with her, I <span class="nodeword">guess</span>.</li><li>They live somewhere between Saginaw and Lansing, I <span class="nodeword">guess</span>.</li><li>From the shape of its lights I <span class="nodeword">guessed</span> it was one of the small Peugeots.</li><li>I <span class="nodeword">guess</span> how each one I guess.</li><li>I am weary. I <span class="nodeword">guess</span> you read one porno magazine and the second one is just the same.</li><li>I did not know the name, but I <span class="nodeword">guessed</span> at once who she was.</li><li>Slim was gone-to Montana, I <span class="nodeword">guess</span>.</li><li>Well fifteen, half an hour and then a break, I <span class="nodeword">guess</span>.</li><li>You could add something about the hurricane, I <span class="nodeword">guess</span>.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">I guess so/not</span></span><div class="content"><ul class="exas"><li>"She wasn't happy?" "I <span class="nodeword">guess</span> not."</li><li>Dope, is it? I <span class="nodeword">guess</span> so.</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">a conservative estimate/guess</span></span><div class="content"><ul class="exas"><li><span class="nodeword">Conservative</span> estimates indicate at least 150 people were killed in the military coup.</li><li>All the data published on the effect of gill-nets on small cetaceans probably represents a <span class="nodeword">conservative</span> estimate of the number of deaths.</li><li>At a <span class="nodeword">conservative</span> estimate the water was nearly a yard deep, even near the edge.</li><li>At a <span class="nodeword">conservative</span> estimate they had cost considerably more than she had just paid for her night's lodging.</li><li>By even a <span class="nodeword">conservative</span> estimate, about 10,000 Dall's porpoise were harpooned each year from 1976 to 1987.</li><li>I understand that is a <span class="nodeword">conservative</span> estimate.</li><li>Narcotics agents believe a <span class="nodeword">conservative</span> estimate of the number of laboratories is between 200 and 300.</li><li>On a <span class="nodeword">conservative</span> estimate, there are now about 5,000 books or articles that deal with it, at least in part.</li><li>On this basis imitation tasks ought to be regarded as giving a <span class="nodeword">conservative</span> estimate of the child's grammatical knowledge.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">educated guess</span></span><div class="content"><ul class="exas"><li>Are at least able to make an <span class="nodeword">educated</span> guess as to who is collapsing the scrummage. 7.</li><li>But, beyond hunches and <span class="nodeword">educated</span> guesses, what about other human characteristics such as beliefs, prejudices and emotions?</li><li>Other of the source studies, however, used patient values, clinician values, or <span class="nodeword">educated</span> guesses.</li><li>Still, money managers, analysts, and economists are taking their best <span class="nodeword">educated</span> guesses.</li><li>That sometimes means making <span class="nodeword">educated</span> guesses.</li><li>The law requires the chief of police to make <span class="nodeword">educated</span> guesses about the likelihood that disorder, damage or disruption will occur.</li><li>Where and what kinds they will be is an open question that is, at best, an <span class="nodeword">educated</span> guess.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">inspired guess/choice etc</span></span><div class="content"><ul class="exas"><li>As it happens, he made an <span class="nodeword">inspired</span> choice.</li><li>But my father's was an <span class="nodeword">inspired</span> guess.</li><li>If he can be persuaded to keep religion out of politics, he could prove an <span class="nodeword">inspired</span> choice.</li><li>In Whitham's case it was different and he certainly was what could be termed an <span class="nodeword">inspired</span> choice by the Lightweight club.</li><li>It could prove an <span class="nodeword">inspired</span> choice for New Zealand.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">I might have known/guessed etc</span></span><div class="content"><ul class="exas"><li>All those years I <span class="nodeword">might</span> have known her!</li><li>Although I <span class="nodeword">might</span> have known you'd arrive just as drinks were being ordered!</li><li>Dear little Papa, as I <span class="nodeword">might</span> have known!</li><li>If you'd had a big fat bottom I <span class="nodeword">might</span> have guessed.</li><li>It was nothing I <span class="nodeword">might</span> have guessed.</li><li>Of course, I <span class="nodeword">might</span> have known that you'd have some clever way of dealing with everything, though.</li><li>Ooh! I <span class="nodeword">might</span> have known it!</li><li>Some years before, I <span class="nodeword">might</span> have guessed Bond's enigmatic presence in the scene.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">no prizes for guessing something</span></span><div class="content"><ul class="exas"><li>Enclosed his picture - no <span class="nodeword">prizes</span> for guessing his breed.</li><li>There are no <span class="nodeword">prizes</span> for guessing why this should be.</li></ul></div></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="gram">[intransitive, transitive]</span><span class="def"> to try to answer a question or form an opinion when you are not sure whether you will be correct</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001309289"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001309289').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’d say he’s around 50, but I’m only guessing.</span><span class="colloexa"><span class="collo">guess right/correctly/wrong</span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001702557"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001702557').play();"><img src="LDOCE6_img_spkr_g.png"/></a> If you guess correctly, you have another turn.</span></span><span class="gramexa"><span class="propform">guess what/who/how etc</span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001702560"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001702560').play();"><img src="LDOCE6_img_spkr_g.png"/></a> You can guess what happened next.</span></span><span class="gramexa"><span class="propformprep">guess at</span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001309295"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001309295').play();"><img src="LDOCE6_img_spkr_g.png"/></a> We can only guess at the cause of the crash.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001702563"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001702563').play();"><img src="LDOCE6_img_spkr_g.png"/></a> What star sign are you? No, <span class="colloinexa">let</span> me <span class="colloinexa">guess</span>.</span><span class="colloexa"><span class="collo">difficult/hard/easy etc to guess</span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001702569"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001702569').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It’s hard to guess his age because he dyes his hair.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="gram">[intransitive, transitive]</span><span class="def"> to realize that something is true even though you do not know for certain</span><span class="gramexa"><span class="propform">guess (that)</span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001309306"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001309306').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I guessed that you must be related because you look so similar.</span></span><span class="gramexa"><span class="propformprep">guess from</span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001309303"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001309303').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I guessed from his expression that he already knew about the accident.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001702578"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001702578').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Can you guess the identity of this week’s special guest?</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s3"></a><span class="sense newline"><span class="sensenum">3</span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s4"></a><span class="lexunit">keep somebody guessing</span><span class="def"> to make someone feel excited or not sure about what will happen next</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-001702579"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-001702579').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a thriller that keeps audiences guessing</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s5"></a><span class="sense newline"><span class="sensenum">4</span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s6"></a><span class="lexunit">I guess</span><span class="registerlab"> spoken</span><span class="subsense"> <span class="sensenum">a)</span><span class="def">used to say that you think something is true or likely, although you are not sure</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001309320"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001309320').play();"><img src="LDOCE6_img_spkr_g.png"/></a> His light’s on, so I guess he’s still up.</span></span><span class="subsense"> <span class="sensenum">b)</span><span class="def">used to say that you will do something even though you do not really want to</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_d_p008-001702583"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-001702583').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’m tired, so I guess I’ll stay home tonight.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s7"></a><span class="sense newline"><span class="sensenum">5</span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s8"></a><span class="lexunit">I guess so/not</span><span class="registerlab"> spoken</span><span class="def"> used to agree or disagree with a statement or question</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001702590"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001702590').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘You’re one lucky guy.’ ‘I guess so.’</span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001702591"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001702591').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘I don’t really have any choice, do I?’ ‘I guess not.’</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s9"></a><span class="sense newline"><span class="sensenum">6</span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_1_s10"></a><span class="lexunit">guess what/you’ll never guess who/what etc</span><span class="registerlab"> spoken</span><span class="def"> used before you tell someone something that will surprise them</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001309333"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001309333').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Guess what! Bradley’s resigned.</span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001309334"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001309334').play();"><img src="LDOCE6_img_spkr_g.png"/></a> You’ll never guess who I saw today.</span></span><span class="tail"><span class="crossref"><span class="neutral"> →</span><a> <span class="refhwd">second-guess</span></a></span></span></span></div><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_guess_2"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_guess_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_guess_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_guess_2"></a><span class="entry"><span class="entryhead"><span class="hwd">guess</span><span class="hyphenation frequent">guess</span><span class="homnum frequent">2</span><span class="level tooltip"> ●●●</span> <span class="freq">S3</span><span class="pos"> noun</span><span class="gram"> [countable]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_2_guess_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_d_guess1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>"Did Cindy tell you that she's sold the business?" "No, it was just an educated <span class="nodeword">guess</span>."</li><li>"When was the house built - about 1600?" "That's a good <span class="nodeword">guess</span> - it was 1624."</li><li>"Who's her new boyfriend then?" "I'll give you three <span class="nodeword">guesses</span>!''</li><li>I'll give you three <span class="nodeword">guesses</span>.</li><li>I'm not sure why she left him, but I think I can make a <span class="nodeword">guess</span>.</li><li>I didn't really know the answer. It was just a lucky <span class="nodeword">guess</span>.</li><li>This is only a <span class="nodeword">guess</span>, but I think Barbara might have gone to Jan's house.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>And those <span class="nodeword">guesses</span> will come more from our own sense of meaning and beauty than the actual beliefs of the cave artists.</li><li>It is anybody's <span class="nodeword">guess</span> as to how the sale will fare.</li><li>The reporters could not tell whether this was because Kalmbach was a lucky <span class="nodeword">guess</span> or a ridiculous one.</li><li>What happens to Mr Mandelson next is anyone's <span class="nodeword">guess</span>.</li><li>Your <span class="nodeword">guess</span> is as good as mine.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to guess something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guess</span></span><div class="content"><span class="def"> to give an answer or decide that something is probably true, when you do not know enough to be definitely sure</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Are you sure Linda's pregnant, or are you just guessing?</span><span class="example"><span class="neutral">· </span>I'm only guessing, but I should think their house is worth over a million.</span><span class="example"><span class="neutral">· </span>I didn't know all the answers so I just had to guess some of them.</span><span class="thespropform">guess who/what/how etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Guess who I saw in town yesterday.</span><span class="example"><span class="neutral">· </span>Guess how much I paid for this watch!</span><span class="thespropform">guess at</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We can only guess at the cause of the crash.</span><span class="thespropform">guess (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sally guessed that he had been drinking for most of the afternoon.</span><span class="example"><span class="neutral">· </span>Detectives guess the attacker must be aged from 25 - 30.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">make a guess</span></span><div class="content"><span class="def"> to guess something, especially when you are trying to answer a question or explain how something might have happened</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I've no idea where I lost the keys. I couldn't even make a guess.</span><span class="thespropform">make a guess at/about/as to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's possible to make a guess at who the woman in the story might be.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">have a guess</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British</span><span class="variant"> <span class="lexvar">/take a guess</span><span class="neutral"> </span><span class="registerlab">especially American</span></span><span class="def"> to guess an answer, amount, or number - use this especially when you are telling someone to do this</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How much rent do you pay for your apartment?" "Take a guess."</span><span class="thespropform">have a guess at</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Have a guess at the answer, then check it with your calculator.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">estimate</span></span><div class="content"><span class="def"> to say how much something will cost, how many of something there are etc, partly by calculating and partly by guessing</span><span class="thespropform"><span class="neutral">: </span>estimate (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police estimate that over 10,000 people took part in the demonstration.</span><span class="thespropform">estimate something at $350/£400 etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His personal fortune is estimated at £150 million.</span><span class="example"><span class="neutral">· </span>The mechanic estimated the cost of repairs at $350.</span><span class="thespropform">estimate how much/how many etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you estimate how much fabric you will need for the curtains?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put something at</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>put the cost/the value/somebody's age etc at something</span><span class="def"> to guess that the cost, value etc is a particular amount without trying to be very exact</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'd put her age at around 35.</span><span class="example"><span class="neutral">· </span>The cost of the war was put at more than $10 billion.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">speculate</span></span><div class="content"><span class="def"> to think about or discuss something in order to guess what its causes or results might be</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We don't know why the prehistoric stone circles were built. We can only speculate.</span><span class="thespropform">speculate about/on/as to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Edward began to speculate on what life would be like if he were single again.</span><span class="example"><span class="neutral">· </span>People have been speculating about interstellar flight for years.</span></div></span></span><span class="section"><span class="secheading">what you say when you guess something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">my guess is</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are telling someone what you think has probably happened or will probably happen</span><span class="thespropform"><span class="neutral">: </span>my guess is (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My guess is that they've been delayed in a traffic jam.</span><span class="example"><span class="neutral">· </span>My guess is she'll move back to the States after the divorce.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I bet</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are fairly sure that something is true or something is happening, although you cannot prove this</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He'll be really pleased to see you, I bet!</span><span class="thespropform">I bet (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Come and sit down, I bet you're exhausted.</span><span class="example"><span class="neutral">· </span>I bet that the meeting will be cancelled again.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">off the top of my head</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are guessing a number or amount and you have not checked it exactly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Off the top of my head, I think that the figure is about 25%.</span><span class="example"><span class="neutral">· </span>"What's the painting worth?" "Three million or so," he said, off the top of his head.</span></div></span></span><span class="section"><span class="secheading">to guess correctly</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guess</span></span><div class="content"><span class="def"> to guess correctly that something is true, or guess the right answer to something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Are you Dan's brother?'' "Yes, how did you guess?''</span><span class="example"><span class="neutral">· </span>Luckily, I guessed the right answer and won the prize!</span><span class="thespropform">guess what/who/where etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Guy looked at her face, and guessed what she was thinking.</span><span class="thespropform">guess (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>From their behaviour it was easy to guess that they were married.</span><span class="thespropform">guess the truth</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They had already guessed the truth about their son's disappearance.</span><span class="thespropform">guess right</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Which hand have I got the chocolate in? If you guess right you can have it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">you're getting warm</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when someone's guess about something is nearly correct</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Where are you going? Egypt, Morocco?" "You're getting warm!"</span></div></span></span><span class="section"><span class="secheading">to guess incorrectly</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guess wrong</span></span><div class="content"><span class="def"> to guess incorrectly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I guessed she was over sixteen, but I guessed wrong.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">overestimate</span></span><div class="content"><span class="def"> to guess wrongly, by thinking that the amount, level, or cost of something is bigger, more expensive etc than it really is</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>People overestimated the risk of catching the disease.</span><span class="thespropform">overestimate how much/how long etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We overestimated how long the journey would take, and arrived far too early.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">underestimate</span></span><div class="content"><span class="def"> to guess wrongly, by thinking that the amount, level, or cost of something is smaller, less expensive etc than it really is</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They underestimated the amount of time it would take to finish the work.</span><span class="thespropform">underestimate how much/ how long etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You must never underestimate how much I love you.</span><span class="thespropform">how difficult/ how important etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Don't underestimate how good the other team is, or how hard they're going to play.</span><span class="thespropform">badly/greatly underestimate</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They had badly underestimated how many lifeboats would be needed.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be wide of the mark</span></span><div class="content"><span class="def"> to guess incorrectly - use this when someone is not at all close to being correct</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The movie was expected to gross millions, but that forecast was wide of the mark.</span><span class="thespropform">be seriously/hopelessly wide of the mark</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>All the opinion polls turned out to be hopelessly wide of the mark.</span></div></span></span><span class="section"><span class="secheading">an attempt to guess something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guess</span></span><div class="content"><span class="def"> an attempt to guess something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>This is only a guess, but I think Barbara might have gone to Jan's house.</span><span class="example"><span class="neutral">· </span>I didn't really know the answer. It was just a lucky guess.</span><span class="thespropform">make a guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not sure why she left him, but I think I can make a guess.</span><span class="thespropform">good guess</span><span class="neutral"> </span><span class="registerlab">spoken</span><span class="gloss"><span class="neutral"> (=</span>say this when someone's guess is almost correct<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"When was the house built - about 1600?" "That's a good guess - it was 1624."</span><span class="thespropform">I'll give you two/three guesses</span><span class="neutral"> </span><span class="registerlab">spoken</span><span class="gloss"><span class="neutral"> (=</span>say this when you think the other person already knows the answer to the question<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Who's her new boyfriend then?" "I'll give you three guesses!''</span><span class="thespropform">an educated guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Did Cindy tell you that she's sold the business?" "No, it was just an educated guess."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">estimate</span></span><div class="content"><span class="def"> an opinion about the value, size, speed etc of something that is formed partly by calculating and partly by guessing</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>According to some estimates, almost two thirds of the city has been destroyed by the earthquake.</span><span class="thespropform">rough estimate</span><span class="gloss"><span class="neutral"> (=</span>an estimate that is not intended to be exact<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>These are the figures, but they're only a rough estimate.</span><span class="thespropform">at/as a rough estimate</span><span class="gloss"><span class="neutral"> (=</span>making a rough estimate<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>At a rough estimate, staff are recycling less than a quarter of the paper we buy.</span><span class="thespropform">a conservative estimate</span><span class="gloss"><span class="neutral"> (=</span>an estimate that is deliberately low<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We're predicting a 10% rise in oil prices -- and that's a conservative estimate.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guesswork</span></span><div class="content"><span class="def"> when you try to understand something or find the answer to something by guessing, because you do not have all the information you need</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's important to find out what consumers want to buy, rather than relying on guesswork.</span><span class="example"><span class="neutral">· </span>At the beginning, the police investigation was largely based on guesswork.</span><span class="thespropform">it was pure/sheer guesswork</span><span class="neutral"> </span><span class="registerlab">spoken</span><span class="gloss"><span class="neutral"> (=</span>use this to say that you found out something by guessing<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How did you know where she'd gone?" "It was pure guesswork."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">speculation</span></span><div class="content"><span class="def"> when a lot of people, especially in newspapers and on television, try to guess what is happening or what will happen because they do not have much definite information</span><span class="thespropform"><span class="neutral">: </span>speculation about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There has been a lot of speculation about the date of the next election.</span><span class="example"><span class="neutral">· </span>The success of the book was heightened by media speculation about who the characters were in real life.</span><span class="thespropform">speculation that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A further defeat for the government led to increasing speculation that the Prime Minister would resign.</span><span class="thespropform">amid speculation (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The investigation into the crash continued amid speculation that terrorists had destroyed the plane.</span><span class="thespropform">wild speculation</span><span class="gloss"><span class="neutral"> (=</span>guesses that are not sensible<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>the wild speculation that surrounded Princess Diana's death</span><span class="thespropform">pure speculation</span><span class="gloss"><span class="neutral"> (=</span> guesses that are not sensible based only on guessing, and not on informaion<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Any suggestion of an imminent crash in property prices is pure speculation.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">speculative</span></span><div class="content"><span class="def"> an opinion, explanation etc that is speculative is based on guessing and not on facts</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Theories of the origin of life are partly speculative.</span><span class="thespropform">highly speculative</span><span class="gloss"><span class="neutral"> (=</span>based almost completely on guessing and probably not correct<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Until further research has been done, any figures that I can give you are highly speculative.</span><span class="thespropform">entirely/purely speculative</span><span class="gloss"><span class="neutral"> (=</span>based completely on guessing<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The papers were full of talk of Lucan's whereabouts, all of it entirely speculative.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">conjecture</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> guesses that are based on information that is not complete</span><span class="thespropform"><span class="neutral">: </span>a matter for/of conjecture</span><span class="gloss"><span class="neutral"> (=</span>something that people can try to guess but cannot know<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's a matter for conjecture who wrote the original text in the fifteenth century.</span><span class="thespropform">pure conjecture</span><span class="gloss"><span class="neutral"> (=</span>based only on guessing, and not on facts<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The judge dismissed the evidence as pure conjecture.</span></div></span></span><span class="section"><span class="secheading">a guess that is based on very little information</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">rough guess</span></span><div class="content"><span class="def"> the answer you give when you are trying to guess a number or amount but are not able to be exact</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'd say Mrs Roberts was about 35, but that's only a rough guess.</span><span class="thespropform">make/take/have a rough guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's terribly difficult to calculate, but I could make a rough guess and say the cost will be about half a million.</span><span class="thespropform">at a rough guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How long will the journey take?" "At a rough guess, about six hours."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">hunch</span></span><div class="content"><span class="def"> a strange feeling that you know the answer to something, even though you have very little information to help you to guess</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's just a hunch, but it's possible the murderer may have been a woman.</span><span class="thespropform">hunch that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My hunch that he was lying turned out to be correct.</span><span class="thespropform">have a hunch</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I have a hunch that Jodie may be planning a surprise party.</span><span class="thespropform">hunch about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How did you know the answer?" "I just had a hunch about it."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">wild guess</span></span><div class="content"><span class="def"> a guess, especially an answer, that seems stupid or is very likely to be wrong</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>This is a wild guess, but is the answer Michael Jackson?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">a shot in the dark</span></span><div class="content"><span class="def"> a guess that you make when you have no information at all, so that it is almost certainly wrong</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Let's see if she's at Fiona's house. It's a shot in the dark, but we've got to start looking somewhere.</span></div></span></span><span class="section"><span class="secheading">approximately a number or amount</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">about</span></span><div class="content"><span class="def"> a little more or a little less than a number, amount, distance, or time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It should cost about $1500.</span><span class="example"><span class="neutral">· </span>The church is about a mile away.</span><span class="example"><span class="neutral">· </span>It's been about five years since I've seen Linda.</span><span class="example"><span class="neutral">· </span>The chance of men being born colourblind is about 1 in 12.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">approximately</span></span><div class="content"><span class="def"> a little more or a little less than a number, amount, distance, or time. Approximately is a little more formal than about and is used especially in written English</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Approximately 30% of the community is Polish.</span><span class="example"><span class="neutral">· </span>Each disk stores approximately 144 pages of text.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">roughly</span></span><div class="content"><span class="def"> approximately - used especially when you are trying to give someone a general idea of the number or amount</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A new kitchen would cost roughly $6,000.</span><span class="example"><span class="neutral">· </span>The man was roughly my own age.</span><span class="example"><span class="neutral">· </span>There were roughly 50 people there.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">or so</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>3 days/a minute/fifteen people etc or so</span><span class="def"> approximately 3 days, a minute etc, or perhaps even more</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The baby usually sleeps for an hour or so after breakfast.</span><span class="example"><span class="neutral">· </span>He suggested that I take a week or so off work.</span><span class="example"><span class="neutral">· </span>There must be thirty people or so in the class.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">odd</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal spoken</span><span class="thespropform"><span class="neutral">: </span>a hundred/forty/thirty etc odd</span><span class="def"> use this after numbers in tens, hundreds, or thousands</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How old do you think he is?'' "Oh, I don't know. Seventy odd.''</span><span class="example"><span class="neutral">· </span>It's been 30 odd years since I last saw him.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">give or take</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="thespropform"><span class="neutral">: </span>give or take a few miles/a couple of minutes/a pound etc</span><span class="def"> use this when saying approximately what a number or amount is, when it may be a few miles more or less, a few minutes more or less etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The village is about fifty miles north of here, give or take a few miles.</span><span class="example"><span class="neutral">· </span>He's said to be worth $26 million, give or take a few million.</span><span class="example"><span class="neutral">· </span>"How long will the meeting last?" "A couple of hours, give or take."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">at a guess</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> approximately, especially when you do not know the correct number or amount and are guessing what it is</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>At a guess, I'd say around 3000 people took part in the demonstration.</span><span class="example"><span class="neutral">· </span>It was a cold night. About two or three degrees at a guess.</span><span class="example"><span class="neutral">· </span>"How much will it cost?" "A hundred and twenty pounds, at a guess."</span></div></span></span><span class="section last"><span class="secheading">ways of saying that no one knows something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">who knows/who can say</span></span><div class="content"> <span class="example"><span class="neutral">· </span>Maybe the world will end tomorrow. Who can say?</span><span class="example"><span class="neutral">· </span>He might come back and say he still loves me, who knows? Who cares?</span><span class="thespropform">who knows/who can say what/where/why etc</span> <span class="example"><span class="neutral">· </span>Who knows whether Mimi ever made it to Paris.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">God knows/heaven knows</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you mean that it is impossible to know something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Where'd she go?" "God knows."</span><span class="thespropform">God knows/heaven knows what/who/why etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I've just missed my train, so God knows what time I'll get home now.</span><span class="example"><span class="neutral">· </span>Heaven knows why she feels she can't trust her own parents.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">it's anybody's guess</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when neither you, nor anyone else knows the answer to a question or the truth about something, and any answer could be correct</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>How he'd lived through it all is anybody's guess.</span><span class="thespropform">it's anybody's guess who/what/why etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's anybody's guess who will come out on top when the winners are announced at the Grammy Awards this year.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">there's no telling/knowing</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when it is impossible to know what will happen, especially when you are worried that something bad might happen</span><span class="thespropform"><span class="neutral">: </span>there's no telling/knowing who/what/why etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"He's a desperate man. There's no telling what he'll do next," said Holmes.</span><span class="example"><span class="neutral">· </span>There was no knowing when the flood waters might recede with so much rain in the forecast.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">your guess is as good as mine</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when someone asks you a question and you do not know the answer, so that they are just as likely to guess the right answer as you are</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Who do you think will win the World Cup?" "Your guess is as good as mine."</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="section"><span class="secheading">verbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">make a guess</span></span><div class="content"><span class="example"><span class="neutral">· </span>I didn't know the answer to question 7, so I just had to make a guess.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">have a guess</span></span><div class="content"><span class="geo"> British English</span><span class="variant"><span class="neutral">, </span><span class="lexvar">take a guess</span><span class="geo"> American English:</span></span><span class="example"><span class="neutral">· </span>Go on, have a guess at how much it cost.</span><span class="example"><span class="neutral">· </span>Take a guess. How many people do you think showed up?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">hazard a guess</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>guess something, when you feel very uncertain<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>No one at this stage is prepared to hazard a guess about the outcome of the elections.</span></div></span></span><span class="section"><span class="secheading">adjectives</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a rough guess</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one that is not exact<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>This is just a rough guess, but I think it would cost about $50.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a wild guess</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one made without much thought or information<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I made a wild guess and I got the answer right first time.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a lucky guess</span></span><div class="content"><span class="example"><span class="neutral">· </span>‘How did you know?’ ‘It was just a lucky guess.’</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a good guess</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one that is likely to be right<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I'm not sure how old she is, but I can make a good guess!</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">somebody’s best guess</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one that you think is most likely to be right<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>My best guess is that it will take around six months.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">an educated/informed guess</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>a guess based on things that you know are correct<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Stockbrokers try to make educated guesses as to which stocks will do well.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">an intelligent guess</span></span><div class="content"><span class="example"><span class="neutral">· </span>Analysis of the archaeological site will help us make an intelligent guess as to what it was used for.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">an inspired guess</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>a very good guess that you make suddenly<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>It’s hard to believe he got that right with just an inspired guess.</span></div></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">my guess is (that)</span></span><div class="content"><span class="example"><span class="neutral">· </span>My guess is there won’t be many people there.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">at a guess</span></span><div class="content"><span class="geo"> British English</span><span class="collgloss"><span class="neutral"> (=</span>used when saying that you are making a guess<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I'd say it was built around the turn of the century, at a guess.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">I’ll give you three guesses</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>used to tell someone that it should be easy for them to guess the answer to their question<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>‘Where is he?’ ‘I’ll give you three guesses.’</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">guess the answer</span></span><div class="content"><span class="example"><span class="neutral">· </span>If you don’t know the answer, try guessing it.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">make ... educated guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-001637614').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Investors must <span class="colloinexa">make</span> an <span class="colloinexa">educated guess</span> as to the company’s potential.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a good guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001722897').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’m not sure, but I could make <span class="colloinexa">a good guess</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">hazarding a guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001756565').play();"><img src="LDOCE6_img_spkr_g.png"/></a> $50,000? I don’t know. I’m only <span class="colloinexa">hazarding a guess</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">wild guess</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001740523').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’m just making a <span class="colloinexa">wild guess</span> here, so correct me if I’m wrong.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADJECTIVE</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">educated</span></span><div class="content"><span class="example"><span class="neutral">· </span>Are at least able to make an <span class="colloinexa">educated guess</span> as to who is collapsing the scrummage. 7.</span><span class="example"><span class="neutral">· </span>Other of the source studies, however, used patient values, clinician values, or <span class="colloinexa">educated guesses</span>.</span><span class="example"><span class="neutral">· </span>But, beyond hunches and <span class="colloinexa">educated guesses</span>, what about other human characteristics such as beliefs, prejudices and emotions?</span><span class="example"><span class="neutral">· </span>The law requires the chief of police to make <span class="colloinexa">educated guesses</span> about the likelihood that disorder, damage or disruption will occur.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">good</span></span><div class="content"><span class="example"><span class="neutral">· </span>April is the <span class="colloinexa">best guess</span> for first deliveries.</span><span class="example"><span class="neutral">· </span>About once a century is our <span class="colloinexa">best guess</span> of how often such a cosmic calamity must occur somewhere on Earth.</span><span class="example"><span class="neutral">· </span>Ellen made a <span class="colloinexa">best guess</span> as to how likely each one was to happen in the current situation.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">best guess</span> put her in her late twenties.</span><span class="example"><span class="neutral">· </span>A <span class="colloinexa">good guess</span>, but false none the less.</span><span class="example"><span class="neutral">· </span>Still, money managers, analysts, and economists are taking their <span class="colloinexa">best</span> educated <span class="colloinexa">guesses</span>.</span><span class="example"><span class="neutral">· </span>My <span class="colloinexa">best guess</span> is that the forests will not recover for a long time, no matter what we do.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">lucky</span></span><div class="content"><span class="example"><span class="neutral">· </span>It had been a <span class="colloinexa">lucky guess</span>, that was all.</span><span class="example"><span class="neutral">· </span>Maybe they made a <span class="colloinexa">lucky guess</span>.</span><span class="example"><span class="neutral">· </span>That had just been a <span class="colloinexa">lucky guess</span>.</span><span class="example"><span class="neutral">· </span>The reporters could not tell whether this was because Kalmbach was a <span class="colloinexa">lucky guess</span> or a ridiculous one.</span><span class="example"><span class="neutral">· </span>And a rather malicious <span class="colloinexa">lucky guess</span> to boot.</span><span class="example"><span class="neutral">· </span>It is a very addictive game of logic, deduction, and lots of <span class="colloinexa">lucky guesses</span> when you first play.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">rough</span></span><div class="content"><span class="example"><span class="neutral">· </span>At the side put down a <span class="colloinexa">rough guess</span> of the cost that might be involved.</span><span class="example"><span class="neutral">· </span>At a <span class="colloinexa">rough guess</span>, Pierremont Gardens today stands where the real gardens of Pierremont used to be.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">wild</span></span><div class="content"><span class="example"><span class="neutral">· </span>It had thrown her when Luke Calder had made that seemingly <span class="colloinexa">wild guess</span> about her, but now she could understand it.</span><span class="example"><span class="neutral">· </span>Shall we take a <span class="colloinexa">wild guess</span>?</span><span class="example"><span class="neutral">· </span>The glitter in his hooded eyes made it impossible for her to hazard even the <span class="colloinexa">wildest guess</span> at what he was thinking.</span><span class="example"><span class="neutral">· </span>That was Renato, still entertaining the class with <span class="colloinexa">wild guesses</span>.</span><span class="example"><span class="neutral">· </span>If any child makes <span class="colloinexa">wild</span> random <span class="colloinexa">guesses</span> note quietly who it is: those pupils will need your attention.</span><span class="example"><span class="neutral">· </span>Still, it was discouraging that no one had ventured even a <span class="colloinexa">wild guess</span>.</span></div></span></span><span class="section last"><span class="secheading">VERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">hazard</span></span><div class="content"><span class="example"><span class="neutral">· </span>Not being a builder, nor an architect, I can only <span class="colloinexa">hazard</span> a <span class="colloinexa">guess</span>.</span><span class="example"><span class="neutral">· </span>It would be foolish even to <span class="colloinexa">hazard</span> a <span class="colloinexa">guess</span>.</span><span class="example"><span class="neutral">· </span>The glitter in his hooded eyes made it impossible for her to <span class="colloinexa">hazard</span> even the wildest <span class="colloinexa">guess</span> at what he was thinking.</span><span class="example"><span class="neutral">· </span>We <span class="colloinexa">hazard</span> a <span class="colloinexa">guess</span> that they're lurking in a shoebox or, worse still, the ubiquitous carrier bag!</span><span class="example"><span class="neutral">· </span>What on earth the referee had done to incur such mindless, moronic abuse, one can only <span class="colloinexa">hazard</span> a <span class="colloinexa">guess</span>.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">hazard</span> a <span class="colloinexa">guess</span> that there are not only pure stallions on the moor; fate and carelessness always complicates herd life.</span><span class="example"><span class="neutral">· </span>It was hard to say, even <span class="colloinexa">hazard</span> a <span class="colloinexa">guess</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">make</span></span><div class="content"><span class="example"><span class="neutral">· </span>I <span class="colloinexa">guessed</span>, but did not <span class="colloinexa">make</span> him confirm my guess.</span><span class="example"><span class="neutral">· </span>Maybe they <span class="colloinexa">made</span> a lucky <span class="colloinexa">guess</span>.</span><span class="example"><span class="neutral">· </span>So let me begin by <span class="colloinexa">making</span> some impressionistic <span class="colloinexa">guesses</span> about the views women do in fact currently hold on morality.</span><span class="example"><span class="neutral">· </span>Ellen <span class="colloinexa">made</span> a best <span class="colloinexa">guess</span> as to how likely each one was to happen in the current situation.</span><span class="example"><span class="neutral">· </span>Nor is there much existing evidence, here or in other countries, on which to <span class="colloinexa">make</span> a reasonable <span class="colloinexa">guess</span>.</span><span class="example"><span class="neutral">· </span>That sometimes means <span class="colloinexa">making</span> educated <span class="colloinexa">guesses</span>.</span><span class="example"><span class="neutral">· </span>Why do I <span class="colloinexa">make</span> this bold <span class="colloinexa">guess</span>?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">take</span></span><div class="content"><span class="example"><span class="neutral">· </span>Shall we <span class="colloinexa">take</span> a wild <span class="colloinexa">guess</span>?</span><span class="example"><span class="neutral">· </span>Every time the father <span class="colloinexa">took</span> a wrong <span class="colloinexa">guess</span>, the youngest daughter laughed loudly.</span><span class="example"><span class="neutral">· </span>Still, money managers, analysts, and economists are <span class="colloinexa">taking</span> their best educated <span class="colloinexa">guesses</span>.</span><span class="example"><span class="neutral">· </span>Where does all that money come from and where does it go? <span class="colloinexa">Take</span> a <span class="colloinexa">guess</span>.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">be anybody’s guess</span></span><div class="content"><ul class="exas"><li>It's anybody's <span class="nodeword">guess</span> where he's disappeared to.</li><li>And how many days and weeks and years made up a duration was anybody's <span class="nodeword">guess</span>.</li><li>Disenfranchised voters are believed to number at least 100,000 - though why the lenders want their business is anybody's <span class="nodeword">guess</span>.</li><li>How often, when and where women-only units would see active service is anybody's <span class="nodeword">guess</span>.</li><li>It is anybody's <span class="nodeword">guess</span> as to how the sale will fare.</li><li>Strangely, what may happen at the taking of the sample itself seems to be anybody's <span class="nodeword">guess</span>.</li><li>What the Spice Girls' eight-year-old girl fans make of this is anybody's <span class="nodeword">guess</span>.</li><li>Whether the idea will catch on is anybody's <span class="nodeword">guess</span>.</li><li>Which came first is anybody's <span class="nodeword">guess</span>, but the two are now working together to make the patient even more ill.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">your guess is as good as mine</span></span><div class="content"><ul class="exas"><li>"When's the next bus coming?" "Your <span class="nodeword">guess</span> is as good as mine."</li><li>"Who do you think will win the World Cup?" "Your <span class="nodeword">guess</span> is as good as mine."</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">a conservative estimate/guess</span></span><div class="content"><ul class="exas"><li><span class="nodeword">Conservative</span> estimates indicate at least 150 people were killed in the military coup.</li><li>All the data published on the effect of gill-nets on small cetaceans probably represents a <span class="nodeword">conservative</span> estimate of the number of deaths.</li><li>At a <span class="nodeword">conservative</span> estimate the water was nearly a yard deep, even near the edge.</li><li>At a <span class="nodeword">conservative</span> estimate they had cost considerably more than she had just paid for her night's lodging.</li><li>By even a <span class="nodeword">conservative</span> estimate, about 10,000 Dall's porpoise were harpooned each year from 1976 to 1987.</li><li>I understand that is a <span class="nodeword">conservative</span> estimate.</li><li>Narcotics agents believe a <span class="nodeword">conservative</span> estimate of the number of laboratories is between 200 and 300.</li><li>On a <span class="nodeword">conservative</span> estimate, there are now about 5,000 books or articles that deal with it, at least in part.</li><li>On this basis imitation tasks ought to be regarded as giving a <span class="nodeword">conservative</span> estimate of the child's grammatical knowledge.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">educated guess</span></span><div class="content"><ul class="exas"><li>Are at least able to make an <span class="nodeword">educated</span> guess as to who is collapsing the scrummage. 7.</li><li>But, beyond hunches and <span class="nodeword">educated</span> guesses, what about other human characteristics such as beliefs, prejudices and emotions?</li><li>Other of the source studies, however, used patient values, clinician values, or <span class="nodeword">educated</span> guesses.</li><li>Still, money managers, analysts, and economists are taking their best <span class="nodeword">educated</span> guesses.</li><li>That sometimes means making <span class="nodeword">educated</span> guesses.</li><li>The law requires the chief of police to make <span class="nodeword">educated</span> guesses about the likelihood that disorder, damage or disruption will occur.</li><li>Where and what kinds they will be is an open question that is, at best, an <span class="nodeword">educated</span> guess.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">inspired guess/choice etc</span></span><div class="content"><ul class="exas"><li>As it happens, he made an <span class="nodeword">inspired</span> choice.</li><li>But my father's was an <span class="nodeword">inspired</span> guess.</li><li>If he can be persuaded to keep religion out of politics, he could prove an <span class="nodeword">inspired</span> choice.</li><li>In Whitham's case it was different and he certainly was what could be termed an <span class="nodeword">inspired</span> choice by the Lightweight club.</li><li>It could prove an <span class="nodeword">inspired</span> choice for New Zealand.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">I might have known/guessed etc</span></span><div class="content"><ul class="exas"><li>All those years I <span class="nodeword">might</span> have known her!</li><li>Although I <span class="nodeword">might</span> have known you'd arrive just as drinks were being ordered!</li><li>Dear little Papa, as I <span class="nodeword">might</span> have known!</li><li>If you'd had a big fat bottom I <span class="nodeword">might</span> have guessed.</li><li>It was nothing I <span class="nodeword">might</span> have guessed.</li><li>Of course, I <span class="nodeword">might</span> have known that you'd have some clever way of dealing with everything, though.</li><li>Ooh! I <span class="nodeword">might</span> have known it!</li><li>Some years before, I <span class="nodeword">might</span> have guessed Bond's enigmatic presence in the scene.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">no prizes for guessing something</span></span><div class="content"><ul class="exas"><li>Enclosed his picture - no <span class="nodeword">prizes</span> for guessing his breed.</li><li>There are no <span class="nodeword">prizes</span> for guessing why this should be.</li></ul></div></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_2_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">an attempt to answer a question or make a judgment when you are not sure whether you will be correct</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_f_ldoce5_exa000736"><a onclick="document.getElementById('LDOCE6_exa_bre_f_ldoce5_exa000736').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’d say she’s about 35, but that’s only a guess.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_2_s2"></a><span class="sense newline"><span class="sensenum">2</span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_2_s3"></a><span class="lexunit">be anybody’s guess</span><span class="def"> to be something that no one knows</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-001309857"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-001309857').play();"><img src="LDOCE6_img_spkr_g.png"/></a> What she’s going to do with her life now is anybody’s guess.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_2_s4"></a><span class="sense newline"><span class="sensenum">3</span><a id="6504937708254a64972bf4d4ccdfcc9d_guess_2_s5"></a><span class="lexunit">your guess is as good as mine</span><span class="registerlab"> spoken</span><span class="def"> used to tell someone that you do not know any more than they do about something</span></span><span class="tail"><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="section"><span class="secheading">verbs</span><span class="collocate"><span class="colloc collo">make a guess</span><span class="example"><span class="neutral">· </span>I didn't know the answer to question 7, so I just had to make a guess.</span></span><span class="collocate"><span class="colloc collo">have a guess</span><span class="geo"> British English</span><span class="variant"><span class="neutral">, </span><span class="lexvar">take a guess</span><span class="geo"> American English:</span></span><span class="example"><span class="neutral">· </span>Go on, have a guess at how much it cost.</span><span class="example"><span class="neutral">· </span>Take a guess. How many people do you think showed up?</span></span><span class="collocate"><span class="colloc collo">hazard a guess</span><span class="collgloss"><span class="neutral"> (=</span>guess something, when you feel very uncertain<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>No one at this stage is prepared to hazard a guess about the outcome of the elections.</span></span></span><span class="section"><span class="secheading">adjectives</span><span class="collocate"><span class="colloc collo">a rough guess</span><span class="collgloss"><span class="neutral"> (=</span>one that is not exact<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>This is just a rough guess, but I think it would cost about $50.</span></span><span class="collocate"><span class="colloc collo">a wild guess</span><span class="collgloss"><span class="neutral"> (=</span>one made without much thought or information<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I made a wild guess and I got the answer right first time.</span></span><span class="collocate"><span class="colloc collo">a lucky guess</span><span class="example"><span class="neutral">· </span>‘How did you know?’ ‘It was just a lucky guess.’</span></span><span class="collocate"><span class="colloc collo">a good guess</span><span class="collgloss"><span class="neutral"> (=</span>one that is likely to be right<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I'm not sure how old she is, but I can make a good guess!</span></span><span class="collocate"><span class="colloc collo">somebody’s best guess</span><span class="collgloss"><span class="neutral"> (=</span>one that you think is most likely to be right<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>My best guess is that it will take around six months.</span></span><span class="collocate"><span class="colloc collo">an educated/informed guess</span><span class="collgloss"><span class="neutral"> (=</span>a guess based on things that you know are correct<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Stockbrokers try to make educated guesses as to which stocks will do well.</span></span><span class="collocate"><span class="colloc collo">an intelligent guess</span><span class="example"><span class="neutral">· </span>Analysis of the archaeological site will help us make an intelligent guess as to what it was used for.</span></span><span class="collocate"><span class="colloc collo">an inspired guess</span><span class="collgloss"><span class="neutral"> (=</span>a very good guess that you make suddenly<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>It’s hard to believe he got that right with just an inspired guess.</span></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="colloc collo">my guess is (that)</span><span class="example"><span class="neutral">· </span>My guess is there won’t be many people there.</span></span><span class="collocate"><span class="colloc collo">at a guess</span><span class="geo"> British English</span><span class="collgloss"><span class="neutral"> (=</span>used when saying that you are making a guess<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I'd say it was built around the turn of the century, at a guess.</span></span><span class="collocate"><span class="colloc collo">I’ll give you three guesses</span><span class="collgloss"><span class="neutral"> (=</span>used to tell someone that it should be easy for them to guess the answer to their question<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>‘Where is he?’ ‘I’ll give you three guesses.’</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_2_guess_n0205" preload="none" src="LDOCE6_hwd_bre_2_guess_n0205.mp3"></audio><audio id="LDOCE6_hwd_ame_d_guess1" preload="none" src="LDOCE6_hwd_ame_d_guess1.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001702557" preload="none" src="LDOCE6_exa_bre_9_p008-001702557.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001702563" preload="none" src="LDOCE6_exa_bre_4_p008-001702563.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001702569" preload="none" src="LDOCE6_exa_bre_7_p008-001702569.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-001637614" preload="none" src="LDOCE6_exa_bre_a_p008-001637614.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001722897" preload="none" src="LDOCE6_exa_bre_f_p008-001722897.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001756565" preload="none" src="LDOCE6_exa_bre_f_p008-001756565.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001740523" preload="none" src="LDOCE6_exa_bre_8_p008-001740523.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001309289" preload="none" src="LDOCE6_exa_bre_7_p008-001309289.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001702560" preload="none" src="LDOCE6_exa_bre_7_p008-001702560.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001309295" preload="none" src="LDOCE6_exa_bre_7_p008-001309295.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001309306" preload="none" src="LDOCE6_exa_bre_c_p008-001309306.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001309303" preload="none" src="LDOCE6_exa_bre_c_p008-001309303.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001702578" preload="none" src="LDOCE6_exa_bre_c_p008-001702578.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-001702579" preload="none" src="LDOCE6_exa_bre_b_p008-001702579.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001309320" preload="none" src="LDOCE6_exa_bre_9_p008-001309320.mp3"></audio><audio id="LDOCE6_exa_bre_d_p008-001702583" preload="none" src="LDOCE6_exa_bre_d_p008-001702583.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001702590" preload="none" src="LDOCE6_exa_bre_8_p008-001702590.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001702591" preload="none" src="LDOCE6_exa_bre_9_p008-001702591.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001309333" preload="none" src="LDOCE6_exa_bre_2_p008-001309333.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001309334" preload="none" src="LDOCE6_exa_bre_2_p008-001309334.mp3"></audio><audio id="LDOCE6_exa_bre_f_ldoce5_exa000736" preload="none" src="LDOCE6_exa_bre_f_ldoce5_exa000736.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-001309857" preload="none" src="LDOCE6_exa_bre_6_p008-001309857.mp3"></audio>`;
