var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_listen_1"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_listen_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_listen_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1"></a><span class="entry"><span class="entryhead"><span class="hwd">listen</span><span class="hyphenation frequent">lis<span class="hs0"></span>ten</span><span class="homnum frequent">1</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">ˈlɪs<i>ə</i>n</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S1</span> <span class="freq">W1</span><span class="pos"> verb</span><span class="gram"> [intransitive]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_3_listen_v0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_e_listen').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">listen</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"> <span class="etymlang">Old English</span> <span class="etymorigin">hlysnan</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">listen</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">listen</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">listens</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">listened</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">listened</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">listened</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">listened</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">listen</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">listened</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">listening</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">listening</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">listening</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">listening</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">listening</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">listening</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">listening</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">listening</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">listening</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">listening</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li><span class="nodeword">Listen</span>! There's someone coming upstairs!</li><li><span class="nodeword">Listen</span>, don't panic. I'm sure we can get your money back.</li><li><span class="nodeword">Listen</span>, I've just had a really good idea.</li><li>He <span class="nodeword">listened</span> carefully to every word I said.</li><li>I could <span class="nodeword">listen</span> to Placido Domingo all day -- he has such a beautiful voice!</li><li>I didn't hear the answer, because I wasn't <span class="nodeword">listening</span> when she read it out.</li><li>If you <span class="nodeword">listen</span> hard, you can hear the sound of the sea in the distance.</li><li>If you <span class="nodeword">listened</span> in class you might get better grades.</li><li>Mrs Singh leaned forward, <span class="nodeword">listening</span> intently as they explained the procedure to her.</li><li>The company has begun to <span class="nodeword">listen</span> more carefully to its customers, in a determined attempt to improve customer service.</li><li>You had better be <span class="nodeword">listening</span> because I'm not going to repeat myself.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>He <span class="nodeword">listened</span> to relaxation tapes and practiced breathing exercises to stop hyperventilating.</li><li>I also <span class="nodeword">listen</span> to Oscar Peterson.</li><li>I could hear foreign voices raised in the kitchen and stopped <span class="nodeword">listening</span> to my companions.</li><li>Jezebel ... Don't <span class="nodeword">listen</span> to a word they say.</li><li>One of Maya Angelou's own pleasures is <span class="nodeword">listening</span> to great voices.</li><li>Take the time to sit back and <span class="nodeword">listen</span> and establish a routine for yourself.</li><li>They had to sit <span class="nodeword">listening</span> to talk about people they had not met, only heard of.</li><li>Why should the adventurers stop and <span class="nodeword">listen</span> to any of this?</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">listen</span></span><div class="content"><span class="def"> to pay attention to what someone is saying or to a sound that you hear</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I didn’t hear the answer, because I wasn’t listening when she read it out.</span><span class="example"><span class="neutral">· </span>He listened carefully to every word I said.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pay attention</span></span><div class="content"><span class="def"> to listen carefully to what someone is saying</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I nodded to show I was paying attention.</span><span class="example"><span class="neutral">· </span>She was tired and wasn’t paying attention to what he was saying.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">eavesdrop</span></span><div class="content"><span class="def"> to secretly listen to someone else’s conversation by standing near them, hiding behind a door etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I caught him eavesdropping on our conversation.</span><span class="example"><span class="neutral">· </span>They spoke in quiet voices which made it hard to eavesdrop.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">overhear</span></span><div class="content"><span class="def"> to hear someone say something, especially accidentally</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I overheard him say something about wanting to move house.</span><span class="example"><span class="neutral">· </span>Excuse me! I couldn’t help overhearing that you were planning a trip to Thailand.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tune in (to something)</span></span><div class="content"><span class="def"> to listen to a radio programme, or to someone using a radio <span class="nondv"><span class="refhwd">transmitter</span></span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Over a million people tune in to the programme each week.</span><span class="example"><span class="neutral">· </span>Tune in at the same time next week for the next episode.</span><span class="example"><span class="neutral">· </span>The equipment could be used by criminals to tune in to police broadcasts.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tap</span></span><div class="content"><span class="def"> to connect a piece of electronic recording equipment to a telephone system so that you can listen to people’s telephone conversations</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The police had tapped the phones of all three suspects.</span><span class="example"><span class="neutral">· </span>The president had to resign over an illegal phone-tapping operation.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">bug</span></span><div class="content"><span class="def"> to hide a small piece of electronic recording equipment in someone’s room, car, office etc in order to listen secretly to what is said there</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Security agents bugged their offices and managed to get some evidence against them.</span><span class="example"><span class="neutral">· </span>Wells was convinced the house was bugged and insisted on playing loud music while we talked.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to listen to someone or something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">listen</span></span><div class="content"><span class="def"> to pay attention to what someone is saying or to a sound that you hear</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I didn't hear the answer, because I wasn't listening when she read it out.</span><span class="example"><span class="neutral">· </span>If you listened in class you might get better grades.</span><span class="thespropform">listen to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He listened carefully to every word I said.</span><span class="example"><span class="neutral">· </span>I could listen to Placido Domingo all day -- he has such a beautiful voice!</span><span class="thespropform">listen hard</span><span class="gloss"><span class="neutral"> (=</span>try hard to hear something that is very quiet<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If you listen hard, you can hear the sound of the sea in the distance.</span><span class="thespropform">listen carefully</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The company has begun to listen more carefully to its customers, in a determined attempt to improve customer service.</span><span class="thespropform">listen intently</span><span class="gloss"><span class="neutral"> (=</span>listen very carefully to hear what someone is saying<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mrs Singh leaned forward, listening intently as they explained the procedure to her.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pay attention</span></span><div class="content"><span class="def"> to listen carefully to what someone is saying</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I have some important information about travel arrangements, so please pay attention.</span><span class="example"><span class="neutral">· </span>Billy's a smart kid, but he doesn't pay attention in class.</span><span class="thespropform">pay attention to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She went on talking, but I wasn't really paying attention to what she was saying.</span><span class="example"><span class="neutral">· </span>According to the survey, two-thirds of young people do not follow politics and pay no attention to election campaigns.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">listen for/listen out for</span></span><div class="content"><span class="def"> to listen carefully for a sound that you are expecting to hear, although you do not know when it will happen</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We hid behind the door, listening for the sound of the guard's footsteps.</span><span class="example"><span class="neutral">· </span>The flight had been delayed, and so I waited in the departure lounge listening for announcements.</span><span class="example"><span class="neutral">· </span>She lay in bed, listening out for the sound of his feet on the stairs.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">hear somebody out</span></span><div class="content"><span class="def"> to listen to all of what someone wants to tell you without interrupting them, especially when you disagree with them or do not believe them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Hear me out first, Jane, and then you can say what you think. </span><span class="example"><span class="neutral">· </span>Knapp heard him out patiently but still refused to change his mind.</span></div></span></span><span class="section"><span class="secheading">to secretly listen to someone</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">listen in</span></span><div class="content"><span class="def"> to listen to someone else's conversation when they do not know that you are listening, either on the telephone or when you are near them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Whenever her boss had one of his "private meetings", she always used to listen in.</span><span class="thespropform">listen in on</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We tried to listen in on their conversation, but they were talking too quietly.</span><span class="example"><span class="neutral">· </span>They used to have hours of fun listening in on what people were doing in their hotel rooms.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">eavesdrop</span></span><div class="content"><span class="def"> to secretly listen to someone else's conversation by standing near them, hiding behind a door etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>How did you know I was going? You've been eavesdropping, haven't you!</span><span class="thespropform">eavesdrop on</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I caught him eavesdropping on our conversation.</span><span class="example"><span class="neutral">· </span>Sue was able to eavesdrop on them through the open window.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">bug</span></span><div class="content"><span class="def"> to hide a small piece of electronic recording equipment in someone's room, car, office etc in order to listen secretly to what is said there</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Security agents bugged their offices and managed to get some evidence against them.</span><span class="example"><span class="neutral">· </span>Wells was convinced the house was bugged and insisted on playing loud music while we talked.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tap</span></span><div class="content"><span class="def"> to connect a piece of electronic recording equipment to a telephone system so that you can listen to people's telephone conversations</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Later we realized our phones had been tapped and the police knew everything.</span><span class="example"><span class="neutral">· </span>The President had to resign over an illegal phone-tapping operation.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">monitor</span></span><div class="content"><span class="def"> to listen to another country's radio or television broadcasts or radio messages in order to get information about that country</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Satellite technology means that enemy airwaves can be monitored more closely than ever before.</span></div></span></span><span class="section"><span class="secheading">what you say when telling someone to listen</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">listen</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you want someone to listen and pay attention to what you are saying</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Listen! There's someone coming upstairs!</span><span class="example"><span class="neutral">· </span>Listen, I've just had a really good idea.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">listen up</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you want someone, especially a group of people, to pay close attention to what you are saying, especially when you are giving them instructions</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Okay, class. Listen up. Open your books at page 33.</span><span class="example"><span class="neutral">· </span>The directions are complicated so listen up.</span></div></span></span><span class="section"><span class="secheading">to listen to the radio</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">listen to</span></span><div class="content"><span class="def"> to listen to a radio programme or a particular radio station</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Lucy sat in her car, listening to the radio.</span><span class="example"><span class="neutral">· </span>I always listen to the news while I have my breakfast.</span><span class="example"><span class="neutral">· </span>What station are you listening to?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tune in</span></span><div class="content"><span class="def"> to turn on your radio and listen to a particular programme, especially if you do this at the same time each week</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Be sure to tune in next week at the same time for another exciting episode of "Death Ray".</span><span class="thespropform">tune in to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Tune in to the Breakfast Show tomorrow to win VIP tickets to the Lollapalooza festival on Long Island. </span></div></span></span><span class="section"><span class="secheading">someone who listens</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">listener</span></span><div class="content"><span class="def"> someone who is listening to a speech, piece of music etc, or who regularly listens to a particular radio programme or station</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He paused momentarily to check that his listeners had fully appreciated the humour of his remark.</span><span class="example"><span class="neutral">· </span>The programme already has more than two million listeners across the country.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">audience</span></span><div class="content"><span class="def"> a group of people who watch and listen to someone speaking or performing in public, or who listen to a radio or television programme or station</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The second comedian really made the audience laugh.</span><span class="example"><span class="neutral">· </span>The audience consisted mainly of young girls under sixteen.</span><span class="example"><span class="neutral">· </span>WMLD's audience is mainly young and black.</span><span class="example"><span class="neutral">· </span>These two programs are both news and current affairs, but they cater for very different audiences.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be a good listener</span></span><div class="content"><span class="def"> if someone is a good listener , they always listen carefully and sympathetically when someone else is talking</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Cara's a really good listener, so she always has someone telling her their problems.</span></div></span></span><span class="section"><span class="secheading">determined in a way that is annoying or silly</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">stubborn</span></span><div class="content"><span class="def"> someone who is stubborn refuses to change their mind about something, even when people think they are wrong or are being unreasonable</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I told him it was a bad idea, but Dave's so stubborn that he just never listens.</span><span class="example"><span class="neutral">· </span>a stubborn old man</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">obstinate</span></span><div class="content"><span class="def"> someone who is obstinate always does what they want and refuses to change their mind, even when this is annoying and unreasonable</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>How do you deal with an obstinate teenager who always says she isn't hungry?</span><span class="example"><span class="neutral">· </span>You know I'm right really. You're just being obstinate.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pig-headed</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> use this about someone who refuses to change their mind when you think that what they want to do is stupid</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He really was the most pig-headed man I've ever had the misfortune to meet.</span><span class="example"><span class="neutral">· </span>Don't be so pig-headed! You can't possibly drive home after the amount you've had to drink.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">headstrong</span></span><div class="content"><span class="def"> someone who is headstrong is very independent and wants to do things in the way that they want, without listening to other people's advice or thinking about the results of their actions</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Leo's parents soon found that they were completely unable to control their headstrong son.</span><span class="example"><span class="neutral">· </span>Suzie was headstrong, and sometimes thoughtless of other people's feelings.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">wilful</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British</span><span class="variant"> <span class="lexvar">/willful</span><span class="neutral"> </span><span class="registerlab">American</span></span><span class="def"> someone who is wilful , especially a child or young person, deliberately behaves badly by continuing to do what they want to do, even after they have been told to stop</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Billy is a very wilful little boy who's constantly being punished for not doing as he's told.</span><span class="example"><span class="neutral">· </span>Sometimes kids who are described as difficult or wilful just need a little extra love and attention.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not listen</span></span><div class="content"><span class="def"> if you say that someone will not listen , you mean that they refuse to accept other people's helpful advice or opinions</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I've told him again and again what I think, but he won't listen.</span><span class="thespropform">not listen to reason</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Wait until she calms down. She's far too upset at the moment to listen to reason.</span></div></span></span><span class="section last"><span class="secheading">to not pay attention to what someone says or does</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">ignore</span></span><div class="content"><span class="def"> to deliberately pay no attention to what someone does or says, and pretend it is not important or does not affect you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She ignored my question and continued her story.</span><span class="example"><span class="neutral">· </span>Someone made a rude noise, which the teacher decided to ignore.</span><span class="example"><span class="neutral">· </span>My father's always telling me what to do, but I usually just ignore him.</span><span class="thespropform">ignore somebody's advice/warning</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was very stupid of you to ignore your mother's advice.</span><span class="example"><span class="neutral">· </span>Ignoring my warnings, he dived straight into the shallow water.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pay no attention/not pay any attention</span></span><div class="content"><span class="def"> to ignore someone or something, especially by not watching or listening to them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"What's this injection for?'' he asked the nurse. She paid no attention, rolling up his sleeve in silence.</span><span class="example"><span class="neutral">· </span>People living near the crash scene paid no attention when they heard the aircraft's engine cut out in mid-air. </span><span class="thespropform">pay no attention/not pay any attention to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>More than half the people questioned said they did not pay any attention to political broadcasts.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">take no notice/not take any notice</span></span><div class="content"><span class="def"> not to let someone affect what you do or the way you feel</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Stan has fired me before. I usually take no notice and turn up for work again the next day.</span><span class="thespropform">take no notice/not take any notice of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Take no notice of him. He's just being silly.</span><span class="example"><span class="neutral">· </span>He never took any notice of the baby, even when it screamed.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not listen to</span></span><div class="content"><span class="def"> to ignore someone's advice when they talk to you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You never listen to me. You just do whatever you want.</span><span class="example"><span class="neutral">· </span>It's because you didn't listen to Roger that you are in this trouble now.</span><span class="thespropform">not listen to reason</span><span class="gloss"><span class="neutral"> (=</span>not listen to sensible advice<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We all warned Susan not to marry that man, but she wouldn't listen to reason.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fall on deaf ears</span></span><div class="content"><span class="def"> if your request or suggestion falls on deaf ears , it is ignored by the people who have the power to deal with it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The workers' demand for a wage increase has fallen on deaf ears.</span><span class="example"><span class="neutral">· </span>As rioting continued, Mayor Warren appealed for calm, but his words fell on deaf ears.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not want to know</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> to not be willing to listen to people's problems, complaints, bad news etc because you do not want to be worried by them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You'd think the government would be concerned about people sleeping rough, but they just don't want to know.</span><span class="thespropform">not want to know about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If you're going to start an argument with Alex, I don't want to know about it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tune out</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially American</span><span class="def"> to deliberately not listen to what someone is saying, especially because it is unpleasant or you have heard it before</span><span class="thespropform"><span class="neutral">: </span>tune out something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He had learned to tune out the kids' constant questions.</span><span class="thespropform">tune something out</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There was a warning voice in the back of her mind, but she tuned it out.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen carefully/intently/hard etc</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001742329').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The whole class was listening attentively.</span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001742330').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Liz stood still and listened hard <span class="collgloss"><span class="neutral"> (=</span>very carefully<span class="neutral">)</span></span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to reason</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001742335').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She refused to <span class="colloinexa">listen to reason</span><span class="collgloss"><span class="neutral"> (=</span>accept sensible advice<span class="neutral">)</span></span>.</span></div></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to somebody’s advice</span></span><div class="content"><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> heed somebody’s advice</span><span class="registerlab"> formal</span><span class="neutral">)</span></span><span class="collgloss"><span class="neutral"> (=</span>pay attention to someone’s advice<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I wish I had listened to her advice.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">look/listen/think etc carefully</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001666344').play();"><img src="LDOCE6_img_spkr_g.png"/></a> You need to think very carefully about which course you want to do.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to a CD</span></span><div class="content"><span class="example"><span class="neutral">· </span>He's upstairs listening to his CDs.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to a commentary</span></span><div class="content"><span class="example"><span class="neutral">· </span>I was listening to the football commentary on the radio.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to gossip</span></span><div class="content"><span class="example"><span class="neutral">· </span>He was always willing to listen to gossip.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to a lecture</span></span><div class="content"><span class="example"><span class="neutral">· </span>Most students spend about a quarter of their time listening to lectures.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to music</span></span><div class="content"><span class="example"><span class="neutral">· </span>Ella was listening to music on her iPod.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to a programme</span></span><div class="content"><span class="example"><span class="neutral">· </span>A lot of people listen to that programme on the way to work.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to the radio</span></span><div class="content"><span class="example"><span class="neutral">· </span>She was sitting up in bed, listening to the radio.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a spelling/reading/listening test</span></span><div class="content"><span class="example"><span class="neutral">· </span>I didn’t do very well in the listening test.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">attentively</span></span><div class="content"><span class="example"><span class="neutral">· </span><span class="colloinexa">Listen attentively</span> and quietly to what your teenager thinks.</span><span class="example"><span class="neutral">· </span>He talked with them about everything-politics, religion, music-and <span class="colloinexa">listened attentively</span> when they spoke.</span><span class="example"><span class="neutral">· </span>The accused, Eddie Gilfoyle, 32, <span class="colloinexa">listened attentively</span> to the lengthy discussions.</span><span class="example"><span class="neutral">· </span>He moved quickly, and the students <span class="colloinexa">listened attentively</span>.</span><span class="example"><span class="neutral">· </span>Laura <span class="colloinexa">listened attentively</span> while Yoyo read the speech out loud, and in the end, her eyes were glistening too.</span><span class="example"><span class="neutral">· </span>The resource person <span class="colloinexa">listens attentively</span> and reflects back what is said, in the L1, in a non-critical way.</span><span class="example"><span class="neutral">· </span>I sat up and <span class="colloinexa">listened attentively</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">carefully</span></span><div class="content"><span class="example"><span class="neutral">· </span>Budgeting your time Before the exam begins, <span class="colloinexa">listen carefully</span> to the instructions given by the invigilator.</span><span class="example"><span class="neutral">· </span>They were frank discussions and he <span class="colloinexa">listened carefully</span>.</span><span class="example"><span class="neutral">· </span>Begin by <span class="colloinexa">listening carefully</span> to how the bereaved speak about their loss.</span><span class="example"><span class="neutral">· </span>So <span class="colloinexa">listen carefully</span> and take it to your hearts.</span><span class="example"><span class="neutral">· </span>She was standing slightly curled, <span class="colloinexa">listening carefully</span> to some one.</span><span class="example"><span class="neutral">· </span>Gingrich <span class="colloinexa">listened carefully</span> to the Tuesday Lunch Bunch, and sometimes came along to their meetings.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">intently</span></span><div class="content"><span class="example"><span class="neutral">· </span>Johnny lifted his head, <span class="colloinexa">listening intently</span>.</span><span class="example"><span class="neutral">· </span>Peter <span class="colloinexa">listened intently</span> as Manion gave him a detailed account of the conversation.</span><span class="example"><span class="neutral">· </span>Pressing his ear to the edge of the door, he <span class="colloinexa">listened intently</span>, while a full three minutes ticked past.</span><span class="example"><span class="neutral">· </span>The president <span class="colloinexa">listened intently</span> and asked many questions.</span><span class="example"><span class="neutral">· </span>She smiled little and <span class="colloinexa">listened intently</span> whenever the man, sober-suited and authoritative, spoke.</span><span class="example"><span class="neutral">· </span>They held their breath and <span class="colloinexa">listened intently</span>.</span><span class="example"><span class="neutral">· </span>The dead were <span class="colloinexa">listening intently</span>, enjoying the psychodrama.</span><span class="example"><span class="neutral">· </span>Miss Pinkerton, the math teacher; writes problems and equations on the board, and I <span class="colloinexa">listen intently</span> to her explanations.</span></div></span></span><span class="section"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">conversation</span></span><div class="content"><span class="example"><span class="neutral">· </span>Four Galks stood <span class="colloinexa">listening</span> to the <span class="colloinexa">conversation</span>.</span><span class="example"><span class="neutral">· </span>I was certain that all the writers were <span class="colloinexa">listening</span> to my <span class="colloinexa">conversation</span> and were amused by it.</span><span class="example"><span class="neutral">· </span>Molassi had <span class="colloinexa">listened</span> to the <span class="colloinexa">conversation</span> between the clever boy and the pretty lady.</span><span class="example"><span class="neutral">· </span>Day after day, skilled linguists don headsets and <span class="colloinexa">listen</span> to the stolen <span class="colloinexa">conversations</span> of foreign leaders in more than 100 languages.</span><span class="example"><span class="neutral">· </span>I can also <span class="colloinexa">listen</span> to a <span class="colloinexa">conversation</span> between mining engineers and understand ninety-five percent of it, even after all these years.</span><span class="example"><span class="neutral">· </span>I melted into the wall and <span class="colloinexa">listened</span> to snatches of <span class="colloinexa">conversation</span>.</span><span class="example"><span class="neutral">· </span>Also, <span class="colloinexa">listening</span> to people's <span class="colloinexa">conversations</span> can be very enlightening and suggestive of ideas.</span><span class="example"><span class="neutral">· </span>I was <span class="colloinexa">listening</span> to a <span class="colloinexa">conversation</span> that had been going on for generations.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">music</span></span><div class="content"><span class="example"><span class="neutral">· </span>The truth of the matter is that it depends on the person and not the <span class="colloinexa">music</span> we <span class="colloinexa">listen</span> to.</span><span class="example"><span class="neutral">· </span>Q: What kind of <span class="colloinexa">music</span> do you <span class="colloinexa">listen</span> to?</span><span class="example"><span class="neutral">· </span>Morris says, before noting that neither he nor the rest of the band is exclusive when it comes to <span class="colloinexa">music listening</span>.</span><span class="example"><span class="neutral">· </span>Curve is interesting <span class="colloinexa">music</span> to <span class="colloinexa">listen</span> to even if you don't really like it.</span><span class="example"><span class="neutral">· </span>There is <span class="colloinexa">music</span> that is heard, and there is music that must be <span class="colloinexa">listened</span> to.</span><span class="example"><span class="neutral">· </span>The talk turns to <span class="colloinexa">music</span> and Kiefer <span class="colloinexa">listens</span>.</span><span class="example"><span class="neutral">· </span>If the <span class="colloinexa">music</span> leads, you <span class="colloinexa">listen</span> and sing with it.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">news</span></span><div class="content"><span class="example"><span class="neutral">· </span>Countless thousands all over the globe <span class="colloinexa">listen</span> to the hourly <span class="colloinexa">news</span> broadcasts with interest, respect and admiration.</span><span class="example"><span class="neutral">· </span>He has called them here to <span class="colloinexa">listen</span> to his <span class="colloinexa">news</span>.</span><span class="example"><span class="neutral">· </span>She <span class="colloinexa">listened</span> to the bad <span class="colloinexa">news</span> without any noticeable reaction.</span><span class="example"><span class="neutral">· </span>I never read the papers very much, or <span class="colloinexa">listened</span> to the <span class="colloinexa">news</span>.</span><span class="example"><span class="neutral">· </span>Frank had no real interest in the radio, beyond sometimes <span class="colloinexa">listening</span> to the sports <span class="colloinexa">news</span>.</span><span class="example"><span class="neutral">· </span>At regular intervals, she <span class="colloinexa">listened</span> to the <span class="colloinexa">news</span> on the radio.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">radio</span></span><div class="content"><span class="example"><span class="neutral">· </span>The age of people carrying <span class="colloinexa">radios</span> around or <span class="colloinexa">listening</span> in cars was undreamt of.</span><span class="example"><span class="neutral">· </span>We sat behind a berm and took off our <span class="colloinexa">radios</span> while still <span class="colloinexa">listening</span> to them.</span><span class="example"><span class="neutral">· </span>The idea behind radar was to send out <span class="colloinexa">radio</span> waves and <span class="colloinexa">listen</span> for echoes from enemy craft.</span><span class="example"><span class="neutral">· </span>Turned on the <span class="colloinexa">radio</span> to <span class="colloinexa">listen</span> to a play, but it was stupid, so went to bed.</span><span class="example"><span class="neutral">· </span>Frank had no real interest in the <span class="colloinexa">radio</span>, beyond sometimes <span class="colloinexa">listening</span> to the sports news.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">record</span></span><div class="content"><span class="example"><span class="neutral">· </span>But one <span class="colloinexa">listens</span> to her final <span class="colloinexa">records</span> with dismay after hearing the fresh, uncanny beauty of the early ones.</span><span class="example"><span class="neutral">· </span>The two teenagers had only been <span class="colloinexa">listening</span> to <span class="colloinexa">records</span>, but the dad was pissed, none the less.</span><span class="example"><span class="neutral">· </span>I spent a lot of time <span class="colloinexa">listening</span> to <span class="colloinexa">records</span> and just hanging out with friends.</span><span class="example"><span class="neutral">· </span>They had a light supper, played backgammon for dimes, sat <span class="colloinexa">listening</span> to <span class="colloinexa">records</span> in the living room.</span><span class="example"><span class="neutral">· </span>He read some of Rosa's books, and <span class="colloinexa">listened</span> to her <span class="colloinexa">records</span>.</span><span class="example"><span class="neutral">· </span>So I usually sipped spicy tea and <span class="colloinexa">listened</span> to <span class="colloinexa">records</span> all night.</span><span class="example"><span class="neutral">· </span>When you <span class="colloinexa">listen</span> to their <span class="colloinexa">records</span> they're always a lot slower than you perceive them to be.</span><span class="example"><span class="neutral">· </span>I recommend that all my students <span class="colloinexa">listen</span> to <span class="colloinexa">records</span> at least two or three hours a day.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">sound</span></span><div class="content"><span class="example"><span class="neutral">· </span>As she washed, she <span class="colloinexa">listened</span> tensely for the <span class="colloinexa">sounds</span> of movement.</span><span class="example"><span class="neutral">· </span>We would lie in bed together, <span class="colloinexa">listening</span> to the <span class="colloinexa">sounds</span> of Paris outside.</span><span class="example"><span class="neutral">· </span>Here ... Valerie <span class="colloinexa">listened</span> for suspicious <span class="colloinexa">sounds</span> on the tape, and despised herself for so doing.</span><span class="example"><span class="neutral">· </span>He <span class="colloinexa">listened</span> to the muffled <span class="colloinexa">sound</span> waver and fall in pitch, like a faraway siren.</span><span class="example"><span class="neutral">· </span>Claudia lying in her hut <span class="colloinexa">listening</span> for the first <span class="colloinexa">sounds</span> of life.</span><span class="example"><span class="neutral">· </span>But he was tired so he just lay there, <span class="colloinexa">listening</span> to the street <span class="colloinexa">sounds</span>, and waited for morning.</span><span class="example"><span class="neutral">· </span>In the complete and oppressive silence of his tomb, John <span class="colloinexa">listened</span> for the <span class="colloinexa">sound</span> of rescuers.</span><span class="example"><span class="neutral">· </span>We are hear to <span class="colloinexa">listen</span> to the <span class="colloinexa">sounds</span> of a didgeridoo.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">story</span></span><div class="content"><span class="example"><span class="neutral">· </span>Pupils at Key Stage 2 will also benefit from <span class="colloinexa">listening</span> to <span class="colloinexa">stories</span>.</span><span class="example"><span class="neutral">· </span>But Paul <span class="colloinexa">listened</span> quietly to her <span class="colloinexa">story</span> and told her that he still wanted to marry her.</span><span class="example"><span class="neutral">· </span>She <span class="colloinexa">listened</span> to the <span class="colloinexa">stories</span> of small triumphs brought back from the dances.</span><span class="example"><span class="neutral">· </span>Toos and Alvin Sharpes arrived and sat on the stairs to <span class="colloinexa">listen</span> to the <span class="colloinexa">story</span> and stare at the storyteller.</span><span class="example"><span class="neutral">· </span>Thank you also for <span class="colloinexa">listening</span> to my <span class="colloinexa">story</span>.</span><span class="example"><span class="neutral">· </span>They <span class="colloinexa">listen</span> to <span class="colloinexa">stories</span>, memorize nursery rhymes, look at picture books and gain other experiences that prepare them to read.</span><span class="example"><span class="neutral">· </span>Trent had <span class="colloinexa">listened</span> to <span class="colloinexa">stories</span> of the 1961 hurricane.</span><span class="example"><span class="neutral">· </span>Opening their hearts to Jane, the brother finds her work in the village school and the sisters <span class="colloinexa">listen</span> to her <span class="colloinexa">story</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">voice</span></span><div class="content"><span class="example"><span class="neutral">· </span>Both of them <span class="colloinexa">listened</span> to the raised <span class="colloinexa">voices</span>.</span><span class="example"><span class="neutral">· </span>They had been <span class="colloinexa">listening</span> to so many other <span class="colloinexa">voices</span>, why would they not listen to the voices of their sons?</span><span class="example"><span class="neutral">· </span>One of Maya Angelou's own pleasures is <span class="colloinexa">listening</span> to great <span class="colloinexa">voices</span>.</span><span class="example"><span class="neutral">· </span>Emerson talks about <span class="colloinexa">listening</span> to that inner <span class="colloinexa">voice</span> and going with it, all voices to the contrary.</span><span class="example"><span class="neutral">· </span>But isn't it a problem that governments tend to <span class="colloinexa">listen</span> to the <span class="colloinexa">voices</span> of business, rather than civil society?</span><span class="example"><span class="neutral">· </span>Do not <span class="colloinexa">listen</span> to those <span class="colloinexa">voices</span> inside you who want to limit your potential.</span><span class="example"><span class="neutral">· </span>They have said that they need to <span class="colloinexa">listen</span> to the <span class="colloinexa">voices</span> of women and they are ready to do so.</span><span class="example"><span class="neutral">· </span>Now though, <span class="colloinexa">listening</span> only to a <span class="colloinexa">voice</span>, Rainville did believe her.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">word</span></span><div class="content"><span class="example"><span class="neutral">· </span>She wasn't <span class="colloinexa">listening</span> to a <span class="colloinexa">word</span> he said.</span><span class="example"><span class="neutral">· </span>Jezebel ... Don't <span class="colloinexa">listen</span> to a <span class="colloinexa">word</span> they say.</span><span class="example"><span class="neutral">· </span>In fact, Barnabas opened his eyes and <span class="colloinexa">listened</span> intently to every <span class="colloinexa">word</span>.</span><span class="example"><span class="neutral">· </span>After that she stood in a hazy dream, <span class="colloinexa">listening</span> to the <span class="colloinexa">words</span> that made them husband and wife.</span><span class="example"><span class="neutral">· </span>I pause on the steps to <span class="colloinexa">listen</span> to the <span class="colloinexa">words</span>.</span><span class="example"><span class="neutral">· </span>This can help them overcome their tendency to <span class="colloinexa">listen</span> for the <span class="colloinexa">words</span> rather than the message.</span><span class="example"><span class="neutral">· </span>Evidently he was <span class="colloinexa">listening</span> to every <span class="colloinexa">word</span> we said.</span></div></span></span><span class="section last"><span class="secheading">VERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">lie</span></span><div class="content"><span class="example"><span class="neutral">· </span>In the dead of night I woke up for some reason, and <span class="colloinexa">lay listening</span> to the others as they slept.</span><span class="example"><span class="neutral">· </span>I would <span class="colloinexa">lie</span> in bed, <span class="colloinexa">listening</span> to my parents whisper about them.</span><span class="example"><span class="neutral">· </span>She <span class="colloinexa">lay listening</span> to these signs of a weather change and wondered whether Tristram had decided not to come after all.</span><span class="example"><span class="neutral">· </span>But he was tired so he just <span class="colloinexa">lay</span> there, <span class="colloinexa">listening</span> to the street sounds, and waited for morning.</span><span class="example"><span class="neutral">· </span>Later, as they <span class="colloinexa">lay</span> in bed <span class="colloinexa">listening</span> to the rain, John wondered how their new life together would ever work.</span><span class="example"><span class="neutral">· </span>But after we got into bed, and after Frank had fallen asleep, I <span class="colloinexa">lay</span> awake, <span class="colloinexa">listening</span> to him breathe.</span><span class="example"><span class="neutral">· </span>Claudia <span class="colloinexa">lying</span> in her hut <span class="colloinexa">listening</span> for the first sounds of life.</span><span class="example"><span class="neutral">· </span>I would wake up first and <span class="colloinexa">lie</span> next to her <span class="colloinexa">listening</span> to her breathing.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">sit</span></span><div class="content"><span class="example"><span class="neutral">· </span>I could then <span class="colloinexa">sit</span> and relax and <span class="colloinexa">listen</span> to the other violinists.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">sat</span> quietly and <span class="colloinexa">listened</span> to their exchange.</span><span class="example"><span class="neutral">· </span>In the small back room Danny Tanner <span class="colloinexa">sat listening</span> to his old friend Billy Sullivan's problems.</span><span class="example"><span class="neutral">· </span>On the darkened deck of the Minerva they <span class="colloinexa">sat listening</span> contentedly to Strauss waltzes from the stereo.</span><span class="example"><span class="neutral">· </span>Slim and in his sixties, he <span class="colloinexa">sat</span> and <span class="colloinexa">listened</span> as I proceeded to explain my mission.</span><span class="example"><span class="neutral">· </span>So I tell him about the search for her, and he <span class="colloinexa">sits</span> and <span class="colloinexa">listens</span>.</span><span class="example"><span class="neutral">· </span>Typically, 157 parents <span class="colloinexa">sit</span> and <span class="colloinexa">listen</span> as children read aloud, and assist with difficult sections of the text.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">stand</span></span><div class="content"><span class="example"><span class="neutral">· </span>He <span class="colloinexa">stood</span> and <span class="colloinexa">listened</span>, stiff and unmoving.</span><span class="example"><span class="neutral">· </span>Had <span class="colloinexa">stood</span> outside the window <span class="colloinexa">listening</span>.</span><span class="example"><span class="neutral">· </span>He <span class="colloinexa">stood listening</span> to the footsteps coming closer to him.</span><span class="example"><span class="neutral">· </span>She was <span class="colloinexa">standing</span> slightly curled, <span class="colloinexa">listening</span> carefully to some one.</span><span class="example"><span class="neutral">· </span>She could have <span class="colloinexa">stood</span> there all day <span class="colloinexa">listening</span> to him repeat it over and over again.</span><span class="example"><span class="neutral">· </span>There was a click, but she <span class="colloinexa">stood listening</span> to the dial tone, steadied by its urgent drone.</span><span class="example"><span class="neutral">· </span>Four Galks <span class="colloinexa">stood listening</span> to the conversation.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">stood</span> on that porch <span class="colloinexa">listening</span> to the silence, watching the white clouds in the dark blue summer sky.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">stop</span></span><div class="content"><span class="example"><span class="neutral">· </span>Will you please <span class="colloinexa">stop</span> screaming and simply <span class="colloinexa">listen</span>?</span><span class="example"><span class="neutral">· </span>Our unsolicited advice: Cut off the dreads, peel off the bumper sticker, and just <span class="colloinexa">stop listening</span> to reggae entirely.</span><span class="example"><span class="neutral">· </span>Archie ignored the bell and continued talking but everyone <span class="colloinexa">stopped listening</span> and started worming their way free of the hard plastic chairs.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">stopped listening</span> to Jake Rosso's records, and took his pictures off my bedroom wall.</span><span class="example"><span class="neutral">· </span>Some musicians were playing on the street, and the man <span class="colloinexa">stopped</span> to <span class="colloinexa">listen</span>.</span><span class="example"><span class="neutral">· </span>When he <span class="colloinexa">stopped</span>, they <span class="colloinexa">listened</span>, and heard answering barks.</span><span class="example"><span class="neutral">· </span>In hushed silence I <span class="colloinexa">stopped</span> and <span class="colloinexa">listened</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">watch</span></span><div class="content"><span class="example"><span class="neutral">· </span>Sara, <span class="colloinexa">watching</span>, and <span class="colloinexa">listening</span> to him, felt her heart expand with sweetness.</span><span class="example"><span class="neutral">· </span>Voters expecting to meet him were asked instead to <span class="colloinexa">watch</span> a video and <span class="colloinexa">listen</span> to a telephone conference call.</span><span class="example"><span class="neutral">· </span>Dustin spent hours <span class="colloinexa">watching</span> and <span class="colloinexa">listening</span> to the old man.</span><span class="example"><span class="neutral">· </span>Finally, I asked if he minded if I <span class="colloinexa">watched</span>, and <span class="colloinexa">listened</span>.</span><span class="example"><span class="neutral">· </span>You were the one there, on the edge, <span class="colloinexa">watching</span> and <span class="colloinexa">listening</span>.</span><span class="example"><span class="neutral">· </span>Jody stands outside the circle, <span class="colloinexa">watching</span> and <span class="colloinexa">listening</span>.</span><span class="example"><span class="neutral">· </span>Theodora had followed her brief and for three months she had <span class="colloinexa">watched</span> and <span class="colloinexa">listened</span>.</span><span class="example"><span class="neutral">· </span>But my daughter no longer <span class="colloinexa">watches</span> or <span class="colloinexa">listens</span> to me.</span></div></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">to pay attention to what someone is saying or to a sound that you can hear</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001502925"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001502925').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Listen! There’s a strange noise in the engine.</span><span class="gramexa"><span class="propformprep">listen to</span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001502928"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001502928').play();"><img src="LDOCE6_img_spkr_g.png"/></a> We sat around listening to music.</span></span><span class="colloexa"><span class="collo">listen carefully/intently/hard etc</span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001742329"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001742329').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The whole class was listening attentively.</span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001742330"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001742330').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Liz stood still and listened hard <span class="gloss"><span class="neutral"> (=</span>very carefully<span class="neutral">)</span></span>.</span></span><span class="thesref"><span class="thesaurus">►</span> see <span class="thesaurus">thesaurus</span> at <a><span class="refhwd">hear</span></a></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="registerlab">spoken</span><span class="def"> used to tell someone to pay attention to what you are going to say</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001502939"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001502939').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Listen, I want you to come with me.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s3"></a><span class="sense newline"><span class="sensenum">3</span><span class="def">to consider what someone says and accept their advice</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001502943"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001502943').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I told him not to go, but he just wouldn’t listen.</span><span class="gramexa"><span class="propformprep">listen to</span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-001502946"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001502946').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I wish I’d listened to Dad.</span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001742335"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001742335').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She refused to <span class="colloinexa">listen to reason</span><span class="gloss"><span class="neutral"> (=</span>accept sensible advice<span class="neutral">)</span></span>.</span></span></span><span class="tail"><span class="grambox"><span class="heading">GRAMMAR: Comparison</span><span class="compareword"><span class="exp display">listen</span><span class="expl"><span class="neutral">• </span>You <span class="expr">listen to</span> someone or something: <span class="example"><span class="neutral">· </span>I love listening to classical music.</span> <span class="cross"><span class="cross">✗</span></span>Don’t say: <span class="badexa">I love listening classical music.</span></span><span class="expl"><span class="neutral">• </span>You <span class="expr">listen to</span> someone or something <span class="expr">doing</span> something: <span class="example"><span class="neutral">· </span>I listened to the rain falling on the roof.</span></span></span><span class="compareword last"><span class="exp display">hear</span><span class="expl"><span class="neutral">• </span>You <span class="expr">hear</span> someone or something: <span class="example"><span class="neutral">· </span>I heard a sudden shout.</span></span><span class="expl"><span class="neutral">• </span>You <span class="expr">hear</span> someone or something <span class="expr">do</span> something: <span class="example"><span class="neutral">· </span>I heard something fall into the water.</span></span><span class="expl"><span class="neutral">• </span>You <span class="expr">hear</span> someone or something <span class="expr">doing</span> something: <span class="example"><span class="neutral">· </span>I heard someone shouting.</span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s4"></a><span class="thesbox"><span class="heading">THESAURUS</span><span class="section last"><span class="exponent"><span class="exp display">listen</span><span class="def"> to pay attention to what someone is saying or to a sound that you hear</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I didn’t hear the answer, because I wasn’t listening when she read it out.</span><span class="example"><span class="neutral">· </span>He listened carefully to every word I said.</span></span><span class="exponent"><span class="exp display">pay attention</span><span class="def"> to listen carefully to what someone is saying</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I nodded to show I was paying attention.</span><span class="example"><span class="neutral">· </span>She was tired and wasn’t paying attention to what he was saying.</span></span><span class="exponent"><span class="exp display">eavesdrop</span><span class="def"> to secretly listen to someone else’s conversation by standing near them, hiding behind a door etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I caught him eavesdropping on our conversation.</span><span class="example"><span class="neutral">· </span>They spoke in quiet voices which made it hard to eavesdrop.</span></span><span class="exponent"><span class="exp display">overhear</span><span class="def"> to hear someone say something, especially accidentally</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I overheard him say something about wanting to move house.</span><span class="example"><span class="neutral">· </span>Excuse me! I couldn’t help overhearing that you were planning a trip to Thailand.</span></span><span class="exponent"><span class="exp display">tune in (to something)</span><span class="def"> to listen to a radio programme, or to someone using a radio <span class="nondv"><span class="refhwd">transmitter</span></span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Over a million people tune in to the programme each week.</span><span class="example"><span class="neutral">· </span>Tune in at the same time next week for the next episode.</span><span class="example"><span class="neutral">· </span>The equipment could be used by criminals to tune in to police broadcasts.</span></span><span class="exponent"><span class="exp display">tap</span><span class="def"> to connect a piece of electronic recording equipment to a telephone system so that you can listen to people’s telephone conversations</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The police had tapped the phones of all three suspects.</span><span class="example"><span class="neutral">· </span>The president had to resign over an illegal phone-tapping operation.</span></span><span class="exponent"><span class="exp display">bug</span><span class="def"> to hide a small piece of electronic recording equipment in someone’s room, car, office etc in order to listen secretly to what is said there</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Security agents bugged their offices and managed to get some evidence against them.</span><span class="example"><span class="neutral">· </span>Wells was convinced the house was bugged and insisted on playing loud music while we talked.</span></span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s5"></a><span class="phrvbentry"><span class="entryhead"><span class="phrvbhwd">listen for<span class="object"> something</span></span><span class="pos"> phrasal verb</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s6"></a><span class="sense"> <span class="def">to listen carefully so that you will notice a particular sound</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001502957"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001502957').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Listen for the moment when the music changes.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s7"></a><span class="phrvbentry"><span class="entryhead"><span class="phrvbhwd">listen in</span><span class="pos"> phrasal verb</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s8"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">to listen to a broadcast on the radio</span><span class="gramexa"><span class="propformprep">listen in to</span><span class="example" id="anchor_LDOCE6_exa_bre_d_p008-001502968"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-001502968').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I must remember to listen in to the news.</span></span><span class="crossref"><span class="neutral"> →</span><a> <span class="refhwd">tune in</span></a></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s9"></a><span class="sense newline"><span class="sensenum">2</span><span class="def">to listen to someone’s conversation when they do not want you to</span><span class="gramexa"><span class="propformprep">listen in on</span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001502980"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001502980').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It sounded like someone was listening in on us.</span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s10"></a><span class="phrvbentry"><span class="entryhead"><span class="phrvbhwd">listen out</span><span class="pos"> phrasal verb</span><span class="geo"> British English</span><span class="registerlab"> informal</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s11"></a><span class="sense"> <span class="def">to listen carefully so that you will notice a particular sound</span><span class="gramexa"><span class="propformprep">listen out for</span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-001742342"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-001742342').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Listen out for the baby in case she wakes up.</span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s12"></a><span class="phrvbentry"><span class="entryhead"><span class="phrvbhwd">listen up</span><span class="pos"> phrasal verb</span><span class="registerlab"> spoken</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_1_s13"></a><span class="sense"> <span class="def">used to get people’s attention so they can hear what you are going to say</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001503005"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001503005').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Hey everybody, listen up!</span></span></span></span></div><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_listen_2"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_listen_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_listen_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_listen_2"></a><span class="entry"><span class="entryhead"><span class="hwd">listen</span><span class="hyphenation">listen</span><span class="homnum">2</span><span class="pos"> noun</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_9_listen_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_a_ld41listen').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>So go on Sounds readers, give All a <span class="nodeword">listen</span>.</li><li>They're definitely worth a <span class="nodeword">listen</span>.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">Have a listen</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001503030').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">Have a listen</span> to this new album!</span></div></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to somebody’s advice</span></span><div class="content"><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> heed somebody’s advice</span><span class="registerlab"> formal</span><span class="neutral">)</span></span><span class="collgloss"><span class="neutral"> (=</span>pay attention to someone’s advice<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I wish I had listened to her advice.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">look/listen/think etc carefully</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001666344').play();"><img src="LDOCE6_img_spkr_g.png"/></a> You need to think very carefully about which course you want to do.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to a CD</span></span><div class="content"><span class="example"><span class="neutral">· </span>He's upstairs listening to his CDs.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to a commentary</span></span><div class="content"><span class="example"><span class="neutral">· </span>I was listening to the football commentary on the radio.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to gossip</span></span><div class="content"><span class="example"><span class="neutral">· </span>He was always willing to listen to gossip.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to a lecture</span></span><div class="content"><span class="example"><span class="neutral">· </span>Most students spend about a quarter of their time listening to lectures.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to music</span></span><div class="content"><span class="example"><span class="neutral">· </span>Ella was listening to music on her iPod.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to a programme</span></span><div class="content"><span class="example"><span class="neutral">· </span>A lot of people listen to that programme on the way to work.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">listen to the radio</span></span><div class="content"><span class="example"><span class="neutral">· </span>She was sitting up in bed, listening to the radio.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a spelling/reading/listening test</span></span><div class="content"><span class="example"><span class="neutral">· </span>I didn’t do very well in the listening test.</span></div></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_listen_2_s1"></a><span class="sense"> <a id="6504937708254a64972bf4d4ccdfcc9d_listen_2_s2"></a><span class="lexunit">a listen</span><span class="geo"> British English</span><span class="registerlab"> informal</span><span class="def"> an act of listening</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001503030"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001503030').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">Have a listen</span> to this new album!</span></span></span></div><audio id="LDOCE6_hwd_bre_3_listen_v0205" preload="none" src="LDOCE6_hwd_bre_3_listen_v0205.mp3"></audio><audio id="LDOCE6_hwd_ame_e_listen" preload="none" src="LDOCE6_hwd_ame_e_listen.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001742329" preload="none" src="LDOCE6_exa_bre_f_p008-001742329.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001742330" preload="none" src="LDOCE6_exa_bre_5_p008-001742330.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001742335" preload="none" src="LDOCE6_exa_bre_f_p008-001742335.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001666344" preload="none" src="LDOCE6_exa_bre_9_p008-001666344.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001502925" preload="none" src="LDOCE6_exa_bre_2_p008-001502925.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001502928" preload="none" src="LDOCE6_exa_bre_8_p008-001502928.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001502939" preload="none" src="LDOCE6_exa_bre_7_p008-001502939.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001502943" preload="none" src="LDOCE6_exa_bre_c_p008-001502943.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001502946" preload="none" src="LDOCE6_exa_bre_e_p008-001502946.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001502957" preload="none" src="LDOCE6_exa_bre_9_p008-001502957.mp3"></audio><audio id="LDOCE6_exa_bre_d_p008-001502968" preload="none" src="LDOCE6_exa_bre_d_p008-001502968.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001502980" preload="none" src="LDOCE6_exa_bre_4_p008-001502980.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-001742342" preload="none" src="LDOCE6_exa_bre_6_p008-001742342.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001503005" preload="none" src="LDOCE6_exa_bre_9_p008-001503005.mp3"></audio><audio id="LDOCE6_hwd_bre_9_listen_n0205" preload="none" src="LDOCE6_hwd_bre_9_listen_n0205.mp3"></audio><audio id="LDOCE6_hwd_ame_a_ld41listen" preload="none" src="LDOCE6_hwd_ame_a_ld41listen.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001503030" preload="none" src="LDOCE6_exa_bre_1_p008-001503030.mp3"></audio>`;
