var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_popular_1"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_popular_1').scrollIntoView();"> <i>adjective</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_popular_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_popular_1"></a><span class="entry"><span class="entryhead"><span class="hwd">popular</span><span class="hyphenation frequent">pop<span class="hs0"></span>u<span class="hs0"></span>lar</span><span class="homnum frequent">1</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">ˈpɒpjələ</span><span class="amevarpron"><span class="neutral"> $ </span>ˈpɑːpjəposlər</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S2</span> <span class="freq">W1</span><span class="pos"> adjective</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_a_popular0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_8_popular').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">popular</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1400-1500</span> <span class="etymlang">Latin</span> <span class="etymorigin">popularis</span>, from <span class="etymorigin">populus</span> <span class="etymtran"> ‘people’</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>"Cracker Jacks" are a snack with a long history in American <span class="nodeword">popular</span> culture.</li><li><span class="nodeword">popular</span> entertainment such as TV</li><li>Chatlines have proved very <span class="nodeword">popular</span> with young people.</li><li>Contrary to <span class="nodeword">popular</span> belief, the Australian desert is often full of wildlife.</li><li>I don't accept the <span class="nodeword">popular</span> view that all criminals should be put in prison.</li><li>Jazz has been <span class="nodeword">popular</span> in Japan since the 1960s.</li><li>Lisa's one of the most <span class="nodeword">popular</span> girls in class.</li><li>Old-fashioned names are getting <span class="nodeword">popular</span> again.</li><li>The Sears Tower is a <span class="nodeword">popular</span> tourist destination.</li><li>There is still a lot <span class="nodeword">popular</span> support for the ex-president.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>Chef Tony Rea said a <span class="nodeword">popular</span> entree is ostrich pepper steak in a wine sauce for $ 22. 95.</li><li>Hus quickly became a <span class="nodeword">popular</span> figure in Bohemia.</li><li>Karen Quinlan falls into this third category, despite initial medical and <span class="nodeword">popular</span> views to the contrary.</li><li>Small, simple, cosy and extremely <span class="nodeword">popular</span>.</li><li>Swaps that deal brokers out of small share sales Share exchanges are an increasingly <span class="nodeword">popular</span> alternative for investors to selling small holdings.</li><li>The answer becomes clear when one looks deeper into the history of <span class="nodeword">popular</span> religiosity and superstition in Sicily.</li><li>The drama was <span class="nodeword">popular</span> with the complete social spectrum.</li><li>Western <span class="nodeword">popular</span> prints and Soviet official art both displayed a penchant for landscapes, flower pieces, still lifes and genre.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">popular</span></span><div class="content"><span class="def"> liked by a lot of people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a popular restaurant</span><span class="example"><span class="neutral">· </span>Marmaris is one of Turkey’s most popular tourist destinations.</span><span class="example"><span class="neutral">· </span>His films were <span class="colloinexa">hugely popular</span>.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">well-liked</span></span><div class="content"><span class="def"> used about someone who many people like</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He is an experienced and well-liked member of the team.</span><span class="example"><span class="neutral">· </span>Stein is well-respected and well-liked by the troops.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">big/huge</span></span><div class="content"><span class="gram"> [not before noun]</span><span class="registerlab"> informal</span><span class="def"> very popular, especially in a particular place</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The band are big in Europe.</span><span class="example"><span class="neutral">· </span>Apparently this type of game is huge in Japan.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">hot</span></span><div class="content"><span class="registerlab"> informal</span><span class="def"> someone or something that is hot, is very popular or fashionable and everyone wants to use them, see them, buy them etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>one of this year’s hottest fashion trends</span><span class="example"><span class="neutral">· </span>a hot young singer from Nashville</span></div></span></span><span class="section"><span class="secheading">something that is popular</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">bestseller</span></span><div class="content"><span class="def"> a book that a lot of people buy</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His prize-winning book ‘A Year in Provence’ became an international bestseller.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">blockbuster</span></span><div class="content"><span class="def"> a film that a lot of people watch, especially an exciting film</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a Hollywood blockbuster</span><span class="example"><span class="neutral">· </span>a blockbuster movie</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">hit</span></span><div class="content"><span class="def"> something such as a song, show, or film which is very popular and successful</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The band played all their old hits.</span><span class="example"><span class="neutral">· </span>The film was <span class="colloinexa">a box-office hit</span> <span class="gloss"><span class="neutral"> (=</span>a lot of people went to see it at the cinema<span class="neutral">)</span></span>.</span><span class="example"><span class="neutral">· </span>She stars in ABC’s <span class="colloinexa">hit show</span> ‘Desperate Housewives’.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">sell-out</span></span><div class="content"><span class="def"> a concert, sports event etc which so many people want to see that all the tickets are sold</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The concert was a sell-out.</span><span class="example"><span class="neutral">· </span>the band’s <span class="colloinexa">sell-out tour</span> of the US</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">cult movie/band/figure etc</span></span><div class="content"><span class="def"> a film, band, person etc that has become very popular and fashionable with a particular group of people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a cult TV programme</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">craze</span></span><div class="content"><span class="def"> something that suddenly becomes popular, so that a lot of people do it, buy it etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>the latest dance craze that has been sweeping the US</span><span class="example"><span class="neutral">· </span>the craze for ultra expensive designer jeans</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fad</span></span><div class="content"><span class="registerlab"> informal</span><span class="def"> something that is very popular for a short time – used about something that you disapprove of, which you do not think will last for very long</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Most diets are just fads.</span><span class="example"><span class="neutral">· </span>I think it’s a passing fad.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">people, places, activities etc that a lot of people like</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">popular</span></span><div class="content"><span class="def"> if someone or something is popular , a lot of people like them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Lisa's one of the most popular girls in class.</span><span class="example"><span class="neutral">· </span>Benidorm soon became a popular holiday resort.</span><span class="example"><span class="neutral">· </span>Old-fashioned names are getting popular again.</span><span class="thespropform">popular with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Chatlines have proved very popular with young people.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be in favour</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British</span><span class="variant"> <span class="lexvar">/be in favor</span><span class="neutral"> </span><span class="registerlab">American</span></span><span class="def"> if someone is in favour , they are liked and approved of at the present time, although this may not last</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Suzannah and I are both in favour at work at the moment.</span><span class="thespropform">be back in favour</span><span class="gloss"><span class="neutral"> (=</span>be in favour again<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It looks as if Joey, her old boyfriend, is back in favor again.</span><span class="thespropform">be in favour with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Her fresh approach to environmental issues makes her very much in favor with young voters.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">well-liked</span></span><div class="content"><span class="def"> someone who is well-liked has a lot of friends and is liked by most people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She's a cheerful, good-natured girl, well-liked by all the people she works with.</span><span class="example"><span class="neutral">· </span>As a politician, he may lack experience, but he's very well-liked.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be in demand</span></span><div class="content"><span class="def"> if something such as a product or skill or a person is in demand , it is considered to be very valuable and a lot of people want to have it or use it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>High quality furniture will always be in demand.</span><span class="thespropform">be in great/big demand</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Graduates in Chinese are in great demand in an exciting variety of occupations.</span><span class="thespropform">be much/heavily in demand</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Her dramatic Latin looks caused her to be much in demand as a model.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be big</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> if a product, activity, or performer is big in a particular place or at a particular time, they are very popular</span><span class="thespropform"><span class="neutral">: </span>be big in</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Oasis were big in the early 90's.</span><span class="example"><span class="neutral">· </span>The single is already big in the clubs, and has been remixed by the band.</span></div></span></span><span class="section"><span class="secheading">a popular book, film, song etc</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">best-seller</span></span><div class="content"><span class="def"> a book that a lot of people buy</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>J.K. Rowling's latest book is certain to be a bestseller.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">blockbuster</span></span><div class="content"><span class="def"> a film that a lot of people watch and that makes a lot of money, especially a film with a lot of exciting action</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Bruce Willis's new blockbuster took $10.6 million in its first weekend.</span><span class="example"><span class="neutral">· </span>"Roots" became a blockbuster TV series.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">hit</span></span><div class="content"><span class="def"> a record, film, show etc that a lot of people buy or go to see</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>When I first heard the song I knew it would be a hit.</span><span class="thespropform">hit song/single/musical etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a new hit single from Janet Jackson</span><span class="thespropform">big hit</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Titanic" was a big hit all over the world.</span></div></span></span><span class="section"><span class="secheading">to make something popular</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">popularize</span></span><div class="content"><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">popularise</span><span class="neutral"> </span><span class="registerlab">British</span></span> <span class="example"><span class="neutral">· </span>Most attempts to popularise science and technology have failed.</span><span class="example"><span class="neutral">· </span>Self-service supermarkets were first popularized by businessman Clarence Saunders.</span></div></span></span><span class="section"><span class="secheading">when something becomes popular again</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">revival</span></span><div class="content"><span class="def"> when something or someone becomes popular and fashionable again, for example a kind of music, a style of clothes, a writer, or a singer</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sixties pop music enjoyed a big revival in the mid-90s.</span><span class="example"><span class="neutral">· </span>There's been something of an Abba revival recently.</span></div></span></span><span class="section"><span class="secheading">not popular</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">unpopular</span></span><div class="content"><span class="def"> if someone or something is unpopular , a lot of people do not like them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The government is more unpopular now than it has been for years.</span><span class="example"><span class="neutral">· </span>Mr Venables must be the most unpopular teacher in school.</span><span class="thespropform">unpopular with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The taxes proved extremely unpopular with the electorate.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">out of favour</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British</span><span class="variant"> <span class="lexvar">/out of favor</span><span class="neutral"> </span><span class="registerlab">American</span></span><span class="def"> if a person, idea, or other thing is out of favour , people no longer approve of them or use them, although they used to be popular</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Smacking children seems to be out of favour these days.</span><span class="thespropform">go/fall out of favour (with somebody)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The classic jigsaw puzzle never goes out of favour with kids.</span><span class="example"><span class="neutral">· </span>Journalists and producers who fell out of favour were fired immediately.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">there is no demand/call for</span></span><div class="content"><span class="def"> if there is no demand for a product or service people do not want to buy it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There's no demand for heavy immovable furniture any more.</span><span class="example"><span class="neutral">· </span>Where there is no call for a continued food market, market buildings have proved highly adaptable. </span></div></span></span><span class="section"><span class="secheading">done or believed by a lot of people</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">common</span></span><div class="content"> <span class="example"><span class="neutral">· </span>Using "lay" instead of "lie" is a very common mistake.</span><span class="example"><span class="neutral">· </span>It's becoming more and more common for women to keep their family name when they marry.</span><span class="example"><span class="neutral">· </span>My daughter says politics is boring, which is a common attitude among teenagers.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">popular</span></span><div class="content"><span class="def"> done, believed, or felt by a lot of ordinary people have</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There is still a lot of popular support for the ex-president.</span><span class="example"><span class="neutral">· </span>I don't accept the popular view that all criminals should be put in prison.</span><span class="thespropform">contrary to popular belief</span><span class="gloss"><span class="neutral"> (=</span>the opposite of what most ordinary people believe<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Contrary to popular belief, the Australian desert is often full of wildlife.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">widespread</span></span><div class="content"><span class="def"> done, believed or felt by a lot of people in many different places</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There was now widespread public support for healthcare reform.</span><span class="example"><span class="neutral">· </span>The airlines' failures were in part caused by widespread concern about air safety.</span><span class="example"><span class="neutral">· </span>The practice of adding preservatives to basic foods is widespread.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">widely</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>widely accepted/believed/known/practised/used etc</span><span class="def"> done, believed or felt by a lot of people in many different places</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Silicon Valley is widely known as California's high-tech center.</span><span class="example"><span class="neutral">· </span>A hundred years ago it was widely believed that there was life on Mars.</span><span class="example"><span class="neutral">· </span>Asbestos was once widely used in the building industry.</span></div></span></span><span class="section last"><span class="secheading">what most people think about something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">public opinion/popular opinion</span></span><div class="content"><span class="def"> what most of the people of a country think about a particular subject, idea, or problem</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Responding to public opinion, the government introduced new controls on guns.</span><span class="example"><span class="neutral">· </span>Popular opinion is quite easily swayed by the media.</span><span class="example"><span class="neutral">· </span>Public opinion should not influence every policy decision.</span><span class="example"><span class="neutral">· </span>The shooting of an intruder by a farmer has sharply divided popular opinion.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">consensus</span></span><div class="content"><span class="def"> a basic opinion with which most of the people in a particular group agree in a general way</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Events in Eastern Europe shifted popular consensus against a new generation of nuclear weapons.</span><span class="thespropform">consensus that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There was a growing consensus that the Prime Minister should resign.</span><span class="thespropform">consensus of opinion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There appears to be a consensus of opinion that the pilot was not at fault.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">majority view</span></span><div class="content"><span class="def"> what most of the people in a group or a society think about something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The majority view seems to be that we need more police officers on local streets.</span><span class="example"><span class="neutral">· </span>The mayor was quick to point out that the racist group did not represent a majority view in the community.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="colloboxheading"> Meaning 1</span><span class="section last"><span class="secheading">adverbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">very popular</span></span><div class="content"><span class="example"><span class="neutral">· </span>She was a very popular teacher.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">extremely popular</span></span><div class="content"><span class="example"><span class="neutral">· </span>The restaurant is extremely popular, owing to its high standard of food.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">highly popular</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>extremely popular<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>a highly popular radio station</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">immensely/hugely/enormously popular</span></span><div class="content"><span class="example"><span class="neutral">· </span>His plays were immensely popular.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">wildly popular</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>extremely popular – used especially about something that excites people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>These bands are wildly popular in Cuba.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">phenomenally popular</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>extremely popular, to a degree that is surprising or unusual<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Still her books remain phenomenally popular.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">increasingly popular</span></span><div class="content"><span class="example"><span class="neutral">· </span>Business management courses are increasingly popular.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">universally popular</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>liked by everyone<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Some foods are universally popular.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">enduringly/perennially popular</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>always popular<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>His most enduringly popular film is ‘Singin' in the Rain’.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">genuinely popular</span></span><div class="content"><span class="example"><span class="neutral">· </span>He became Russia's first genuinely popular politician in a long time.</span></div></span></span><span class="colloboxheading"> Meaning 2</span><span class="section last"><span class="secheading">nouns</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular support</span></span><div class="content"><span class="example"><span class="neutral">· </span>There was widespread popular support for the new law.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular demand</span></span><div class="content"><span class="example"><span class="neutral">· </span>She will be performing here again next month, by popular demand.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular belief/opinion</span></span><div class="content"><span class="example"><span class="neutral">· </span>Contrary to popular belief, cats are solitary animals.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the popular view</span></span><div class="content"><span class="example"><span class="neutral">· </span>The popular view bears little relation to the known facts.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular misconception</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>a wrong idea that many people have<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>There is a popular misconception that the town is at the most northerly tip of the country.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the popular vote</span></span><div class="content"><span class="example"><span class="neutral">· </span>He won the presidency with almost 86 percent of the popular vote.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the popular imagination</span></span><div class="content"><span class="example"><span class="neutral">· </span>The characters in the series failed to catch the popular imagination.</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">international/great/popular/public etc acclaim</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001654550').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Their recordings have <span class="colloinexa">won</span> great <span class="colloinexa">acclaim</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular anger</span></span><div class="content"><span class="example"><span class="neutral">· </span>By now public anger in America was mounting.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular attraction</span></span><div class="content"><span class="example"><span class="neutral">· </span>The zoo is by far the most popular attraction.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a common/popular/widespread belief</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>that a lot of people believe<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>There is a common belief that educational standards are declining.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">contrary to popular belief</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>opposite to what most people think<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Contrary to popular belief, boys are not usually better at maths than girls.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular brand</span></span><div class="content"><span class="example"><span class="neutral">· </span>It’s the most popular brand of tequila in Mexico.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular cliché</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one used by a lot of people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The term 'information revolution' is a popular cliché.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">deservedly popular/well-known/famous etc</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000818496').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Bistro Roti is a deservedly popular restaurant.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular destination</span></span><div class="content"><span class="example"><span class="neutral">· </span>Scotland is a popular destination for conferences.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">mass/popular entertainment</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>popular with large numbers of people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Reality TV has been a very successful form of mass entertainment.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">general/popular/widespread expectations</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>shared by a lot of people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The general expectation was for married couples to have children.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular hero</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>someone whom many people admire<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Ross was an arctic explorer and popular hero.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular hostility</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001728883').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Pictures of refugees aroused <span class="colloinexa">popular hostility</span><span class="collgloss"><span class="neutral"> (=</span>felt by a lot of people<span class="neutral">)</span></span> towards the war.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the popular image of something</span></span><div class="content"><span class="example"><span class="neutral">· </span>The popular image of the spy as a glamorous figure of mystery is far from the reality.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">immensely popular</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001719223').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Champagne wines became <span class="colloinexa">immensely popular</span> in the 18th century.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular legend</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one that many people believe<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>A popular legend grew up about him.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular mandate</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>when someone or something wins a vote by a large amount<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He called the election in the hope of receiving a popular mandate.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the mainstream/popular media</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>television, newspapers etc, that most people are able to see or read<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Few of these events were reported in the mainstream media.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular/common misconception</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-000200866').play();"><img src="LDOCE6_img_spkr_g.png"/></a> There is a popular misconception that too much exercise is bad for you.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a common/popular myth</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>that many people believe<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Contrary to popular myth, most road accidents are not the result of speeding.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular mythology</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000198858').play();"><img src="LDOCE6_img_spkr_g.png"/></a> According to <span class="colloinexa">popular mythology</span>, school days are the best days of your life.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular/public opinion</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>what ordinary people think about something<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>How much do newspapers influence popular opinion?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">contrary to popular opinion</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>in spite of what most people think<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Contrary to popular opinion, many cats dislike milk.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular option</span></span><div class="content"><span class="example"><span class="neutral">· </span>Independent sixth-form colleges are becoming a popular option.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular outrage</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001824587').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The case generated public outrage.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular politician</span></span><div class="content"><span class="example"><span class="neutral">· </span>He is the most popular politician in the country.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the tabloid/popular press</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>popular newspapers that have a lot of news about famous people etc, rather than serious news<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He regularly appeared in the tabloid press alongside well-known actresses.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular pressure</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>pressure from the public<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He faces mounting public pressure to resign.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a public/popular protest</span></span><div class="content"><span class="example"><span class="neutral">· </span>The announcement led to widespread public protests.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular resort</span></span><div class="content"><span class="example"><span class="neutral">· </span>The popular seaside resort of Brighton is 40 minutes away.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular revolt</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one involving a lot of ordinary people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Opposition groups had called for a popular revolt against the president.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular/public sentiment</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>what most people think<span class="neutral">)</span></span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001635351').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He was more in touch with public sentiment than many of his critics.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular song</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>used mainly of songs written before the 1960s<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>a popular song from the 1930s</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular/common stereotype</span></span><div class="content"><span class="example"><span class="neutral">· </span>Current evidence indicates that older people are more healthy than popular stereotypes suggest.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular support</span></span><div class="content"><span class="example"><span class="neutral">· </span>There seemed to be no popular support for war.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular taste</span></span><div class="content"><span class="example"><span class="neutral">· </span>The shop created a unique style of goods that appealed to the popular taste.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular uprising</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-000526193').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a <span class="colloinexa">popular uprising</span> <span class="collgloss"><span class="neutral"> (=</span>by the ordinary people in a country<span class="neutral">)</span></span></span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular/unpopular view</span></span><div class="content"><span class="example"><span class="neutral">· </span>This view has become increasingly popular in society.</span><span class="example"><span class="neutral">· </span>It’s now a rather unpopular view.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">as</span></span><div class="content"><span class="example"><span class="neutral">· </span>If they did not, the myth of the sense behind the sentence would not be <span class="colloinexa">as popular</span> as it is.</span><span class="example"><span class="neutral">· </span>Sea burials were <span class="colloinexa">as popular</span> as ever, but they weren't cheap.</span><span class="example"><span class="neutral">· </span>Stuart was <span class="colloinexa">as popular</span> as he was courageous and capable, and the South was stunned by his death.</span><span class="example"><span class="neutral">· </span>Virgin hopes a day in the cockpit will become <span class="colloinexa">as popular</span> as a day at the races for company gatherings.</span><span class="example"><span class="neutral">· </span>And just to say some one is a strict conduct disorder is not <span class="colloinexa">as popular</span> today as it was a few years ago.</span><span class="example"><span class="neutral">· </span>Weston-super-Mare, Southsea, Folkestone and Lowestoft were the other venues and all proved <span class="colloinexa">as popular</span> as on previous visits.</span><span class="example"><span class="neutral">· </span>And the topic of war, even the Revolution, was no longer <span class="colloinexa">as popular</span> as it had once been.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">enormously</span></span><div class="content"><span class="example"><span class="neutral">· </span>This is an important part of the rhythm of a cathedral's worship and it remains <span class="colloinexa">enormously popular</span>.</span><span class="example"><span class="neutral">· </span>He is an <span class="colloinexa">enormously popular</span> president.</span><span class="example"><span class="neutral">· </span>Lyte was <span class="colloinexa">enormously popular</span> during his 25 years of ministry there and built up a Sunday School of over 700 children.</span><span class="example"><span class="neutral">· </span>Lunch was slightly less raucous than the evening meal, but still <span class="colloinexa">enormously popular</span> with the masses.</span><span class="example"><span class="neutral">· </span>Handel had been dead for five years, but his music was still <span class="colloinexa">enormously popular</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">extremely</span></span><div class="content"><span class="example"><span class="neutral">· </span>During the 1993/94 programme we shall continue the series of afternoon events which have proved <span class="colloinexa">extremely popular</span>.</span><span class="example"><span class="neutral">· </span>Cruises, foreign vacation or job experiences, glamorous career situations, and other such settings are <span class="colloinexa">extremely popular</span>.</span><span class="example"><span class="neutral">· </span>Nevertheless, these works were <span class="colloinexa">extremely popular</span> in their day and are still excellent examples of pure terror and suspense.</span><span class="example"><span class="neutral">· </span>Health and fitness, in particular, have proved <span class="colloinexa">extremely popular</span> and the clubs gymnasia are experiencing increased usage.</span><span class="example"><span class="neutral">· </span>It's <span class="colloinexa">extremely popular</span>, so book ahead.</span><span class="example"><span class="neutral">· </span>The Select-A-Play system has been <span class="colloinexa">extremely popular</span> since being instituted last season.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">highly</span></span><div class="content"><span class="example"><span class="neutral">· </span>Newsgroups are also <span class="colloinexa">highly popular</span> as a means of tracing family members who may have fled conflict or natural disaster.</span><span class="example"><span class="neutral">· </span>Once <span class="colloinexa">highly popular</span>, his ratings in the polls sometimes dropped into single digits.</span><span class="example"><span class="neutral">· </span>An example is the once <span class="colloinexa">highly popular</span> low-carbohydrate method of slimming.</span><span class="example"><span class="neutral">· </span>President Hashemi Rafsanjani, a <span class="colloinexa">highly popular</span> figure, chose not to run again.</span><span class="example"><span class="neutral">· </span>PERRIER-JOUET Good consistent quality wines produced by this mainstream <span class="colloinexa">highly popular</span> house.</span><span class="example"><span class="neutral">· </span>And the booklets costing $ 5. 95 are also <span class="colloinexa">highly popular</span>.</span><span class="example"><span class="neutral">· </span>These courses proved <span class="colloinexa">highly popular</span> with our lads and there was practically a hundred percent pass rate.</span><span class="example"><span class="neutral">· </span>And Stewart was <span class="colloinexa">highly popular</span> among his peers.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">hugely</span></span><div class="content"><span class="example"><span class="neutral">· </span>Bill, 54, stopped making the <span class="colloinexa">hugely popular</span> Cosby Show - seen here on Channel 4 - earlier this year.</span><span class="example"><span class="neutral">· </span>Doctors at the time criticized the <span class="colloinexa">hugely popular</span> diet.</span><span class="example"><span class="neutral">· </span>And that would mean tackling the <span class="colloinexa">hugely popular</span> Mr Yeltsin himself.</span><span class="example"><span class="neutral">· </span>They were <span class="colloinexa">hugely popular</span> and widely read.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">immensely</span></span><div class="content"><span class="example"><span class="neutral">· </span>This had not prevented him from becoming an <span class="colloinexa">immensely popular</span> pastor.</span><span class="example"><span class="neutral">· </span>The President personally remained <span class="colloinexa">immensely popular</span> and in 1944 he fought and won his fourth presidential campaign.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">immensely popular</span> company was the only one on the bill, Alvin noted, that was not subsidized by its country.</span><span class="example"><span class="neutral">· </span>Beveridge's legacy endures because the welfare state works tolerably well and is <span class="colloinexa">immensely popular</span>.</span><span class="example"><span class="neutral">· </span>Corporate intranets, <span class="colloinexa">immensely popular</span> in the business world, may prove too confusing to use and too expensive to maintain.</span><span class="example"><span class="neutral">· </span>The film was <span class="colloinexa">immensely popular</span> and had so clearly struck a chord that Hammer carried out a rethink of its production policy.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">increasingly</span></span><div class="content"><span class="example"><span class="neutral">· </span>Snowboarding games are becoming <span class="colloinexa">increasingly popular</span> and PlayStation 2 is proving the perfect platform for them.</span><span class="example"><span class="neutral">· </span>Because bond insurance helps municipalities trim borrowing costs, it has become <span class="colloinexa">increasingly popular</span> nationwide.</span><span class="example"><span class="neutral">· </span>This method is becoming <span class="colloinexa">increasingly popular</span> and has great benefits in terms of aerobatic flying.</span><span class="example"><span class="neutral">· </span>Such high-octane tours have become <span class="colloinexa">increasingly popular</span>.</span><span class="example"><span class="neutral">· </span>But kidnapping as an income source is an <span class="colloinexa">increasingly popular</span> and frightening practice.</span><span class="example"><span class="neutral">· </span>An injunction, on the other hand, is becoming <span class="colloinexa">increasingly popular</span> as an effective remedy for breach of contract.</span><span class="example"><span class="neutral">· </span>Possibly because there are so many middle-aged guys like me struggling with graphical interfaces, 17-inch monitors are becoming <span class="colloinexa">increasingly popular</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">more</span></span><div class="content"><span class="example"><span class="neutral">· </span>There are so many forms of alternative medicine and there's no doubt it's getting <span class="colloinexa">more popular</span>.</span><span class="example"><span class="neutral">· </span>You can buy hamburgers, but hot-and-spicy noodles are much <span class="colloinexa">more popular</span>.</span><span class="example"><span class="neutral">· </span>Giraffes and upstanding bears are <span class="colloinexa">more popular</span> than snakes, rats and spiders.</span><span class="example"><span class="neutral">· </span>There are also other definitions which can be given and which are rather <span class="colloinexa">more popular</span> these days.</span><span class="example"><span class="neutral">· </span>The car, with all its hidden costs in pollution, traffic accidents and congestion, will continue to be <span class="colloinexa">more popular</span>.</span><span class="example"><span class="neutral">· </span>In death, Rabin has become larger than life and <span class="colloinexa">more popular</span> than he ever was as prime minister.</span><span class="example"><span class="neutral">· </span>He is <span class="colloinexa">more popular</span> than either Margaret Thatcher or Neil Kinnock.</span><span class="example"><span class="neutral">· </span>Chretien continues to be <span class="colloinexa">more popular</span> than unpopular, although he has given voters some ground for displeasure.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">most</span></span><div class="content"><span class="example"><span class="neutral">· </span>Among the hundred <span class="colloinexa">most popular</span> museums in Britain, it was visited by more than 900,000 people.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">most popular</span> athletes had more than one card.</span><span class="example"><span class="neutral">· </span>During the summer months weddings are the <span class="colloinexa">most popular</span> function.</span><span class="example"><span class="neutral">· </span>It is the pale, icy blues and strong medium shades that are now the <span class="colloinexa">most popular</span>.</span><span class="example"><span class="neutral">· </span>Even the <span class="colloinexa">most popular</span> personalities, as William Whitelaw was in 1981, can run into difficulty.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">most popular</span> option is the DOS-based WordPerfect system which has launched version 6.0, being the first significant enhancement since 5.1.</span><span class="example"><span class="neutral">· </span>It was this element of unreality which made tunnelling the <span class="colloinexa">most popular</span> scheme for escape.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">so</span></span><div class="content"><span class="example"><span class="neutral">· </span>It isn't difficult to see why the Panda is <span class="colloinexa">so popular</span>.</span><span class="example"><span class="neutral">· </span>At one point the broadcast became <span class="colloinexa">so popular</span> that a line of Sonny Boy cornmeal was introduced.</span><span class="example"><span class="neutral">· </span>This series was <span class="colloinexa">so popular</span> that it was repeated in the same year.</span><span class="example"><span class="neutral">· </span>The site became <span class="colloinexa">so popular</span> that it received more than 50 million hits on its first day, crashing the system.</span><span class="example"><span class="neutral">· </span>Waterers Landscaping became involved in the project in September and proved <span class="colloinexa">so popular</span> that the original £150,000 contract grew and grew.</span><span class="example"><span class="neutral">· </span>This bread is <span class="colloinexa">so popular</span> that it broke my heart having to throw it out.</span><span class="example"><span class="neutral">· </span>These friendly classes are <span class="colloinexa">so popular</span>, numbers are restricted and booking is a must.</span><span class="example"><span class="neutral">· </span>Oddly enough, what makes Gingrich <span class="colloinexa">so popular</span> with conservative Republicans is also what can make him unpopular with others.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">very</span></span><div class="content"><span class="example"><span class="neutral">· </span>The oily fish, like mackerel and herring, are not generally <span class="colloinexa">very popular</span>.</span><span class="example"><span class="neutral">· </span>The host program is <span class="colloinexa">very popular</span>.</span><span class="example"><span class="neutral">· </span>It is <span class="colloinexa">very popular</span> with the Milanese, and the seats around it are filled at most times of the day.</span><span class="example"><span class="neutral">· </span>To tell the truth, he was never <span class="colloinexa">very popular</span>.</span><span class="example"><span class="neutral">· </span>Booklets can be <span class="colloinexa">very popular</span> indeed and some booklets have been taken up in very large quantities.</span><span class="example"><span class="neutral">· </span>The cards are now larger than in previous years and we have found that this size proved <span class="colloinexa">very popular</span>.</span><span class="example"><span class="neutral">· </span>In consequence the use of sedimentation columns or tubes to determine the fall velocities of sediment particles has become <span class="colloinexa">very popular</span>.</span><span class="example"><span class="neutral">· </span>Caniço has probably the best selection of restaurants outside Funchal and is <span class="colloinexa">very popular</span> with both Madeirans and foreigners.</span></div></span></span><span class="section last"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">belief</span></span><div class="content"><span class="example"><span class="neutral">· </span>Contrary to <span class="colloinexa">popular belief</span>, Soviet economic sources provided rich picking for the researcher, as long as the right subject was chosen.</span><span class="example"><span class="neutral">· </span>Contrary to <span class="colloinexa">popular belief</span> by many, Memorial Day is not the day summer vacation begins.</span><span class="example"><span class="neutral">· </span>Dryden's position was that <span class="colloinexa">popular belief</span> in such beings was enough to justify their representation in poetry.</span><span class="example"><span class="neutral">· </span>Contrary to <span class="colloinexa">popular belief</span> a preference for boys over girls is not universal.</span><span class="example"><span class="neutral">· </span>Contrary to <span class="colloinexa">popular belief</span>, income tax provides only just over a quarter of all tax revenue.</span><span class="example"><span class="neutral">· </span>In general, however, it was simply reinterpreting in new language a set of ancient <span class="colloinexa">popular beliefs</span>.</span><span class="example"><span class="neutral">· </span>Contrary to <span class="colloinexa">popular belief</span>, shoplifting is neither a new word nor a new occurrence.</span><span class="example"><span class="neutral">· </span>Actually, contrary to <span class="colloinexa">popular belief</span>, hallucinations were not part of the original definition of schizophrenia.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">choice</span></span><div class="content"><span class="example"><span class="neutral">· </span>The Cheshire Ring Canal Walk is another <span class="colloinexa">popular choice</span>.</span><span class="example"><span class="neutral">· </span>For the past two decades, the national party conventions have been reduced to rubber-stamping the primaries' <span class="colloinexa">popular choices</span>.</span><span class="example"><span class="neutral">· </span>A continuation of wartime industrial conscription was a <span class="colloinexa">popular choice</span>.</span><span class="example"><span class="neutral">· </span>Looking for ideas to replace the risky, <span class="colloinexa">popular choices</span>?</span><span class="example"><span class="neutral">· </span>Both are <span class="colloinexa">popular choices</span> with republicans.</span><span class="example"><span class="neutral">· </span>Benzodiazepines Benzodiazepines are much safer than the older barbiturates and are said to be a <span class="colloinexa">popular choice</span> among physicians for treating insomnia.</span><span class="example"><span class="neutral">· </span>Brushes, like giant bottle brushes are one <span class="colloinexa">popular choice</span> of mechanical media.</span><span class="example"><span class="neutral">· </span>Jim Zorn, naturally, was a <span class="colloinexa">popular choice</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">culture</span></span><div class="content"><span class="example"><span class="neutral">· </span>There were deliberate attempts to develop elements of both high and <span class="colloinexa">popular culture</span> in music, poetry, dance, and games.</span><span class="example"><span class="neutral">· </span>Yet, Fox still managed to change television and <span class="colloinexa">popular culture</span>, for better and for worse.</span><span class="example"><span class="neutral">· </span>Photo: Elaine Prisk, 1997 <span class="colloinexa">popular culture</span>.</span><span class="example"><span class="neutral">· </span>In another single-room installation, 15 photographs by Diane Arbus explore the psychology of <span class="colloinexa">popular culture</span>.</span><span class="example"><span class="neutral">· </span>Portrayals of computers in <span class="colloinexa">popular culture</span> attribute individuality.</span><span class="example"><span class="neutral">· </span>Both define the <span class="colloinexa">popular culture</span>, and in my life, movies and fashion are constantly crossing over.</span><span class="example"><span class="neutral">· </span>Consequently, more attention was paid to working-class and <span class="colloinexa">popular culture</span>.</span><span class="example"><span class="neutral">· </span>But as nettlesome as these issues can be, <span class="colloinexa">popular culture</span> raises another issue of paternity with another kind of collaboration.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">demand</span></span><div class="content"><span class="example"><span class="neutral">· </span>But there is no <span class="colloinexa">popular demand</span>, and no need, to overturn our institutions in a fit of impatience.</span><span class="example"><span class="neutral">· </span>Both men created through their activities a <span class="colloinexa">popular demand</span> for access to the very wilderness they sought to protect.</span><span class="example"><span class="neutral">· </span>As in many other countries, <span class="colloinexa">popular demands</span> for the introduction of multiparty democracy grew in the first half of 1990.</span><span class="example"><span class="neutral">· </span>The formative years of many of the elderly who were surveyed was a ti-me of <span class="colloinexa">popular demand</span> for greater equality.</span><span class="example"><span class="neutral">· </span>And now, due to <span class="colloinexa">popular demand</span>, we can announce the arrival of the Megadisk!</span><span class="example"><span class="neutral">· </span>It was a thing that was created by <span class="colloinexa">popular demand</span>.</span><span class="example"><span class="neutral">· </span>Sir Peter was scheduled to sign for one hour, but gave way to <span class="colloinexa">popular demand</span> and stayed for three.</span><span class="example"><span class="neutral">· </span>Due to <span class="colloinexa">popular demand</span>, levels one to three of the original Fast Forward have been split, producing six short intensive courses.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">movement</span></span><div class="content"><span class="example"><span class="neutral">· </span>By 1984 the <span class="colloinexa">popular movement</span> against the excesses and injustices of the Marcos regime was well-developed.</span><span class="example"><span class="neutral">· </span>I learned something about the difference between a serious and quiet pursuit and a <span class="colloinexa">popular movement</span>.</span><span class="example"><span class="neutral">· </span>After 1848, the <span class="colloinexa">popular movement</span> declined.</span><span class="example"><span class="neutral">· </span>The arenas hired for these meetings seemed to match the modern creed of the new <span class="colloinexa">popular movement</span>.</span><span class="example"><span class="neutral">· </span>The history of <span class="colloinexa">popular movements</span> and popular disturbances.</span><span class="example"><span class="neutral">· </span>In agriculture, the <span class="colloinexa">popular movement</span> began to respond to the kind of difficulties that farmers like those in Zubaydat had encountered.</span><span class="example"><span class="neutral">· </span>Just why Fitzthomas threw in his lot so completely with Montfort and the <span class="colloinexa">popular movement</span> in the city is unclear.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">music</span></span><div class="content"><span class="example"><span class="neutral">· </span>It is the centrality of recordings within <span class="colloinexa">popular music</span> today.</span><span class="example"><span class="neutral">· </span>The box set has given a history to <span class="colloinexa">popular music</span>, by providing perspective.</span><span class="example"><span class="neutral">· </span>Its cabaret tradition and its <span class="colloinexa">popular music</span> are unrivalled.</span><span class="example"><span class="neutral">· </span>The timeless street has been the heartbeat of <span class="colloinexa">popular music</span>, and source of countless classics.</span><span class="example"><span class="neutral">· </span>Already highly successful in <span class="colloinexa">popular music</span>, dance and commercial television, blacks have found the movies a tougher nut to crack.</span><span class="example"><span class="neutral">· </span>So Green turned to <span class="colloinexa">popular music</span>.</span><span class="example"><span class="neutral">· </span>In <span class="colloinexa">popular music</span>, such functions are either lacking or much weaker.</span><span class="example"><span class="neutral">· </span>But they are generally happy just to programme and share the pleasant experience of listening to <span class="colloinexa">popular music</span> on the radio.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">myth</span></span><div class="content"><span class="example"><span class="neutral">· </span>Contrary to <span class="colloinexa">popular myth</span> Darwin was not thunderstruck by the theory of evolution during his voyage on the Beagle.</span><span class="example"><span class="neutral">· </span>In addition, peo-ple have new questions, arising as often from media reports of scientific studies as from <span class="colloinexa">popular myth</span>.</span><span class="example"><span class="neutral">· </span>Contrary to <span class="colloinexa">popular myth</span>, women have gained very little over the last ten years-and lost a lot.</span><span class="example"><span class="neutral">· </span>Other <span class="colloinexa">popular myths</span> also fail to withstand close scrutiny.</span><span class="example"><span class="neutral">· </span>In fact, the Great and Good are not nearly as changed as <span class="colloinexa">popular myth</span> would have it.</span><span class="example"><span class="neutral">· </span>Contrary to the <span class="colloinexa">popular myth</span>, Galileo seems to have performed few experiments in mechanics.</span><span class="example"><span class="neutral">· </span>One <span class="colloinexa">popular myth</span> needs, perhaps, to be dispelled at this stage.</span><span class="example"><span class="neutral">· </span>One of the most <span class="colloinexa">popular myths</span> in weight training is that higher reps produce bigger abdominals.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">opinion</span></span><div class="content"><span class="example"><span class="neutral">· </span>Contrary to modem <span class="colloinexa">popular opinion</span> these were splendid ships with excellent accommodation and many modern conveniences for both crew and passengers.</span><span class="example"><span class="neutral">· </span>Well, <span class="colloinexa">popular opinion</span> is wrong.</span><span class="example"><span class="neutral">· </span>But the fullness of the material makes possible qualitative study with particularly interesting insights into <span class="colloinexa">popular opinions</span> and activities.</span><span class="example"><span class="neutral">· </span>Mary Pinciotti knows that red clay is good for your garden, despite <span class="colloinexa">popular opinion</span>.</span><span class="example"><span class="neutral">· </span>These large juries were clearly intended to provide a reasonably representative sample of <span class="colloinexa">popular opinion</span>.</span><span class="example"><span class="neutral">· </span>She said that, contrary to <span class="colloinexa">popular opinion</span>, traumas caused by such a disaster were not short-lived.</span><span class="example"><span class="neutral">· </span>There are various ways in which <span class="colloinexa">popular opinion</span> can be represented with reasonable accuracy.</span><span class="example"><span class="neutral">· </span>Contrary to <span class="colloinexa">popular opinion</span>, the non-conformist Sunday was not a dismal day full of restrictions.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">press</span></span><div class="content"><span class="example"><span class="neutral">· </span>The new <span class="colloinexa">popular press</span> played a crucial role in orchestrating public opinion over the affair.</span><span class="example"><span class="neutral">· </span>The public can be forgiven for finding the concept perplexing, since the <span class="colloinexa">popular press</span> uses the terms multimedia and cross-media interchangeably.</span><span class="example"><span class="neutral">· </span>Such a low-key comment is not what is required by the <span class="colloinexa">popular press</span> at the conclusion of a traumatic trial.</span><span class="example"><span class="neutral">· </span>These anxieties were mercilessly sharpened by the <span class="colloinexa">popular press</span>.</span><span class="example"><span class="neutral">· </span>But it is not just the <span class="colloinexa">popular press</span> who have argued such a case.</span><span class="example"><span class="neutral">· </span>Reading about it in the <span class="colloinexa">popular press</span> is no substitute for the scrutiny that follows the disclosure required by technical journals.</span><span class="example"><span class="neutral">· </span>This attitude is reflected in a variety of ways in the <span class="colloinexa">popular press</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">song</span></span><div class="content"><span class="example"><span class="neutral">· </span>One day Modi had made a collage by pasting a verse from a <span class="colloinexa">popular song</span> around a painting, in Cubist fashion.</span><span class="example"><span class="neutral">· </span>I asked him what kind of <span class="colloinexa">songs</span> he knew and he said he knew some <span class="colloinexa">popular</span> songs.</span><span class="example"><span class="neutral">· </span>A woman was singing <span class="colloinexa">popular songs</span>, and the holidaymakers were drinking and laughing as they ate their steaks.</span><span class="example"><span class="neutral">· </span>Neuter nouns ending in o take a. He wrote the lyrics of a <span class="colloinexa">popular song</span>.</span><span class="example"><span class="neutral">· </span>Once upon a time there was a film with a jolly <span class="colloinexa">popular song</span>, sung as a duet.</span><span class="example"><span class="neutral">· </span>As I was standing before the sink,, I heard Edusha singing a recently <span class="colloinexa">popular song</span>.</span><span class="example"><span class="neutral">· </span>I would write parodies of <span class="colloinexa">popular songs</span> and my selection would go all around the circuit.</span><span class="example"><span class="neutral">· </span>This is the principal reason that the great voices of opera seldom sing <span class="colloinexa">popular songs</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">support</span></span><div class="content"><span class="example"><span class="neutral">· </span>For governments and corporations this may be the price of <span class="colloinexa">popular support</span> for globalisation on a human scale.</span><span class="example"><span class="neutral">· </span>Surveys continue to show strong <span class="colloinexa">popular support</span> for such direct democratic measures.</span><span class="example"><span class="neutral">· </span>It now had members in Stormont and increasing <span class="colloinexa">popular support</span> and, more importantly, it had a clear Protestant identity.</span><span class="example"><span class="neutral">· </span>All three bills had <span class="colloinexa">popular support</span>, according to polls.</span><span class="example"><span class="neutral">· </span>The extent of <span class="colloinexa">popular support</span>, if any, for this enterprise was never clear.</span><span class="example"><span class="neutral">· </span>It would not be surprising if, to gain <span class="colloinexa">popular support</span> for emancipationist petitions, reformers had to work very hard.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">uprising</span></span><div class="content"><span class="example"><span class="neutral">· </span>The regime was overthrown by a <span class="colloinexa">popular uprising</span> on Dec. 22, 1989.</span><span class="example"><span class="neutral">· </span>But as normality resumes, a strange myth still hovers around the <span class="colloinexa">popular uprising</span> that overthrew Slobodan Milosevic.</span><span class="example"><span class="neutral">· </span>Marcos was overthrown in 1986 after a <span class="colloinexa">popular uprising</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">view</span></span><div class="content"><span class="example"><span class="neutral">· </span>He developed something of a studio to produce his most <span class="colloinexa">popular views</span>.</span><span class="example"><span class="neutral">· </span>As George likes to say, the <span class="colloinexa">popular view</span> is slightly more than half right.</span><span class="example"><span class="neutral">· </span>Karen Quinlan falls into this third category, despite initial medical and <span class="colloinexa">popular views</span> to the contrary.</span><span class="example"><span class="neutral">· </span>How is a judge to gauge the <span class="colloinexa">popular view</span>?</span><span class="example"><span class="neutral">· </span>The picture thus sketched is in strong contrast to the <span class="colloinexa">popular view</span> of housewives as a leisured class.</span><span class="example"><span class="neutral">· </span>I believe that the <span class="colloinexa">popular view</span> that it was the rainbow is incorrect.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">vote</span></span><div class="content"><span class="example"><span class="neutral">· </span>The President is elected for a five-year term by <span class="colloinexa">popular vote</span>.</span><span class="example"><span class="neutral">· </span>In 1992, he ended up with 19 percent of the <span class="colloinexa">popular vote</span>.</span><span class="example"><span class="neutral">· </span>Liberal forces have already begun to collect the million signatures needed to call a <span class="colloinexa">popular vote</span>.</span><span class="example"><span class="neutral">· </span>Perot garnered 19 percent of the <span class="colloinexa">popular vote</span> in the three-way race in 1992</span><span class="example"><span class="neutral">· </span>The 1973 Constitution provides for a National Assembly made up of the Cabinet and 30 members elected by <span class="colloinexa">popular vote</span>.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">contrary to popular belief/opinion</span></span><div class="content"><ul class="exas"><li><span class="nodeword">Contrary</span> to popular belief, gorillas are shy and gentle creatures.</li><li>Actually, <span class="nodeword">contrary</span> to popular belief, hallucinations were not part of the original definition of schizophrenia.</li><li>And <span class="nodeword">contrary</span> to popular belief, we don't want to wear the trousers at home.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">popular demand</span></span><div class="content"><ul class="exas"><li>And now, due to popular <span class="nodeword">demand</span>, we can announce the arrival of the Megadisk!</li><li>As in many other countries, popular <span class="nodeword">demands</span> for the introduction of multiparty democracy grew in the first half of 1990.</li><li>Both men created through their activities a popular <span class="nodeword">demand</span> for access to the very wilderness they sought to protect.</li><li>But there is no popular <span class="nodeword">demand</span>, and no need, to overturn our institutions in a fit of impatience.</li><li>Due to popular <span class="nodeword">demand</span>, levels one to three of the original Fast Forward have been split, producing six short intensive courses.</li><li>It was a thing that was created by popular <span class="nodeword">demand</span>.</li><li>Sir Peter was scheduled to sign for one hour, but gave way to popular <span class="nodeword">demand</span> and stayed for three.</li><li>The formative years of many of the elderly who were surveyed was a ti-me of popular <span class="nodeword">demand</span> for greater equality.</li></ul></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word family</span><div class="at-link"><span class="entry"><span class="popheader popwf">WORD FAMILY</span><span class="wf"><span class="group"><span class="pos">noun</span><span class="w"><span class="wfwd">popularity</span> ≠ <span class="opp"><span class="wfwd">unpopularity</span></span></span><span class="w"><span class="wfwd">popularization</span></span></span><span class="group"><span class="pos">adjective</span><span class="w"><span class="wfwd">popular</span> ≠ <span class="opp"><span class="wfwd">unpopular</span></span></span></span><span class="group"><span class="pos">verb</span><span class="w"><span class="wfwd">popularize</span></span></span><span class="group"><span class="pos">adverb</span><span class="w"><span class="wfwd">popularly</span></span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_popular_1_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">liked by a lot of people</span><span class="opp"> <span class="synopp">OPP</span>  unpopular</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-000309604"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000309604').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Hilary was popular at school.</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001636462"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001636462').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a popular holiday resort</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001636463"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001636463').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Coffee is probably the most popular drink in the world.</span><span class="colloexa"><span class="collo">hugely/enormously/immensely etc popular</span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001636466"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001636466').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Guerrero’s music is hugely popular in Latin America.</span></span><span class="gramexa"><span class="propformprep">popular with/among</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001636468"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001636468').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The president is very popular with Jewish voters.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_popular_1_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="gram">[only before noun]</span><span class="def"> done by a lot of people in a society, group etc</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-000309630"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-000309630').play();"><img src="LDOCE6_img_spkr_g.png"/></a> the closest popular vote in U.S. presidential history</span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001636469"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001636469').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The government has little <span class="colloinexa">popular support</span> among women voters.</span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-001636473"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-001636473').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Kaplan’s latest recording has received considerable <span class="colloinexa">popular acclaim</span><span class="gloss"><span class="neutral"> (=</span>it is liked by a lot of people<span class="neutral">)</span></span>.</span><span class="colloexa"><span class="collo">popular belief/opinion/view</span><span class="gloss"><span class="neutral"> (=</span>a belief, opinion etc that a lot of people have<span class="neutral">)</span></span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001636486"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001636486').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a survey of Hispanic-American popular opinion</span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001636487"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001636487').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">Contrary to popular belief</span><span class="gloss"><span class="neutral"> (=</span>in spite of what many people believe<span class="neutral">)</span></span>, gorillas are basically shy, gentle creatures.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001636491"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001636491').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a <span class="colloinexa">popular movement</span> for democracy</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_popular_1_s3"></a><span class="sense newline"><span class="sensenum">3</span><span class="gram">[only before noun]</span><span class="def"> relating to ordinary people, or intended for ordinary people</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001636495"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001636495').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Wintour’s writing is full of references to American <span class="colloinexa">popular culture</span>.</span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-001636499"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001636499').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Steele was ridiculed by the popular press.</span><span class="crossref"><span class="neutral"> →</span><a> <span class="refhwd">pop music</span></a></span></span><span class="tail"><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="heading">– Meaning 1</span><span class="section last"><span class="secheading">adverbs</span><span class="collocate"><span class="colloc collo">very popular</span><span class="example"><span class="neutral">· </span>She was a very popular teacher.</span></span><span class="collocate"><span class="colloc collo">extremely popular</span><span class="example"><span class="neutral">· </span>The restaurant is extremely popular, owing to its high standard of food.</span></span><span class="collocate"><span class="colloc collo">highly popular</span><span class="collgloss"><span class="neutral"> (=</span>extremely popular<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>a highly popular radio station</span></span><span class="collocate"><span class="colloc collo">immensely/hugely/enormously popular</span><span class="example"><span class="neutral">· </span>His plays were immensely popular.</span></span><span class="collocate"><span class="colloc collo">wildly popular</span><span class="collgloss"><span class="neutral"> (=</span>extremely popular – used especially about something that excites people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>These bands are wildly popular in Cuba.</span></span><span class="collocate"><span class="colloc collo">phenomenally popular</span><span class="collgloss"><span class="neutral"> (=</span>extremely popular, to a degree that is surprising or unusual<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Still her books remain phenomenally popular.</span></span><span class="collocate"><span class="colloc collo">increasingly popular</span><span class="example"><span class="neutral">· </span>Business management courses are increasingly popular.</span></span><span class="collocate"><span class="colloc collo">universally popular</span><span class="collgloss"><span class="neutral"> (=</span>liked by everyone<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Some foods are universally popular.</span></span><span class="collocate"><span class="colloc collo">enduringly/perennially popular</span><span class="collgloss"><span class="neutral"> (=</span>always popular<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>His most enduringly popular film is ‘Singin' in the Rain’.</span></span><span class="collocate"><span class="colloc collo">genuinely popular</span><span class="example"><span class="neutral">· </span>He became Russia's first genuinely popular politician in a long time.</span></span></span></span><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="heading">– Meaning 2</span><span class="section last"><span class="secheading">nouns</span><span class="collocate"><span class="colloc collo">popular support</span><span class="example"><span class="neutral">· </span>There was widespread popular support for the new law.</span></span><span class="collocate"><span class="colloc collo">popular demand</span><span class="example"><span class="neutral">· </span>She will be performing here again next month, by popular demand.</span></span><span class="collocate"><span class="colloc collo">popular belief/opinion</span><span class="example"><span class="neutral">· </span>Contrary to popular belief, cats are solitary animals.</span></span><span class="collocate"><span class="colloc collo">the popular view</span><span class="example"><span class="neutral">· </span>The popular view bears little relation to the known facts.</span></span><span class="collocate"><span class="colloc collo">a popular misconception</span><span class="collgloss"><span class="neutral"> (=</span>a wrong idea that many people have<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>There is a popular misconception that the town is at the most northerly tip of the country.</span></span><span class="collocate"><span class="colloc collo">the popular vote</span><span class="example"><span class="neutral">· </span>He won the presidency with almost 86 percent of the popular vote.</span></span><span class="collocate"><span class="colloc collo">the popular imagination</span><span class="example"><span class="neutral">· </span>The characters in the series failed to catch the popular imagination.</span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_popular_1_s4"></a><span class="thesbox"><span class="heading">THESAURUS</span><span class="section"><span class="exponent"><span class="exp display">popular</span><span class="def"> liked by a lot of people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a popular restaurant</span><span class="example"><span class="neutral">· </span>Marmaris is one of Turkey’s most popular tourist destinations.</span><span class="example"><span class="neutral">· </span>His films were <span class="colloinexa">hugely popular</span>.</span></span><span class="exponent"><span class="exp display">well-liked</span><span class="def"> used about someone who many people like</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He is an experienced and well-liked member of the team.</span><span class="example"><span class="neutral">· </span>Stein is well-respected and well-liked by the troops.</span></span><span class="exponent"><span class="exp display">big/huge</span><span class="gram"> [not before noun]</span><span class="registerlab"> informal</span><span class="def"> very popular, especially in a particular place</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The band are big in Europe.</span><span class="example"><span class="neutral">· </span>Apparently this type of game is huge in Japan.</span></span><span class="exponent"><span class="exp display">hot</span><span class="registerlab"> informal</span><span class="def"> someone or something that is hot, is very popular or fashionable and everyone wants to use them, see them, buy them etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>one of this year’s hottest fashion trends</span><span class="example"><span class="neutral">· </span>a hot young singer from Nashville</span></span></span><span class="section last"><span class="secheading">something that is popular</span><span class="exponent"><span class="exp display">bestseller</span><span class="def"> a book that a lot of people buy</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His prize-winning book ‘A Year in Provence’ became an international bestseller.</span></span><span class="exponent"><span class="exp display">blockbuster</span><span class="def"> a film that a lot of people watch, especially an exciting film</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a Hollywood blockbuster</span><span class="example"><span class="neutral">· </span>a blockbuster movie</span></span><span class="exponent"><span class="exp display">hit</span><span class="def"> something such as a song, show, or film which is very popular and successful</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The band played all their old hits.</span><span class="example"><span class="neutral">· </span>The film was <span class="colloinexa">a box-office hit</span> <span class="gloss"><span class="neutral"> (=</span>a lot of people went to see it at the cinema<span class="neutral">)</span></span>.</span><span class="example"><span class="neutral">· </span>She stars in ABC’s <span class="colloinexa">hit show</span> ‘Desperate Housewives’.</span></span><span class="exponent"><span class="exp display">sell-out</span><span class="def"> a concert, sports event etc which so many people want to see that all the tickets are sold</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The concert was a sell-out.</span><span class="example"><span class="neutral">· </span>the band’s <span class="colloinexa">sell-out tour</span> of the US</span></span><span class="exponent"><span class="exp display">cult movie/band/figure etc</span><span class="def"> a film, band, person etc that has become very popular and fashionable with a particular group of people</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a cult TV programme</span></span><span class="exponent"><span class="exp display">craze</span><span class="def"> something that suddenly becomes popular, so that a lot of people do it, buy it etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>the latest dance craze that has been sweeping the US</span><span class="example"><span class="neutral">· </span>the craze for ultra expensive designer jeans</span></span><span class="exponent"><span class="exp display">fad</span><span class="registerlab"> informal</span><span class="def"> something that is very popular for a short time – used about something that you disapprove of, which you do not think will last for very long</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Most diets are just fads.</span><span class="example"><span class="neutral">· </span>I think it’s a passing fad.</span></span></span></span></span></span></div><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_popular_2"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_popular_1').scrollIntoView();"> <i>adjective</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_popular_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_popular_2"></a><span class="entry"><span class="entryhead"><span class="hwd">popular</span><span class="hyphenation">popular</span><span class="homnum">2</span><span class="pos"> noun</span><span class="gram"> [countable usually plural]</span><span class="geo"> American English</span><span class="registerlab"> informal</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_7_ld5_popular').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_7_ld5_popular').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">international/great/popular/public etc acclaim</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001654550').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Their recordings have <span class="colloinexa">won</span> great <span class="colloinexa">acclaim</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular anger</span></span><div class="content"><span class="example"><span class="neutral">· </span>By now public anger in America was mounting.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular attraction</span></span><div class="content"><span class="example"><span class="neutral">· </span>The zoo is by far the most popular attraction.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a common/popular/widespread belief</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>that a lot of people believe<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>There is a common belief that educational standards are declining.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">contrary to popular belief</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>opposite to what most people think<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Contrary to popular belief, boys are not usually better at maths than girls.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular brand</span></span><div class="content"><span class="example"><span class="neutral">· </span>It’s the most popular brand of tequila in Mexico.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular cliché</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one used by a lot of people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The term 'information revolution' is a popular cliché.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">deservedly popular/well-known/famous etc</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000818496').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Bistro Roti is a deservedly popular restaurant.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular destination</span></span><div class="content"><span class="example"><span class="neutral">· </span>Scotland is a popular destination for conferences.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">mass/popular entertainment</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>popular with large numbers of people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Reality TV has been a very successful form of mass entertainment.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">general/popular/widespread expectations</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>shared by a lot of people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The general expectation was for married couples to have children.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular hero</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>someone whom many people admire<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Ross was an arctic explorer and popular hero.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular hostility</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001728883').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Pictures of refugees aroused <span class="colloinexa">popular hostility</span><span class="collgloss"><span class="neutral"> (=</span>felt by a lot of people<span class="neutral">)</span></span> towards the war.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the popular image of something</span></span><div class="content"><span class="example"><span class="neutral">· </span>The popular image of the spy as a glamorous figure of mystery is far from the reality.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">immensely popular</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001719223').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Champagne wines became <span class="colloinexa">immensely popular</span> in the 18th century.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular legend</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one that many people believe<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>A popular legend grew up about him.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular mandate</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>when someone or something wins a vote by a large amount<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He called the election in the hope of receiving a popular mandate.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the mainstream/popular media</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>television, newspapers etc, that most people are able to see or read<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Few of these events were reported in the mainstream media.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular/common misconception</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-000200866').play();"><img src="LDOCE6_img_spkr_g.png"/></a> There is a popular misconception that too much exercise is bad for you.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a common/popular myth</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>that many people believe<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Contrary to popular myth, most road accidents are not the result of speeding.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular mythology</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000198858').play();"><img src="LDOCE6_img_spkr_g.png"/></a> According to <span class="colloinexa">popular mythology</span>, school days are the best days of your life.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular/public opinion</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>what ordinary people think about something<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>How much do newspapers influence popular opinion?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">contrary to popular opinion</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>in spite of what most people think<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Contrary to popular opinion, many cats dislike milk.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular option</span></span><div class="content"><span class="example"><span class="neutral">· </span>Independent sixth-form colleges are becoming a popular option.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular outrage</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001824587').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The case generated public outrage.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular politician</span></span><div class="content"><span class="example"><span class="neutral">· </span>He is the most popular politician in the country.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the tabloid/popular press</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>popular newspapers that have a lot of news about famous people etc, rather than serious news<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He regularly appeared in the tabloid press alongside well-known actresses.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular pressure</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>pressure from the public<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He faces mounting public pressure to resign.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a public/popular protest</span></span><div class="content"><span class="example"><span class="neutral">· </span>The announcement led to widespread public protests.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular resort</span></span><div class="content"><span class="example"><span class="neutral">· </span>The popular seaside resort of Brighton is 40 minutes away.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular revolt</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one involving a lot of ordinary people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Opposition groups had called for a popular revolt against the president.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular/public sentiment</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>what most people think<span class="neutral">)</span></span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001635351').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He was more in touch with public sentiment than many of his critics.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular song</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>used mainly of songs written before the 1960s<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>a popular song from the 1930s</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a popular/common stereotype</span></span><div class="content"><span class="example"><span class="neutral">· </span>Current evidence indicates that older people are more healthy than popular stereotypes suggest.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular support</span></span><div class="content"><span class="example"><span class="neutral">· </span>There seemed to be no popular support for war.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">public/popular taste</span></span><div class="content"><span class="example"><span class="neutral">· </span>The shop created a unique style of goods that appealed to the popular taste.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular uprising</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-000526193').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a <span class="colloinexa">popular uprising</span> <span class="collgloss"><span class="neutral"> (=</span>by the ordinary people in a country<span class="neutral">)</span></span></span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">popular/unpopular view</span></span><div class="content"><span class="example"><span class="neutral">· </span>This view has become increasingly popular in society.</span><span class="example"><span class="neutral">· </span>It’s now a rather unpopular view.</span></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">contrary to popular belief/opinion</span></span><div class="content"><ul class="exas"><li><span class="nodeword">Contrary</span> to popular belief, gorillas are shy and gentle creatures.</li><li>Actually, <span class="nodeword">contrary</span> to popular belief, hallucinations were not part of the original definition of schizophrenia.</li><li>And <span class="nodeword">contrary</span> to popular belief, we don't want to wear the trousers at home.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">popular demand</span></span><div class="content"><ul class="exas"><li>And now, due to popular <span class="nodeword">demand</span>, we can announce the arrival of the Megadisk!</li><li>As in many other countries, popular <span class="nodeword">demands</span> for the introduction of multiparty democracy grew in the first half of 1990.</li><li>Both men created through their activities a popular <span class="nodeword">demand</span> for access to the very wilderness they sought to protect.</li><li>But there is no popular <span class="nodeword">demand</span>, and no need, to overturn our institutions in a fit of impatience.</li><li>Due to popular <span class="nodeword">demand</span>, levels one to three of the original Fast Forward have been split, producing six short intensive courses.</li><li>It was a thing that was created by popular <span class="nodeword">demand</span>.</li><li>Sir Peter was scheduled to sign for one hour, but gave way to popular <span class="nodeword">demand</span> and stayed for three.</li><li>The formative years of many of the elderly who were surveyed was a ti-me of popular <span class="nodeword">demand</span> for greater equality.</li></ul></div></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_popular_2_s1"></a><span class="sense"> <span class="def">a student who is popular at school, especially one who is part of a group of popular students</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_8_ld5_upd11_007"><a onclick="document.getElementById('LDOCE6_exa_bre_8_ld5_upd11_007').play();"><img src="LDOCE6_img_spkr_g.png"/></a> At high school she was one of the populars and everyone wanted to be her best friend.</span></span></span></div><audio id="LDOCE6_hwd_bre_a_popular0205" preload="none" src="LDOCE6_hwd_bre_a_popular0205.mp3"></audio><audio id="LDOCE6_hwd_ame_8_popular" preload="none" src="LDOCE6_hwd_ame_8_popular.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001654550" preload="none" src="LDOCE6_exa_bre_5_p008-001654550.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-000818496" preload="none" src="LDOCE6_exa_bre_2_p008-000818496.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001728883" preload="none" src="LDOCE6_exa_bre_2_p008-001728883.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001719223" preload="none" src="LDOCE6_exa_bre_e_p008-001719223.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-000200866" preload="none" src="LDOCE6_exa_bre_1_p008-000200866.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-000198858" preload="none" src="LDOCE6_exa_bre_f_p008-000198858.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001824587" preload="none" src="LDOCE6_exa_bre_c_p008-001824587.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001635351" preload="none" src="LDOCE6_exa_bre_1_p008-001635351.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-000526193" preload="none" src="LDOCE6_exa_bre_b_p008-000526193.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-000309604" preload="none" src="LDOCE6_exa_bre_2_p008-000309604.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001636462" preload="none" src="LDOCE6_exa_bre_1_p008-001636462.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001636463" preload="none" src="LDOCE6_exa_bre_1_p008-001636463.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001636466" preload="none" src="LDOCE6_exa_bre_f_p008-001636466.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001636468" preload="none" src="LDOCE6_exa_bre_1_p008-001636468.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-000309630" preload="none" src="LDOCE6_exa_bre_3_p008-000309630.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001636469" preload="none" src="LDOCE6_exa_bre_8_p008-001636469.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-001636473" preload="none" src="LDOCE6_exa_bre_6_p008-001636473.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001636486" preload="none" src="LDOCE6_exa_bre_9_p008-001636486.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001636487" preload="none" src="LDOCE6_exa_bre_9_p008-001636487.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001636491" preload="none" src="LDOCE6_exa_bre_7_p008-001636491.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001636495" preload="none" src="LDOCE6_exa_bre_7_p008-001636495.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001636499" preload="none" src="LDOCE6_exa_bre_e_p008-001636499.mp3"></audio><audio id="LDOCE6_hwd_bre_7_ld5_popular" preload="none" src="LDOCE6_hwd_bre_7_ld5_popular.mp3"></audio><audio id="LDOCE6_hwd_ame_7_ld5_popular" preload="none" src="LDOCE6_hwd_ame_7_ld5_popular.mp3"></audio><audio id="LDOCE6_exa_bre_8_ld5_upd11_007" preload="none" src="LDOCE6_exa_bre_8_ld5_upd11_007.mp3"></audio>`;
