var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_recommend_1"></a><div><a id="6504937708254a64972bf4d4ccdfcc9d_recommend"></a><span class="entry"><span class="entryhead"><span class="hwd">recommend</span><span class="hyphenation frequent">rec<span class="hs0"></span>om<span class="hs0"></span>mend</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">ˌrekəˈmend</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S2</span> <span class="freq">W2</span><span class="pos"> verb</span><span class="gram"> [transitive]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_d_recommend0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_d_recommend').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">recommend</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">recommend</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">recommends</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">recommended</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">recommended</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">recommended</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">recommended</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">recommend</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">recommended</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">recommending</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">recommending</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">recommending</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">recommending</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">recommending</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">recommending</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">recommending</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">recommending</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">recommending</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">recommending</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>Ask friends to <span class="nodeword">recommend</span> babysitters. That's the safest way.</li><li>Ask your tour guide to <span class="nodeword">recommend</span> the best places to eat.</li><li>Can you <span class="nodeword">recommend</span> a good hotel near here?</li><li>Corfu was wonderful - I'd <span class="nodeword">recommend</span> it to anyone.</li><li>Doctors <span class="nodeword">recommend</span> that all children be immunized against polio and tuberculosis.</li><li>I strongly <span class="nodeword">recommend</span> that you get your brakes checked before you go on a long drive.</li><li>I would always <span class="nodeword">recommend</span> buying a good quality bicycle rather than a cheap one.</li><li>The <span class="nodeword">recommended</span> dosage for children is 20 milligrams.</li><li>The directors are <span class="nodeword">recommending</span> that shareholders accept Baldwin's offer.</li><li>The first applicant was <span class="nodeword">recommended</span> by a friend of the boss.</li><li>The Forsyth Report <span class="nodeword">recommended</span> stricter supervision of the trade in live animals.</li><li>The hotel's restaurant comes highly <span class="nodeword">recommended</span>.</li><li>The prosecutor <span class="nodeword">recommended</span> a 15-year sentence.</li><li>The report <span class="nodeword">recommends</span> a number of changes in the existing law.</li><li>Who would you <span class="nodeword">recommend</span> for this job, Stuart?</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>And all over the nation, older women are getting far fewer than the <span class="nodeword">recommended</span> number of mammography exams.</li><li>For a number of reasons, therefore, planners may <span class="nodeword">recommend</span> that growth should be channelled into selected settlements.</li><li>I didn't <span class="nodeword">recommend</span> it because I had seen what it had done to sensitive artists like Scott Walker.</li><li>In summary, it is difficult for this writer to <span class="nodeword">recommend</span> this book.</li><li>McGehee advocates the creation of an independent Office of Ethics Counsel to interpret the rules, investigate complaints and <span class="nodeword">recommend</span> sanctions.</li><li>They would interview applicants and they would <span class="nodeword">recommend</span> a man to him.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to advise someone to do something, especially when you have special knowledge of a situation or subject</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I would strongly recommend buying a good quality bicycle rather than a cheap one.</span><span class="example"><span class="neutral">· </span>The report recommended stricter supervision of the trade in live animals.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tell</span></span><div class="content"><span class="def"> to tell someone that you think they should do something, especially in order to avoid problems</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We’ve been told that we should start revising early.</span><span class="example"><span class="neutral">· </span>I told you not to drink the water here.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">urge</span></span><div class="content"><span class="def"> to strongly advise someone to do something because you think it is very important</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Her doctor has urged her to see a specialist.</span><span class="example"><span class="neutral">· </span>Police are urging drivers not to come into London this weekend.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">advocate</span></span><div class="content"><span class="def"> to say publicly that something should be done, often something that a lot of people disagree about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In 1984, he advocated the use of force against Nicaragua.</span><span class="example"><span class="neutral">· </span>I am surprised that dentists don’t advocate the use of fluoride tablets.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">endorse</span></span><div class="content"><span class="def"> to say in an advertisement that you like a product and think that people should use it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A lot of companies use sports stars to endorse their products.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to advise someone</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">advise</span></span><div class="content"><span class="def"> to tell someone what you think they should do, especially when you have more knowledge or experience than they have</span><span class="thespropform"><span class="neutral">: </span>advise somebody to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I advise you to think very carefully before making any decision.</span><span class="example"><span class="neutral">· </span>All US citizens in the area have been advised to return home.</span><span class="thespropform">advise somebody against doing something</span><span class="gloss"><span class="neutral"> (=</span>advise them not to do it<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Her lawyers have advised her against saying anything to the newspapers.</span><span class="thespropform">advise somebody on/about something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Your teacher will be able to advise you about what qualifications you will need.</span><span class="thespropform">strongly advise</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I strongly advise you to get medical insurance if you're going skiing.</span><span class="thespropform">advise caution/restraint/patience etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Health experts advise extreme caution when handling these materials.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say somebody should do something/ought to do something</span></span><div class="content"><span class="def"> to tell someone what you think they should do, especially when this is your own personal opinion and not the result of particular information or knowledge</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My friends keep saying I ought to learn to drive.</span><span class="example"><span class="neutral">· </span>Her mother said she should call the police immediately.</span><span class="example"><span class="neutral">· </span>They say you should drink at least eight glasses of water a day.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tell</span></span><div class="content"><span class="def"> to tell someone that you think they should do something, especially in order to avoid problems</span><span class="thespropform"><span class="neutral">: </span>tell somebody to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I told him to go and see a doctor if he was worried.</span><span class="thespropform">tell somebody (that) they should do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Jimmy has told him he should keep away from the gang for the next couple of weeks.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"><span class="def"> to tell someone your ideas about what they should do, where they should go etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Why not ask Dad?" he suggested.</span><span class="example"><span class="neutral">· </span>I'm not sure which is the best wine to order. What would you suggest?</span><span class="thespropform">suggest (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sarah suggested that I should apply for this job.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to advise someone to do something, especially when you have special knowledge of a particular subject or situation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The Forsyth Report recommended stricter supervision of the trade in live animals.</span><span class="example"><span class="neutral">· </span>Ask your tour guide to recommend the best places to eat.</span><span class="thespropform">recommend that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Doctors recommend that all children be immunized against polio and tuberculosis.</span><span class="thespropform">recommend doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I would always recommend buying a good quality bicycle rather than a cheap one.</span><span class="thespropform">strongly recommend</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I strongly recommend that you get your brakes checked before you go on a long drive.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">urge</span></span><div class="content"><span class="def"> to strongly advise someone to do something because you think it is very important</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The company's bosses are urging full cooperation with the trade union.</span><span class="thespropform">urge somebody to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police are urging drivers not to come into London this weekend.</span><span class="thespropform">urge that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He gave copies of the report to all those present at the meeting, urging that they read and digest its contents.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">give advice</span></span><div class="content"><span class="def"> to advise someone about a problem or subject, especially something that they have asked you about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm afraid that's the only advice I can give you.</span><span class="thespropform">give advice to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The centre gives free advice to young people who have drug problems.</span><span class="thespropform">give somebody advice</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you give me some advice? I'm thinking of buying a computer.</span><span class="thespropform">give advice on/about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ask your bank to give you some advice about special student packages.</span></div></span></span><span class="section"><span class="secheading">to make a formal suggestion in a meeting, report etc</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">propose</span></span><div class="content"><span class="def"> to formally suggest that something should be done, especially at a meeting</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The Russians proposed a treaty banning all nuclear tests.</span><span class="thespropform">propose (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I propose that we discuss this at the next meeting.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to officially suggest that something should be done, after you have considered the situation carefully</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The report recommends a number of changes in the existing law.</span><span class="thespropform">recommend that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The directors are recommending that shareholders accept Baldwin's offer.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put forward</span></span><div class="content"><span class="def"> to suggest plans, proposals etc, especially in order to start discussions about something that needs to be decided</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The United Nations has put forward a peace plan that it hopes will form the basis for discussions.</span><span class="example"><span class="neutral">· </span>Management initially put forward a number of proposals which were wholly unacceptable to the union.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put something to/before</span></span><div class="content"><span class="def"> to offer a group something such as a proposal or plan which they can accept or refuse</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The Government has spent £1 million on putting its case to the public.</span><span class="example"><span class="neutral">· </span>We're going to put our plans before the committee on Monday and we'll just have to hope that they are approved.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">float</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>float an idea/plan etc</span><span class="def"> to suggest an idea, plan etc in order to find out what other people think about it </span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The administration had floated the idea of increased taxes on beer, spirits and tobacco.</span><span class="example"><span class="neutral">· </span>The following month, David floated the possibility of launching a new TV company.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">submit</span></span><div class="content"><span class="def"> to offer a proposal, application etc so that an official person or group can consider it and decide whether to accept it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Applications for planning permission must be submitted before noon tomorrow.</span><span class="thespropform">submit something to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We have submitted proposals for a new pay structure to the board of management.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">present</span></span><div class="content"><span class="def"> to explain your ideas or plans to an official group so that they can decide whether to accept them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We shall give you reasonable time to prepare and present your proposals.</span><span class="thespropform">present something to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ms Rogers will present her ideas to the Board at next week's meeting.</span><span class="thespropform">present somebody with something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We have been presented with a number of plans and will give careful consideration to all of them.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be mooted</span></span><div class="content"><span class="def"> if an idea or plan is mooted , it is suggested as something that could be done</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The scheme was first mooted two years ago.</span><span class="thespropform">be mooted for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A 3,000 house development has been mooted for the disused airfield.</span></div></span></span><span class="section"><span class="secheading">to suggest someone as a suitable person for a job or official position</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"> <span class="example"><span class="neutral">· </span>All members are invited to suggest names.</span><span class="thespropform">suggest somebody for something</span> <span class="example"><span class="neutral">· </span>Robert suggested his son for the vacant directorship.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to suggest someone you know personally as suitable for a job or position, because you think they would do a good job</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ask friends to recommend babysitters. That's the safest way.</span><span class="example"><span class="neutral">· </span>The first applicant was recommended by a friend of the boss.</span><span class="thespropform">recommend somebody for something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Who would you recommend for this job, Stuart?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put somebody's name forward</span></span><div class="content"><span class="def"> to formally suggest someone, usually in writing, to be elected to an official or political position</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The local Democratic party has put several names forward.</span><span class="thespropform">put sb's name forward for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The opposition leader announced that he would not be putting his name forward for re-election at the party's annual conference.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">nominate</span></span><div class="content"><span class="def"> to suggest someone for an important job or prize, especially when people will vote to make a decision</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We need a treasurer. Does anyone want to nominate somebody?</span><span class="thespropform">nominate somebody for something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Jane Campion was one of the people nominated for the 'Best Director' award.</span><span class="thespropform">nominate somebody as something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was expected that he would nominate Bramwell as his successor.</span><span class="thespropform">nominate somebody to something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The President has power to nominate people to certain key offices, including judge of the Supreme Court.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">propose</span></span><div class="content"><span class="def"> to formally suggest someone for an official position</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>At the last meeting, Mrs Williams was proposed by several members.</span><span class="thespropform">propose for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I would like to propose Mr Harrison for the position of Party Treasurer.</span></div></span></span><span class="section last"><span class="secheading">to suggest something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"><span class="def"> to tell someone your idea about what they should do, where they should go etc, or about what you and they should do together</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>''Why don't you come with us?" Alan suggested.</span><span class="example"><span class="neutral">· </span>It was a sunny afternoon, and Jim suggested a trip to the beach.</span><span class="thespropform">suggest (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My Dad suggested that I should apply for the job.</span><span class="example"><span class="neutral">· </span>I suggest we take a break and finish this later.</span><span class="thespropform">suggest doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was raining heavily, and she suggested calling a taxi.</span><span class="thespropform">suggest where/how/when etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you suggest where we might be able to get a decent meal?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">make a suggestion</span></span><div class="content"><span class="def"> to suggest something that you think will help someone or will solve a problem</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mr Chairman - may I make a suggestion?</span><span class="example"><span class="neutral">· </span>One day her mother made a suggestion. "Why don't you come back and live with your father and me?''</span><span class="example"><span class="neutral">· </span>A professional consultant will make suggestions about the most suitable clients to approach for your particular type of work.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to suggest something to someone because you know that it is good and you are sure that they will like it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you recommend a good hotel near here?</span><span class="thespropform">recommend something to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Corfu was wonderful - I'd recommend it to anyone.</span><span class="thespropform">be highly recommended</span><span class="gloss"><span class="neutral"> (=</span>people say that it is very good<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The hotel's restaurant comes highly recommended. </span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="colloboxheading"> Meaning 2</span><span class="section last"><span class="secheading">adverbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">highly recommend</span></span><div class="content"><span class="example"><span class="neutral">· </span>This book is highly recommended by those who have used it.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">heartily recommend</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>strongly and enthusiastically<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I heartily recommend the cheese sandwiches.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">thoroughly recommend</span></span><div class="content"><span class="example"><span class="neutral">· </span>Overall I can thoroughly recommend this film to anyone with an interest in space travel.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">definitely/certainly recommend</span></span><div class="content"><span class="example"><span class="neutral">· </span>We would definitely recommend these books to students in fourth to sixth grade.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">personally recommend</span></span><div class="content"><span class="example"><span class="neutral">· </span>Are there any hair colouring products you can personally recommend?</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a commission recommends something</span></span><div class="content"><span class="example"><span class="neutral">· </span>The commission recommended that the federal government change the way it measures inflation.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the recommended dose</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>recommended by medical experts<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>The current recommended dose is 250 mg a day.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">experts advise/recommend something</span></span><div class="content"><span class="example"><span class="neutral">· </span>Most financial experts recommend that you don’t borrow money on credit cards.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">heartily recommend</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001758018').play();"><img src="LDOCE6_img_spkr_g.png"/></a> This is a book I <span class="colloinexa">heartily recommend</span> to all hill walkers.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">the agreed/stipulated/recommended maximum</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one that is agreed between people<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>A pregnant woman should drink no more than the recommended maximum of 4 units per week.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">strongly suggest/advise/recommend something</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-001620902').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Before taking action, you are strongly recommended to consult an accountant.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">also</span></span><div class="content"><span class="example"><span class="neutral">· </span>Our route descriptions <span class="colloinexa">also recommend</span> a variety of restaurants or bars to visit enroute for light refreshment.</span><span class="example"><span class="neutral">· </span>The Committee is <span class="colloinexa">also recommending</span> campaign finance provisions similar to those now in place in the City of Tucson.</span><span class="example"><span class="neutral">· </span>The group will <span class="colloinexa">also recommend</span> improved access to private rented accommodation through rent deposit schemes.</span><span class="example"><span class="neutral">· </span>It <span class="colloinexa">also recommended</span> that insurers cover such care.</span><span class="example"><span class="neutral">· </span>However, it is <span class="colloinexa">also recommending</span> urban extensions to cities like Bath and Cheltenham, and new settlements in Devon.</span><span class="example"><span class="neutral">· </span>It <span class="colloinexa">also recommends</span> a study of how to protect aircraft from missiles.</span><span class="example"><span class="neutral">· </span>It <span class="colloinexa">also recommends</span> that the directors should be required to make a statement about their responsibilities in relation to the accounts.</span><span class="example"><span class="neutral">· </span>We <span class="colloinexa">also recommend</span> buying good-quality paint.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">highly</span></span><div class="content"><span class="example"><span class="neutral">· </span>The holiday was one I have always dreamed about and I would <span class="colloinexa">highly recommend</span> the centre to any outdoor enthusiast.</span><span class="example"><span class="neutral">· </span>Many of these products are as close to the natural diet as one can get and are <span class="colloinexa">highly recommended</span>.</span><span class="example"><span class="neutral">· </span>She had come <span class="colloinexa">highly recommended</span>, her latest play having run for six months in London.</span><span class="example"><span class="neutral">· </span>A kitchen fan or open windows are <span class="colloinexa">highly recommended</span>.</span><span class="example"><span class="neutral">· </span>The hotel grows its own produce and its wines are <span class="colloinexa">highly recommended</span>.</span><span class="example"><span class="neutral">· </span>All ages are welcome, and warm clothes are <span class="colloinexa">highly recommended</span>, along with a flashlight, binoculars and a blanket.</span><span class="example"><span class="neutral">· </span>Their booklets are written by specialists in the field, and are <span class="colloinexa">highly recommended</span>.</span><span class="example"><span class="neutral">· </span>A dunk into that aforementioned garlic butter sauce is <span class="colloinexa">highly recommended</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">strongly</span></span><div class="content"><span class="example"><span class="neutral">· </span>The Association would <span class="colloinexa">strongly recommend</span> that they should take effect at the earliest in January of the following year.</span><span class="example"><span class="neutral">· </span>After the third such bout in the space of a few months, a tonsillectomy was <span class="colloinexa">strongly recommended</span> by the family physician.</span><span class="example"><span class="neutral">· </span>The type of detailed empirical research reviewed above suggests that scepticism is to be <span class="colloinexa">strongly recommended</span>.</span><span class="example"><span class="neutral">· </span>So we <span class="colloinexa">recommend strongly</span> that you use your emotions to help set your compass.</span><span class="example"><span class="neutral">· </span>This is a good book and I <span class="colloinexa">strongly recommend</span> it.</span><span class="example"><span class="neutral">· </span>We <span class="colloinexa">strongly recommend</span> the use of an instant read thermometer to determine doneness more accurately.</span><span class="example"><span class="neutral">· </span>Stapledon <span class="colloinexa">strongly recommends</span> a second harrowing to cover the seeds before rolling.</span><span class="example"><span class="neutral">· </span>Johnson <span class="colloinexa">strongly recommended</span> Ben Rich to succeed him.</span></div></span></span><span class="section last"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">action</span></span><div class="content"><span class="example"><span class="neutral">· </span>The Head of Department in consultation with the Dean is responsible for <span class="colloinexa">recommending</span> appropriate <span class="colloinexa">action</span> in cases of suspected cheating or plagiarism by students.</span><span class="example"><span class="neutral">· </span>He called for the four counties to hire a consultant to study the region before <span class="colloinexa">recommending</span> a course of <span class="colloinexa">action</span>.</span><span class="example"><span class="neutral">· </span>It <span class="colloinexa">recommended</span> disciplinary <span class="colloinexa">action</span> against the department's bureau of political-military affairs for failing to stop the violations.</span><span class="example"><span class="neutral">· </span>Can you <span class="colloinexa">recommend</span> a course of <span class="colloinexa">action</span>?</span><span class="example"><span class="neutral">· </span>Planners have <span class="colloinexa">recommended</span> enforcement <span class="colloinexa">action</span> compelling the club to remove the wire with 12 months lee-way.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">recommend</span> that the following <span class="colloinexa">action</span> now be taken: 1.</span><span class="example"><span class="neutral">· </span>On the radio, police supervisors were <span class="colloinexa">recommending</span> alternate courses of <span class="colloinexa">action</span>.</span><span class="example"><span class="neutral">· </span>Each concluded that there was no demonstrable basis for concern about independence and therefore they <span class="colloinexa">recommended</span> no special <span class="colloinexa">action</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">board</span></span><div class="content"><span class="example"><span class="neutral">· </span>The <span class="colloinexa">board</span> has <span class="colloinexa">recommended</span> a final dividend of 6 pence per share, for an unchanged 10 pence total for the year.</span><span class="example"><span class="neutral">· </span>Those <span class="colloinexa">boards recommend</span> the promotions that then must be confirmed by the Senate.</span><span class="example"><span class="neutral">· </span>Late last year the Northern Ireland sentence review <span class="colloinexa">board recommended</span> that Adair be freed from Maghaberry.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">board</span> is <span class="colloinexa">recommending</span> a final dividend of 4.65 pence per share.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">board</span> are <span class="colloinexa">recommending</span> a 10% increase in dividend to 7.4 pence.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">board</span> has <span class="colloinexa">recommended</span> a dividend of 0.7p per share, broadly in line with last year.</span><span class="example"><span class="neutral">· </span>But family feuding and falling profits led a majority of the <span class="colloinexa">board</span> to <span class="colloinexa">recommend</span> a sell-out.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">board</span> has <span class="colloinexa">recommended</span> a 10% increase in interim dividend to 3.32 pence, compared with 1992's 3.02 pence.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">book</span></span><div class="content"><span class="example"><span class="neutral">· </span>Summarizing, it is difficult to <span class="colloinexa">recommend</span> this <span class="colloinexa">book</span>.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">recommend</span> covering the <span class="colloinexa">book</span> with a brown paper cover if you are easily embarrassed.</span><span class="example"><span class="neutral">· </span>In summary, it is difficult for this writer to <span class="colloinexa">recommend</span> this <span class="colloinexa">book</span>.</span><span class="example"><span class="neutral">· </span>But this will make it richer and more real when you <span class="colloinexa">recommend books</span> to others who are questioning and searching.</span><span class="example"><span class="neutral">· </span>So I can <span class="colloinexa">recommend</span> this <span class="colloinexa">book</span> only to some one who has little knowledge of green issues and the science involved.</span><span class="example"><span class="neutral">· </span>I would particularly <span class="colloinexa">recommend</span> this <span class="colloinexa">book</span> to the non-professional reader, including industrial managers, journalists, teachers and politicians.</span><span class="example"><span class="neutral">· </span>It's a pity not to be able to <span class="colloinexa">recommend</span> any <span class="colloinexa">books</span> by Noam Chomsky himself.</span><span class="example"><span class="neutral">· </span>I would really like to <span class="colloinexa">recommend</span> this <span class="colloinexa">book</span> to research-oriented analysts and researchers in the field.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">change</span></span><div class="content"><span class="example"><span class="neutral">· </span>A Royal Commission on Criminal Justice is due to <span class="colloinexa">recommend changes</span> next year.</span><span class="example"><span class="neutral">· </span>During the meeting, I <span class="colloinexa">recommended</span> several <span class="colloinexa">changes</span>.</span><span class="example"><span class="neutral">· </span>Those who have <span class="colloinexa">recommended</span> a <span class="colloinexa">change</span> in the law to require the installation of smoke detectors have now got their wish.</span><span class="example"><span class="neutral">· </span>We will set up an Animal Protection Commission to enforce and <span class="colloinexa">recommend changes</span> to legislation.</span><span class="example"><span class="neutral">· </span>Dan regularly empowered frontline teams to <span class="colloinexa">recommend changes</span>.</span><span class="example"><span class="neutral">· </span>They can examine plans and <span class="colloinexa">recommend changes</span> to reduce the incidence of crime.</span><span class="example"><span class="neutral">· </span>Simplify the practice rules: to review the existing principles on which regulation is undertaken, and <span class="colloinexa">recommend changes</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">commission</span></span><div class="content"><span class="example"><span class="neutral">· </span>The <span class="colloinexa">Commission recommends</span> that parishes explore alternative ways of singing the Psalms.</span><span class="example"><span class="neutral">· </span>The discovery prompted the safety <span class="colloinexa">commission</span> to <span class="colloinexa">recommend</span> a lead standard of 200 parts per million for mini-blinds.</span><span class="example"><span class="neutral">· </span>The Law <span class="colloinexa">Commission</span> has <span class="colloinexa">recommended</span> the partial abolition of these rules.</span><span class="example"><span class="neutral">· </span>The city Planning <span class="colloinexa">Commission recommended</span> that the club be forced to pay a law-enforcement tax to help the department recover its costs.</span><span class="example"><span class="neutral">· </span>Rather, we desire that the <span class="colloinexa">commission</span> should <span class="colloinexa">recommend</span> the most appropriate form of local government for a particular area.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">commission recommends</span> to the government.</span><span class="example"><span class="neutral">· </span>We will set up an Animal Protection <span class="colloinexa">Commission</span> to enforce and <span class="colloinexa">recommend</span> changes to legislation.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">commission</span> will probably also <span class="colloinexa">recommend</span> that the defence be given improved access to forensic facilities.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">committee</span></span><div class="content"><span class="example"><span class="neutral">· </span>On Monday Darlington council's transport <span class="colloinexa">committee</span> will be <span class="colloinexa">recommended</span> to join the steering group and appoint a member.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">Committee</span> is also <span class="colloinexa">recommending</span> campaign finance provisions similar to those now in place in the City of Tucson.</span><span class="example"><span class="neutral">· </span>A report to Midlothian's planning <span class="colloinexa">committee recommends</span> the proposal gets the go ahead.</span><span class="example"><span class="neutral">· </span>He chaired the <span class="colloinexa">committee</span> that <span class="colloinexa">recommended</span> it to Clinton in 1998.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">committee recommends</span> a two-tier system of tests for identifying mutagens.</span><span class="example"><span class="neutral">· </span>After all, Pitino, a Massachusetts graduate, was on the search <span class="colloinexa">committee</span> and <span class="colloinexa">recommended</span> the hiring of Calipari in 1988.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">committee recommends</span> that building regulations be amended to ensure that offices and other similar buildings have adequate ventilation.</span><span class="example"><span class="neutral">· </span>He was scheduled to give it to the Ethics <span class="colloinexa">Committee</span>, which will <span class="colloinexa">recommend</span> what punishment the speaker should receive.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">doctor</span></span><div class="content"><span class="example"><span class="neutral">· </span>Few <span class="colloinexa">doctors</span> would <span class="colloinexa">recommend</span> such long-term use.</span><span class="example"><span class="neutral">· </span>He was taken to a local hospital, where <span class="colloinexa">doctors recommended</span> that he see orthopedic surgeons here.</span><span class="example"><span class="neutral">· </span>Some <span class="colloinexa">doctors recommend</span> support tights or compression bandages-but beware.</span><span class="example"><span class="neutral">· </span>It also would protect <span class="colloinexa">doctors</span> for <span class="colloinexa">recommending</span> marijuana for medical purposes.</span><span class="example"><span class="neutral">· </span>A Harley Street <span class="colloinexa">doctor</span> had <span class="colloinexa">recommended</span> in-patient care but this would have cost the family too much.</span><span class="example"><span class="neutral">· </span>Because Type 1 diabetes tends to run in families, Chris' <span class="colloinexa">doctor recommended</span> testing for all of the Birminghams.</span><span class="example"><span class="neutral">· </span>His wife was feeling the strain and the family <span class="colloinexa">doctor</span> had <span class="colloinexa">recommended</span> it.</span><span class="example"><span class="neutral">· </span>Her <span class="colloinexa">doctor recommended</span> she consider a hysterectomy.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">government</span></span><div class="content"><span class="example"><span class="neutral">· </span>Treasury does, however, recognise a social dimension to education and <span class="colloinexa">recommends government</span> intervention to help the disadvantaged.</span><span class="example"><span class="neutral">· </span>So if Stigler admits the problem, why does he <span class="colloinexa">recommend</span> that the <span class="colloinexa">government</span> should usually stay away?</span><span class="example"><span class="neutral">· </span>He <span class="colloinexa">recommended</span> that local <span class="colloinexa">governments</span> start buying undeveloped land for affordable housing.</span><span class="example"><span class="neutral">· </span>It <span class="colloinexa">recommends</span> that the <span class="colloinexa">government</span> reduce spending to encourage growth in private industry to bring about faster rates of economic growth.</span><span class="example"><span class="neutral">· </span>The commission <span class="colloinexa">recommends</span> to the <span class="colloinexa">government</span>.</span><span class="example"><span class="neutral">· </span>There was nothing to <span class="colloinexa">recommend</span> preserving the old <span class="colloinexa">Government</span> until it had been formally defeated at Westminster.</span><span class="example"><span class="neutral">· </span>It <span class="colloinexa">recommends</span> that the <span class="colloinexa">government</span> should set a firm date for these authorities to complete surveys of their water supplies.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">panel</span></span><div class="content"><span class="example"><span class="neutral">· </span>Mrs Thatcher's <span class="colloinexa">panel</span> of advisers <span class="colloinexa">recommend</span> against linking homes with optical fibres on the grounds of cost.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">panel recommended</span> 12 months of therapy, rather than the previous standard six months because studies have shown better long-term results.</span><span class="example"><span class="neutral">· </span>Instead of tree and branch, the <span class="colloinexa">panel recommend</span> the newer concept of a switched, or star, system.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">panel</span> also could <span class="colloinexa">recommend</span> Gingrich be fined and the matter be referred to the Justice Department.</span><span class="example"><span class="neutral">· </span>In that case the <span class="colloinexa">panel</span> might <span class="colloinexa">recommend</span> reductions in funding, which could then be applied to other areas.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">panel recommended</span> that officers refrain from such actions, Eide said.</span><span class="example"><span class="neutral">· </span>However, it also has its own <span class="colloinexa">panel</span> to whom it <span class="colloinexa">recommends</span> people wishing to pursue a medical negligence claim.</span><span class="example"><span class="neutral">· </span>In 1990, when the ethics <span class="colloinexa">panel recommended</span> a reprimand for Rep.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">report</span></span><div class="content"><span class="example"><span class="neutral">· </span>Specifically the Heilbron <span class="colloinexa">Report</span> did not <span class="colloinexa">recommend</span> similar anonymity for defendants in rape cases.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">report recommended</span> that each midshipman and cadet spend a semester at another service academy.</span><span class="example"><span class="neutral">· </span>They studied referees' <span class="colloinexa">reports</span> and may <span class="colloinexa">recommend</span> pulling the plug again after the final at Wembley on March 27.</span><span class="example"><span class="neutral">· </span>The 64-page <span class="colloinexa">report recommended</span> that all military education programs follow the lead of the service academies and provide intensified ethics training.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">report recommends</span> the setting up of a national environment institute and national education programmes.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">report recommends</span> that alternative methods of pain and anxiety control should be used to reduce further the use of general anaesthetics.</span><span class="example"><span class="neutral">· </span>For this reason, the <span class="colloinexa">report</span> has <span class="colloinexa">recommended</span> the setting up of new agencies to monitor government spending and environmental impact.</span><span class="example"><span class="neutral">· </span>The full <span class="colloinexa">report</span> is likely to <span class="colloinexa">recommend</span> the curriculum tread a line between principles and content.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">use</span></span><div class="content"><span class="example"><span class="neutral">· </span>Surely manufacturers who offer these products and <span class="colloinexa">recommend</span> them of <span class="colloinexa">use</span> as a priming support for oils have done their research properly?</span><span class="example"><span class="neutral">· </span>Heretofore, the drugs have been <span class="colloinexa">recommended</span> only for <span class="colloinexa">use</span> in patients who have had heart attacks.</span><span class="example"><span class="neutral">· </span>We strongly <span class="colloinexa">recommend</span> the <span class="colloinexa">use</span> of an instant read thermometer to determine doneness more accurately.</span><span class="example"><span class="neutral">· </span>Thus co-ordinate indexing was not <span class="colloinexa">recommended</span> for <span class="colloinexa">use</span> in schools without very skilled staff being present to operate and coordinate its use.</span><span class="example"><span class="neutral">· </span>This species is not <span class="colloinexa">recommended</span> for <span class="colloinexa">use</span> in paludariums.</span><span class="example"><span class="neutral">· </span>The latter <span class="colloinexa">recommends</span> the <span class="colloinexa">use</span> of Ketrul 211-a Totalsolvants product-to clean up beaches.</span><span class="example"><span class="neutral">· </span>Luntz <span class="colloinexa">recommends</span> that Dole <span class="colloinexa">use</span> welfare overhaul as a unifying issue, because it appeals to the Buchanan Brigades without offending moderates.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">something has much/little/nothing to recommend it</span></span><div class="content"><ul class="exas"><li>The hotel has little except price to <span class="nodeword">recommend</span> it.</li><li>An alternative approach-optical fibre - has much to <span class="nodeword">recommend</span> it.</li><li>As such, it has much to <span class="nodeword">recommend</span> it.</li><li>But in terms of an effective solution the voting method has little to <span class="nodeword">recommend</span> it.</li><li>In principle this format has much to <span class="nodeword">recommend</span> it, but in this case the practice has not been successful.</li><li>It is plain that, in the long run, the gentle art of compromise has much to <span class="nodeword">recommend</span> it.</li><li>Nevertheless, the principle of chisel ploughing has much to <span class="nodeword">recommend</span> it in the right conditions.</li><li>Such a way of proceeding has much to <span class="nodeword">recommend</span> it, but scant progress has been made in that direction.</li><li>This cooperative family decision-making has much to <span class="nodeword">recommend</span> it.</li></ul></div></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_recommend_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">to advise someone to do something, especially because you have special knowledge of a situation or subject</span><span class="gramexa"><span class="propform">recommend (that)</span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-000761138"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000761138').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I recommend that you get some professional advice.</span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-001771360"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-001771360').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Doctors <span class="colloinexa">strongly recommend</span> that fathers should be present at their baby’s birth.</span></span><span class="gramexa"><span class="propform">recommend doing something</span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001614053"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001614053').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I would never recommend using a sunbed on a regular basis.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-000761135"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-000761135').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Sleeping tablets are not recommended in this case.</span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001614045"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001614045').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It is dangerous to exceed the <span class="colloinexa">recommended dose</span>.</span><span class="gramexa"><span class="propform">recommend somebody to do something</span><span class="example" id="anchor_LDOCE6_exa_bre_4_ldoce6exa_00717"><a onclick="document.getElementById('LDOCE6_exa_bre_4_ldoce6exa_00717').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Students are recommended to make an appointment with a counselor.</span></span><span class="thesref"><span class="thesaurus">►</span> see <span class="thesaurus">thesaurus</span> at <a><span class="refhwd">advise</span></a></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_recommend_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="def">to say that something or someone is good, or suggest them for a particular purpose or job</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-000761151"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-000761151').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I recommend the butter chicken – it’s delicious.</span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-000761152"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-000761152').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Can you recommend a good lawyer?</span><span class="gramexa"><span class="propform">recommend something to somebody</span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-000761155"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-000761155').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I recommend this book to anyone with an interest in chemistry.</span></span><span class="gramexa"><span class="propform">recommend something for something/somebody</span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-000761158"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000761158').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Which type of oil do you recommend for my car?</span></span><span class="gramexa"><span class="propform">recommend somebody for something</span><span class="example" id="anchor_LDOCE6_exa_bre_d_p008-000761161"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-000761161').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I have decided to recommend you for the directorship.</span></span><span class="colloexa"><span class="collo">highly/thoroughly recommend</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-000761164"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-000761164').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The hotel is highly recommended.</span></span><span class="thesref"><span class="thesaurus">►</span> see <span class="thesaurus">thesaurus</span> at <a><span class="refhwd">suggest</span></a></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_recommend_s3"></a><span class="sense newline"><span class="sensenum">3</span><a id="6504937708254a64972bf4d4ccdfcc9d_recommend_s4"></a><span class="lexunit">something has much/little/nothing to recommend it</span><span class="def"> used to say that something has many, few, or no good qualities</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-000761169"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-000761169').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The town itself has little to recommend it.</span></span><span class="tail"><span class="grambox"><span class="heading">GRAMMAR</span><span class="heading newline">Patterns with recommend (meaning 1)</span><span class="expl"><span class="neutral">• </span>In everyday English, you <span class="expr">recommend that</span> someone <span class="expr">does</span> something: <span class="example"><span class="neutral">· </span>I recommend that she speaks to a lawyer.</span> ‘That’ can be omitted: <span class="example"><span class="neutral">· </span>I recommend she speaks to a lawyer</span></span><span class="expl"><span class="neutral">• </span>In more formal English, you <span class="expr">recommend that</span> someone <span class="expr">do</span> something, using the base form of the verb <span class="gloss"><span class="neutral"> (=</span>the infinitive without ‘to’<span class="neutral">)</span></span>: <span class="example"><span class="neutral">· </span>I recommend that she speak to a lawyer.</span> <span class="cross"><span class="cross">✗</span></span>Don’t say: <span class="badexa">I recommend her to speak to a lawyer.</span></span><span class="expl"><span class="neutral">• </span>In more formal English, you also use the base form of the verb when talking about the past: <span class="example"><span class="neutral">· </span>I recommended that she speak to a lawyer.</span></span><span class="expl"><span class="neutral">• </span>You <span class="expr">recommend</span> that someone <span class="expr">should do</span> something: <span class="example"><span class="neutral">· </span>I recommended that they should get insurance.</span></span><span class="expl"><span class="neutral">• </span>You can say <span class="expr">it is recommended that</span> someone <span class="expr">(should) do</span> something: <span class="example"><span class="neutral">· </span>It is recommended that everyone (should) take the test.</span></span><span class="heading newline">Patterns with recommend (meaning 2)</span><span class="expl last"><span class="neutral">• </span>You <span class="expr">recommend</span> something <span class="expr">to</span> someone: <span class="example"><span class="neutral">· </span>I recommended a book to her.</span> <span class="cross"><span class="cross">✗</span></span>Don’t say: <span class="badexa">I recommended her a book.</span><span class="gramrefcont"><span>Grammar guide ‒ </span>VERBS</span></span></span><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="heading">– Meaning 2</span><span class="section last"><span class="secheading">adverbs</span><span class="collocate"><span class="colloc collo">highly recommend</span><span class="example"><span class="neutral">· </span>This book is highly recommended by those who have used it.</span></span><span class="collocate"><span class="colloc collo">heartily recommend</span><span class="collgloss"><span class="neutral"> (=</span>strongly and enthusiastically<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I heartily recommend the cheese sandwiches.</span></span><span class="collocate"><span class="colloc collo">thoroughly recommend</span><span class="example"><span class="neutral">· </span>Overall I can thoroughly recommend this film to anyone with an interest in space travel.</span></span><span class="collocate"><span class="colloc collo">definitely/certainly recommend</span><span class="example"><span class="neutral">· </span>We would definitely recommend these books to students in fourth to sixth grade.</span></span><span class="collocate"><span class="colloc collo">personally recommend</span><span class="example"><span class="neutral">· </span>Are there any hair colouring products you can personally recommend?</span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_recommend_s5"></a><span class="thesbox"><span class="heading">THESAURUS</span><span class="section last"><span class="exponent"><span class="exp display">recommend</span><span class="def"> to advise someone to do something, especially when you have special knowledge of a situation or subject</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I would strongly recommend buying a good quality bicycle rather than a cheap one.</span><span class="example"><span class="neutral">· </span>The report recommended stricter supervision of the trade in live animals.</span></span><span class="exponent"><span class="exp display">tell</span><span class="def"> to tell someone that you think they should do something, especially in order to avoid problems</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We’ve been told that we should start revising early.</span><span class="example"><span class="neutral">· </span>I told you not to drink the water here.</span></span><span class="exponent"><span class="exp display">urge</span><span class="def"> to strongly advise someone to do something because you think it is very important</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Her doctor has urged her to see a specialist.</span><span class="example"><span class="neutral">· </span>Police are urging drivers not to come into London this weekend.</span></span><span class="exponent"><span class="exp display">advocate</span><span class="def"> to say publicly that something should be done, often something that a lot of people disagree about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In 1984, he advocated the use of force against Nicaragua.</span><span class="example"><span class="neutral">· </span>I am surprised that dentists don’t advocate the use of fluoride tablets.</span></span><span class="exponent"><span class="exp display">endorse</span><span class="def"> to say in an advertisement that you like a product and think that people should use it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A lot of companies use sports stars to endorse their products.</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_d_recommend0205" preload="none" src="LDOCE6_hwd_bre_d_recommend0205.mp3"></audio><audio id="LDOCE6_hwd_ame_d_recommend" preload="none" src="LDOCE6_hwd_ame_d_recommend.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001758018" preload="none" src="LDOCE6_exa_bre_9_p008-001758018.mp3"></audio><audio id="LDOCE6_exa_bre_d_p008-001620902" preload="none" src="LDOCE6_exa_bre_d_p008-001620902.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-000761138" preload="none" src="LDOCE6_exa_bre_f_p008-000761138.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-001771360" preload="none" src="LDOCE6_exa_bre_6_p008-001771360.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001614053" preload="none" src="LDOCE6_exa_bre_5_p008-001614053.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-000761135" preload="none" src="LDOCE6_exa_bre_1_p008-000761135.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001614045" preload="none" src="LDOCE6_exa_bre_4_p008-001614045.mp3"></audio><audio id="LDOCE6_exa_bre_4_ldoce6exa_00717" preload="none" src="LDOCE6_exa_bre_4_ldoce6exa_00717.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-000761151" preload="none" src="LDOCE6_exa_bre_b_p008-000761151.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-000761152" preload="none" src="LDOCE6_exa_bre_3_p008-000761152.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-000761155" preload="none" src="LDOCE6_exa_bre_3_p008-000761155.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-000761158" preload="none" src="LDOCE6_exa_bre_2_p008-000761158.mp3"></audio><audio id="LDOCE6_exa_bre_d_p008-000761161" preload="none" src="LDOCE6_exa_bre_d_p008-000761161.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-000761164" preload="none" src="LDOCE6_exa_bre_1_p008-000761164.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-000761169" preload="none" src="LDOCE6_exa_bre_6_p008-000761169.mp3"></audio>`;
