var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_refuse_1"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_refuse_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_refuse_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_refuse_1"></a><span class="entry"><span class="entryhead"><span class="hwd">refuse</span><span class="hyphenation frequent">re<span class="hs0"></span>fuse</span><span class="homnum frequent">1</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">rɪˈfjuːz</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S2</span> <span class="freq">W1</span><span class="pos"> verb</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_3_brelasderefuse').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_3_l3refuse').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">refuse</span><span class="homnum">1</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1300-1400</span> <span class="etymlang">Old French</span> <span class="etymorigin">refuser</span>, from <span class="etymlang">Latin</span> <span class="etymorigin">refundere</span> <span class="etymtran"> ‘to pour back’</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">refuse</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">refuse</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">refuses</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">refused</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">refused</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">refused</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">refused</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">refuse</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">refused</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">refusing</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">refusing</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">refusing</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">refusing</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">refusing</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">refusing</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">refusing</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">refusing</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">refusing</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">refusing</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>He flatly <span class="nodeword">refuses</span> any offers of financial help.</li><li>He never <span class="nodeword">refuses</span> a drink, does he?</li><li>He tried to persuade her to come with him, but she <span class="nodeword">refused</span>.</li><li>He was unable to attend the meeting in Moscow, because the Russian authorities had <span class="nodeword">refused</span> him a visa.</li><li>I asked Stevie if she would help us, but she <span class="nodeword">refused</span>.</li><li>If they <span class="nodeword">refuse</span> to leave, call the police.</li><li>Judge Eyck <span class="nodeword">refused</span> his request for bail.</li><li>Mother flatly <span class="nodeword">refused</span> to see the doctor.</li><li>Ms. Knight <span class="nodeword">refused</span> to accept the manager's apology.</li><li>Over 2,000 applications for political asylum were <span class="nodeword">refused</span> last year.</li><li>Some banks are threatening to <span class="nodeword">refuse</span> loans to anyone who cannot provide suitable guarantees.</li><li>Sutton <span class="nodeword">refused</span> food in protest against conditions in the prison.</li><li>The church <span class="nodeword">refused</span> to give legitimacy to the new state.</li><li>The city is <span class="nodeword">refusing</span> contracts to firms that do not practice an equal opportunities policy.</li><li>The offer was so good how could I <span class="nodeword">refuse</span>?</li><li>Under the law, doctors cannot <span class="nodeword">refuse</span> patients access to their own medical records.</li><li>You wouldn't <span class="nodeword">refuse</span> an old friend a favour, would you?</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>It is understood that a number of applications for advance clearance under s 707 for such arrangements have been <span class="nodeword">refused</span>.</li><li>Players were pressing and self-doubt was evident, especially when shots <span class="nodeword">refused</span> to drop.</li><li>She <span class="nodeword">refused</span> his advances and confounded a multitude of scholars assembled by him to overcome her scruples.</li><li>The 1976 Act also makes a radical change as regards the grounds for granting or <span class="nodeword">refusing</span> an application for a licence.</li><li>The charge was dropped when Lewinksy <span class="nodeword">refused</span> to testify.</li><li>The committee was forcing Dozoretz to attend Thursday's hearing to publicly <span class="nodeword">refuse</span> to testify.</li><li>The following day, doctors at Great Ormond Street Hospital <span class="nodeword">refused</span> to operate because the parents had not given consent.</li><li>Via his lawyer, Sam <span class="nodeword">refused</span> to give Clare money because he didn't want a divorce.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse</span></span><div class="content"><span class="def"> to say firmly that you will not do something that someone has asked you to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I asked the bank for a loan, but they refused.</span><span class="example"><span class="neutral">· </span>When they refused to leave, we had to call the police.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say no</span></span><div class="content"><span class="registerlab"> spoken</span><span class="def"> to say that you will not do something when someone asks you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They asked me so nicely that I couldn’t really say no.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">turn somebody/something down</span></span><div class="content"><span class="def"> to refuse to accept an offer or invitation, or a formal request</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They offered me the job but I turned it down.</span><span class="example"><span class="neutral">· </span>The board turned down a request for $25,000 to sponsor an art exhibition.</span><span class="example"><span class="neutral">· </span>I’ve already been turned down by three colleges.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">reject</span></span><div class="content"><span class="def"> to refuse to accept an idea, offer, suggestion, or plan</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They rejected the idea because it would cost too much money.</span><span class="example"><span class="neutral">· </span>The Senate rejected a proposal to limit the program to two years.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">decline</span></span><div class="content"><span class="registerlab"> formal</span><span class="def"> to politely refuse to accept an offer or invitation, or refuse to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She has declined all offers of help.</span><span class="example"><span class="neutral">· </span>A palace spokesman declined to comment on the rumours.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">deny</span></span><div class="content"><span class="def"> to refuse to allow someone to do something or enter somewhere</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They were denied permission to publish the book.</span><span class="example"><span class="neutral">· </span>He was denied access to the US.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">veto</span></span><div class="content"><span class="def"> to officially refuse to allow a law or plan, or to refuse to accept someone’s suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Congress vetoed the bill.</span><span class="example"><span class="neutral">· </span>The suggestion was quickly vetoed by the other members of the team.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">disallow</span></span><div class="content"><span class="def"> to officially refuse to accept something because someone has broken the rules, or not done it in the correct way</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The goal was disallowed by the referee.</span><span class="example"><span class="neutral">· </span>The court decided to disallow his evidence.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">rebuff</span></span><div class="content"><span class="registerlab"> formal</span><span class="def"> to refuse to accept someone’s offer, request, or suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The company raised its offer to $6 billion, but was rebuffed.</span><span class="example"><span class="neutral">· </span>He was politely rebuffed when he suggested holding the show in Dublin.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">give somebody/something the thumbs down</span></span><div class="content"><span class="registerlab"> informal</span><span class="def"> to refuse to allow or accept a plan or suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The plan was given the thumbs down by the local authority.</span><span class="example"><span class="neutral">· </span>They gave us the thumbs down.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to say that you will not do something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse</span></span><div class="content"><span class="def"> to tell someone firmly that you will not do something they asked you to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I asked Stevie if she would help us, but she refused.</span><span class="thespropform">refuse to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If they refuse to leave, call the police.</span><span class="example"><span class="neutral">· </span>The church refused to give legitimacy to the new state.</span><span class="thespropform">flatly refuse</span><span class="gloss"><span class="neutral"> (=</span>refuse without explanation in a way that seems unreasonable<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mother flatly refused to see the doctor.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refusal</span></span><div class="content"><span class="def"> when someone refuses to do something that they have been asked to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was upset by her refusal.</span><span class="example"><span class="neutral">· </span>His request for a bigger room met with a blunt refusal.</span><span class="thespropform">refusal to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She must understand the consequences of her refusal to accept medical treatment.</span><span class="example"><span class="neutral">· </span>Refusal to do military service was a criminal offence.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say no</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> to tell someone that you will not do what they asked you to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I asked Dad to lend me some money, but he said no.</span><span class="thespropform">say no no</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They asked me so nicely, I couldn't really say no to them.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">will not/won't</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> if someone will not or won't do something that they have been asked to do or told to do, they are determined not to do it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He won't do anything that I ask him to do.</span><span class="example"><span class="neutral">· </span>I won't sign the contract unless they offer me more money.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not be prepared to do something</span></span><div class="content"><span class="def"> to refuse to do something - use this when you think it is wrong or unfair that anyone should expect you to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not prepared to wait any longer.</span><span class="example"><span class="neutral">· </span>The landlord says that he is not prepared to pay for the repairs.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">draw the line</span></span><div class="content"><span class="def"> to allow certain things, but feel that you must refuse when a particular point is reached</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Our rules about time-keeping are fairly flexible, but we have to draw the line somewhere.</span><span class="thespropform">draw the line at</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I don't mind your brother coming to stay, but I draw the line at him moving in!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">decline</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> to politely refuse to do what someone has asked you to do, especially when they have asked for your opinion or asked for information</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>When asked to comment on details of the agreement, the President declined.</span><span class="thespropform">decline to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A security officer at the factory, who declined to give his name, said he had seen two men leave the building.</span><span class="example"><span class="neutral">· </span>I asked Mr Hughes if he was satisfied with the jury's verdict but he declined to comment.</span></div></span></span><span class="section"><span class="secheading">what you say when you refuse to do something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">no</span></span><div class="content"> <span class="example"><span class="neutral">· </span>"Can you come and help move these boxes?" "No, sorry, I'm busy."</span><span class="example"><span class="neutral">· </span>"You're sick - you need to stay in bed." "No, I have to be at this meeting."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">no way</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to firmly refuse to do something and say that nothing will change your mind</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Why don't you phone him and apologize.'' "No way -- I haven't done anything wrong!''</span><span class="example"><span class="neutral">· </span>No way am I going to look after all three children on my own!</span><span class="example"><span class="neutral">· </span>There's no way I'm going to put up with rudeness like that!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">forget it</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to tell someone that there is no chance at all of you doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Maybe things will sort themselves out in time." "Forget it, the relationship's over."</span><span class="example"><span class="neutral">· </span>As for the idea of going on holiday together, forget it!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not likely</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British spoken</span><span class="def"> use this when you think that what someone has asked you to do is stupid or dangerous</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Are you coming to that lecture this evening?" "Not likely - there's free beer at the Queen's Arms tonight!"</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">and that's flat</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British spoken</span><span class="variant"> <span class="lexvar">that's final</span><span class="neutral"> </span><span class="registerlab">American spoken</span></span><span class="def"> used after you have refused to do something, in order to show that you will not change your mind or discuss your decision</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not coming, and that's flat. Now stop bothering me.</span><span class="example"><span class="neutral">· </span>There's no way I'm helping Ted move again, and that's final.</span></div></span></span><span class="section"><span class="secheading">to refuse to give someone something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>refuse somebody something</span> <span class="example"><span class="neutral">· </span>He was unable to attend the meeting in Moscow, because the Russian authorities had refused him a visa.</span><span class="example"><span class="neutral">· </span>You wouldn't refuse an old friend a favour, would you?</span><span class="thespropform">refuse something to somebody</span> <span class="example"><span class="neutral">· </span>Some banks are threatening to refuse loans to anyone who cannot provide suitable guarantees.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">deny</span></span><div class="content"><span class="def"> if someone in authority denies someone something that they want, for example money, a right, or the chance to do something, they refuse to give it to them</span><span class="thespropform"><span class="neutral">: </span>deny somebody something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His parents denied him the opportunity to go to university.</span><span class="thespropform">be denied something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The scientists have been denied the necessary funds for their research program.</span><span class="example"><span class="neutral">· </span>Up to 450 disaster victims were denied compensation by their insurers.</span><span class="example"><span class="neutral">· </span>She has been denied the right to appeal to the Supreme Court.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">withhold</span></span><div class="content"><span class="def"> to refuse to give someone something that they want - use this especially in official or legal contexts</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The new law allows you to withhold payment if you think a bill is incorrect.</span><span class="thespropform">withhold something from somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Doctors do not have the right to withhold necessary treatment from a patient.</span></div></span></span><span class="section"><span class="secheading">to not let someone enter</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">keep out</span></span><div class="content"><span class="def"> to prevent someone from entering a place, for example by locking doors and windows, or building fences</span><span class="thespropform"><span class="neutral">: </span>keep out somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He bought a new security system to keep out intruders.</span><span class="thespropform">keep somebody out</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Family members can go in to visit him, but we need to keep everyone else out.</span><span class="thespropform">keep somebody out of something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Try to keep Ed out of the bedroom while I finish wrapping his present.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">shut out</span></span><div class="content"><span class="def"> to shut a door, window etc in order to prevent someone from entering, especially because they would be interrupting you or annoying you</span><span class="thespropform"><span class="neutral">: </span>shut out somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He slammed the door, shutting out the dogs.</span><span class="thespropform">shut somebody out (of something)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>John shut everybody out of the kitchen so that he could prepare his grand surprise.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">lock out</span></span><div class="content"><span class="def"> to stop someone from entering a place by locking a door</span><span class="thespropform"><span class="neutral">: </span>lock somebody out/lock out somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Her husband threw her out of the trailer without shoes or clothes and locked her out.</span><span class="thespropform">lock somebody out of something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I can't believe I locked myself out of the house again.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse entry</span></span><div class="content"><span class="def"> to refuse to allow someone to enter a country or a public place</span><span class="thespropform"><span class="neutral">: </span>refuse somebody entry</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Immigration officials refused her entry because they thought she was planning to stay.</span><span class="thespropform">refuse entry to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The management reserves the right to refuse entry to anyone who is improperly dressed.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">turn away</span></span><div class="content"><span class="def"> to refuse to let someone into a place where a public event is happening, especially because it is full</span><span class="thespropform"><span class="neutral">: </span>turn somebody away/turn away somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Hundreds of disappointed fans were turned away at the gates.</span><span class="example"><span class="neutral">· </span>The club's so popular, we have to turn people away every night.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">bar/ban</span></span><div class="content"><span class="def"> to officially forbid someone from entering a building or area, especially because they have caused trouble or because it is dangerous for them to go there</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The tavern banned Ted for starting a fight.</span><span class="thespropform">bar/ban somebody from something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We've had to bar visitors from the garden because some of the pathways aren't safe.</span><span class="thespropform">bar/ban somebody for life</span><span class="gloss"><span class="neutral"> (=</span>forbidden from entering for the rest of your life<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>After the incident at the country club, Chuck was banned for life.</span></div></span></span><span class="section"><span class="secheading">to reject an offer or suggestion</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">reject</span></span><div class="content"><span class="def"> to say no very firmly to an offer or suggestion. Reject is more formal than not accept and say no</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Lauren rejected her parents' offer of financial help.</span><span class="example"><span class="neutral">· </span>She rejected the idea that she should sue him.</span><span class="example"><span class="neutral">· </span>The Secretary of State offered his resignation, which the President promptly rejected.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not accept</span></span><div class="content"><span class="def"> to say no to an offer or invitation, especially because you think it would not be right to accept it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She's given us all this stuff and she won't accept any money for it.</span><span class="example"><span class="neutral">· </span>I decided not to accept their invitation.</span><span class="example"><span class="neutral">· </span>Laney wouldn't accept what he considered an insulting pay offer.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say no</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> not accept an offer or suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I asked him if he wanted a drink, but he said no.</span><span class="example"><span class="neutral">· </span>I'll offer to buy it from her, but I expect she'll say no.</span><span class="thespropform">say no to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mrs. Hill, still mentally alert, said no to any suggestions of further operations.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse</span></span><div class="content"><span class="def"> to say you do not want something that you have been offered</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The offer was so good how could I refuse?</span><span class="example"><span class="neutral">· </span>He never refuses a drink, does he?</span><span class="thespropform">refuse to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ms. Knight refused to accept the manager's apology.</span><span class="thespropform">flatly refuse</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He flatly refuses any offers of financial help.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">turn down</span></span><div class="content"><span class="def"> to say no to an offer - use this especially when someone refuses a good offer or opportunity, and this is surprising</span><span class="thespropform"><span class="neutral">: </span>turn somebody/something down</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They offered her a really good job, but she turned it down.</span><span class="example"><span class="neutral">· </span>He said he'd help her with her training, but she turned him down.</span><span class="thespropform">turn down somebody/something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If you turn down the opportunity to go to college, you'll always regret it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">decline</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> to say no politely when someone invites you to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mr Casey regrets that he will have to decline your kind invitation owing to a prior engagement.</span><span class="example"><span class="neutral">· </span>The bishop was invited to attend the opening ceremony, but he declined.</span><span class="thespropform">decline to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The Prime Minister was asked for his opinion but declined to comment.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I'll take a rain check</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to tell someone that you cannot accept their invitation at the time they have suggested, but would like to do it at some time in the future</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How about dinner tonight?" "Sorry -- I'll have to take a rain check on that."</span></div></span></span><span class="section last"><span class="secheading">to officially reject a request or suggestion</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">reject</span></span><div class="content"><span class="def"> to use your official authority to formally refuse a request or suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Judge Gifford rejected the defense's request.</span><span class="example"><span class="neutral">· </span>The immigration authorities have rejected his application for refugee status.</span><span class="example"><span class="neutral">· </span>It was predicted that the Senate would reject the bill by about 60 to 40.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">throw out</span></span><div class="content"><span class="def"> if a parliament, a council, or other official political organization throws out a plan or suggestion, they refuse to accept it or make it legal, especially after voting on it</span><span class="thespropform"><span class="neutral">: </span>throw out something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Local councillors threw out proposals for the building of a new stadium.</span><span class="thespropform">throw something out</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The House passed the bill, but the Senate threw it out.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">turn down</span></span><div class="content"><span class="def"> to refuse to accept a request or to give someone permission to do something, especially when the request is a reasonable one</span><span class="thespropform"><span class="neutral">: </span>turn down something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Their application to build a new extension has been turned down by the planning authority.</span><span class="thespropform">turn something down</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We put in a request for a little extra time for us to finish the project, but the board turned it down.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse</span></span><div class="content"><span class="def"> to officially decide that someone cannot have something they have asked for, or cannot do something they want to do</span><span class="thespropform"><span class="neutral">: </span>refuse an application/request/demand etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Judge Eyck refused his request for bail.</span><span class="example"><span class="neutral">· </span>Over 2,000 applications for political asylum were refused last year.</span><span class="thespropform">refuse somebody something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Under the law, doctors cannot refuse patients access to their own medical records.</span><span class="thespropform">refuse something to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The city is refusing contracts to firms that do not practice an equal opportunities policy.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say no</span></span><div class="content"><span class="def"> to officially refuse to accept a request, suggestion, or bad situation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Employees have repeatedly requested child care facilities, but the company has always said no.</span><span class="thespropform">say no to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We're hoping the government will at last say no to low wages and poverty.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">vote against/vote no</span></span><div class="content"><span class="def"> to refuse to accept a plan, proposal, or new law by voting</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The majority of union members voted against further industrial action.</span><span class="example"><span class="neutral">· </span>Homeowners voted against new bonds and higher taxes.</span><span class="example"><span class="neutral">· </span>Only Councilwoman Shirley Lanion voted no.</span><span class="thespropform">vote no on something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I urge you to vote no on Measure A.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">veto</span></span><div class="content"><span class="def"> to use your position of power to refuse to allow something to happen, especially something that other people, organizations, or countries have agreed</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>European plans to deregulate air fares were vetoed by Spain.</span><span class="example"><span class="neutral">· </span>Requests to take foster children abroad are often vetoed by the biological parent.</span><span class="example"><span class="neutral">· </span>The governor vetoed a bill that would have given some much-needed money to public libraries.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">give something the thumbs down</span></span><div class="content"><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">turn thumbs down on something</span><span class="neutral"> </span><span class="registerlab">American</span></span><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> to reject a plan or suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The commission wisely gave the golf course proposal the thumbs down.</span><span class="example"><span class="neutral">· </span>The city council turned thumbs down on Marison's new proposal, citing potential parking problems.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="section"><span class="secheading">adverbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">stubbornly refuse</span></span><div class="content"><span class="example"><span class="neutral">· </span>He stubbornly refused to pay the fine.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">adamantly refuse</span></span><div class="content"><span class="registerlab"> formal</span><span class="collgloss"><span class="neutral"> (=</span>in a determined way<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>They are still adamantly refusing to release the data to the general public.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">steadfastly refuse</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>refuse, and continue to refuse<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>She steadfastly refused to marry him.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">consistently refuse</span></span><div class="content"><span class="example"><span class="neutral">· </span>Politicians have consistently refused to listen to us.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">flatly refuse</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>in a firm and definite way<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He flatly refused to tell me anything.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">simply refuse</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>used for emphasis<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Some children simply refuse to do what they’re told.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse point-blank</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>directly and without explanation<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I refused point-blank to let him in.</span></div></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">somebody can hardly refuse</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>it would be unreasonable or very difficult to refuse<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Going out was the last thing I wanted to do, but I could hardly refuse.</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refused admission</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001646321').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The young men tried to enter a nightclub but were <span class="colloinexa">refused admission</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse to admit something</span></span><div class="content"><span class="example"><span class="neutral">· </span>He refused to admit that it was his fault.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/reject/turn down an application</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>say no to an application<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Their planning application was rejected because of a lack of parking facilities.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">be refused bail</span></span><div class="content"><span class="example"><span class="neutral">· </span>Relatively few defendants are refused bail.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">categorically deny/refuse etc something</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-000897707').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He has categorically denied his guilt all along.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">tax/ticket/debt/refuse collector</span></span><div class="content"></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse (your) consent</span></span><div class="content"><span class="example"><span class="neutral">· </span>When the firm applied for consent to build on the site, it was refused.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse to co-operate</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001686166').play();"><img src="LDOCE6_img_spkr_g.png"/></a> If you <span class="colloinexa">refuse to co-operate</span>, I’ll kill you.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse somebody credit</span></span><div class="content"><span class="example"><span class="neutral">· </span>You may be refused credit if you have a bad financial record.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refused to disclose</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001830989').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He <span class="colloinexa">refused to disclose</span> the identity of the politician.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse disposal</span></span><div class="content"><span class="registerlab"> formal</span><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> rubbish disposal</span><span class="geo"> British English</span><span class="lexvar"><span class="neutral">, </span>garbage disposal</span><span class="geo"> American English</span><span class="neutral">)</span></span><span class="collgloss"><span class="neutral"> (=</span>getting rid of things people throw out of houses, shops etc<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Refuse disposal is the responsibility of county councils.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/deny (somebody) entry</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>stop someone entering<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He was refused entry to the club because he was wearing trainers.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/turn down an invitation</span></span><div class="content"><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> decline an invitation</span><span class="registerlab"> formal</span><span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>She turned down an invitation to take part in a televised debate.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">be refused membership</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>not be accepted as a member<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>She was refused membership of the club because she was a woman.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse to obey</span></span><div class="content"><span class="example"><span class="neutral">· </span>Many people felt the law was unfair, and refused to obey it.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">turn down/refuse/reject/decline an offer</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>say no to it<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>She declined the offer of a lift.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/deny (somebody) permission</span></span><div class="content"><span class="example"><span class="neutral">· </span>Betty's father refused her permission to attend the dance.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refused point-blank</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001634687').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He <span class="colloinexa">refused point-blank</span> to identify his accomplices.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/reject a request</span></span><div class="content"><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> turn down somebody’s request</span><span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He rejected their request for a meeting.</span><span class="example"><span class="neutral">· </span>This request was turned down because of the cost.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refused to talk</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-000614028').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Even under torture, Maskell <span class="colloinexa">refused to talk</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/deny somebody a visa</span></span><div class="content"><span class="example"><span class="neutral">· </span>The Lebanese embassy refused him a visa.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">still</span></span><div class="content"><span class="example"><span class="neutral">· </span>It <span class="colloinexa">still refuses</span> to join the United Nations, though it is happy to welcome its free-spending bureaucracies in Geneva.</span><span class="example"><span class="neutral">· </span>Imam Malik <span class="colloinexa">still refused</span> to take back his words.</span><span class="example"><span class="neutral">· </span>It <span class="colloinexa">still refuses</span> to join peacekeeping operations in less harmonious countries.</span><span class="example"><span class="neutral">· </span>Mulholland, of course, knew this, but <span class="colloinexa">still refused</span> to build the dam at Long Valley.</span><span class="example"><span class="neutral">· </span>Campaigners against the arms trade also voiced concern that the Government is <span class="colloinexa">still refusing</span> to allow Parliament proper scrutiny of exports.</span><span class="example"><span class="neutral">· </span>And immigration is <span class="colloinexa">still refusing</span> to see that this man deserves consideration for his sacrifice.</span><span class="example"><span class="neutral">· </span>I do about three hundred sit-ups a day and it <span class="colloinexa">still refuses</span> to firm up, but what else can I do?</span><span class="example"><span class="neutral">· </span>Two kids <span class="colloinexa">still refused</span> to write unless I helped them right then and there.</span></div></span></span><span class="section last"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">offer</span></span><div class="content"><span class="example"><span class="neutral">· </span>Maybe he's moonlighting on an <span class="colloinexa">offer</span> he couldn't <span class="colloinexa">refuse</span>.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">offer</span> of food was <span class="colloinexa">refused</span> but food came anyway.</span><span class="example"><span class="neutral">· </span>Please contact the Office if you are interested-no reasonable <span class="colloinexa">offer</span> will be <span class="colloinexa">refused</span>!</span><span class="example"><span class="neutral">· </span>As the bribe would hardly benefit a dead man, not surprisingly the <span class="colloinexa">offer</span> was <span class="colloinexa">refused</span>.</span><span class="example"><span class="neutral">· </span>It was so hot and dry, another party on Gimer made us an <span class="colloinexa">offer</span> we couldn't <span class="colloinexa">refuse</span>.</span><span class="example"><span class="neutral">· </span>And make him an <span class="colloinexa">offer</span> he couldn't <span class="colloinexa">refuse</span>.</span><span class="example"><span class="neutral">· </span>Stratton was presented with an <span class="colloinexa">offer</span> he could hardly <span class="colloinexa">refuse</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">permission</span></span><div class="content"><span class="example"><span class="neutral">· </span>Had he <span class="colloinexa">refused</span> her <span class="colloinexa">permission</span> to marry?</span><span class="example"><span class="neutral">· </span>However, one month after this deadline Judge Gilbert Thiel in Nancy <span class="colloinexa">refused permission</span> for the objects to leave the city.</span><span class="example"><span class="neutral">· </span>He faced up to the notorious Chelsea Boot Boys with an electrified fence but was <span class="colloinexa">refused permission</span> to switch it on.</span><span class="example"><span class="neutral">· </span>Members of the economic development and planning subcommittee voted to <span class="colloinexa">refuse</span> planning <span class="colloinexa">permission</span> after a site visit yesterday.</span><span class="example"><span class="neutral">· </span>The reasons for the Council's decision to <span class="colloinexa">refuse permission</span> for the development are set out in the attached schedule.</span><span class="example"><span class="neutral">· </span>In another case, the committee has <span class="colloinexa">refused permission</span> for health authorities to link their computer files with personal tax records.</span><span class="example"><span class="neutral">· </span>The council says a holy coal house doesn't qualify as a home improvement and has <span class="colloinexa">refused</span> planning <span class="colloinexa">permission</span>.</span><span class="example"><span class="neutral">· </span>Furthermore, an applicant who had been <span class="colloinexa">refused</span> planning <span class="colloinexa">permission</span> could appeal to the ministry.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">request</span></span><div class="content"><span class="example"><span class="neutral">· </span>The driver <span class="colloinexa">refused</span> her <span class="colloinexa">requests</span> for his number, and dumped her at the next stop.</span><span class="example"><span class="neutral">· </span>A judge <span class="colloinexa">refused</span> last week a <span class="colloinexa">request</span> by rival insurers to remove the Pennsylvania regulator from ruling on the case.</span><span class="example"><span class="neutral">· </span>It has <span class="colloinexa">refused</span> the Cree's <span class="colloinexa">request</span> for an undertaking not to commence logging until their outstanding land claim has been settled.</span><span class="example"><span class="neutral">· </span>To date, the Planning Department has <span class="colloinexa">refused</span> the <span class="colloinexa">request</span>, choosing instead to prepare a brief addendum.</span><span class="example"><span class="neutral">· </span>The small number of <span class="colloinexa">refused requests</span> may appear unusual.</span><span class="example"><span class="neutral">· </span>But the judge <span class="colloinexa">refused</span> a prosecution <span class="colloinexa">request</span> to keep the accused rapist in jail by increasing his bail to $ 1 million.</span><span class="example"><span class="neutral">· </span>Technically she might also <span class="colloinexa">refuse</span> a <span class="colloinexa">request</span> for a dissolution, although it is hard to imagine her ever doing so.</span></div></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_refuse_1_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="gram">[intransitive]</span><span class="def"> to say firmly that you will not do something that someone has asked you to do</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-000769291"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-000769291').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She asked him to leave, but he refused.</span><span class="gramexa"><span class="propform">refuse to do something</span><span class="example" id="anchor_LDOCE6_exa_bre_5_ldoce6exa_00721"><a onclick="document.getElementById('LDOCE6_exa_bre_5_ldoce6exa_00721').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The chairman refused to answer any more questions.</span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-000769294"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-000769294').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I absolutely refuse to take part in anything illegal.</span></span><span class="colloexa"><span class="collo">flatly refuse/refuse point-blank (to do something)</span><span class="gloss"><span class="neutral"> (=</span>refuse immediately and directly without giving a reason<span class="neutral">)</span></span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-000769298"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-000769298').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Mom flatly refused to go back into the hospital.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_refuse_1_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="gram">[intransitive, transitive]</span><span class="def"> to say no to something that you have been offered</span><span class="syn"> <span class="synopp">SYN</span>  turn down</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-000769308"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-000769308').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She refused a second piece of cake.</span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001661733"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001661733').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The offer seemed <span class="colloinexa">too good to refuse</span>.</span><span class="example" id="anchor_LDOCE6_exa_bre_6_ldoce6exa_00722"><a onclick="document.getElementById('LDOCE6_exa_bre_6_ldoce6exa_00722').play();"><img src="LDOCE6_img_spkr_g.png"/></a> When they offered all that money, I <span class="colloinexa">could hardly refuse</span>, could I? <span class="gloss"><span class="neutral"> (=</span>it was very unlikely that you would refuse<span class="neutral">)</span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_refuse_1_s3"></a><span class="sense newline"><span class="sensenum">3</span><span class="gram">[transitive]</span><span class="def"> to not give or allow someone something that they want, especially when they have asked for it officially</span><span class="gramexa"><span class="propform">refuse somebody something</span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-001661737"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001661737').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She was refused a work permit.</span></span></span><span class="tail"><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="section"><span class="secheading">adverbs</span><span class="collocate"><span class="colloc collo">stubbornly refuse</span><span class="example"><span class="neutral">· </span>He stubbornly refused to pay the fine.</span></span><span class="collocate"><span class="colloc collo">adamantly refuse</span><span class="registerlab"> formal</span><span class="collgloss"><span class="neutral"> (=</span>in a determined way<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>They are still adamantly refusing to release the data to the general public.</span></span><span class="collocate"><span class="colloc collo">steadfastly refuse</span><span class="collgloss"><span class="neutral"> (=</span>refuse, and continue to refuse<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>She steadfastly refused to marry him.</span></span><span class="collocate"><span class="colloc collo">consistently refuse</span><span class="example"><span class="neutral">· </span>Politicians have consistently refused to listen to us.</span></span><span class="collocate"><span class="colloc collo">flatly refuse</span><span class="collgloss"><span class="neutral"> (=</span>in a firm and definite way<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He flatly refused to tell me anything.</span></span><span class="collocate"><span class="colloc collo">simply refuse</span><span class="collgloss"><span class="neutral"> (=</span>used for emphasis<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Some children simply refuse to do what they’re told.</span></span><span class="collocate"><span class="colloc collo">refuse point-blank</span><span class="collgloss"><span class="neutral"> (=</span>directly and without explanation<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I refused point-blank to let him in.</span></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="colloc collo">somebody can hardly refuse</span><span class="collgloss"><span class="neutral"> (=</span>it would be unreasonable or very difficult to refuse<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Going out was the last thing I wanted to do, but I could hardly refuse.</span></span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_refuse_1_s4"></a><span class="thesbox"><span class="heading">THESAURUS</span><span class="section last"><span class="exponent"><span class="exp display">refuse</span><span class="def"> to say firmly that you will not do something that someone has asked you to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I asked the bank for a loan, but they refused.</span><span class="example"><span class="neutral">· </span>When they refused to leave, we had to call the police.</span></span><span class="exponent"><span class="exp display">say no</span><span class="registerlab"> spoken</span><span class="def"> to say that you will not do something when someone asks you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They asked me so nicely that I couldn’t really say no.</span></span><span class="exponent"><span class="exp display">turn somebody/something down</span><span class="def"> to refuse to accept an offer or invitation, or a formal request</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They offered me the job but I turned it down.</span><span class="example"><span class="neutral">· </span>The board turned down a request for $25,000 to sponsor an art exhibition.</span><span class="example"><span class="neutral">· </span>I’ve already been turned down by three colleges.</span></span><span class="exponent"><span class="exp display">reject</span><span class="def"> to refuse to accept an idea, offer, suggestion, or plan</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They rejected the idea because it would cost too much money.</span><span class="example"><span class="neutral">· </span>The Senate rejected a proposal to limit the program to two years.</span></span><span class="exponent"><span class="exp display">decline</span><span class="registerlab"> formal</span><span class="def"> to politely refuse to accept an offer or invitation, or refuse to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She has declined all offers of help.</span><span class="example"><span class="neutral">· </span>A palace spokesman declined to comment on the rumours.</span></span><span class="exponent"><span class="exp display">deny</span><span class="def"> to refuse to allow someone to do something or enter somewhere</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They were denied permission to publish the book.</span><span class="example"><span class="neutral">· </span>He was denied access to the US.</span></span><span class="exponent"><span class="exp display">veto</span><span class="def"> to officially refuse to allow a law or plan, or to refuse to accept someone’s suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Congress vetoed the bill.</span><span class="example"><span class="neutral">· </span>The suggestion was quickly vetoed by the other members of the team.</span></span><span class="exponent"><span class="exp display">disallow</span><span class="def"> to officially refuse to accept something because someone has broken the rules, or not done it in the correct way</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The goal was disallowed by the referee.</span><span class="example"><span class="neutral">· </span>The court decided to disallow his evidence.</span></span><span class="exponent"><span class="exp display">rebuff</span><span class="registerlab"> formal</span><span class="def"> to refuse to accept someone’s offer, request, or suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The company raised its offer to $6 billion, but was rebuffed.</span><span class="example"><span class="neutral">· </span>He was politely rebuffed when he suggested holding the show in Dublin.</span></span><span class="exponent"><span class="exp display">give somebody/something the thumbs down</span><span class="registerlab"> informal</span><span class="def"> to refuse to allow or accept a plan or suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The plan was given the thumbs down by the local authority.</span><span class="example"><span class="neutral">· </span>They gave us the thumbs down.</span></span></span></span></span></span></div><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_refuse_2"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_refuse_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_refuse_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_refuse_2"></a><span class="entry"><span class="entryhead"><span class="hwd">refuse</span><span class="hyphenation">ref<span class="hs0"></span>use</span><span class="homnum">2</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">ˈrefjuːs</span><span class="neutral">/</span></span><span class="pos"> noun</span><span class="gram"> [uncountable]</span><span class="registerlab"> formal</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_0_brelasderefuse1').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_f_l3refuse2').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">refuse</span><span class="homnum">2</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1300-1400</span> <span class="etymlang">Old French</span> <span class="etymorigin">refus</span>, from <span class="etymorigin">refuser</span>; <span> ➔ </span><span class="refhwd">REFUSE</span><span class="etymrefhom">1</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li><span class="nodeword">Refuse</span> collection has been seriously affected by the strike.</li><li>facilities for recycling household <span class="nodeword">refuse</span></li><li>Heaps of decaying <span class="nodeword">refuse</span> littered every street.</li><li>We are gradually developing safer and more effective methods of <span class="nodeword">refuse</span> disposal.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>Paul's guide to this mighty sauce is three large <span class="nodeword">refuse</span> sacks of breadcrumbs to 60 pints of milk.</li><li>The colonies were a disposal area for social <span class="nodeword">refuse</span>.</li><li>This was made up of the organic residues of farms, forestry, industry and domestic <span class="nodeword">refuse</span>.</li><li>Workers on Monday mucked out basement and main-floor <span class="nodeword">refuse</span> left by the water.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">rubbish</span></span><div class="content"><span class="geo"> especially British English</span><span class="def"> things that people throw away, such as old food, dirty paper etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>People are being encouraged to recycle their household rubbish.</span><span class="example"><span class="neutral">· </span>the rubbish bin</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">garbage/trash</span></span><div class="content"><span class="geo"> American English</span><span class="def"> rubbish</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The garbage is collected every Tuesday.</span><span class="example"><span class="neutral">· </span>There were piles of trash in the backyard.</span><span class="example"><span class="neutral">· </span>a black plastic garbage bag</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse</span></span><div class="content"><span class="registerlab"> formal</span><span class="def"> rubbish</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The strike has disrupted <span class="colloinexa">refuse collection</span>.</span><span class="example"><span class="neutral">· </span>It’s a site which is used for domestic refuse.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">litter</span></span><div class="content"><span class="def"> empty bottles, pieces of paper etc that people have dropped on the ground</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Parents should teach children not to drop litter.</span><span class="example"><span class="neutral">· </span>There was a lot of litter on the beach.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">waste</span></span><div class="content"><span class="def"> rubbish, or materials that need to be dealt with after they have been used in industrial processes</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>nuclear waste</span><span class="example"><span class="neutral">· </span>toxic waste</span><span class="example"><span class="neutral">· </span>household waste</span><span class="example"><span class="neutral">· </span>The company was fined for dumping toxic waste in the sea.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to say that you will not do something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse</span></span><div class="content"><span class="def"> to tell someone firmly that you will not do something they asked you to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I asked Stevie if she would help us, but she refused.</span><span class="thespropform">refuse to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>If they refuse to leave, call the police.</span><span class="example"><span class="neutral">· </span>The church refused to give legitimacy to the new state.</span><span class="thespropform">flatly refuse</span><span class="gloss"><span class="neutral"> (=</span>refuse without explanation in a way that seems unreasonable<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mother flatly refused to see the doctor.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refusal</span></span><div class="content"><span class="def"> when someone refuses to do something that they have been asked to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was upset by her refusal.</span><span class="example"><span class="neutral">· </span>His request for a bigger room met with a blunt refusal.</span><span class="thespropform">refusal to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She must understand the consequences of her refusal to accept medical treatment.</span><span class="example"><span class="neutral">· </span>Refusal to do military service was a criminal offence.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say no</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> to tell someone that you will not do what they asked you to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I asked Dad to lend me some money, but he said no.</span><span class="thespropform">say no no</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They asked me so nicely, I couldn't really say no to them.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">will not/won't</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> if someone will not or won't do something that they have been asked to do or told to do, they are determined not to do it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He won't do anything that I ask him to do.</span><span class="example"><span class="neutral">· </span>I won't sign the contract unless they offer me more money.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not be prepared to do something</span></span><div class="content"><span class="def"> to refuse to do something - use this when you think it is wrong or unfair that anyone should expect you to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not prepared to wait any longer.</span><span class="example"><span class="neutral">· </span>The landlord says that he is not prepared to pay for the repairs.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">draw the line</span></span><div class="content"><span class="def"> to allow certain things, but feel that you must refuse when a particular point is reached</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Our rules about time-keeping are fairly flexible, but we have to draw the line somewhere.</span><span class="thespropform">draw the line at</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I don't mind your brother coming to stay, but I draw the line at him moving in!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">decline</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> to politely refuse to do what someone has asked you to do, especially when they have asked for your opinion or asked for information</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>When asked to comment on details of the agreement, the President declined.</span><span class="thespropform">decline to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A security officer at the factory, who declined to give his name, said he had seen two men leave the building.</span><span class="example"><span class="neutral">· </span>I asked Mr Hughes if he was satisfied with the jury's verdict but he declined to comment.</span></div></span></span><span class="section"><span class="secheading">what you say when you refuse to do something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">no</span></span><div class="content"> <span class="example"><span class="neutral">· </span>"Can you come and help move these boxes?" "No, sorry, I'm busy."</span><span class="example"><span class="neutral">· </span>"You're sick - you need to stay in bed." "No, I have to be at this meeting."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">no way</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to firmly refuse to do something and say that nothing will change your mind</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Why don't you phone him and apologize.'' "No way -- I haven't done anything wrong!''</span><span class="example"><span class="neutral">· </span>No way am I going to look after all three children on my own!</span><span class="example"><span class="neutral">· </span>There's no way I'm going to put up with rudeness like that!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">forget it</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to tell someone that there is no chance at all of you doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Maybe things will sort themselves out in time." "Forget it, the relationship's over."</span><span class="example"><span class="neutral">· </span>As for the idea of going on holiday together, forget it!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not likely</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British spoken</span><span class="def"> use this when you think that what someone has asked you to do is stupid or dangerous</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Are you coming to that lecture this evening?" "Not likely - there's free beer at the Queen's Arms tonight!"</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">and that's flat</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British spoken</span><span class="variant"> <span class="lexvar">that's final</span><span class="neutral"> </span><span class="registerlab">American spoken</span></span><span class="def"> used after you have refused to do something, in order to show that you will not change your mind or discuss your decision</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not coming, and that's flat. Now stop bothering me.</span><span class="example"><span class="neutral">· </span>There's no way I'm helping Ted move again, and that's final.</span></div></span></span><span class="section"><span class="secheading">to refuse to give someone something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>refuse somebody something</span> <span class="example"><span class="neutral">· </span>He was unable to attend the meeting in Moscow, because the Russian authorities had refused him a visa.</span><span class="example"><span class="neutral">· </span>You wouldn't refuse an old friend a favour, would you?</span><span class="thespropform">refuse something to somebody</span> <span class="example"><span class="neutral">· </span>Some banks are threatening to refuse loans to anyone who cannot provide suitable guarantees.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">deny</span></span><div class="content"><span class="def"> if someone in authority denies someone something that they want, for example money, a right, or the chance to do something, they refuse to give it to them</span><span class="thespropform"><span class="neutral">: </span>deny somebody something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His parents denied him the opportunity to go to university.</span><span class="thespropform">be denied something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The scientists have been denied the necessary funds for their research program.</span><span class="example"><span class="neutral">· </span>Up to 450 disaster victims were denied compensation by their insurers.</span><span class="example"><span class="neutral">· </span>She has been denied the right to appeal to the Supreme Court.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">withhold</span></span><div class="content"><span class="def"> to refuse to give someone something that they want - use this especially in official or legal contexts</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The new law allows you to withhold payment if you think a bill is incorrect.</span><span class="thespropform">withhold something from somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Doctors do not have the right to withhold necessary treatment from a patient.</span></div></span></span><span class="section last"><span class="secheading">to move over a surface while pressing against it</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">rubbish</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British</span><span class="def"> all the paper, empty bottles, cans, pieces of food etc that you throw away</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The dustmen collect the rubbish on Wednesdays.</span><span class="example"><span class="neutral">· </span>There was rubbish and broken glass all over the grass.</span><span class="thespropform">put/take out the rubbish</span><span class="gloss"><span class="neutral"> (=</span>put it in a rubbish bin outside your house ready to be collected<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Don't forget to put the rubbish out before you go to bed.</span><span class="thespropform">a rubbish bin</span><span class="gloss"><span class="neutral"> (=</span>a container for rubbish<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Two stolen paintings have been found dumped in a rubbish bin.</span><span class="thespropform">rubbish dump/tip</span><span class="gloss"><span class="neutral"> (=</span>a large open area where people's rubbish is taken after it is collected<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I rescued this table from a rubbish dump.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">garbage/trash</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">American</span><span class="def"> all the paper, empty bottles, cans, pieces of food etc that you throw away</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There were piles of trash in the backyard.</span><span class="thespropform">take out the garbage/trash</span><span class="gloss"><span class="neutral"> (=</span>put it in a garbage can outside your house ready to be collected<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I do all the chores, from picking up the groceries to taking out the garbage.</span><span class="thespropform">garbage/trash can</span><span class="gloss"><span class="neutral"> (=</span>a container for garbage<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Will someone please empty this trash can!</span><span class="thespropform">garbage truck</span><span class="gloss"><span class="neutral"> (=</span>a truck that takes away garbage<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ken drives a garbage truck for a living.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">waste paper</span></span><div class="content"><span class="def"> paper that you throw away, especially because it has been used</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There are two bins. One is for glass and one is for waste paper.</span><span class="thespropform">waste paper bin<span class="registerlab">British</span> /waste paper basket</span><span class="neutral"> </span><span class="registerlab">American</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She crumpled the letter up and put it in the waste paper basket.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">litter</span></span><div class="content"><span class="def"> empty bottles, packets, and pieces of paper that people have dropped on the street or in a park</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>These streets are full of litter.</span><span class="thespropform">drop litter</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You can be fined £100 for dropping litter.</span><span class="thespropform">pick up litter</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I am tired of picking up litter thrown by other people.</span><span class="thespropform">litter bin<span class="registerlab">British</span> /litter basket</span><span class="neutral"> </span><span class="registerlab">American</span><span class="gloss"><span class="neutral"> (=</span>a container for litter<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a picnic area with large wooden tables and litter bins</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refuse</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> all the things that are regularly thrown away from the houses, shops etc in an area</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Heaps of decaying refuse littered every street. </span><span class="thespropform">refuse collection</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Refuse collection has been seriously affected by the strike.</span><span class="thespropform">refuse disposal</span><span class="gloss"><span class="neutral"> (=</span>destroying or burying refuse<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We are gradually developing safer and more effective methods of refuse disposal.</span><span class="thespropform">household/domestic refuse</span><span class="gloss"><span class="neutral"> (=</span>refuse from houses<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>facilities for recycling household refuse</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">waste</span></span><div class="content"><span class="def"> useless materials which are left over, especially after an industrial process, and which must be thrown away</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Too much waste has been dumped into the North Sea.</span><span class="thespropform">industrial/chemical/nuclear waste</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Industrial waste had leaked into the water supply.</span><span class="thespropform">radioactive/toxic/hazardous waste</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The government has announced a ban on all imports of toxic waste from abroad.</span><span class="thespropform">waste disposal</span><span class="gloss"><span class="neutral"> (=</span>destroying or burying waste<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The costs of waste disposal are rising all the time.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">household/domestic refuse</span></span><div class="content"></div></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refused admission</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001646321').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The young men tried to enter a nightclub but were <span class="colloinexa">refused admission</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse to admit something</span></span><div class="content"><span class="example"><span class="neutral">· </span>He refused to admit that it was his fault.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/reject/turn down an application</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>say no to an application<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Their planning application was rejected because of a lack of parking facilities.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">be refused bail</span></span><div class="content"><span class="example"><span class="neutral">· </span>Relatively few defendants are refused bail.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">categorically deny/refuse etc something</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-000897707').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He has categorically denied his guilt all along.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">tax/ticket/debt/refuse collector</span></span><div class="content"></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse (your) consent</span></span><div class="content"><span class="example"><span class="neutral">· </span>When the firm applied for consent to build on the site, it was refused.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse to co-operate</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001686166').play();"><img src="LDOCE6_img_spkr_g.png"/></a> If you <span class="colloinexa">refuse to co-operate</span>, I’ll kill you.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse somebody credit</span></span><div class="content"><span class="example"><span class="neutral">· </span>You may be refused credit if you have a bad financial record.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refused to disclose</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001830989').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He <span class="colloinexa">refused to disclose</span> the identity of the politician.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse disposal</span></span><div class="content"><span class="registerlab"> formal</span><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> rubbish disposal</span><span class="geo"> British English</span><span class="lexvar"><span class="neutral">, </span>garbage disposal</span><span class="geo"> American English</span><span class="neutral">)</span></span><span class="collgloss"><span class="neutral"> (=</span>getting rid of things people throw out of houses, shops etc<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Refuse disposal is the responsibility of county councils.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/deny (somebody) entry</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>stop someone entering<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He was refused entry to the club because he was wearing trainers.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/turn down an invitation</span></span><div class="content"><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> decline an invitation</span><span class="registerlab"> formal</span><span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>She turned down an invitation to take part in a televised debate.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">be refused membership</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>not be accepted as a member<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>She was refused membership of the club because she was a woman.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse to obey</span></span><div class="content"><span class="example"><span class="neutral">· </span>Many people felt the law was unfair, and refused to obey it.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">turn down/refuse/reject/decline an offer</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>say no to it<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>She declined the offer of a lift.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/deny (somebody) permission</span></span><div class="content"><span class="example"><span class="neutral">· </span>Betty's father refused her permission to attend the dance.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refused point-blank</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001634687').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He <span class="colloinexa">refused point-blank</span> to identify his accomplices.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/reject a request</span></span><div class="content"><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> turn down somebody’s request</span><span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>He rejected their request for a meeting.</span><span class="example"><span class="neutral">· </span>This request was turned down because of the cost.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refused to talk</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-000614028').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Even under torture, Maskell <span class="colloinexa">refused to talk</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">refuse/deny somebody a visa</span></span><div class="content"><span class="example"><span class="neutral">· </span>The Lebanese embassy refused him a visa.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADJECTIVE</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">domestic</span></span><div class="content"><span class="example"><span class="neutral">· </span>As with <span class="colloinexa">domestic refuse</span>, the problems of methane gas generation also exist when disposing of industrial waste underground.</span><span class="example"><span class="neutral">· </span>This was made up of the organic residues of farms, forestry, industry and <span class="colloinexa">domestic refuse</span>.</span><span class="example"><span class="neutral">· </span>Unlike habitation sites, they have little <span class="colloinexa">domestic refuse</span> and, unlike cemetery sites, they do not normally contain burials.</span></div></span></span><span class="section last"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">collection</span></span><div class="content"><span class="example"><span class="neutral">· </span>A number of local authorities, for example, put out <span class="colloinexa">refuse collection</span> to private tender.</span><span class="example"><span class="neutral">· </span>The main tasks of the non-metropolitan districts were concerned with housing and basic services such as street cleaning and <span class="colloinexa">refuse collection</span>.</span><span class="example"><span class="neutral">· </span>But it was opposed by the Senate's Environment Committee, industry, local authority <span class="colloinexa">refuse collection</span> agencies and environmentalists.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">collector</span></span><div class="content"><span class="example"><span class="neutral">· </span>Aylesbury's <span class="colloinexa">refuse collectors</span> are out on the job around seven hours a day.</span><span class="example"><span class="neutral">· </span>This was demonstrated in the resettlement of Vila Planetario, a squatter settlement of <span class="colloinexa">refuse collectors</span> in the centre of the city.</span><span class="example"><span class="neutral">· </span>Many were formed from the grouping together of small <span class="colloinexa">refuse collectors</span>.</span><span class="example"><span class="neutral">· </span>Whether one considers lawyers and doctors as more important than farm labourers and <span class="colloinexa">refuse collectors</span> is simply a matter of opinion.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">household</span></span><div class="content"><span class="example"><span class="neutral">· </span>This aims to reduce the mounting flood of <span class="colloinexa">household refuse</span>, which currently stands at 70 million tonnes a year.</span><span class="example"><span class="neutral">· </span>Talk is of <span class="colloinexa">household refuse</span> trains going back to the moth-balled Gobowen to Nanbrynmawr line - from Manchester.</span></div></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_refuse_2_s1"></a><span class="sense"> <span class="def">waste material that has been thrown away</span><span class="brequiv"> <span class="synopp">SYN</span> rubbish<span class="geo"> British English</span></span><span class="amequiv"><span class="normal">, </span>trash, garbage<span class="geo"> American English</span></span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001661744"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001661744').play();"><img src="LDOCE6_img_spkr_g.png"/></a> a refuse dump</span><span class="colloexa"><span class="collo">household/domestic refuse</span></span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001876610"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001876610').play();"><img src="LDOCE6_img_spkr_g.png"/></a> refuse collection</span></span></span></div><audio id="LDOCE6_hwd_bre_3_brelasderefuse" preload="none" src="LDOCE6_hwd_bre_3_brelasderefuse.mp3"></audio><audio id="LDOCE6_hwd_ame_3_l3refuse" preload="none" src="LDOCE6_hwd_ame_3_l3refuse.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001646321" preload="none" src="LDOCE6_exa_bre_0_p008-001646321.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-000897707" preload="none" src="LDOCE6_exa_bre_7_p008-000897707.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001686166" preload="none" src="LDOCE6_exa_bre_7_p008-001686166.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001830989" preload="none" src="LDOCE6_exa_bre_c_p008-001830989.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001634687" preload="none" src="LDOCE6_exa_bre_4_p008-001634687.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-000614028" preload="none" src="LDOCE6_exa_bre_5_p008-000614028.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-000769291" preload="none" src="LDOCE6_exa_bre_c_p008-000769291.mp3"></audio><audio id="LDOCE6_exa_bre_5_ldoce6exa_00721" preload="none" src="LDOCE6_exa_bre_5_ldoce6exa_00721.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-000769294" preload="none" src="LDOCE6_exa_bre_5_p008-000769294.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-000769298" preload="none" src="LDOCE6_exa_bre_c_p008-000769298.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-000769308" preload="none" src="LDOCE6_exa_bre_9_p008-000769308.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001661733" preload="none" src="LDOCE6_exa_bre_4_p008-001661733.mp3"></audio><audio id="LDOCE6_exa_bre_6_ldoce6exa_00722" preload="none" src="LDOCE6_exa_bre_6_ldoce6exa_00722.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001661737" preload="none" src="LDOCE6_exa_bre_3_p008-001661737.mp3"></audio><audio id="LDOCE6_hwd_bre_0_brelasderefuse1" preload="none" src="LDOCE6_hwd_bre_0_brelasderefuse1.mp3"></audio><audio id="LDOCE6_hwd_ame_f_l3refuse2" preload="none" src="LDOCE6_hwd_ame_f_l3refuse2.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001661744" preload="none" src="LDOCE6_exa_bre_8_p008-001661744.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001876610" preload="none" src="LDOCE6_exa_bre_c_p008-001876610.mp3"></audio>`;
