var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_regret_1"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_regret_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_regret_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_regret_1"></a><span class="entry"><span class="entryhead"><span class="hwd">regret</span><span class="hyphenation frequent">re<span class="hs0"></span>gret</span><span class="homnum frequent">1</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">rɪˈɡret</span><span class="neutral">/</span></span><span class="level tooltip"> ●●○</span> <span class="freq">W3</span><span class="pos"> verb</span><span class="inflections"><span class="neutral"> (</span><span class="ptandpp"><span class="infllab">past tense and past participle </span>regretted</span><span class="prespart"><span class="neutral">, </span><span class="infllab">present participle </span>regretting</span><span class="neutral">)</span></span><span class="gram"> [transitive]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_0_regret_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_c_regret1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">regret</span><span class="homnum">1</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1400-1500</span> <span class="etymlang">Old French</span> <span class="etymorigin">regreter</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">regret</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">regret</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">regrets</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">regretted</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">regretted</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">regretted</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">regretted</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">regret</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">regretted</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">regretting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">regretting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">regretting</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">regretting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">regretting</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">regretting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">regretting</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">regretting</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">regretting</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">regretting</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>Do you ever <span class="nodeword">regret</span> taking this job?</li><li>I <span class="nodeword">regret</span> to say that a number of statements made by the department were incorrect.</li><li>I have always <span class="nodeword">regretted</span> giving up my piano lessons.</li><li>I think she <span class="nodeword">regrets</span> now that she never went to college.</li><li>It was a stupid thing to say, and I immediately <span class="nodeword">regretted</span> it.</li><li>The airport announced that due to bad weather all flights were cancelled, and they <span class="nodeword">regretted</span> any inconvenience this would cause.</li><li>The management <span class="nodeword">regrets</span> to inform you that your contract will not be renewed next month.</li><li>The owners said they <span class="nodeword">regretted</span> the decision to close the store, but felt unable to do otherwise.</li><li>The Public Health ministry said it deeply <span class="nodeword">regretted</span> the error.</li><li>We truly <span class="nodeword">regret</span> any inconvenience this mistake caused you.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>A shy smile hinted that he did not entirely <span class="nodeword">regret</span> it.</li><li>I have definitely chosen and I don't <span class="nodeword">regret</span> me choice.</li><li>I instantly <span class="nodeword">regret</span> my indiscretion and beg him to keep the news to himself.</li><li>That was a name he was already beginning to <span class="nodeword">regret</span>.</li><li>We also read about women who tried to do something and failed and now <span class="nodeword">regret</span> that they even tried.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to wish you had not done something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be sorry/feel sorry</span></span><div class="content"><span class="def"> to feel sad, embarrassed, or annoyed about something bad or stupid that you have done, and wish you had not done it</span><span class="thespropform"><span class="neutral">: </span>be sorry/feel sorry that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Aunt Jean always felt a little sorry that she had never had children.</span><span class="example"><span class="neutral">· </span>I was sorry I ever agreed to go with them.</span><span class="thespropform">be sorry/feel sorry for</span><span class="gloss"><span class="neutral"> (=</span>sorry for something bad that you have done<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I am sorry for any embarrassment I may have caused.</span><span class="thespropform">be sorry/feel sorry about</span><span class="gloss"><span class="neutral"> (=</span>sorry about something that has happened<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Richardson said she was very sorry about the incident.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regret</span></span><div class="content"><span class="def"> to wish that you had not done something, especially because it has bad results that affect you for a long time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was a stupid thing to say, and I immediately regretted it.</span><span class="example"><span class="neutral">· </span>The owners said they regretted the decision to close the store, but felt unable to do otherwise.</span><span class="thespropform">regret (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I think she regrets now that she never went to college.</span><span class="thespropform">regret doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I have always regretted giving up my piano lessons.</span><span class="thespropform">deeply regret</span><span class="gloss"><span class="neutral"> (=</span>regret very much<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The Public Health ministry said it deeply regretted the error.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">wish (that) you had/hadn't done something</span></span><div class="content"><span class="def"> to feel that you have done the wrong thing, and wish that you had behaved differently</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I wish I had told him the truth.</span><span class="example"><span class="neutral">· </span>I wish I'd realized earlier how much money I could save.</span><span class="example"><span class="neutral">· </span>She wished now that she had not agreed to go to the party.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regret</span></span><div class="content"><span class="def"> a feeling of sadness that you have when you think you have done the wrong thing, especially because it has bad results that affect you for a long time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My main regret is that I never finished my college degree.</span><span class="thespropform">sigh/expression/feeling of regret</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"I was too young to know what I was doing," said Martha with a deep sigh of regret.</span><span class="thespropform">express regret</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Officers expressed regret about the boy's death.</span></div></span></span><span class="section"><span class="secheading">what you say when you wish that you had not done something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">why did/didn't I do something</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you feel you have done the wrong thing, because things would have been better if you had done something differently</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Why did I say I'd clean the car? It's going to take hours!</span><span class="example"><span class="neutral">· </span>He's such an idiot! Why did I ever find him attractive?</span><span class="example"><span class="neutral">· </span>I'm so stupid. Why didn't I just write it down?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I could've kicked myself/I've been kicking myself etc</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are very sorry about something you have said or done, for example something embarrassing or upsetting, or something you did not want people to know</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I could've kicked myself when I realized she was standing right behind me.</span><span class="example"><span class="neutral">· </span>I have kicked myself every day since then for not checking again to see that the doors were locked.</span></div></span></span><span class="section"><span class="secheading">something that you wish you had not done</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regret</span></span><div class="content"> <span class="example"><span class="neutral">· </span>My one regret is that I never visited the Taj Mahal when I was in India.</span><span class="example"><span class="neutral">· </span>Her biggest regret is that she turned down a job with Gardner's in 1985.</span><span class="example"><span class="neutral">· </span>I knew that if I didn't make a clean break with Victor I should be filled with regrets for the rest of my life.</span></div></span></span><span class="section"><span class="secheading">when you think someone else will wish they had not done something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">somebody will be sorry</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to say that someone will later regret what they have done, because something bad will happen as a result of their actions</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ed, you'll be sorry you ever said that.</span><span class="example"><span class="neutral">· </span>If you don't start studying for your exams, you'll be sorry later.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display"> somebody will live to regret it</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to say that someone will regret what they have done at a later time, especially a few years from now</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He may think leaving his wife for the other woman is a good idea, but he'll live to regret it.</span><span class="example"><span class="neutral">· </span>If you put all your money in this real estate deal, I guarantee you'll live to regret it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">somebody will be laughing on the other side of their face/mouth</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to say that although someone is happy about what they have done now, they will wish they had not done it later</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You'll be laughing out of the other side of your face when I start making money with this.</span></div></span></span><span class="section"><span class="secheading">to not regret doing something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not regret</span></span><div class="content"> <span class="example"><span class="neutral">· </span>Even though this year has been a difficult one, I have never regretted my decision.</span><span class="thespropform">not regret doing something</span> <span class="example"><span class="neutral">· </span>I still miss him sometimes, but I don't regret breaking up with him.</span><span class="thespropform">not regret it</span> <span class="example"><span class="neutral">· </span>It's worth trying to save a little every month -- you won't regret it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not be sorry</span></span><div class="content"><span class="def"> to not feel sorry that you did something, especially when other people think you should</span><span class="thespropform"><span class="neutral">: </span>not be sorry (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not sorry I never got married -- I'm only sorry I didn't have any children.</span><span class="thespropform">not be sorry to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She wasn't sorry to leave her job.</span><span class="thespropform">not be sorry about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Richards says she still isn't sorry about making jokes about the president.</span><span class="thespropform">not be sorry for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not sorry for what I said. Somebody had to tell the truth, even if people don't like it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">have no regrets</span></span><div class="content"><span class="def"> to be glad that you did a particular thing or that things happened in a particular way</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm glad I left Britain and came to live in the US. I have no regrets.</span><span class="example"><span class="neutral">· </span>She had decided to have the baby and had no regrets about her decision.</span><span class="thespropform">have few regrets</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Even though it's hard work they have few regrets about setting up their own business.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">somebody would do the same (thing) again/somebody would do it again</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this when you are not at all sorry that you did something, especially something important in your life</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Are you sorry you left home so young?" "Oh no, I'd do the same thing again."</span><span class="example"><span class="neutral">· </span>He insulted me, so I hit him. And I'd do it again.</span><span class="example"><span class="neutral">· </span>Julia doesn't regret having Ben, and if she could turn the clock back she'd do the same again.</span><span class="example"><span class="neutral">· </span>I wasn't always happy, but I'd do it again if I had the chance.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I wouldn't change a thing</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you do not regret a situation that happened in the past, even if it involved problems or if it upset you at that time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sure, I was devastated when Harry went back to his wife, but even so I wouldn't change a thing.</span></div></span></span><span class="section"><span class="secheading">what you say when you are sorry to have to tell someone some bad news</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I'm sorry</span></span><div class="content"> <span class="example"><span class="neutral">· </span>"Could you lend me a couple of pounds, Katy?" "Sorry, I don't have any money."</span><span class="example"><span class="neutral">· </span>I'm sorry that I can't help.</span><span class="thespropform">I'm sorry to do something</span> <span class="example"><span class="neutral">· </span>I'm sorry to have to tell you that your grandmother is in hospital.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regret</span></span><div class="content"><span class="def"> used in formal letters and official announcements giving someone some bad news</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The airport announced that due to bad weather all flights were cancelled, and they regretted any inconvenience this would cause.</span><span class="thespropform">regret to inform/tell/say etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The management regrets to inform you that your contract will not be renewed next month.</span><span class="example"><span class="neutral">· </span>I regret to say that a number of statements made by the department were incorrect.</span></div></span></span><span class="section last"><span class="secheading">with a feeling of sadness because you wish a situation was different</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">with regret</span></span><div class="content"> <span class="example"><span class="neutral">· </span>He spoke with regret of his decision to leave office.</span><span class="example"><span class="neutral">· </span>People who've left the town and then returned notice the changes most, sometimes with regret.</span><span class="thespropform">with deep/great regret</span><span class="gloss"><span class="neutral"> (=</span>with a lot of regret<span class="neutral">)</span></span> <span class="example"><span class="neutral">· </span>We have accepted her resignation with deep regret.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regretfully</span></span><div class="content"><span class="def"> if you say or do something regretfully , you say or do it sadly because you do not want to say or do it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"We'd better go back," she said regretfully, "before it rains."</span><span class="example"><span class="neutral">· </span>A news release issued by the singer's publicist said he had "regretfully" severed all ties to the band because of "creative differences".</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="section"><span class="secheading">adverbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">deeply/greatly</span></span><div class="content"><span class="example"><span class="neutral">· </span>I deeply regretted what had happened.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">bitterly</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>with a feeling of great sadness<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I bitterly regretted my decision to leave.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">sincerely</span></span><div class="content"><span class="example"><span class="neutral">· </span>The airline sincerely regrets any delays to passengers.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">very much</span></span><div class="content"><span class="example"><span class="neutral">· </span>We very much regret that there will be job losses.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">instantly/immediately</span></span><div class="content"><span class="example"><span class="neutral">· </span>‘No,’ she blurted out, instantly regretting her response.</span></div></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">live to regret something</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>regret it in the future<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>If you don’t go, you may live to regret it.</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">regret a decision</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>wish you had not made a particular decision<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I was already regretting my decision to go on holiday with him.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">an expression of regret</span></span><div class="content"><span class="example"><span class="neutral">· </span>The military has not offered any expression of regret over the civilian loss of life.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">murmur of agreement/surprise/regret etc</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one that expresses a particular feeling<span class="neutral">)</span></span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-001555824').play();"><img src="LDOCE6_img_spkr_g.png"/></a> There was a murmur of agreement from the crowd.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">pang of jealousy/guilt/remorse/regret</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001722217').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She felt a sudden pang of guilt.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section last"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">bitterly</span></span><div class="content"><span class="example"><span class="neutral">· </span>It was an agreement that Lear was to <span class="colloinexa">regret bitterly</span> in later years.</span><span class="example"><span class="neutral">· </span>Many who have left the Association already <span class="colloinexa">bitterly regret</span> it.</span><span class="example"><span class="neutral">· </span>They had rushed into it too quickly, and lived to <span class="colloinexa">bitterly regret</span> their impulsiveness.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">deeply</span></span><div class="content"><span class="example"><span class="neutral">· </span>I <span class="colloinexa">deeply regret</span> that I was unable to have a word with the housekeeper when she was more collected in mind.</span><span class="example"><span class="neutral">· </span>It read: The Governor <span class="colloinexa">deeply regrets</span> you were inconvenienced by Mr Dach, a member of his advance team.</span><span class="example"><span class="neutral">· </span>But when this was over, Eleanor would <span class="colloinexa">deeply regret</span> that she had tried to interfere with his personal life.</span><span class="example"><span class="neutral">· </span>He communicates less and less with his wife, who now <span class="colloinexa">deeply regrets</span> the entire affair.</span><span class="example"><span class="neutral">· </span>We <span class="colloinexa">deeply regret</span> the death of this noble person.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">greatly</span></span><div class="content"><span class="example"><span class="neutral">· </span>Fitzroy said they would <span class="colloinexa">greatly regret</span> losing me but understood my reasons.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">somebody will live to regret it</span></span><div class="content"><ul class="exas"><li>He may think leaving his wife for the other woman is a good idea, but he'll <span class="nodeword">live</span> to regret it.</li><li>If you put all your money in this real estate deal, I guarantee you'll <span class="nodeword">live</span> to regret it.</li></ul></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word family</span><div class="at-link"><span class="entry"><span class="popheader popwf">WORD FAMILY</span><span class="wf"><span class="group"><span class="pos">adjective</span><span class="w"><span class="wfwd">regrettable</span></span><span class="w"><span class="wfwd">regretful</span></span></span><span class="group"><span class="pos">adverb</span><span class="w"><span class="wfwd">regrettably</span></span><span class="w"><span class="wfwd">regretfully</span></span></span><span class="group"><span class="pos">noun</span><span class="w"><span class="wfwd">regret</span></span></span><span class="group"><span class="pos">verb</span><span class="w"><span class="wfwd">regret</span></span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_regret_1_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">to feel sorry about something you have done and wish you had not done it</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001652337"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001652337').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Don’t do anything you might regret.</span><span class="gramexa"><span class="propform">regret doing something</span><span class="example" id="anchor_LDOCE6_exa_bre_c_ldoce5_exa001322"><a onclick="document.getElementById('LDOCE6_exa_bre_c_ldoce5_exa001322').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I regret leaving school so young.</span></span><span class="gramexa"><span class="propform">regret (that)</span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001652338"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001652338').play();"><img src="LDOCE6_img_spkr_g.png"/></a> He was beginning to regret that he’d come along.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_regret_1_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="gram">[not in progressive]</span><span class="registerlab"> formal</span><span class="def"> used in official letters or statements when saying that you are sorry or sad about something</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-000770247"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000770247').play();"><img src="LDOCE6_img_spkr_g.png"/></a> We regret any inconvenience caused to our customers.</span><span class="gramexa"><span class="propform">regret (that)</span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-001652346"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-001652346').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I regret that I will be unable to attend.</span></span><span class="colloexa"><span class="collo">regret to say/inform/tell</span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-000770256"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-000770256').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I regret to inform you that your contract will not be renewed.</span></span></span><span class="tail"><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="section"><span class="secheading">adverbs</span><span class="collocate"><span class="colloc collo">deeply/greatly</span><span class="example"><span class="neutral">· </span>I deeply regretted what had happened.</span></span><span class="collocate"><span class="colloc collo">bitterly</span><span class="collgloss"><span class="neutral"> (=</span>with a feeling of great sadness<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I bitterly regretted my decision to leave.</span></span><span class="collocate"><span class="colloc collo">sincerely</span><span class="example"><span class="neutral">· </span>The airline sincerely regrets any delays to passengers.</span></span><span class="collocate"><span class="colloc collo">very much</span><span class="example"><span class="neutral">· </span>We very much regret that there will be job losses.</span></span><span class="collocate"><span class="colloc collo">instantly/immediately</span><span class="example"><span class="neutral">· </span>‘No,’ she blurted out, instantly regretting her response.</span></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="colloc collo">live to regret something</span><span class="collgloss"><span class="neutral"> (=</span>regret it in the future<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>If you don’t go, you may live to regret it.</span></span></span></span></span></span></div><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_regret_2"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_regret_1').scrollIntoView();"> <i>verb</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_regret_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_regret_2"></a><span class="entry"><span class="entryhead"><span class="hwd">regret</span><span class="hyphenation frequent">regret</span><span class="homnum frequent">2</span><span class="level tooltip"> ●○○</span><span class="pos"> noun</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_0_regret_n0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_c_regret1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>"I was too young to know what I was doing," said Martha with a deep sigh of <span class="nodeword">regret</span>.</li><li>Her biggest <span class="nodeword">regret</span> is that she turned down a job with Gardner's in 1985.</li><li>I knew that if I didn't make a clean break with Victor I should be filled with <span class="nodeword">regrets</span> for the rest of my life.</li><li>Jason detected a note of <span class="nodeword">regret</span> in her voice.</li><li>My main <span class="nodeword">regret</span> is that I never finished my college degree.</li><li>My one <span class="nodeword">regret</span> is that I never visited the Taj Mahal when I was in India.</li><li>Officers expressed <span class="nodeword">regret</span> about the boy's death.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>A life for a telegram from the War Department stating <span class="nodeword">regret</span>.</li><li>But he insists <span class="nodeword">regret</span> should never make a footballer compromise on toughness.</li><li>Did he feel <span class="nodeword">regret</span>, and if so, did he allow anyone to see it?</li><li>His only <span class="nodeword">regret</span> was that he could not drive, because of his epilepsy.</li><li>Mchboob looked disappointed; his whole face relaxed into <span class="nodeword">regret</span>.</li><li>She looked at him a moment as if there were some kind of <span class="nodeword">regret</span> or shame fighting it out inside of her.</li><li>There was <span class="nodeword">regret</span> in his voice.</li><li>Warnod had to decline, to his later <span class="nodeword">regret</span>.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">guilt</span></span><div class="content"><span class="def"> the feeling you have when you have done something you know is wrong</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Divorce often leaves people with feelings of guilt.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">shame</span></span><div class="content"><span class="def"> the feeling of being guilty or embarrassed that you have after doing something that is wrong, when you feel you have lost people’s respect</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I was too scared to help him, and I was filled with shame.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regret</span></span><div class="content"><span class="def"> a feeling of sadness about something, especially because you wish it had not happened</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Kate watched her go with a pang of regret.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">remorse</span></span><div class="content"><span class="def"> a strong feeling of being sorry for doing something very bad</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a murderer who showed no remorse</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">contrition</span></span><div class="content"><span class="registerlab"> formal</span><span class="def"> a feeling of being guilty and sorry for something wrong that you have done</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The company CEO expressed contrition for the errors that led to the crash.</span><span class="example"><span class="neutral">· </span>He sounded full of contrition.</span><span class="example"><span class="neutral">· </span>They wanted to perform some kind of <span class="colloinexa">act of contrition</span><span class="gloss"><span class="neutral"> (=</span>do something that shows you feel sorry for something<span class="neutral">)</span></span>.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">penitence</span></span><div class="content"><span class="registerlab"> formal</span><span class="def"> a feeling of being sorry for something that you have done wrong, when you do not intend to do it again</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He expressed genuine penitence at the harm he had done her.</span><span class="example"><span class="neutral">· </span>a period of reflection and penitence</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">conscience</span></span><div class="content"><span class="def"> the part of your mind that tells you whether what you are doing is morally right or wrong</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My conscience has been troubling me ever since.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to wish you had not done something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be sorry/feel sorry</span></span><div class="content"><span class="def"> to feel sad, embarrassed, or annoyed about something bad or stupid that you have done, and wish you had not done it</span><span class="thespropform"><span class="neutral">: </span>be sorry/feel sorry that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Aunt Jean always felt a little sorry that she had never had children.</span><span class="example"><span class="neutral">· </span>I was sorry I ever agreed to go with them.</span><span class="thespropform">be sorry/feel sorry for</span><span class="gloss"><span class="neutral"> (=</span>sorry for something bad that you have done<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I am sorry for any embarrassment I may have caused.</span><span class="thespropform">be sorry/feel sorry about</span><span class="gloss"><span class="neutral"> (=</span>sorry about something that has happened<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Richardson said she was very sorry about the incident.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regret</span></span><div class="content"><span class="def"> to wish that you had not done something, especially because it has bad results that affect you for a long time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was a stupid thing to say, and I immediately regretted it.</span><span class="example"><span class="neutral">· </span>The owners said they regretted the decision to close the store, but felt unable to do otherwise.</span><span class="thespropform">regret (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I think she regrets now that she never went to college.</span><span class="thespropform">regret doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I have always regretted giving up my piano lessons.</span><span class="thespropform">deeply regret</span><span class="gloss"><span class="neutral"> (=</span>regret very much<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The Public Health ministry said it deeply regretted the error.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">wish (that) you had/hadn't done something</span></span><div class="content"><span class="def"> to feel that you have done the wrong thing, and wish that you had behaved differently</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I wish I had told him the truth.</span><span class="example"><span class="neutral">· </span>I wish I'd realized earlier how much money I could save.</span><span class="example"><span class="neutral">· </span>She wished now that she had not agreed to go to the party.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regret</span></span><div class="content"><span class="def"> a feeling of sadness that you have when you think you have done the wrong thing, especially because it has bad results that affect you for a long time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My main regret is that I never finished my college degree.</span><span class="thespropform">sigh/expression/feeling of regret</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"I was too young to know what I was doing," said Martha with a deep sigh of regret.</span><span class="thespropform">express regret</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Officers expressed regret about the boy's death.</span></div></span></span><span class="section"><span class="secheading">what you say when you wish that you had not done something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">why did/didn't I do something</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you feel you have done the wrong thing, because things would have been better if you had done something differently</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Why did I say I'd clean the car? It's going to take hours!</span><span class="example"><span class="neutral">· </span>He's such an idiot! Why did I ever find him attractive?</span><span class="example"><span class="neutral">· </span>I'm so stupid. Why didn't I just write it down?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I could've kicked myself/I've been kicking myself etc</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you are very sorry about something you have said or done, for example something embarrassing or upsetting, or something you did not want people to know</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I could've kicked myself when I realized she was standing right behind me.</span><span class="example"><span class="neutral">· </span>I have kicked myself every day since then for not checking again to see that the doors were locked.</span></div></span></span><span class="section"><span class="secheading">something that you wish you had not done</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regret</span></span><div class="content"> <span class="example"><span class="neutral">· </span>My one regret is that I never visited the Taj Mahal when I was in India.</span><span class="example"><span class="neutral">· </span>Her biggest regret is that she turned down a job with Gardner's in 1985.</span><span class="example"><span class="neutral">· </span>I knew that if I didn't make a clean break with Victor I should be filled with regrets for the rest of my life.</span></div></span></span><span class="section"><span class="secheading">when you think someone else will wish they had not done something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">somebody will be sorry</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to say that someone will later regret what they have done, because something bad will happen as a result of their actions</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ed, you'll be sorry you ever said that.</span><span class="example"><span class="neutral">· </span>If you don't start studying for your exams, you'll be sorry later.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display"> somebody will live to regret it</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to say that someone will regret what they have done at a later time, especially a few years from now</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He may think leaving his wife for the other woman is a good idea, but he'll live to regret it.</span><span class="example"><span class="neutral">· </span>If you put all your money in this real estate deal, I guarantee you'll live to regret it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">somebody will be laughing on the other side of their face/mouth</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to say that although someone is happy about what they have done now, they will wish they had not done it later</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You'll be laughing out of the other side of your face when I start making money with this.</span></div></span></span><span class="section"><span class="secheading">to not regret doing something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not regret</span></span><div class="content"> <span class="example"><span class="neutral">· </span>Even though this year has been a difficult one, I have never regretted my decision.</span><span class="thespropform">not regret doing something</span> <span class="example"><span class="neutral">· </span>I still miss him sometimes, but I don't regret breaking up with him.</span><span class="thespropform">not regret it</span> <span class="example"><span class="neutral">· </span>It's worth trying to save a little every month -- you won't regret it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">not be sorry</span></span><div class="content"><span class="def"> to not feel sorry that you did something, especially when other people think you should</span><span class="thespropform"><span class="neutral">: </span>not be sorry (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not sorry I never got married -- I'm only sorry I didn't have any children.</span><span class="thespropform">not be sorry to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She wasn't sorry to leave her job.</span><span class="thespropform">not be sorry about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Richards says she still isn't sorry about making jokes about the president.</span><span class="thespropform">not be sorry for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not sorry for what I said. Somebody had to tell the truth, even if people don't like it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">have no regrets</span></span><div class="content"><span class="def"> to be glad that you did a particular thing or that things happened in a particular way</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm glad I left Britain and came to live in the US. I have no regrets.</span><span class="example"><span class="neutral">· </span>She had decided to have the baby and had no regrets about her decision.</span><span class="thespropform">have few regrets</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Even though it's hard work they have few regrets about setting up their own business.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">somebody would do the same (thing) again/somebody would do it again</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this when you are not at all sorry that you did something, especially something important in your life</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Are you sorry you left home so young?" "Oh no, I'd do the same thing again."</span><span class="example"><span class="neutral">· </span>He insulted me, so I hit him. And I'd do it again.</span><span class="example"><span class="neutral">· </span>Julia doesn't regret having Ben, and if she could turn the clock back she'd do the same again.</span><span class="example"><span class="neutral">· </span>I wasn't always happy, but I'd do it again if I had the chance.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I wouldn't change a thing</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you do not regret a situation that happened in the past, even if it involved problems or if it upset you at that time</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sure, I was devastated when Harry went back to his wife, but even so I wouldn't change a thing.</span></div></span></span><span class="section"><span class="secheading">what you say when you are sorry to have to tell someone some bad news</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I'm sorry</span></span><div class="content"> <span class="example"><span class="neutral">· </span>"Could you lend me a couple of pounds, Katy?" "Sorry, I don't have any money."</span><span class="example"><span class="neutral">· </span>I'm sorry that I can't help.</span><span class="thespropform">I'm sorry to do something</span> <span class="example"><span class="neutral">· </span>I'm sorry to have to tell you that your grandmother is in hospital.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regret</span></span><div class="content"><span class="def"> used in formal letters and official announcements giving someone some bad news</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The airport announced that due to bad weather all flights were cancelled, and they regretted any inconvenience this would cause.</span><span class="thespropform">regret to inform/tell/say etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The management regrets to inform you that your contract will not be renewed next month.</span><span class="example"><span class="neutral">· </span>I regret to say that a number of statements made by the department were incorrect.</span></div></span></span><span class="section last"><span class="secheading">with a feeling of sadness because you wish a situation was different</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">with regret</span></span><div class="content"> <span class="example"><span class="neutral">· </span>He spoke with regret of his decision to leave office.</span><span class="example"><span class="neutral">· </span>People who've left the town and then returned notice the changes most, sometimes with regret.</span><span class="thespropform">with deep/great regret</span><span class="gloss"><span class="neutral"> (=</span>with a lot of regret<span class="neutral">)</span></span> <span class="example"><span class="neutral">· </span>We have accepted her resignation with deep regret.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">regretfully</span></span><div class="content"><span class="def"> if you say or do something regretfully , you say or do it sadly because you do not want to say or do it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"We'd better go back," she said regretfully, "before it rains."</span><span class="example"><span class="neutral">· </span>A news release issued by the singer's publicist said he had "regretfully" severed all ties to the band because of "creative differences".</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="section"><span class="secheading">adjectives</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">great/deep regret</span></span><div class="content"><span class="example"><span class="neutral">· </span>I accepted his resignation with great regret.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">bitter regret</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>when you feel sad and angry<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>To the bitter regret of his party, he refused to call an election.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">somebody’s biggest regret</span></span><div class="content"><span class="example"><span class="neutral">· </span>Her biggest regret was not having children.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">somebody’s only regret</span></span><div class="content"><span class="example"><span class="neutral">· </span>My only regret is that my parents did not live to see this day.</span></div></span></span><span class="section"><span class="secheading">verbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">have regrets</span></span><div class="content"><span class="example"><span class="neutral">· </span>I have absolutely no regrets.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">express regret</span></span><div class="content"><span class="example"><span class="neutral">· </span>The president expressed his regret at the deaths.</span></div></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a pang/twinge/stab of regret</span></span><div class="content"><span class="registerlab"> literary</span><span class="collgloss"><span class="neutral"> (=</span>a sudden short feeling of regret<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Kate watched her go with a pang of regret.</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">regret a decision</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>wish you had not made a particular decision<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I was already regretting my decision to go on holiday with him.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">an expression of regret</span></span><div class="content"><span class="example"><span class="neutral">· </span>The military has not offered any expression of regret over the civilian loss of life.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">murmur of agreement/surprise/regret etc</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>one that expresses a particular feeling<span class="neutral">)</span></span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-001555824').play();"><img src="LDOCE6_img_spkr_g.png"/></a> There was a murmur of agreement from the crowd.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">pang of jealousy/guilt/remorse/regret</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001722217').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She felt a sudden pang of guilt.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADJECTIVE</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">deep</span></span><div class="content"><span class="example"><span class="neutral">· </span>With <span class="colloinexa">deep regret</span> he decided that he would have to abort his part of the mission.</span><span class="example"><span class="neutral">· </span>It was with <span class="colloinexa">deep regret</span> that I had to leave for home later that evening.</span><span class="example"><span class="neutral">· </span>He has contemplated suicide, he says; he feels <span class="colloinexa">deep regret</span> about the death.</span><span class="example"><span class="neutral">· </span>As you know, you have always had my warm personal support, and I accepted your decision with <span class="colloinexa">deep regret</span>.</span><span class="example"><span class="neutral">· </span>The government has done so with the <span class="colloinexa">deepest regret</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">great</span></span><div class="content"><span class="example"><span class="neutral">· </span>Rattigan's <span class="colloinexa">great</span> professional <span class="colloinexa">regret</span> was the failure of his 1949 play based on Alexander the Great, Adventure Story.</span><span class="example"><span class="neutral">· </span>My <span class="colloinexa">greatest regret</span> in life was abandoning Amanda, my calico.</span><span class="example"><span class="neutral">· </span>It was a <span class="colloinexa">great regret</span> to me that our partnership was to come to an end.</span><span class="example"><span class="neutral">· </span>It is with <span class="colloinexa">great</span> personal <span class="colloinexa">regret</span> that I have come to this conclusion.</span><span class="example"><span class="neutral">· </span>My <span class="colloinexa">great regret</span> was that I never recorded with Busch.</span><span class="example"><span class="neutral">· </span>With <span class="colloinexa">great regret</span> they decided to abandon the project in view of the ever-increasing cost.</span><span class="example"><span class="neutral">· </span>The tone of Tithonus' words seem to be of <span class="colloinexa">great regret</span> and self lamenting.</span></div></span></span><span class="section last"><span class="secheading">VERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">express</span></span><div class="content"><span class="example"><span class="neutral">· </span>President Afeworki <span class="colloinexa">expressed</span> his <span class="colloinexa">regret</span> at their deaths and insisted they were not intentional.</span><span class="example"><span class="neutral">· </span>I know of not a single surgeon who ever <span class="colloinexa">expressed</span> any <span class="colloinexa">regret</span> over these women or apologized to one of them.</span><span class="example"><span class="neutral">· </span>As reported last month four boys have been suspended for unacceptable behaviour and have <span class="colloinexa">expressed</span> no <span class="colloinexa">regret</span> for their actions.</span><span class="example"><span class="neutral">· </span>When he retired, several civil rights leaders <span class="colloinexa">expressed regret</span>.</span><span class="example"><span class="neutral">· </span>Yet all the while she spoke with me, she never made a sound nor <span class="colloinexa">expressed</span> any sadness or <span class="colloinexa">regret</span>.</span><span class="example"><span class="neutral">· </span>Many of these men <span class="colloinexa">expressed regret</span> at what they had done as soon as they were sober.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">give/send your regrets</span></span><div class="content"><ul class="exas"><li>Henry sends his <span class="nodeword">regrets</span> - he has the flu.</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">somebody will live to regret it</span></span><div class="content"><ul class="exas"><li>He may think leaving his wife for the other woman is a good idea, but he'll <span class="nodeword">live</span> to regret it.</li><li>If you put all your money in this real estate deal, I guarantee you'll <span class="nodeword">live</span> to regret it.</li></ul></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word family</span><div class="at-link"><span class="entry"><span class="popheader popwf">WORD FAMILY</span><span class="wf"><span class="group"><span class="pos">adjective</span><span class="w"><span class="wfwd">regrettable</span></span><span class="w"><span class="wfwd">regretful</span></span></span><span class="group"><span class="pos">adverb</span><span class="w"><span class="wfwd">regrettably</span></span><span class="w"><span class="wfwd">regretfully</span></span></span><span class="group"><span class="pos">noun</span><span class="w"><span class="wfwd">regret</span></span></span><span class="group"><span class="pos">verb</span><span class="w"><span class="wfwd">regret</span></span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_regret_2_s1"></a><span class="sense"><span class="sensenum">1</span><span class="gram">[countable usually plural, uncountable]</span><span class="def"> sadness that you feel about something, especially because you wish it had not happened</span><span class="gramexa"><span class="propformprep">regret about</span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-000770290"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000770290').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I <span class="colloinexa">have</span> no <span class="colloinexa">regrets</span> about leaving.</span></span><span class="colloexa"><span class="collo">great/deep regret</span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001652352"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001652352').play();"><img src="LDOCE6_img_spkr_g.png"/></a> She has already <span class="colloinexa">expressed</span> deep <span class="colloinexa">regret</span> for what happened.</span></span><span class="gramexa"><span class="propform">with regret</span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-001769266"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001769266').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I decided with some regret that it was time to move on.</span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-001769267"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-001769267').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It is <span class="colloinexa">with great regret</span> that I must decline your offer.</span></span><span class="gramexa"><span class="propform">to somebody’s regret</span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001769273"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001769273').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I lost touch with her, much to my regret.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_regret_2_s2"></a><span class="sense"><span class="sensenum">2</span><a id="6504937708254a64972bf4d4ccdfcc9d_regret_2_s3"></a><span class="lexunit">give/send your regrets</span><span class="registerlab"> formal</span><span class="def"> to say that you are unable to go to a meeting, accept an invitation etc</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-000770309"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-000770309').play();"><img src="LDOCE6_img_spkr_g.png"/></a> My father was ill and had to send his regrets.</span></span><span class="tail"><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="section"><span class="secheading">adjectives</span><span class="collocate"><span class="colloc collo">great/deep regret</span><span class="example"><span class="neutral">· </span>I accepted his resignation with great regret.</span></span><span class="collocate"><span class="colloc collo">bitter regret</span><span class="collgloss"><span class="neutral"> (=</span>when you feel sad and angry<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>To the bitter regret of his party, he refused to call an election.</span></span><span class="collocate"><span class="colloc collo">somebody’s biggest regret</span><span class="example"><span class="neutral">· </span>Her biggest regret was not having children.</span></span><span class="collocate"><span class="colloc collo">somebody’s only regret</span><span class="example"><span class="neutral">· </span>My only regret is that my parents did not live to see this day.</span></span></span><span class="section"><span class="secheading">verbs</span><span class="collocate"><span class="colloc collo">have regrets</span><span class="example"><span class="neutral">· </span>I have absolutely no regrets.</span></span><span class="collocate"><span class="colloc collo">express regret</span><span class="example"><span class="neutral">· </span>The president expressed his regret at the deaths.</span></span></span><span class="section last"><span class="secheading">phrases</span><span class="collocate"><span class="colloc collo">a pang/twinge/stab of regret</span><span class="registerlab"> literary</span><span class="collgloss"><span class="neutral"> (=</span>a sudden short feeling of regret<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Kate watched her go with a pang of regret.</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_0_regret_n0205" preload="none" src="LDOCE6_hwd_bre_0_regret_n0205.mp3"></audio><audio id="LDOCE6_hwd_ame_c_regret1" preload="none" src="LDOCE6_hwd_ame_c_regret1.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-001555824" preload="none" src="LDOCE6_exa_bre_b_p008-001555824.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001722217" preload="none" src="LDOCE6_exa_bre_4_p008-001722217.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001652337" preload="none" src="LDOCE6_exa_bre_f_p008-001652337.mp3"></audio><audio id="LDOCE6_exa_bre_c_ldoce5_exa001322" preload="none" src="LDOCE6_exa_bre_c_ldoce5_exa001322.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001652338" preload="none" src="LDOCE6_exa_bre_2_p008-001652338.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-000770247" preload="none" src="LDOCE6_exa_bre_2_p008-000770247.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-001652346" preload="none" src="LDOCE6_exa_bre_a_p008-001652346.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-000770256" preload="none" src="LDOCE6_exa_bre_b_p008-000770256.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-000770290" preload="none" src="LDOCE6_exa_bre_f_p008-000770290.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001652352" preload="none" src="LDOCE6_exa_bre_c_p008-001652352.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001769266" preload="none" src="LDOCE6_exa_bre_0_p008-001769266.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-001769267" preload="none" src="LDOCE6_exa_bre_8_p008-001769267.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001769273" preload="none" src="LDOCE6_exa_bre_2_p008-001769273.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-000770309" preload="none" src="LDOCE6_exa_bre_7_p008-000770309.mp3"></audio>`;
