var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_something_1"></a><div><a id="6504937708254a64972bf4d4ccdfcc9d_something"></a><span class="entry"><span class="entryhead"><span class="hwd">something</span><span class="hyphenation frequent">some<span class="hs0"></span>thing</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">ˈsʌmθɪŋ</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S1</span> <span class="freq">W1</span><span class="pos"> pronoun</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_c_brelasdesomething').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_1_something').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>Come here - I want to show you <span class="nodeword">something</span>.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thing</span></span><div class="content"><span class="def"> used when you do not need to say the name, or when you do not know the name</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>What’s that thing on the kitchen table?</span><span class="example"><span class="neutral">· </span>Have you got all your things?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something</span></span><div class="content"><span class="def"> a thing – used when you are not sure what the thing is</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There’s something on your shirt.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">object</span></span><div class="content"><span class="registerlab"> especially written</span><span class="def"> a solid thing</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a sharp metal object</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">item</span></span><div class="content"><span class="registerlab"> formal</span><span class="def"> a particular kind of thing, or one of a group of things</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>household items</span><span class="example"><span class="neutral">· </span>a luxury item</span><span class="example"><span class="neutral">· </span>an item of equipment</span><span class="example"><span class="neutral">· </span>The items included pieces of old pottery.</span><span class="example"><span class="neutral">· </span>You are not allowed to take sharp items onto the plane.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">article</span></span><div class="content"><span class="registerlab"> formal</span><span class="def"> a particular kind of thing, or one of a group of things. <span class="defbold">Article</span> is very formal, and is used especially in the phrase <span class="defbold">an article of clothing</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They found several articles of clothing in the bushes.</span><span class="example"><span class="neutral">· </span>suspicious articles</span><span class="example"><span class="neutral">· </span>Each article has a card with it giving more information.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">artifact</span></span><div class="content"><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="orthvar"> artefact</span><span class="neutral">)</span></span><span class="registerlab"> formal</span><span class="def"> an object that someone has made, especially one that is very old and has historical value</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The museum has a collection of early Roman artifacts.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thingy</span></span><div class="content"><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> thingamajig/thingamabob</span><span class="neutral">)</span></span><span class="registerlab"> spoken</span><span class="registerlab"> informal</span><span class="def"> a thing – used especially when you cannot remember the name of the thing, but often the other person knows what you are talking about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you pass me the thingy?</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">approximately a large number or amount</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something/somewhere in the region of</span></span><div class="content"> <span class="example"><span class="neutral">· </span>The business is worth something in the region of $25m.</span><span class="example"><span class="neutral">· </span>The universe is estimated to be somewhere in the region of eleven billion years old.</span><span class="example"><span class="neutral">· </span>A typical price would be somewhere in the region of £2,500 per person.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something like</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span> <span class="example"><span class="neutral">· </span>Smith is already something like $10,000 in debt.</span><span class="example"><span class="neutral">· </span> In the USA something like 4000 such accidents occur each year. </span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">an estimated</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>an estimated 3000 people/one million pounds/90% of profits etc</span><span class="def"> approximately that number or amount - use this when you have no exact or detailed figures on which to calculate the exact number</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The event was seen on television by an estimated 250 million people worldwide.</span><span class="example"><span class="neutral">· </span>An estimated 10% of new mothers suffer from severe depression.</span><span class="example"><span class="neutral">· </span>By the end of the month an estimated 1000 people had been killed and 42,000 left homeless.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">some</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>some 100 people/50 years/2000 establishments etc</span><span class="def"> approximately that number or amount - use this especially when you think it is impressive or surprising</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Among the 11 factory sites across Europe, some 2,600 jobs are to be eliminated this year.</span><span class="example"><span class="neutral">· </span>He lectured at the Institut Pasteur for some 50 years.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">or more</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>5000 people/20%/9 days etc or more</span><span class="def"> use this when the total may be a lot more, and you want to emphasize that this is a large number or amount</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>How can you be tired? You slept for ten hours or more last night.</span><span class="example"><span class="neutral">· </span>There were a thousand or more fans at the airport to welcome the band.</span></div></span></span><span class="section"><span class="secheading">to stop working</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something goes wrong</span></span><div class="content"><span class="def"> if something goes wrong with a machine, it stops working normally - use this especially about complicated equipment, when you do not know what the problem is</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Who'll fix my computer if something goes wrong?</span><span class="thespropform">something goes wrong with</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Occasionally something went wrong with the projector and the movie was canceled.</span><span class="example"><span class="neutral">· </span>Something's gone wrong with my washing machine.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">break down</span></span><div class="content"><span class="def"> if a car, bus, train, or large machine breaks down , it stops working completely</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She was late for the meeting because her car broke down.</span><span class="example"><span class="neutral">· </span>The elevators in this building are always breaking down.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">crash</span></span><div class="content"><span class="def"> if a computer crashes , it suddenly stops working, and information is often lost because of this</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I installed the new program and my computer crashed.</span><span class="example"><span class="neutral">· </span>Hundreds of hospital records were wiped out when the network crashed.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">cut out</span></span><div class="content"><span class="def"> if an engine cuts out , it suddenly stops working</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Every time Mark slowed down the engine cut out.</span><span class="example"><span class="neutral">· </span>I started to go up the hill and the engine just cut out on me.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">fail</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially written</span><span class="def"> if a part of a machine or of a piece of electrical equipment fails , it stops working</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The driver of the car claims that his brakes failed and he was unable to stop.</span><span class="example"><span class="neutral">· </span>In the last ten minutes of the game, one of the television cameras failed.</span><span class="example"><span class="neutral">· </span>One of the engines failed at 30,000 feet.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">malfunction</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> to stop working properly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>This is a sign that the computer's hard disk is malfunctioning.</span><span class="example"><span class="neutral">· </span>Both satellites entered orbit but quickly malfunctioned.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pack up</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British informal</span><span class="def"> if a machine packs up , it stops working, especially because it is old</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>When this record player packs up, I'll buy a CD player.</span><span class="example"><span class="neutral">· </span>They won't know what to do if a pipe bursts or if the heater packs up.</span></div></span></span><span class="section"><span class="secheading">clothes</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">clothes</span></span><div class="content"><span class="def"> things that you wear, for example coats, shirts, and dresses</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I need to go buy some new clothes.</span><span class="example"><span class="neutral">· </span>The temperature should be around freezing tonight - it's time to get the winter clothes out.</span><span class="example"><span class="neutral">· </span>My mother always made us wear our good clothes for travelling.</span><span class="example"><span class="neutral">· </span>There are lots of clothes shops on Newbury Street.</span><span class="thespropform">baby/school/dance etc clothes</span><span class="gloss"><span class="neutral"> (=</span>for babies, for school etc<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You can pick up second-hand baby clothes very cheaply.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">clothing</span></span><div class="content"><span class="def"> clothes in general - use this either to talk about a particular type of clothes, or to talk about a large quantity of clothes</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Charities have been delivering food and clothing to the disaster area.</span><span class="thespropform">warm/light/outdoor etc clothing</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Because of the heat, officials are asking people to wear light, loose-fitting clothing and to drink plenty of water.</span><span class="thespropform">piece/item/article of clothing</span><span class="gloss"><span class="neutral"> (=</span>one thing that you wear, for example a shirt or a dress<span class="neutral">)</span></span><span class="neutral"> </span><span class="registerlab">formal</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There was nothing in the chest except for a few items of clothing.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something/anything/nothing to wear</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> clothes, especially clothes for a particular event or occasion </span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I must buy something to wear at Julie's wedding.</span><span class="example"><span class="neutral">· </span>You're always saying you have nothing to wear, but you've got a whole closet full of clothes. </span><span class="example"><span class="neutral">· </span>I can't find anything to wear!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">garment</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> a single piece of clothing - used especially by people who make or sell clothes</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The garment industry has grown by 20% in this area in the past five years.</span><span class="example"><span class="neutral">· </span>Only two garments may be taken into the changing room.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">wardrobe</span></span><div class="content"><span class="def"> all the clothes that you own - used especially by people who write about fashion or by people who sell clothes</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Enter our simple fashion quiz and win a whole new wardrobe.</span><span class="example"><span class="neutral">· </span>We have everything you need to update your business wardrobe.</span><span class="thespropform">winter/summer etc wardrobe</span><span class="gloss"><span class="neutral"> (=</span>the clothes you have that are suitable to wear in the winter, summer etc<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>By adding a few bright buttons or belts to your old clothes you can have a new summer wardrobe.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">wear</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>evening/casual/sports/children's etc wear</span><span class="def"> the clothes worn for a particular occasion or activity or by a particular group of people - used especially by people who make or sell clothes</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a shop that specializes in evening wear</span><span class="example"><span class="neutral">· </span>You'll find a nice range of silk ties in the menswear department on the fourth floor.</span><span class="example"><span class="neutral">· </span>The company's line of casual wear is primarily sold in large discount chain stores.</span></div></span></span><span class="section"><span class="secheading">something that people do for enjoyment</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">activity</span></span><div class="content"> <span class="example"><span class="neutral">· </span>What kind of activities do you enjoy?</span><span class="thespropform">outdoor/indoor activities</span> <span class="example"><span class="neutral">· </span>Rebecca has always loved hiking and other outdoor activities.</span><span class="thespropform">leisure/social/cultural etc activities</span> <span class="example"><span class="neutral">· </span>The retirement home arranges social and cultural activities for its seniors.</span><span class="thespropform">lay on activities</span><span class="neutral"> </span><span class="registerlab">British</span><span class="gloss"><span class="neutral"> (=</span>provide them<span class="neutral">)</span></span> <span class="example"><span class="neutral">· </span>In the afternoon, there will be plenty of activities laid on for the kids.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pursuits</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> things that people do because they enjoy them</span><span class="thespropform"><span class="neutral">: </span>leisure/outdoor/artistic etc pursuits</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Her husband never gave her much support in her artistic pursuits.</span><span class="example"><span class="neutral">· </span>After he retired, my grandfather was able to devote his time to literary pursuits.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">pastime</span></span><div class="content"><span class="def"> an activity that is pleasant, relaxing and usually not very difficult, that you do when you are not working, because you enjoy it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Our cat's favourite pastime is sitting at the window and watching the people walk by.</span><span class="example"><span class="neutral">· </span>Watching talk shows has become a national pastime in this country.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something to do</span></span><div class="content"><span class="def"> something that you can do and that will stop you feeling bored when you have nothing to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I don't mind helping - it'll give me something to do.</span><span class="example"><span class="neutral">· </span>He really needs something to do in his spare time to keep him out of trouble.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">hobby</span></span><div class="content"><span class="def"> something that you do for interest and enjoyment regularly over a long period of time, for example, collecting things or making models</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My hobbies are wind-surfing and playing the guitar.</span><span class="example"><span class="neutral">· </span>I never saw my song-writing as anything more than a hobby until recently.</span></div></span></span><span class="section"><span class="secheading">something that you drink</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">drink</span></span><div class="content"><span class="def"> something that you drink</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Would you like a drink?" "Yes, I'll have a lemonade please."</span><span class="example"><span class="neutral">· </span>a nice cool drink</span><span class="example"><span class="neutral">· </span>She tipped her drink over his head and stormed out.</span><span class="thespropform">a drink of something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Give the children a drink of milk and something to eat.</span><span class="thespropform">food and drink(s)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You can bring your own food and drink to the picnic.</span><span class="example"><span class="neutral">· </span>It's under $10 for lunch and drinks at the Ivy Bush.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something to drink</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> a drink</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm really thirsty. Let's stop for something to drink.</span><span class="example"><span class="neutral">· </span>Can I get you something to drink?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">beverage</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">written</span><span class="def"> a word meaning something that you drink -- used especially in restaurants and by people in the food and drinks business</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Non-alcoholic beverages will be on sale in the foyer.</span></div></span></span><span class="section"><span class="secheading">food</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">food</span></span><div class="content"><span class="def"> what people eat in order to grow and stay alive</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They didn't even have enough money to buy food.</span><span class="example"><span class="neutral">· </span>The church program provides food and clothing for the needy.</span><span class="example"><span class="neutral">· </span>Juntao refused food as a protest against prison conditions.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something to eat</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">especially spoken</span><span class="def"> food, especially a small meal that you eat quickly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You should have something to eat before you go out.</span><span class="example"><span class="neutral">· </span>I'm not hungry -- I had something to eat earlier.</span><span class="example"><span class="neutral">· </span>Do you want me to fix you something to eat?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">grub</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">nosh</span><span class="neutral"> </span><span class="registerlab">British spoken</span></span><span class="def"> food</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Where's the grub? I'm starving.</span><span class="example"><span class="neutral">· </span>There was plenty of nosh at the party.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">diet</span></span><div class="content"><span class="def"> the type of food that someone usually eats</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The doctor told him to reduce the amount of fat in his diet.</span><span class="thespropform">a good/healthy/poor etc diet</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>150,000 Californians die each year from diseases related to a poor diet.</span><span class="thespropform">balanced diet</span><span class="gloss"><span class="neutral"> (=</span>a good mixture of healthy foods<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The secret to a longer life is a balanced diet and regular exercise.</span><span class="thespropform">low-fat/high-fibre etc diet</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a low-fat diet rich in fruits and vegetables</span><span class="thespropform">staple diet</span><span class="gloss"><span class="neutral"> (=</span>a main diet consisting of one or two basic foods<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>People in the coastal region live on a staple diet of rice and fish.</span><span class="thespropform">diet of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>For 27 years in the jungles of Guam, Yokoi survived on a diet of shrimp, coconuts, snails, frogs and rats.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">refreshments</span></span><div class="content"><span class="def"> small amounts of food and drink that are served at a public meeting, a performance, or on a long journey</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mrs Thompson has kindly offered to provide the refreshments for the school sports day.</span><span class="thespropform">light refreshments</span><span class="gloss"><span class="neutral"> (=</span>a small quantity of food<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Light refreshments will be served during the interval.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">nourishment</span></span><div class="content"><span class="def"> the different things that your body needs to grow and be healthy, that you get from food</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A growing child needs proper nourishment.</span><span class="example"><span class="neutral">· </span>Calves rely on their mother's milk to provide nourishment.</span><span class="example"><span class="neutral">· </span>The foetus gets nourishment via the mother's blood supply.</span></div></span></span><span class="section"><span class="secheading">a meal</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">meal</span></span><div class="content"><span class="def"> the food that you eat in the morning, in the middle of the day, or in the evening, either at home or in a restaurant</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The hotel was nice, and the meals were really good.</span><span class="example"><span class="neutral">· </span>Miriam was silent all through the meal.</span><span class="example"><span class="neutral">· </span>You shouldn't exercise after a big meal.</span><span class="thespropform">have a meal</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We had an excellent meal in a Chinese restaurant.</span><span class="thespropform">cook somebody a meal</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Jeff cooked us a delicious meal last night.</span><span class="thespropform">go out for a meal</span><span class="gloss"><span class="neutral"> (=</span>go to a restaurant<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Would you like to go out for a meal sometime, Emma?</span><span class="thespropform">take somebody out for a meal</span><span class="gloss"><span class="neutral"> (=</span>take someone to a restaurant and pay for their meal<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was Lisa's birthday so we took her out for a meal.</span><span class="thespropform">main meal</span><span class="gloss"><span class="neutral"> (=</span>the biggest meal of the day<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We usually have our main meal in the middle of the day.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something to eat</span></span><div class="content"><span class="def"> a meal, especially a small or quick meal</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can I get you something to eat?</span><span class="thespropform">have something to eat</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We'll have something to eat, and then go out.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">feed</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British</span><span class="variant"> <span class="lexvar">/feeding</span><span class="neutral"> </span><span class="registerlab">American</span></span><span class="def"> a meal, consisting only of milk, that a baby has</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A young baby needs small feeds at frequent intervals.</span><span class="example"><span class="neutral">· </span>Lois has gotten tired of the late night feedings.</span><span class="thespropform">have a feed</span><span class="neutral"> </span><span class="registerlab">British</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Her baby has its lunchtime feed, then goes to sleep.</span></div></span></span><span class="section"><span class="secheading">a thing</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thing</span></span><div class="content"><span class="def"> use this instead of the name of something when you do not need to say its name or when you do not know what it is called. You can use thing when you mean a physical object, or something such as an event, an idea, or something that someone says</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>What's that thing on the kitchen table?</span><span class="example"><span class="neutral">· </span>There were several things that I wanted to discuss.</span><span class="example"><span class="neutral">· </span>I cried during the whole thing.</span><span class="example"><span class="neutral">· </span>The first thing we need to do is call Becky.</span><span class="thespropform">adjective thing thing</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We went to the aquarium, and they have this neat new thing where the tide goes in and out over a rock pool.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something</span></span><div class="content"><span class="def"> a thing - use this especially when you do not know what the thing is, or you have not decided yet what it will be</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I need to get something for Greg - it's his birthday tomorrow.</span><span class="example"><span class="neutral">· </span>There's something on your shirt, on the back.</span><span class="example"><span class="neutral">· </span>Something went wrong with the pumping system in the pool.</span><span class="thespropform">something something adjective</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Do you want to hear something really funny?</span><span class="example"><span class="neutral">· </span>He wanted to get her something special, something expensive.</span><span class="thespropform">something else</span><span class="gloss"><span class="neutral"> (=</span>another thing<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I've just remembered something else I wanted to tell you.</span><span class="thespropform">something to eat/wear/read etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I need to have something to eat before we go out.</span><span class="thespropform">or something</span><span class="gloss"><span class="neutral"> (=</span>or something similar<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There's a stone or a nail or something stuck in my shoe.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">object</span></span><div class="content"><span class="def"> a separate solid thing, especially one that you can touch or hold in your hand</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Children should be able to point to each object as they count it.</span><span class="example"><span class="neutral">· </span>The sculpture is made from objects he found on beaches in Mexico.</span><span class="thespropform">inanimate object</span><span class="gloss"><span class="neutral"> (=</span>a thing that is not alive<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>After his stroke, he was able to name inanimate objects like saws and shovels, but unable to name most living things.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">item</span></span><div class="content"><span class="def"> one particular thing, especially one that is being bought or sold</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The line at the checkout was for people with eight items or fewer.</span><span class="example"><span class="neutral">· </span>A comfortable, adjustable chair is the single most important item for the health of a computer user.</span><span class="example"><span class="neutral">· </span>The museum has over 5,000 items of historical interest.</span><span class="thespropform">item of clothing/furniture/jewellery etc</span><span class="gloss"><span class="neutral"> (=</span>a single piece of clothing, furniture, jewellery etc<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The main item of jewellery worn by men, other than a watch, is cuff links.</span><span class="thespropform">household item</span><span class="neutral"> </span><span class="registerlab">American</span><span class="gloss"><span class="neutral"> (=</span>an object used in the house<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Bring any old household items for the sale.</span><span class="thespropform">luxury item</span><span class="gloss"><span class="neutral"> (=</span>an expensive item that is not really necessary<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Many of the things that people in the West take for granted are considered to be luxury items here.</span><span class="thespropform">collector's item</span><span class="gloss"><span class="neutral"> (=</span>a rare item that some people would pay a lot of money to have<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My aunt discovered that her old doll was a collector's item and worth a lot of money.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">article</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> a thing, especially one of a group of things</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Each article has a written history printed on a card below the display.</span><span class="thespropform">article of clothing/furniture/jewellery etc</span><span class="gloss"><span class="neutral"> (=</span>a single piece of clothing, furniture, jewellery etc<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She didn't take much with her, just a few articles of clothing and a towel.</span><span class="thespropform">household article</span><span class="neutral"> </span><span class="registerlab">British</span><span class="gloss"><span class="neutral"> (=</span>an object used in the house<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Most of our wedding presents were household articles.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">artefact/artifact</span></span><div class="content"><span class="def"> an object that someone has made, such as a tool or piece of jewellery, especially one that is interesting because it is very old, comes from a different country etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Many Bronze Age artifacts were discovered at Luddesdown.</span><span class="example"><span class="neutral">· </span>Do not buy any artefacts unless they have an export permit.</span></div></span></span><span class="section last"><span class="secheading">a piece of work that you have to do</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">job</span></span><div class="content"><span class="def"> a specific piece of work that you have to do, often one that you are not paid for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Repairing the roof -- that's going to be the biggest job.</span><span class="example"><span class="neutral">· </span>Cleaning the car's one of my least favorite jobs.</span><span class="thespropform">do a job</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Well, I must go now. I've lots of jobs to do around the house.</span><span class="thespropform">do a good/nice/beautiful etc job</span><span class="gloss"><span class="neutral"> (=</span>do a job well<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I always take my car to York Street garage. They're expensive, but they do a good job.</span><span class="example"><span class="neutral">· </span>Irene did a nice job on those clothes didn't she?</span><span class="thespropform">get on with a job</span><span class="gloss"><span class="neutral"> (=</span>continue doing a job<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He didn't complain or criticize, he just got on with the job.</span><span class="thespropform">odd jobs</span><span class="gloss"><span class="neutral"> (=</span>jobs of different kinds that are not regular<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He does odd jobs for people in his spare time.</span><span class="thespropform">the job in/at hand</span><span class="gloss"><span class="neutral"> (=</span>the job you have to do at the moment<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Let's just concentrate on the job in hand, shall we?</span><span class="example"><span class="neutral">· </span>She was upset, and found it difficult to keep her mind on the job at hand.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">task</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> a piece of work that you have to do - use this especially about a difficult or unpleasant job, or about a specific part of your work</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>One of the first tasks Eva set herself was learning the local language.</span><span class="thespropform">impossible/difficult/arduous etc task</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The UN Peacekeeping Force faces an almost impossible task.</span><span class="example"><span class="neutral">· </span>We knew what had to be done, but it wasn't an easy task.</span><span class="thespropform">task of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Recovery crews continued the grim task of retrieving bodies from the wreckage. </span><span class="thespropform">face/begin/continue a task</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>By 2001, we had begun the task of collecting the materials and information needed for the study.</span><span class="example"><span class="neutral">· </span>This is one of the most difficult and complex tasks we face.</span><span class="thespropform">perform a task/carry out a task</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Most of the workers did not have the skills required to perform the most basic tasks.</span><span class="example"><span class="neutral">· </span>The massacre was never fully investigated because the police were incapable of carrying out the task.</span><span class="thespropform">thankless task</span><span class="gloss"><span class="neutral"> (=</span>one that no one wants to do because they will get no satisfaction from it<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Who on earth would volunteer for such a thankless task?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">something to do/some work to do</span></span><div class="content"><span class="def"> a job that you have to do, either for your work or for yourself, especially things that you have to leave your office or house to do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'll pick the laundry up on Saturday -- I have some shopping to do anyway.</span><span class="example"><span class="neutral">· </span>I've got some work to do this evening.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">piece of work</span></span><div class="content"><span class="def"> something that you have to do, especially something that involves writing or drawing and a lot of thinking</span><span class="thespropform"><span class="neutral">: </span>do/hand in/submit a piece of work</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Do you actually fail the year if you don't hand in a piece of work?</span><span class="example"><span class="neutral">· </span>I've got a merit for every piece of work I've done.</span><span class="thespropform">good/excellent etc piece of work</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I think this is a very fine piece of work and it deserves a first class mark.</span><span class="example"><span class="neutral">· </span>Look at that piece of work and ask yourself the question -- 'Is this the best I can do?'</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">assignment</span></span><div class="content"><span class="def"> a piece of work that someone gives you to do, as part of your job or as part of your studies</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Bart's first assignment for the newspaper was to report on the French elections.</span><span class="example"><span class="neutral">· </span>This is a really tough assignment, and I believe you're the only person who can handle it.</span><span class="thespropform">finish/complete an assignment</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She stayed late to complete a class assignment.</span><span class="thespropform">history/homework/school/military etc assignment</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Robin spent many lunch hours poring over her math assignments.</span><span class="example"><span class="neutral">· </span>I eventually got a teaching assignment at Xibei.</span><span class="thespropform">one-year/two week etc assignment</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>'I figure this will either make or break us,' Cheyne said of the 1-year assignment to get the camp up and running.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">chore</span></span><div class="content"><span class="def"> an unpleasant or boring job, especially one that you have to do regularly in your home</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Washing the kitchen floor was a daily chore, and it was the one I hated most.</span><span class="example"><span class="neutral">· </span>When we opened the store, our ambition was to make shopping less of a chore, more of a pleasure.</span><span class="example"><span class="neutral">· </span>When I got old enough I started to have chores around the house.</span><span class="thespropform">do the/your chores</span><span class="gloss"><span class="neutral"> (=</span>do all the cleaning etc that needs doing in a home<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Michael, come on. Do your chores, bud.</span><span class="thespropform">household chores</span><span class="gloss"><span class="neutral"> (=</span>chores in the home, such as cleaning or cooking<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Husbands should be prepared to do their share of the household chores.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">errand</span></span><div class="content"><span class="def"> a small job that you need to do or that someone has asked you to do, such as buying something, posting something, taking somebody to a place etc</span><span class="thespropform"><span class="neutral">: </span>do/run an errand (for somebody)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Before you disappear, I want you to do an errand for me.</span><span class="example"><span class="neutral">· </span>Peter cleaned equipment, ran errands, answered the phone -- it was all routine.</span><span class="example"><span class="neutral">· </span>I used to pick up her dry cleaning and run errands for her.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">duty</span></span><div class="content"><span class="def"> something that you have a responsibility to do, especially as a regular part of your job</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>For the most part, there was not much to do, other than cleanup duty around the prison camp.</span><span class="example"><span class="neutral">· </span>Part of a park ranger's official duties is to ensure public safety.</span><span class="example"><span class="neutral">· </span>clerical and secretarial duties</span><span class="thespropform">have a duty to do something</span><span class="neutral"> </span><span class="registerlab">formal</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Teachers have a duty to ensure that students are not injured whilst they are in their care.</span><span class="thespropform">perform a duty</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>And now I have a very pleasant duty to perform. I am going to present the prizes to the winning competitors.</span><span class="thespropform">tour of duty</span><span class="gloss"><span class="neutral"> (=</span>a period of time that a soldier or other member of the armed forces spends in a particular place<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He recently completed a tour of duty in Seoul as assistant to the US ambassador there.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">something new/old/good etc</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000648614').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It’s a good car, but I’m looking for something newer.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">something else</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001633716').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The house was too small, so they decided to look for <span class="colloinexa">something else</span><span class="collgloss"><span class="neutral"> (=</span>a different one<span class="neutral">)</span></span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">something wrong</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-001633721').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I think there’s <span class="colloinexa">something wrong</span><span class="collgloss"><span class="neutral"> (=</span>a problem<span class="neutral">)</span></span> with the phone.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">has something to do with</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001633726').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I don’t know what he does exactly, but I know it <span class="colloinexa">has something to do with</span> computers <span class="collgloss"><span class="neutral"> (=</span>is related to them in some way<span class="neutral">)</span></span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">(there is) something different/odd/unusual about somebody/something</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001633744').play();"><img src="LDOCE6_img_spkr_g.png"/></a> There was something rather odd about him.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">There’s something about</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000648624').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">There’s something about</span> her voice that I find really sexy.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">came as something of</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001633758').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The news <span class="colloinexa">came as something of</span> a surprise.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">something like 100/2,000 etc</span></span><div class="content"><ul class="exas"><li>Insignia has <span class="nodeword">something</span> like 100 engineers working on its line.</li></ul></div></span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">be thirty-something/forty-something etc</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">be (really/quite) something</span></span><div class="content"><ul class="exas"><li>But there was <span class="nodeword">something</span> else, too.</li><li>Cancer was <span class="nodeword">something</span> you cut out and that was that.</li><li>It lies unassimilated on the edge of my under-standing; there is <span class="nodeword">something</span> I must learn from it.</li><li>Practice time is <span class="nodeword">something</span> else everyone seems to take for granted.</li><li>She thinks this is <span class="nodeword">something</span> that concerns you, too.</li><li>Surely there was <span class="nodeword">something</span> more to it.</li><li>That is <span class="nodeword">something</span> that special educators have, so far, lamentably failed to offer disabled children and their families.</li><li>Yes, there was <span class="nodeword">something</span> in there and I bet myself it would be Jo's emerald pendant.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">be something else</span></span><div class="content"><ul class="exas"><li>You're really <span class="nodeword">something</span> else, Jeff.</li><li>But to see them as marginal or peripheral is <span class="nodeword">something</span> else again.</li><li>However, there is <span class="nodeword">something</span> else to consider.</li><li>Red, though, is <span class="nodeword">something</span> else.</li><li>Sorting through his things was <span class="nodeword">something</span> else entirely.</li><li>There is <span class="nodeword">something</span> else, though.</li><li>There was <span class="nodeword">something</span> else in them.</li><li>Those two clubs are <span class="nodeword">something</span> else.</li><li>Um, there was <span class="nodeword">something</span> else I want to ask you too.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">there’s something in/to something</span></span><div class="content"><ul class="exas"><li>Hello, I thought, maybe there's <span class="nodeword">something</span> in this for you after all.</li><li>Just as everyone's forgetting about Myra Hindley, there's <span class="nodeword">something</span> in the newspaper again about her.</li><li>Well at least there's <span class="nodeword">something</span> in it for the viewer.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">have something of something</span></span><div class="content"><ul class="exas"><li>At last I have <span class="nodeword">something</span> of Father's.</li><li>Clearly a stranger had emptied it, hence obviously they thought she might have <span class="nodeword">something</span> of interest in it.</li><li>It seems you already have <span class="nodeword">something</span> of the greater power.</li><li>The gardens have <span class="nodeword">something</span> of interest to offer throughout the year.</li><li>They must each have <span class="nodeword">something</span> of reality about them.</li><li>We have <span class="nodeword">something</span> of value to offer you, but you in return have something to offer us.</li><li>William Right-well, then we have <span class="nodeword">something</span> of an impasse.</li><li>You really do have <span class="nodeword">something</span> of Zbigniew in you.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">be something of a gardener/an expert etc</span></span><div class="content"><ul class="exas"><li>Alfred Walter is <span class="nodeword">something</span> of an expert on Viennese music particularly that of the Johann Strauss era.</li><li>In his own way he is <span class="nodeword">something</span> of an expert on the private lives of actresses.</li><li>Richard Holmes was <span class="nodeword">something</span> of an expert at the game, but he ended up as a down-and-out by the end.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">something of a shock/surprise etc</span></span><div class="content"><ul class="exas"><li>At the time it came as <span class="nodeword">something</span> of a shock.</li><li>But perhaps in a way that will be <span class="nodeword">something</span> of a surprise.</li><li>It must have been <span class="nodeword">something</span> of a shock to her system.</li><li>It was <span class="nodeword">something</span> of a shock to him to realize how fond of the little boy he had become.</li><li>Some of their views came as <span class="nodeword">something</span> of a surprise.</li><li>Suddenly she has time to think of herself, and this may be <span class="nodeword">something</span> of a shock.</li><li>That she was shocked came as <span class="nodeword">something</span> of a shock to me, too.</li><li>The feel of his strong fingers enclosing her small palm came as <span class="nodeword">something</span> of a shock to her system.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">a little something</span></span><div class="content"><ul class="exas"><li>Before that, and this should tell you a little <span class="nodeword">something</span>, it was not much more attractive for Stanford.</li><li>Can yet give us a little <span class="nodeword">something</span> now, Morreen.</li><li>Let me just tell you a little <span class="nodeword">something</span>!</li><li>Mrs Fanning also stood up and said she could stand to leave behind a little <span class="nodeword">something</span>.</li><li>Pawlowski knows a little <span class="nodeword">something</span> about horsepower.</li><li>So I thought, a little push, a little shove, a little <span class="nodeword">something</span> extra to shake it loose.</li><li>This time aliens stop by for a visit and leave a little <span class="nodeword">something</span>, um, behind.</li><li>Well, I ate a little <span class="nodeword">something</span> in my room earlier.</li></ul></div></span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">sixty something/John something etc</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">something to eat/drink/read/do etc</span></span><div class="content"><ul class="exas"><li>But he was walking to a truck stop across the street, probably getting <span class="nodeword">something</span> to eat during his break.</li><li>It had <span class="nodeword">something</span> to do with being a man.</li><li>It had <span class="nodeword">something</span> to do with skills, and something to do with expectation and hope.</li><li>Most calendar programs remind you gently when you have <span class="nodeword">something</span> to do.</li><li>No, you can't dash out for <span class="nodeword">something</span> to eat.</li><li>Of course, the beer might have had <span class="nodeword">something</span> to do with this.</li><li>Then I rolled up my things in a blanket and went out and had <span class="nodeword">something</span> to eat.</li><li>You've done it a thousand times already, but you do it again, just for <span class="nodeword">something</span> to do.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">make something of yourself</span></span><div class="content"><ul class="exas"><li>As with all young people she had to make <span class="nodeword">something</span> of herself before she could offer anything to anyone else.</li><li>He looked like a man who might be able to make <span class="nodeword">something</span> of himself if a good woman took him in hand.</li><li>Most ladies maids try to make <span class="nodeword">something</span> of themselves.</li><li>She began to think of it as he, and wondered if he would make <span class="nodeword">something</span> of himself later on in life.</li><li>She too wanted her boys to make <span class="nodeword">something</span> of themselves.</li><li>She was totally uninterested in the proposition that a man ought to make <span class="nodeword">something</span> of himself.</li><li>The one with the ambition and the talent and the brains to really make <span class="nodeword">something</span> of herself.</li><li>Uncle Allen had made <span class="nodeword">something</span> of himself by 1932.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">that’s something</span></span><div class="content"><ul class="exas"><li>And that's <span class="nodeword">something</span> many fans simply don't want.</li><li>And that's <span class="nodeword">something</span> technology alone, however sharp, could never do.</li><li>As for Winterreise itself, that's <span class="nodeword">something</span> else.</li><li>From his talks with players, that's <span class="nodeword">something</span> he discovered was missing in the Phillies clubhouse last season.</li><li>It's a manifestation of the marvellous relationship you had with your husband and that's <span class="nodeword">something</span> you should hand on to dearly.</li><li>Oh, good, that's <span class="nodeword">something</span>.</li><li>Okay, that's <span class="nodeword">something</span> a soldier has to accept, in an abstract sort of way.</li><li>You bought it as somewhere where you could keep me, and that's <span class="nodeword">something</span> quite different.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">do something</span></span><div class="content"><ul class="exas"><li>Quick, do <span class="nodeword">something</span> - there's water all over the kitchen floor.</li><li>Teenagers were dropping out of school in huge numbers, until a group of parents and teachers decided to do <span class="nodeword">something</span> about it.</li><li>We need to do <span class="nodeword">something</span> before everyone gets fed up and quits.</li><li>When are you going to do <span class="nodeword">something</span> about this broken window?</li><li>At this stage, the urge to do <span class="nodeword">something</span> was unfocused, but it was extraordinary how people threw themselves into it.</li><li>But the ballot stuffing was so blatant that even the Labor Department was roused to do <span class="nodeword">something</span>.</li><li>I always feel I have to do <span class="nodeword">something</span> new.</li><li>I mean, who'd do <span class="nodeword">something</span> like this, eh?</li><li>I was vaguely aware that junior was trying to persuade the others to do <span class="nodeword">something</span>.</li><li>If it doesn't want to do <span class="nodeword">something</span> it will refuse to, probably by dropping stitches or jamming the carriage.</li><li>One can always try to do <span class="nodeword">something</span> for individuals.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">something about somebody/something</span></span><div class="content"><ul class="exas"><li><span class="nodeword">Something</span> about Frank's attitude just makes you want to hit him.</li><li>There's <span class="nodeword">something</span> strange about the woman's eyes in the painting.</li><li>After witnessing scurvy's dire effects, naval surgeon James Lind decided to do <span class="nodeword">something</span> about it.</li><li>I know <span class="nodeword">something</span> about him that he doesn't know I know.</li><li>If the result is none too pleasant, it's time to do <span class="nodeword">something</span> about it.</li><li>Once you know how others perceive you, you can do <span class="nodeword">something</span> about it.</li><li>Some may see them entirely differently, arguing that they prove <span class="nodeword">something</span> about homophobia or how gay men react to anti-gay oppression.</li><li>That night I recognized at once <span class="nodeword">something</span> about him.</li><li>The only current team in the majors never to have appeared in postseason play decided to do <span class="nodeword">something</span> about it.</li><li>When will he do <span class="nodeword">something</span> about that deplorable record?</li></ul></div></span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">... or something</span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">used to mean a particular thing when you do not know its name or do not know exactly what it is</span><span class="relatedwd"><span class="neutral"> → </span>anything</span><span class="relatedwd"><span class="neutral">, </span>everything</span><span class="relatedwd"><span class="neutral">, </span>nothing</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-000648610"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-000648610').play();"><img src="LDOCE6_img_spkr_g.png"/></a> There’s something in my eye.</span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-000648611"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-000648611').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Sarah said something about coming over later.</span><span class="colloexa"><span class="collo">something new/old/good etc</span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-000648614"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000648614').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It’s a good car, but I’m looking for something newer.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_9_p008-001633716"><a onclick="document.getElementById('LDOCE6_exa_bre_9_p008-001633716').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The house was too small, so they decided to look for <span class="colloinexa">something else</span><span class="gloss"><span class="neutral"> (=</span>a different one<span class="neutral">)</span></span>.</span><span class="example" id="anchor_LDOCE6_exa_bre_d_p008-001633721"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-001633721').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I think there’s <span class="colloinexa">something wrong</span><span class="gloss"><span class="neutral"> (=</span>a problem<span class="neutral">)</span></span> with the phone.</span><span class="example" id="anchor_LDOCE6_exa_bre_c_p008-001633726"><a onclick="document.getElementById('LDOCE6_exa_bre_c_p008-001633726').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I don’t know what he does exactly, but I know it <span class="colloinexa">has something to do with</span> computers <span class="gloss"><span class="neutral"> (=</span>is related to them in some way<span class="neutral">)</span></span>.</span><span class="thesref"><span class="thesaurus">►</span> see <span class="thesaurus">thesaurus</span> at <a><span class="refhwd">thing</span></a></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s2"></a><span class="sense newline"><span class="sensenum">2</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s3"></a><span class="lexunit">something to eat/drink/read/do etc</span><span class="def"> some food, a drink, a book, an activity etc</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-000648704"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-000648704').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Would you like something to drink?</span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-001633732"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001633732').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I should take something to read on the plane.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s4"></a><span class="sense newline"><span class="sensenum">3</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s5"></a><span class="lexunit">do something</span><span class="def"> to do something in order to deal with a problem or difficult situation</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-001633738"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001633738').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Don’t just stand there – do something!</span><span class="gramexa"><span class="propformprep">something about</span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-001633741"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-001633741').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Can you do something about that noise?</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s6"></a><span class="sense newline"><span class="sensenum">4</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s7"></a><span class="lexunit">something about somebody/something</span><span class="def"> used to say that a person, situation etc has a quality or feature that you recognize but you cannot say exactly what it is</span><span class="colloexa"><span class="collo">(there is) something different/odd/unusual about somebody/something</span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001633744"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001633744').play();"><img src="LDOCE6_img_spkr_g.png"/></a> There was something rather odd about him.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-000648624"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000648624').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">There’s something about</span> her voice that I find really sexy.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s8"></a><span class="sense newline"><span class="sensenum">5</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s9"></a><span class="lexunit">... or something</span><span class="registerlab"> spoken</span><span class="def"> used when you cannot remember, or do not think it is necessary to give, another example of something you are mentioning</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-000648687"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000648687').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Her name was Judith, or Julie, or something.</span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-000648686"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-000648686').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Here’s some money. Get yourself a sandwich or something.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s10"></a><span class="sense newline"><span class="sensenum">6</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s11"></a><span class="lexunit">something like 100/2,000 etc</span><span class="def"> close to but not exactly a large amount such as 100, 2,000 etc</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-000648645"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-000648645').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Something like 50,000 homes are without power.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s12"></a><span class="sense newline"><span class="sensenum">7</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s13"></a><span class="lexunit">be thirty-something/forty-something etc</span><span class="def"> used to say that someone is aged between 30 and 39, between 40 and 49 etc when you do not know exactly</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s14"></a><span class="sense newline"><span class="sensenum">8</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s15"></a><span class="lexunit">be (really/quite) something</span><span class="registerlab"> spoken</span><span class="def"> used to say that something is very good and impressive</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-000648640"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-000648640').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Running your own company at 21 is really something.</span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-001633749"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-001633749').play();"><img src="LDOCE6_img_spkr_g.png"/></a> That was really something, wasn’t it?</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s16"></a><span class="sense newline"><span class="sensenum">9</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s17"></a><span class="lexunit">be something else</span><span class="registerlab"> spoken</span><span class="def"> to be unusual or funny to other people</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_4_p008-001633755"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001633755').play();"><img src="LDOCE6_img_spkr_g.png"/></a> You really are something else!</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s18"></a><span class="sense newline"><span class="sensenum">10</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s19"></a><span class="lexunit">there’s something in/to something</span><span class="def"> used to admit that someone’s words are true or their ideas are successful etc</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-000648665"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000648665').play();"><img src="LDOCE6_img_spkr_g.png"/></a> They had to concede that there was something in his teaching methods.</span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-001633756"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-001633756').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Do you think there’s something to the rumours about Larry and Sue?</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s20"></a><span class="sense newline"><span class="sensenum">11</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s21"></a><span class="lexunit">have something of something</span><span class="def"> to have a few of the same features or qualities that someone else has</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_3_p008-000648660"><a onclick="document.getElementById('LDOCE6_exa_bre_3_p008-000648660').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It was clear that Jenkins had something of his father’s brilliance.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s22"></a><span class="sense newline"><span class="sensenum">12</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s23"></a><span class="lexunit">be something of a gardener/an expert etc</span><span class="def"> to know a lot about something or to be very good at something</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_b_p008-000648655"><a onclick="document.getElementById('LDOCE6_exa_bre_b_p008-000648655').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Charlie’s always been something of an expert on architecture.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s24"></a><span class="sense newline"><span class="sensenum">13</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s25"></a><span class="lexunit">something of a shock/surprise etc</span><span class="registerlab"> formal</span><span class="def"> used to say that something is a shock, surprise etc, but not completely or not in a strong or severe way</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-001633758"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-001633758').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The news <span class="colloinexa">came as something of</span> a surprise.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s26"></a><span class="sense newline"><span class="sensenum">14</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s27"></a><span class="lexunit">a little something</span><span class="def"> used when you are telling someone that you have bought them a present</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_1_p008-000648680"><a onclick="document.getElementById('LDOCE6_exa_bre_1_p008-000648680').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I got you a little something for your birthday.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s28"></a><span class="sense newline"><span class="sensenum">15</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s29"></a><span class="lexunit"> sixty something/John something etc</span><span class="registerlab"> spoken</span><span class="def"> used when you cannot remember the rest of a number or name</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-000648715"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-000648715').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘How much did you spend on groceries?’ ‘A hundred and twenty something.’</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s30"></a><span class="sense newline"><span class="sensenum">16</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s31"></a><span class="lexunit">make something of yourself</span><span class="def"> to become successful</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s32"></a><span class="sense newline"><span class="sensenum">17</span><a id="6504937708254a64972bf4d4ccdfcc9d_something_s33"></a><span class="lexunit">that’s something</span><span class="def"> used to say that there is one thing that you should be glad about</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-000648720"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-000648720').play();"><img src="LDOCE6_img_spkr_g.png"/></a> At least we have some money left. That’s something, isn’t it?</span></span></span></div><audio id="LDOCE6_hwd_bre_c_brelasdesomething" preload="none" src="LDOCE6_hwd_bre_c_brelasdesomething.mp3"></audio><audio id="LDOCE6_hwd_ame_1_something" preload="none" src="LDOCE6_hwd_ame_1_something.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-000648614" preload="none" src="LDOCE6_exa_bre_2_p008-000648614.mp3"></audio><audio id="LDOCE6_exa_bre_9_p008-001633716" preload="none" src="LDOCE6_exa_bre_9_p008-001633716.mp3"></audio><audio id="LDOCE6_exa_bre_d_p008-001633721" preload="none" src="LDOCE6_exa_bre_d_p008-001633721.mp3"></audio><audio id="LDOCE6_exa_bre_c_p008-001633726" preload="none" src="LDOCE6_exa_bre_c_p008-001633726.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001633744" preload="none" src="LDOCE6_exa_bre_7_p008-001633744.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-000648624" preload="none" src="LDOCE6_exa_bre_a_p008-000648624.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-001633758" preload="none" src="LDOCE6_exa_bre_f_p008-001633758.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-000648610" preload="none" src="LDOCE6_exa_bre_7_p008-000648610.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-000648611" preload="none" src="LDOCE6_exa_bre_e_p008-000648611.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-000648704" preload="none" src="LDOCE6_exa_bre_6_p008-000648704.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001633732" preload="none" src="LDOCE6_exa_bre_3_p008-001633732.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001633738" preload="none" src="LDOCE6_exa_bre_e_p008-001633738.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-001633741" preload="none" src="LDOCE6_exa_bre_3_p008-001633741.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-000648687" preload="none" src="LDOCE6_exa_bre_a_p008-000648687.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-000648686" preload="none" src="LDOCE6_exa_bre_5_p008-000648686.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-000648645" preload="none" src="LDOCE6_exa_bre_5_p008-000648645.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-000648640" preload="none" src="LDOCE6_exa_bre_4_p008-000648640.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-001633749" preload="none" src="LDOCE6_exa_bre_1_p008-001633749.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001633755" preload="none" src="LDOCE6_exa_bre_4_p008-001633755.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-000648665" preload="none" src="LDOCE6_exa_bre_a_p008-000648665.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-001633756" preload="none" src="LDOCE6_exa_bre_b_p008-001633756.mp3"></audio><audio id="LDOCE6_exa_bre_3_p008-000648660" preload="none" src="LDOCE6_exa_bre_3_p008-000648660.mp3"></audio><audio id="LDOCE6_exa_bre_b_p008-000648655" preload="none" src="LDOCE6_exa_bre_b_p008-000648655.mp3"></audio><audio id="LDOCE6_exa_bre_1_p008-000648680" preload="none" src="LDOCE6_exa_bre_1_p008-000648680.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-000648715" preload="none" src="LDOCE6_exa_bre_8_p008-000648715.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-000648720" preload="none" src="LDOCE6_exa_bre_5_p008-000648720.mp3"></audio>`;
