var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_suggest_1"></a><div><a id="6504937708254a64972bf4d4ccdfcc9d_suggest"></a><span class="entry"><span class="entryhead"><span class="hwd">suggest</span><span class="hyphenation frequent">sug<span class="hs0"></span>gest</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">səˈdʒest</span><span class="amevarpron"><span class="neutral"> $ </span>səɡˈdʒest</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S1</span> <span class="freq">W1</span><span class="pos"> verb</span><span class="gram"> [transitive]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_1_suggest0205').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_c_suggest').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">suggest</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1500-1600</span> <span class="etymlang">Latin</span> past participle of <span class="etymorigin">suggerere</span> <span class="etymtran"> ‘to put under, provide, suggest’</span>, from <span class="etymorigin">sub-</span> (<span> ➔ </span><span class="refhwd">SUB-</span>) + <span class="etymorigin">gerere</span> <span class="etymtran"> ‘to carry’</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Verb Table</span><div class="at-link"><span class="entry"><div class="verbtable"><span class="popverbs">VERB TABLE</span><div class="lemma">suggest</div><table><tbody><tr><td class="header">Simple Form</td></tr><tr><td class="col1">Present</td><td class="col2">I, you, we, they</td><td><span class="verb_form">suggest</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="verb_form">suggests</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="verb_form">suggested</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have </span><span class="verb_form">suggested</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has </span><span class="verb_form">suggested</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had </span><span class="verb_form">suggested</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will </span><span class="verb_form">suggest</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have </span><span class="verb_form">suggested</span></td></tr></tbody></table><table><tbody><tr><td class="header">Continuous Form</td></tr><tr><td class="col1">Present</td><td class="col2">I</td><td><span class="aux">am </span><span class="verb_form">suggesting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">is </span><span class="verb_form">suggesting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">are </span><span class="verb_form">suggesting</span></td></tr><tr><td class="col1">Past</td><td class="col2">I, he, she, it</td><td><span class="aux">was </span><span class="verb_form">suggesting</span></td></tr><tr><td class="col1"></td><td class="col2">you, we, they</td><td><span class="aux">were </span><span class="verb_form">suggesting</span></td></tr><tr><td class="col1">Present perfect</td><td class="col2">I, you, we, they</td><td><span class="aux">have been </span><span class="verb_form">suggesting</span></td></tr><tr><td class="col1"></td><td class="col2">he, she, it</td><td><span class="aux">has been </span><span class="verb_form">suggesting</span></td></tr><tr><td class="col1">Past perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">had been </span><span class="verb_form">suggesting</span></td></tr><tr><td class="col1">Future</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will be </span><span class="verb_form">suggesting</span></td></tr><tr><td class="col1">Future perfect</td><td class="col2">I, you, he, she, it, we, they</td><td><span class="aux">will have been </span><span class="verb_form">suggesting</span></td></tr></tbody></table></div></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>''Why don't you come with us?" Alan <span class="nodeword">suggested</span>.</li><li>"Why not ask Dad?" he <span class="nodeword">suggested</span>.</li><li>A child's behavior might <span class="nodeword">suggest</span> to others that there are problems at home.</li><li>All members are invited to <span class="nodeword">suggest</span> names.</li><li>All the evidence <span class="nodeword">suggests</span> that the problem has improved in recent years.</li><li>Can you <span class="nodeword">suggest</span> where we might be able to get a decent meal?</li><li>Current data <span class="nodeword">suggests</span> that there could be life on Mars.</li><li>He spread his hands to <span class="nodeword">suggest</span> the size of the fish.</li><li>His letter seemed to <span class="nodeword">suggest</span> that he wasn't satisfied with my work.</li><li>I'm not <span class="nodeword">suggesting</span> you deliberately tried to mislead us, just that you made a mistake.</li><li>I'm not sure which is the best wine to order. What would you <span class="nodeword">suggest</span>?</li><li>I <span class="nodeword">suggest</span> we take a break and finish this later.</li><li>In fact, the situation is far worse than these figures <span class="nodeword">suggest</span>.</li><li>It is still not clear what these facts <span class="nodeword">suggest</span> -- perhaps a new strain of the virus, or a completely new virus.</li><li>It was a sunny afternoon, and Jim <span class="nodeword">suggested</span> a trip to the beach.</li><li>It was raining heavily, and she <span class="nodeword">suggested</span> calling a taxi.</li><li>My Dad <span class="nodeword">suggested</span> that I should apply for the job.</li><li>Robert <span class="nodeword">suggested</span> his son for the vacant directorship.</li><li>Sarah <span class="nodeword">suggested</span> that I should apply for this job.</li><li>The door had not been forced open, which strongly <span class="nodeword">suggests</span> that the victim was known to her killer.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>A $ 10 donation is <span class="nodeword">suggested</span>.</li><li>He <span class="nodeword">suggested</span> abortion two or three times and made it very clear that he thought I was making a mistake.</li><li>It may be <span class="nodeword">suggested</span> that there is an element of rejection in being referred.</li><li>On most patients, the repetition by themselves of one word will cause the associated words to <span class="nodeword">suggest</span> themselves.</li><li>Readers may recall that President Ronald Reagan once <span class="nodeword">suggested</span> himself that trees were polluters.</li><li>The date of this odd but interesting ballet might <span class="nodeword">suggest</span> intimations of flower power.</li><li>These changes in mortality, especially among the old elderly, <span class="nodeword">suggest</span> that we have not reached the maximum human lifespan.</li><li>This <span class="nodeword">suggests</span> that Mr Lion had been deliberately singled out as a scapegoat and given no time to defend himself.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">THESAURUS</span><span class="section"><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"><span class="def"> to tell someone your ideas about what they should do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>'Why don’t you come with us?', Alan suggested.</span><span class="example"><span class="neutral">· </span>It was a sunny afternoon and so I suggested going to the beach.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to suggest that someone goes somewhere, tries something etc because you know that it is good and you think they will like it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you recommend a good restaurant near here?</span><span class="example"><span class="neutral">· </span>It’s a brilliant book - I’d recommend it to anyone.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">propose</span></span><div class="content"><span class="registerlab"> formal</span><span class="def"> to formally suggest a plan or course of action, especially at a meeting</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The government has proposed building a new town just north of the city.</span><span class="example"><span class="neutral">· </span>I propose that we send a delegation to London to discuss our concerns.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put forward</span></span><div class="content"><span class="def"> to suggest an idea, plan, reason etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The party has put forward new proposals to reduce the number of cars in the town centre.</span><span class="example"><span class="neutral">· </span>The idea was first put forward by Aristotle.</span><span class="example"><span class="neutral">· </span>A number of reasons have been put forward to explain these negative results.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">nominate</span></span><div class="content"><span class="def"> to officially suggest someone for an important job or position, or a person, film etc for a prize, especially when people will vote to make a decision</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He’s been nominated for the Nobel Prize in Literature several times.</span><span class="example"><span class="neutral">· </span>Three candidates have been nominated for the position of chairman.</span></div></span></span></span><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">to suggest something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"><span class="def"> to tell someone your idea about what they should do, where they should go etc, or about what you and they should do together</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>''Why don't you come with us?" Alan suggested.</span><span class="example"><span class="neutral">· </span>It was a sunny afternoon, and Jim suggested a trip to the beach.</span><span class="thespropform">suggest (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My Dad suggested that I should apply for the job.</span><span class="example"><span class="neutral">· </span>I suggest we take a break and finish this later.</span><span class="thespropform">suggest doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was raining heavily, and she suggested calling a taxi.</span><span class="thespropform">suggest where/how/when etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you suggest where we might be able to get a decent meal?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">make a suggestion</span></span><div class="content"><span class="def"> to suggest something that you think will help someone or will solve a problem</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mr Chairman - may I make a suggestion?</span><span class="example"><span class="neutral">· </span>One day her mother made a suggestion. "Why don't you come back and live with your father and me?''</span><span class="example"><span class="neutral">· </span>A professional consultant will make suggestions about the most suitable clients to approach for your particular type of work.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to suggest something to someone because you know that it is good and you are sure that they will like it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you recommend a good hotel near here?</span><span class="thespropform">recommend something to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Corfu was wonderful - I'd recommend it to anyone.</span><span class="thespropform">be highly recommended</span><span class="gloss"><span class="neutral"> (=</span>people say that it is very good<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The hotel's restaurant comes highly recommended. </span></div></span></span><span class="section"><span class="secheading">to make a formal suggestion in a meeting, report etc</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">propose</span></span><div class="content"><span class="def"> to formally suggest that something should be done, especially at a meeting</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The Russians proposed a treaty banning all nuclear tests.</span><span class="thespropform">propose (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I propose that we discuss this at the next meeting.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to officially suggest that something should be done, after you have considered the situation carefully</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The report recommends a number of changes in the existing law.</span><span class="thespropform">recommend that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The directors are recommending that shareholders accept Baldwin's offer.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put forward</span></span><div class="content"><span class="def"> to suggest plans, proposals etc, especially in order to start discussions about something that needs to be decided</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The United Nations has put forward a peace plan that it hopes will form the basis for discussions.</span><span class="example"><span class="neutral">· </span>Management initially put forward a number of proposals which were wholly unacceptable to the union.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put something to/before</span></span><div class="content"><span class="def"> to offer a group something such as a proposal or plan which they can accept or refuse</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The Government has spent £1 million on putting its case to the public.</span><span class="example"><span class="neutral">· </span>We're going to put our plans before the committee on Monday and we'll just have to hope that they are approved.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">float</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>float an idea/plan etc</span><span class="def"> to suggest an idea, plan etc in order to find out what other people think about it </span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The administration had floated the idea of increased taxes on beer, spirits and tobacco.</span><span class="example"><span class="neutral">· </span>The following month, David floated the possibility of launching a new TV company.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">submit</span></span><div class="content"><span class="def"> to offer a proposal, application etc so that an official person or group can consider it and decide whether to accept it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Applications for planning permission must be submitted before noon tomorrow.</span><span class="thespropform">submit something to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We have submitted proposals for a new pay structure to the board of management.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">present</span></span><div class="content"><span class="def"> to explain your ideas or plans to an official group so that they can decide whether to accept them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We shall give you reasonable time to prepare and present your proposals.</span><span class="thespropform">present something to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ms Rogers will present her ideas to the Board at next week's meeting.</span><span class="thespropform">present somebody with something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We have been presented with a number of plans and will give careful consideration to all of them.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be mooted</span></span><div class="content"><span class="def"> if an idea or plan is mooted , it is suggested as something that could be done</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The scheme was first mooted two years ago.</span><span class="thespropform">be mooted for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A 3,000 house development has been mooted for the disused airfield.</span></div></span></span><span class="section"><span class="secheading">to suggest someone as a suitable person for a job or official position</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"> <span class="example"><span class="neutral">· </span>All members are invited to suggest names.</span><span class="thespropform">suggest somebody for something</span> <span class="example"><span class="neutral">· </span>Robert suggested his son for the vacant directorship.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to suggest someone you know personally as suitable for a job or position, because you think they would do a good job</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ask friends to recommend babysitters. That's the safest way.</span><span class="example"><span class="neutral">· </span>The first applicant was recommended by a friend of the boss.</span><span class="thespropform">recommend somebody for something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Who would you recommend for this job, Stuart?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">put somebody's name forward</span></span><div class="content"><span class="def"> to formally suggest someone, usually in writing, to be elected to an official or political position</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The local Democratic party has put several names forward.</span><span class="thespropform">put sb's name forward for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The opposition leader announced that he would not be putting his name forward for re-election at the party's annual conference.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">nominate</span></span><div class="content"><span class="def"> to suggest someone for an important job or prize, especially when people will vote to make a decision</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We need a treasurer. Does anyone want to nominate somebody?</span><span class="thespropform">nominate somebody for something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Jane Campion was one of the people nominated for the 'Best Director' award.</span><span class="thespropform">nominate somebody as something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It was expected that he would nominate Bramwell as his successor.</span><span class="thespropform">nominate somebody to something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The President has power to nominate people to certain key offices, including judge of the Supreme Court.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">propose</span></span><div class="content"><span class="def"> to formally suggest someone for an official position</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>At the last meeting, Mrs Williams was proposed by several members.</span><span class="thespropform">propose for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I would like to propose Mr Harrison for the position of Party Treasurer.</span></div></span></span><span class="section"><span class="secheading">something that someone suggests</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggestion</span></span><div class="content"><span class="def"> something that someone suggests</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We welcome any suggestions from our viewers as to how to improve our service.</span><span class="thespropform">make a suggestion</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She made some useful suggestions about places we could visit.</span><span class="thespropform">have a suggestion</span><span class="gloss"><span class="neutral"> (=</span>want to make a suggestion<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Does anyone have any other suggestions?</span><span class="thespropform">suggestion about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We liked your suggestion about changing the timetable.</span><span class="thespropform">suggestion that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Barry ignored my suggestion that he should try phoning her again.</span><span class="thespropform">open to suggestions</span><span class="gloss"><span class="neutral"> (=</span>willing to listen to ideas<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You must be flexible and open to suggestions in this job.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">proposal</span></span><div class="content"><span class="def"> a formal or official suggestion that something should be done</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They will consider our proposal at their next meeting.</span><span class="thespropform">put forward a proposal</span><span class="gloss"><span class="neutral"> (=</span>make one<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Their role is to put forward proposals for change. </span><span class="thespropform">proposal to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Their proposal to build a new airport has finally been rejected.</span><span class="thespropform">proposal for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>They forwarded a list of proposals for the safe disposal of nuclear waste.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommendation</span></span><div class="content"><span class="def"> a suggestion made, for example, by an official person or group, especially a suggestion that is contained in a report</span><span class="thespropform"><span class="neutral">: </span>make a recommendation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The consultants have made several very good and valid recommendations.</span><span class="thespropform">accept a recommendation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We accept that recommendation and will act on it as soon as possible.</span><span class="thespropform">on somebody's recommendation</span><span class="gloss"><span class="neutral"> (=</span>because someone has recommended it<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I bought the house on the realtor's recommendation and have regretted it ever since.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">proposition</span></span><div class="content"><span class="def"> a plan of action that is suggested, especially in business or politics</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'll consider your proposition and let you know.</span><span class="example"><span class="neutral">· </span>We are prepared to look at any reasonable proposition from the council.</span><span class="thespropform">make a proposition</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I have a proposition to make.</span></div></span></span><span class="section"><span class="secheading">what you say to suggest something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">can/may I make a suggestion</span></span><div class="content"><span class="def"> use this to suggest something politely, especially when you think someone may be making a mistake</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can I make a suggestion? Try adding a little more flour.</span><span class="example"><span class="neutral">· </span>May I make a suggestion? I think we should stop and look at the map.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I propose (that)</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this for formally suggesting something that you think should be done, especially at a meeting</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I propose that we continue this meeting tomorrow.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">why don't you/we/I etc</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> say this when you think it would be a good idea to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Why don't you wait for me downstairs? I won't be long.</span><span class="example"><span class="neutral">· </span>If David wants someone to go with him, why doesn't he ask Jacky? I'm sure she'd enjoy it.</span><span class="example"><span class="neutral">· </span>Why don't we go watch a movie tonight?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">how about/what about</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">informal</span><span class="def"> use this to suggest something or offer something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"How about a brandy?'' said Tom.</span><span class="example"><span class="neutral">· </span>What about going out for lunch one day next week? When are you free?</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">maybe/perhaps</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to suggest something in a gentle way</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Maybe we should try again tomorrow.</span><span class="example"><span class="neutral">· </span>Perhaps you ought to introduce her to my son. They should get on well.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">let's</span></span><div class="content"><span class="thespropform"><span class="neutral">: </span>let's go/have/do etc</span><span class="def"> use this when you want to suggest something that you and the people you are with should do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Come on, let's dance.</span><span class="example"><span class="neutral">· </span>We both need a break. Let's go away for the weekend.</span><span class="thespropform">let's not</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Let's not argue on our anniversary.</span><span class="thespropform">don't let's (British)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Come on, don't let's waste any more time here.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">we may as well</span></span><div class="content"><span class="def"> use this to suggest something that is not very interesting or exciting, when you do not have any better ideas</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's too late to go to the movies so we may as well watch TV.</span><span class="example"><span class="neutral">· </span>I think we might as well buy this one. We're not going to find anything cheaper.</span></div></span></span><span class="section"><span class="secheading">to advise someone</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">advise</span></span><div class="content"><span class="def"> to tell someone what you think they should do, especially when you have more knowledge or experience than they have</span><span class="thespropform"><span class="neutral">: </span>advise somebody to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I advise you to think very carefully before making any decision.</span><span class="example"><span class="neutral">· </span>All US citizens in the area have been advised to return home.</span><span class="thespropform">advise somebody against doing something</span><span class="gloss"><span class="neutral"> (=</span>advise them not to do it<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Her lawyers have advised her against saying anything to the newspapers.</span><span class="thespropform">advise somebody on/about something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Your teacher will be able to advise you about what qualifications you will need.</span><span class="thespropform">strongly advise</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I strongly advise you to get medical insurance if you're going skiing.</span><span class="thespropform">advise caution/restraint/patience etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Health experts advise extreme caution when handling these materials.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say somebody should do something/ought to do something</span></span><div class="content"><span class="def"> to tell someone what you think they should do, especially when this is your own personal opinion and not the result of particular information or knowledge</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>My friends keep saying I ought to learn to drive.</span><span class="example"><span class="neutral">· </span>Her mother said she should call the police immediately.</span><span class="example"><span class="neutral">· </span>They say you should drink at least eight glasses of water a day.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">tell</span></span><div class="content"><span class="def"> to tell someone that you think they should do something, especially in order to avoid problems</span><span class="thespropform"><span class="neutral">: </span>tell somebody to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I told him to go and see a doctor if he was worried.</span><span class="thespropform">tell somebody (that) they should do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Jimmy has told him he should keep away from the gang for the next couple of weeks.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"><span class="def"> to tell someone your ideas about what they should do, where they should go etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Why not ask Dad?" he suggested.</span><span class="example"><span class="neutral">· </span>I'm not sure which is the best wine to order. What would you suggest?</span><span class="thespropform">suggest (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sarah suggested that I should apply for this job.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recommend</span></span><div class="content"><span class="def"> to advise someone to do something, especially when you have special knowledge of a particular subject or situation</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The Forsyth Report recommended stricter supervision of the trade in live animals.</span><span class="example"><span class="neutral">· </span>Ask your tour guide to recommend the best places to eat.</span><span class="thespropform">recommend that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Doctors recommend that all children be immunized against polio and tuberculosis.</span><span class="thespropform">recommend doing something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I would always recommend buying a good quality bicycle rather than a cheap one.</span><span class="thespropform">strongly recommend</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I strongly recommend that you get your brakes checked before you go on a long drive.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">urge</span></span><div class="content"><span class="def"> to strongly advise someone to do something because you think it is very important</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The company's bosses are urging full cooperation with the trade union.</span><span class="thespropform">urge somebody to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Police are urging drivers not to come into London this weekend.</span><span class="thespropform">urge that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He gave copies of the report to all those present at the meeting, urging that they read and digest its contents.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">give advice</span></span><div class="content"><span class="def"> to advise someone about a problem or subject, especially something that they have asked you about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm afraid that's the only advice I can give you.</span><span class="thespropform">give advice to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The centre gives free advice to young people who have drug problems.</span><span class="thespropform">give somebody advice</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you give me some advice? I'm thinking of buying a computer.</span><span class="thespropform">give advice on/about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Ask your bank to give you some advice about special student packages.</span></div></span></span><span class="section"><span class="secheading">when something has a meaning that is not directly stated</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"><span class="def"> if a statement, fact, event etc suggests that something is true, it seems to mean that it is true or could be true, but this is not directly stated</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It is still not clear what these facts suggest -- perhaps a new strain of the virus, or a completely new virus.</span><span class="thespropform">suggest (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>All the evidence suggests that the problem has improved in recent years.</span><span class="example"><span class="neutral">· </span>The victim had marks on his neck which seemed to suggest he had been strangled.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">imply</span></span><div class="content"><span class="def"> if a statement, fact, event etc implies that something is true, it suggests very strongly that it is true </span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His criticisms implied a lack of confidence in my work.</span><span class="thespropform">imply (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The way he greeted the boys seemed to imply that he knew them quite well.</span><span class="example"><span class="neutral">· </span>Just the fact that he's written to you implies he likes you.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say</span></span><div class="content"><span class="def"> to say something in an indirect way - use this especially when you are saying what the real meaning of a statement is</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>What do you think the writer is saying in this story?</span><span class="thespropform">say (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Are you saying I'm fat?</span><span class="example"><span class="neutral">· </span>So what you're saying, Mr President, is that you don't have a policy on this issue.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">implication</span></span><div class="content"><span class="def"> a meaning that is not directly stated, but which seems to be intended</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He didn't actually accuse me of stealing, but that was the implication.</span><span class="thespropform">implication that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Staff members were asked to work on Sundays, with the implication that they would lose their jobs if they refused.</span><span class="thespropform">by implication</span><span class="gloss"><span class="neutral"> (=</span>the intended meaning is that<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The law bans organized protests and, by implication, any form of opposition.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">undertone</span></span><div class="content"><span class="def"> a feeling or attitude that seems to be part of a statement, even though it is not directly stated</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>a story with racist undertones</span><span class="thespropform">undertone of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There was an unmistakable undertone of nationalist resentment in many of their remarks.</span></div></span></span><span class="section"><span class="secheading">to say something in an indirect way</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">imply</span></span><div class="content"><span class="def"> to say something that seems to mean that another thing is true, but without saying the other thing directly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You seem to be implying something that is not quite true.</span><span class="thespropform">imply (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Michael did imply that I could have the job if I wanted it.</span><span class="example"><span class="neutral">· </span>The article implied that unemployed people are lazy and do not want to work.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"><span class="def"> to say something in an indirect way, especially something bad that you prefer not to say directly</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>What are you suggesting? Do you think I'm a thief?</span><span class="thespropform">suggest (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>His letter seemed to suggest that he wasn't satisfied with my work.</span><span class="thespropform">I'm not suggesting</span><span class="gloss"><span class="neutral"> (=</span>used for telling someone you are not criticizing them<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'm not suggesting you deliberately tried to mislead us, just that you made a mistake.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">hint</span></span><div class="content"><span class="def"> to say something in a very indirect way, but so that other people can guess what you mean</span><span class="thespropform"><span class="neutral">: </span>hint (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Harry hinted that his friendship with Mona might have contributed to his marriage break-up.</span><span class="thespropform">hint at</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The President hinted at the possibility of military action.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">drop a hint</span></span><div class="content"><span class="def"> to say something that suggests that you want to do something or that you want someone to do something, but without saying it directly</span><span class="thespropform"><span class="neutral">: </span>drop a hint about</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She kept dropping hints about her birthday, just to make sure none of us forgot about it.</span><span class="example"><span class="neutral">· </span>My mother dropped several hints about us wallpapering her sitting room.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">in a roundabout way</span></span><div class="content"><span class="def"> if you say something in a roundabout way , you say it in an indirect way, especially to avoid offending or embarrassing someone</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I told him, in a roundabout way, that he wasn't really good enough to join my English class.</span></div></span></span><span class="section last"><span class="secheading">when something shows that something else is likely to be true</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">suggest</span></span><div class="content"><span class="def"> to show that something is probably true, even though there is no definite proof</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In fact, the situation is far worse than these figures suggest.</span><span class="thespropform">suggest (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>There was nothing in his letter to suggest that he might have been unhappy or depressed.</span><span class="example"><span class="neutral">· </span>The drop in trading suggests the Asian economies may be headed for recession.</span><span class="thespropform">suggest something to somebody</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A child's behavior might suggest to others that there are problems at home.</span><span class="thespropform">strongly suggest</span><span class="gloss"><span class="neutral"> (=</span>show that something is very likely<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The door had not been forced open, which strongly suggests that the victim was known to her killer.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">indicate</span></span><div class="content"><span class="def"> if scientific facts, tests, official figures etc indicate something, they show that it is likely to be true</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>How badly reform is needed was indicated recently by the rising youth crime figures.</span><span class="thespropform">indicate (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Research indicates that the drug can be harmful to pregnant women.</span><span class="example"><span class="neutral">· </span>Campaign finance records indicate many of the guests were donors to his own party.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">give the impression</span></span><div class="content"><span class="def"> to make people think that a particular situation exists, even though this may not actually be true</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She wasn't stupid, though at times she gave that impression.</span><span class="thespropform">give the impression (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The luxurious offices gave the impression that the company had plenty of money to spare.</span><span class="thespropform">give the impression of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>All this splendour and ceremony may at first give the impression of high culture and sophistication.</span><span class="thespropform">give the impression that</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He gave me the impression that he didn't really rate my work.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">point to</span></span><div class="content"><span class="def"> to show that a particular explanation for something is likely to be true or that something is likely to happen in the future</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>All the evidence points to a fatty diet being the main cause of heart disease in the West.</span><span class="example"><span class="neutral">· </span>The poor economic climate and the attitude of leading executives both point to a grim future.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">imply</span></span><div class="content"><span class="def"> if a fact or piece of information, especially scientific information, implies something, it shows that it is likely to be true</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Among the ruins there are inscriptions, implying some degree of literacy even in the 9th century BC.</span><span class="example"><span class="neutral">· </span>As the examples imply, some markets are local while others are national or international in scope.</span><span class="thespropform">imply (that)</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The results imply that the disease originated in West Africa.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">implicate</span></span><div class="content"><span class="def"> to show that someone or something is likely to be involved in or connected to something, especially something bad or harmful</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>According to the prosecution, DNA tests 'irrefutably' implicate Henson.</span><span class="thespropform">implicate in</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Sexually transmitted diseases have long been implicated in infertility.</span><span class="thespropform">implicate as</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Seafood is increasingly implicated as the source of the hepatitis A virus. </span><span class="thespropform">implicate himself/yourself etc</span><span class="gloss"><span class="neutral"> (=</span>show, suggest, or prove that he etc has committed a crime<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Simon knew he couldn't possibly provide a blood sample without implicating himself.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">be indicative of</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> if something is indicative of an event or situation, it shows that the event or situation is likely to exist or be true, but it is not definite that it does</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Persistent abdominal pain may be indicative of appendicitis.</span><span class="example"><span class="neutral">· </span>The absence of any famous female composers is more indicative of male dominance than male genius.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">It has been suggested that</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001621239').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">It has been suggested that</span> the manager will resign if any more players are sold.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">can/may I suggest</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>used to politely suggest a different idea<span class="neutral">)</span></span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000631010').play();"><img src="LDOCE6_img_spkr_g.png"/></a> May I suggest that you think carefully before rushing into this?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">evidence/results/data/studies etc suggest(s) that</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001621255').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The evidence suggests that single fathers are more likely to work than single mothers.</span></div></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">suggest an alternative</span></span><div class="content"><span class="example"><span class="neutral">· </span>Do you have an alternative you can suggest?</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">an analysis suggests/indicates something</span></span><div class="content"><span class="example"><span class="neutral">· </span>Our analysis suggests that these problems are widespread.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">common sense suggests something</span></span><div class="content"><span class="example"><span class="neutral">· </span>People don't always do what common sense suggests.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">something suggests a connection</span></span><div class="content"><span class="example"><span class="neutral">· </span>There is nothing to suggest a connection between the hospital food and the illness.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">data indicates/suggests something</span></span><div class="content"><span class="example"><span class="neutral">· </span>Our data indicates that weather patterns are likely to get more extreme.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">experience suggests/shows something</span></span><div class="content"><span class="example"><span class="neutral">· </span>Experience suggests that children who commit crimes will continue to offend as adults.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">logic suggests something</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>used when you want to argue that something is reasonable<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Logic suggests that if the air is warmer, more water evaporates.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a poll shows/indicates/suggests something</span></span><div class="content"><span class="example"><span class="neutral">· </span>Polls show that older voters are most concerned about economic issues.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">suggest/put forward a solution</span></span><div class="content"><span class="example"><span class="neutral">· </span>The chairman put forward a possible solution.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">statistics suggest something</span></span><div class="content"><span class="example"><span class="neutral">· </span>Housing conditions are far worse than the statistics suggest.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">strongly suggest/advise/recommend something</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-001620902').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Before taking action, you are strongly recommended to consult an accountant.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a study suggests/indicates something</span></span><div class="content"><span class="example"><span class="neutral">· </span>A British study suggests that older drivers are safer drivers.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a survey suggests something</span></span><div class="content"><span class="example"><span class="neutral">· </span>Recent surveys suggest that these are the foreign languages most in demand by employers.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADVERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">also</span></span><div class="content"><span class="example"><span class="neutral">· </span>It <span class="colloinexa">also suggests</span> a unity of interest between the various forces of finance and conservatism.</span><span class="example"><span class="neutral">· </span>The findings <span class="colloinexa">also suggest</span> that recession and growing parental responsibility have resulted in fewer legalized women immigrants working outside the home.</span><span class="example"><span class="neutral">· </span>Some economists have <span class="colloinexa">also suggested</span> that the present sales taxes should be replaced by a valued added tax.</span><span class="example"><span class="neutral">· </span>It <span class="colloinexa">also suggests</span> political standards haven't changed much.</span><span class="example"><span class="neutral">· </span>The research <span class="colloinexa">also suggests</span> that farmers overestimate the size of the fox population.</span><span class="example"><span class="neutral">· </span>We <span class="colloinexa">also suggest</span> that the kind of mix that results has great significance for the stability and performance of the political system.</span><span class="example"><span class="neutral">· </span>The report <span class="colloinexa">also suggests</span> that multinational users now have the clout to force service providers to cut their prices.</span><span class="example"><span class="neutral">· </span>If you have any queries your pharmacist will be pleased to give advice and may <span class="colloinexa">also suggest</span> that you visit your dentist.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">strongly</span></span><div class="content"><span class="example"><span class="neutral">· </span>Circumstantial evidence, I know, but evidence that <span class="colloinexa">strongly suggests</span> that the engineer was clobbered by a heavy metallic instrument.</span><span class="example"><span class="neutral">· </span>All the phenomena to be described under the section Complex Partial Seizures <span class="colloinexa">strongly suggest</span> a temporal lobe origin.</span><span class="example"><span class="neutral">· </span>The experiments with clock-shifted pigeons, however, <span class="colloinexa">strongly suggest</span> that the sun supplies their preferred compass.</span><span class="example"><span class="neutral">· </span>The evidence from excellent companies <span class="colloinexa">strongly suggests</span> that managers who feel this way are doing them selves a disservice.</span><span class="example"><span class="neutral">· </span>This <span class="colloinexa">strongly suggests</span> that the peripheral males are exploiting the calling male's ability to attract females.</span><span class="example"><span class="neutral">· </span>The results from more than a decade of study <span class="colloinexa">strongly suggest</span> that, in fact, there is a connection.</span><span class="example"><span class="neutral">· </span>The foregoing <span class="colloinexa">strongly suggests</span> that dividend policy and practice contains important information for the shareholder.</span><span class="example"><span class="neutral">· </span>Evidence <span class="colloinexa">strongly suggests</span> that a serious problem exists and research is needed to better understand this phenomenon and its causes.</span></div></span></span><span class="section"><span class="secheading">NOUN</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">data</span></span><div class="content"><span class="example"><span class="neutral">· </span>Nevertheless, the <span class="colloinexa">data suggest</span> that if levels were more accurate they would have been lower in the peptic ulcer group.</span><span class="example"><span class="neutral">· </span>However, this result may be misleading, as two of those commenting on Buss's <span class="colloinexa">data</span> have <span class="colloinexa">suggested</span>.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">data suggest</span> that gender role is influential.</span><span class="example"><span class="neutral">· </span>But the polling <span class="colloinexa">data</span> and interviews <span class="colloinexa">suggest</span> something else as well.</span><span class="example"><span class="neutral">· </span>These <span class="colloinexa">data suggest</span> that overall between 1% and 2% of boys need circumcision for medical indications.</span><span class="example"><span class="neutral">· </span>Comparative <span class="colloinexa">data</span> in Chapter 3 <span class="colloinexa">suggested</span> that in most countries few adults are political gladiators.</span><span class="example"><span class="neutral">· </span>All these <span class="colloinexa">data suggest</span> that H5 is associated with chromatin that is inactive in transcription and replication.</span><span class="example"><span class="neutral">· </span>Moreover, the <span class="colloinexa">data suggest</span> that Baumol-type models can account for only a very small proportion of total transactions balances.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">evidence</span></span><div class="content"><span class="example"><span class="neutral">· </span>The central dogma is of course a theory, but there is no <span class="colloinexa">evidence</span> to <span class="colloinexa">suggest</span> that it is wrong.</span><span class="example"><span class="neutral">· </span>This myth survives even though there is not a lot of <span class="colloinexa">evidence</span> to <span class="colloinexa">suggest</span> that it is true.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">evidence</span> of Western civilisation <span class="colloinexa">suggests</span> that the status of older people has been generally low, but variably so.</span><span class="example"><span class="neutral">· </span>There is another piece of <span class="colloinexa">evidence</span> to <span class="colloinexa">suggest</span> that there is not hostility.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">evidence</span> would <span class="colloinexa">suggest</span> at least that Heahberht was established and secured in power in Kent through Offa's intervention and support.</span><span class="example"><span class="neutral">· </span>There is <span class="colloinexa">evidence</span> to <span class="colloinexa">suggest</span> that by the time of Euclid these observations were occurring in a cone of vision.</span><span class="example"><span class="neutral">· </span>Labour spokesman David Oakenson says there's <span class="colloinexa">evidence</span> to <span class="colloinexa">suggest</span> the blame lies with Swindon Police.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">experience</span></span><div class="content"><span class="example"><span class="neutral">· </span>However, <span class="colloinexa">experience</span> around the country <span class="colloinexa">suggests</span> that materials arrive late in the school term or not at all.</span><span class="example"><span class="neutral">· </span>My own <span class="colloinexa">experience suggests</span> that journalists seem pro-change because change is news and status quo is not.</span><span class="example"><span class="neutral">· </span>On the contrary, it is the context, matched, or mismatched, to past <span class="colloinexa">experience</span>, which <span class="colloinexa">suggests</span> appropriate labelling.</span><span class="example"><span class="neutral">· </span>This is an uncommon situation as experimental <span class="colloinexa">experience suggests</span> that the energy change is finite.</span><span class="example"><span class="neutral">· </span>Past <span class="colloinexa">experience suggests</span> that some governments, at least, may not be prepared to sit this one out.</span><span class="example"><span class="neutral">· </span>Case study <span class="colloinexa">experience</span> further <span class="colloinexa">suggests</span> that even this use is questionable.</span><span class="example"><span class="neutral">· </span>Valenzuela's <span class="colloinexa">experience suggest</span> - disturbingly - that it could happen to anyone.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">report</span></span><div class="content"><span class="example"><span class="neutral">· </span>Before his <span class="colloinexa">report</span> Mr Brown had <span class="colloinexa">suggested</span> that he would only be freezing duty.</span><span class="example"><span class="neutral">· </span>Already there are <span class="colloinexa">reports</span> of testimony <span class="colloinexa">suggesting</span> that Steffi knew of the alleged tax-evasion scam all along.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">report suggests</span> that individual products could be labelled to inform the public about how much electricity the appliances consume.</span><span class="example"><span class="neutral">· </span>Even so, recent economic <span class="colloinexa">reports suggest</span> growth is flagging.</span><span class="example"><span class="neutral">· </span>Editor's Note: The <span class="colloinexa">report</span> was misleading in <span class="colloinexa">suggesting</span> Mr Bacon's remarks were made at the inquiry.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">report</span> also <span class="colloinexa">suggested</span> Braun had been guilty of felony mishandling of public funds.</span><span class="example"><span class="neutral">· </span>He's published a <span class="colloinexa">report suggesting</span> new restrictions on bikes, even though he's a rider himself.</span><span class="example"><span class="neutral">· </span>In recent weeks, <span class="colloinexa">reports</span> from the area <span class="colloinexa">suggest</span> the situation has worsened.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">research</span></span><div class="content"><span class="example"><span class="neutral">· </span>There has been <span class="colloinexa">research</span> which <span class="colloinexa">suggests</span> that moderately strong automatic stemming can be applied in specialized online reference retrieval searching.</span><span class="example"><span class="neutral">· </span>There is very little <span class="colloinexa">research</span> to <span class="colloinexa">suggest</span> whether skill standards and certificates will work in the United States.</span><span class="example"><span class="neutral">· </span>The latter's <span class="colloinexa">research suggests</span> that patients receiving clozapine may well cost services less over time by producing more positive clinical outcomes.</span><span class="example"><span class="neutral">· </span>Other <span class="colloinexa">research</span> has <span class="colloinexa">suggested</span> that such chemicals may cause damage when they are combined.</span><span class="example"><span class="neutral">· </span>Today, some <span class="colloinexa">research suggests</span> that 50 percent of the city's intravenous drug users have been infected.</span><span class="example"><span class="neutral">· </span>But <span class="colloinexa">research suggests</span> that in many or most countries, male attitudes are not a major barrier to family planning.</span><span class="example"><span class="neutral">· </span>The subjects of <span class="colloinexa">research</span> can be <span class="colloinexa">suggested</span> by those closest to the problem.</span><span class="example"><span class="neutral">· </span>Whether bound for work or college, this <span class="colloinexa">research suggests</span>, many students are too passive.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">result</span></span><div class="content"><span class="example"><span class="neutral">· </span>Early <span class="colloinexa">results</span> from Roosevelt <span class="colloinexa">suggest</span> that the pathway focus actually may encourage students to take more, not less, academics.</span><span class="example"><span class="neutral">· </span>Our preliminary <span class="colloinexa">results suggest</span> that endosonography is better than computed tomography in the evaluation of tumour infiltration in these patients.</span><span class="example"><span class="neutral">· </span>One might think that the business interests would have more sway, but the <span class="colloinexa">results suggest</span> otherwise.</span><span class="example"><span class="neutral">· </span>These <span class="colloinexa">results suggest</span> that the loss of intercellular communication mediated by gap junctions may be associated with the recurrence of gastric ulcers.</span><span class="example"><span class="neutral">· </span>These <span class="colloinexa">result suggest</span> that most elderly patients have some difficulty in making the best use of eye-drops.</span><span class="example"><span class="neutral">· </span>The <span class="colloinexa">results suggest</span> that polymeric diets are as safe and effective as steroids in inducing short term remission.</span><span class="example"><span class="neutral">· </span>This theory has recently been investigated scientifically, and the <span class="colloinexa">results suggest</span> that it could well be correct.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">study</span></span><div class="content"><span class="example"><span class="neutral">· </span>Our <span class="colloinexa">study</span> therefore <span class="colloinexa">suggests</span> three important conclusions.</span><span class="example"><span class="neutral">· </span>But recent <span class="colloinexa">studies suggest</span> this category makes up less than 12 percent of minimum wage workers.</span><span class="example"><span class="neutral">· </span>This <span class="colloinexa">study suggests</span> some degree of idiopathic autonomic denervation in patients with slow transit constipation.</span><span class="example"><span class="neutral">· </span>But most research <span class="colloinexa">studies suggest</span> that sharing practical support is more characteristic of women.</span><span class="example"><span class="neutral">· </span>Other <span class="colloinexa">studies suggest</span> supplementation at 400 to 800 International Units.</span><span class="example"><span class="neutral">· </span>Neither he nor his aides refuted a recent <span class="colloinexa">study suggesting</span> that the bill would push 2. 6 million people into poverty.</span><span class="example"><span class="neutral">· </span>In fact, some <span class="colloinexa">studies suggest</span> that younger and fitter people fare worse than those who are older and less fit.</span></div></span></span><span class="section last"><span class="secheading">VERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">seem</span></span><div class="content"><span class="example"><span class="neutral">· </span>And it is arguable that the mine closures were a blessing, not the disaster which Susanna Rance <span class="colloinexa">seems</span> to <span class="colloinexa">suggest</span>.</span><span class="example"><span class="neutral">· </span>As I came out of the pinon foothills, the flat openness still <span class="colloinexa">seemed</span> to <span class="colloinexa">suggest</span> swarms of screaming mounted warriors.</span><span class="example"><span class="neutral">· </span>The advertiser <span class="colloinexa">seems</span> to <span class="colloinexa">suggest</span> that it could be, if you ate their product.</span><span class="example"><span class="neutral">· </span>This would <span class="colloinexa">seem</span> to <span class="colloinexa">suggest</span> that the head can not belong to the Aztec civilisation.</span><span class="example"><span class="neutral">· </span>To many of them it <span class="colloinexa">seemed</span> abhorrent to <span class="colloinexa">suggest</span> that the Mosaic law was other than final.</span><span class="example"><span class="neutral">· </span>It <span class="colloinexa">seems</span> reasonable to <span class="colloinexa">suggest</span> that the natural theme we is omitted in the second sentence in order to foreground the rheme.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">something suggests itself</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">I’m not suggesting</span></span><div class="content"><ul class="exas"><li>I'm not <span class="nodeword">suggesting</span> that she's stupid or anything.</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">dare I say/suggest</span></span><div class="content"><ul class="exas"><li>Dohnányi, <span class="nodeword">dare</span> I say it, is merely bland.</li><li>Fashionwise, it was not a night to look, <span class="nodeword">dare</span> I say it, nice.</li><li>For children of all ages this is a fascinating display and one, <span class="nodeword">dare</span> I say it, which reaches new heights.</li><li>Of course some of Our Guild Members are <span class="nodeword">dare</span> I say it just that wee bit narrow minded.</li><li>What if some one could undergo that absorption with a cool ... <span class="nodeword">dare</span> I say it? ... scientific rationale.</li><li>You may, <span class="nodeword">dare</span> I say it, need the money.</li></ul></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word family</span><div class="at-link"><span class="entry"><span class="popheader popwf">WORD FAMILY</span><span class="wf"><span class="group"><span class="pos">noun</span><span class="w"><span class="wfwd">suggestion</span></span><span class="w"><span class="wfwd">suggestiveness</span></span></span><span class="group"><span class="pos">adjective</span><span class="w"><span class="wfwd">suggestive</span></span><span class="w"><span class="wfwd">suggestible</span></span></span><span class="group"><span class="pos">verb</span><span class="w"><span class="wfwd">suggest</span></span></span><span class="group"><span class="pos">adverb</span><span class="w"><span class="wfwd">suggestively</span></span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">to tell someone your ideas about what they should do, where they should go etc</span><span class="relatedwd"><span class="neutral"> → </span>propose</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_4_ldoce5_exa001541"><a onclick="document.getElementById('LDOCE6_exa_bre_4_ldoce5_exa001541').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The zoo asked its visitors to suggest a name for the new baby panda.</span><span class="gramexa"><span class="propform">suggest (that)</span><span class="example" id="anchor_LDOCE6_exa_bre_7_ldoce5_exa001542"><a onclick="document.getElementById('LDOCE6_exa_bre_7_ldoce5_exa001542').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Her mother suggested that she should go and see the doctor.</span><span class="example" id="anchor_LDOCE6_exa_bre_b_ldoce5_exa001543"><a onclick="document.getElementById('LDOCE6_exa_bre_b_ldoce5_exa001543').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I suggest you call him first.</span></span><span class="example" id="anchor_LDOCE6_exa_bre_5_p008-001621239"><a onclick="document.getElementById('LDOCE6_exa_bre_5_p008-001621239').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">It has been suggested that</span> the manager will resign if any more players are sold.</span><span class="gramexa"><span class="propform">suggest something to somebody</span><span class="example" id="anchor_LDOCE6_exa_bre_4_ldoce6exa_00859"><a onclick="document.getElementById('LDOCE6_exa_bre_4_ldoce6exa_00859').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Johnson suggested to Boswell that they should buy the island.</span></span><span class="gramexa"><span class="propform">suggest doing something</span><span class="example" id="anchor_LDOCE6_exa_bre_7_ldoce5_exa001544"><a onclick="document.getElementById('LDOCE6_exa_bre_7_ldoce5_exa001544').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Tracey suggested meeting for a drink after work.</span></span><span class="gramexa"><span class="propform">suggest how/where/what etc</span><span class="example" id="anchor_LDOCE6_exa_bre_2_ldoce5_exa001545"><a onclick="document.getElementById('LDOCE6_exa_bre_2_ldoce5_exa001545').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Can you suggest what kind of tool I should use?</span></span><span class="colloexa"><span class="collo">can/may I suggest</span><span class="gloss"><span class="neutral"> (=</span>used to politely suggest a different idea<span class="neutral">)</span></span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-000631010"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-000631010').play();"><img src="LDOCE6_img_spkr_g.png"/></a> May I suggest that you think carefully before rushing into this?</span></span><span class="thesref"><span class="thesaurus">►</span> see <span class="thesaurus">thesaurus</span> at <a><span class="refhwd">advise</span></a></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="def">to make someone think that a particular thing is true</span><span class="syn"> <span class="synopp">SYN</span>  indicate</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_d_p008-001621252"><a onclick="document.getElementById('LDOCE6_exa_bre_d_p008-001621252').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Trends in spending and investment suggest a gradual economic recovery.</span><span class="gramexa"><span class="propform">suggest (that)</span><span class="example" id="anchor_LDOCE6_exa_bre_2_ldoce5_exa020270"><a onclick="document.getElementById('LDOCE6_exa_bre_2_ldoce5_exa020270').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Opinion polls suggest that only 10% of the population trust the government.</span></span><span class="colloexa"><span class="collo">evidence/results/data/studies etc suggest(s) that</span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001621255"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001621255').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The evidence suggests that single fathers are more likely to work than single mothers.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s3"></a><span class="sense newline"><span class="sensenum">3</span><span class="def">to tell someone about someone or something that is suitable for a particular job or activity</span><span class="syn"> <span class="synopp">SYN</span>  recommend</span><span class="gramexa"><span class="propform">suggest somebody/something for something</span><span class="example" id="anchor_LDOCE6_exa_bre_e_p008-000631024"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-000631024').play();"><img src="LDOCE6_img_spkr_g.png"/></a> John Roberts has been suggested for the post of manager.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s4"></a><span class="sense newline"><span class="sensenum">4</span><span class="def">to state something in an indirect way</span><span class="syn"> <span class="synopp">SYN</span>  imply</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001621263"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001621263').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Are you suggesting my husband’s been drinking?</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s5"></a><span class="sense newline"><span class="sensenum">5</span><span class="def">to remind someone of something or help them to imagine it</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-000631051"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000631051').play();"><img src="LDOCE6_img_spkr_g.png"/></a> The stage was bare, with only the lighting to suggest a prison.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s6"></a><span class="sense newline"><span class="sensenum">6</span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s7"></a><span class="lexunit">something suggests itself</span><span class="def"> if an idea suggests itself, you think of it</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_ldoce5_exa001546"><a onclick="document.getElementById('LDOCE6_exa_bre_a_ldoce5_exa001546').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’ll look at my cookbooks and see if anything suggests itself.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s8"></a><span class="sense newline"><span class="sensenum">7</span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s9"></a><span class="lexunit">I’m not suggesting</span><span class="registerlab"> spoken</span><span class="def"> used to say that what you have said is not exactly what you intended to say</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-000631047"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000631047').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’m not suggesting for one moment that these changes will be easy.</span></span><span class="tail"><span class="grambox"><span class="heading">GRAMMAR: Patterns with suggest</span><span class="expl"><span class="neutral">• </span>You <span class="expr">suggest</span> something <span class="expr">to</span> someone: <span class="example"><span class="neutral">· </span>He suggested a solution to us.</span> <span class="cross"><span class="cross">✗</span></span>Don’t say: <span class="badexa">He suggested us a solution.</span></span><span class="expl"><span class="neutral">• </span>You <span class="expr">suggest doing</span> something: <span class="example"><span class="neutral">· </span>I suggested going for a walk in the park.</span> <span class="cross"><span class="cross">✗</span></span>Don’t say: <span class="badexa">I suggested to go for a walk in the park.</span></span><span class="expl"><span class="neutral">• </span>In everyday English, you <span class="expr">suggest that</span> someone <span class="expr">does</span> something: <span class="example"><span class="neutral">· </span>I suggest that she waits a few weeks.</span> <span class="cross"><span class="cross">✗</span></span>Don’t say: <span class="badexa">I suggest her to wait a few weeks.</span></span><span class="expl"><span class="neutral">• </span>In more formal English, you <span class="expr">suggest that</span> someone <span class="expr">do</span> something, using the base form of the verb <span class="gloss"><span class="neutral"> (=</span>the infinitive without ‘to’<span class="neutral">)</span></span>: <span class="example"><span class="neutral">· </span>I suggest that she wait a few weeks.</span></span><span class="expl"><span class="neutral">• </span>You can also use the base form of the verb when talking about the past: <span class="example"><span class="neutral">· </span>I suggested that she wait a few weeks.</span></span><span class="expl"><span class="neutral">• </span>You <span class="expr">suggest that</span> someone <span class="expr">should do</span> something: <span class="example"><span class="neutral">· </span>The doctor suggested that she should sit down.</span></span><span class="expl last"><span class="neutral">• </span>In more formal English, you can say <span class="expr">it has been suggested that</span> something is true: <span class="example"><span class="neutral">· </span>It has been suggested that the substance can cause cancer.</span><span class="gramrefcont"><span>Grammar guide ‒ </span>VERBS</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_suggest_s10"></a><span class="thesbox"><span class="heading">THESAURUS</span><span class="section last"><span class="exponent"><span class="exp display">suggest</span><span class="def"> to tell someone your ideas about what they should do</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>'Why don’t you come with us?', Alan suggested.</span><span class="example"><span class="neutral">· </span>It was a sunny afternoon and so I suggested going to the beach.</span></span><span class="exponent"><span class="exp display">recommend</span><span class="def"> to suggest that someone goes somewhere, tries something etc because you know that it is good and you think they will like it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Can you recommend a good restaurant near here?</span><span class="example"><span class="neutral">· </span>It’s a brilliant book - I’d recommend it to anyone.</span></span><span class="exponent"><span class="exp display">propose</span><span class="registerlab"> formal</span><span class="def"> to formally suggest a plan or course of action, especially at a meeting</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The government has proposed building a new town just north of the city.</span><span class="example"><span class="neutral">· </span>I propose that we send a delegation to London to discuss our concerns.</span></span><span class="exponent"><span class="exp display">put forward</span><span class="def"> to suggest an idea, plan, reason etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The party has put forward new proposals to reduce the number of cars in the town centre.</span><span class="example"><span class="neutral">· </span>The idea was first put forward by Aristotle.</span><span class="example"><span class="neutral">· </span>A number of reasons have been put forward to explain these negative results.</span></span><span class="exponent"><span class="exp display">nominate</span><span class="def"> to officially suggest someone for an important job or position, or a person, film etc for a prize, especially when people will vote to make a decision</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He’s been nominated for the Nobel Prize in Literature several times.</span><span class="example"><span class="neutral">· </span>Three candidates have been nominated for the position of chairman.</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_1_suggest0205" preload="none" src="LDOCE6_hwd_bre_1_suggest0205.mp3"></audio><audio id="LDOCE6_hwd_ame_c_suggest" preload="none" src="LDOCE6_hwd_ame_c_suggest.mp3"></audio><audio id="LDOCE6_exa_bre_5_p008-001621239" preload="none" src="LDOCE6_exa_bre_5_p008-001621239.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-000631010" preload="none" src="LDOCE6_exa_bre_2_p008-000631010.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001621255" preload="none" src="LDOCE6_exa_bre_2_p008-001621255.mp3"></audio><audio id="LDOCE6_exa_bre_d_p008-001620902" preload="none" src="LDOCE6_exa_bre_d_p008-001620902.mp3"></audio><audio id="LDOCE6_exa_bre_4_ldoce5_exa001541" preload="none" src="LDOCE6_exa_bre_4_ldoce5_exa001541.mp3"></audio><audio id="LDOCE6_exa_bre_7_ldoce5_exa001542" preload="none" src="LDOCE6_exa_bre_7_ldoce5_exa001542.mp3"></audio><audio id="LDOCE6_exa_bre_b_ldoce5_exa001543" preload="none" src="LDOCE6_exa_bre_b_ldoce5_exa001543.mp3"></audio><audio id="LDOCE6_exa_bre_4_ldoce6exa_00859" preload="none" src="LDOCE6_exa_bre_4_ldoce6exa_00859.mp3"></audio><audio id="LDOCE6_exa_bre_7_ldoce5_exa001544" preload="none" src="LDOCE6_exa_bre_7_ldoce5_exa001544.mp3"></audio><audio id="LDOCE6_exa_bre_2_ldoce5_exa001545" preload="none" src="LDOCE6_exa_bre_2_ldoce5_exa001545.mp3"></audio><audio id="LDOCE6_exa_bre_d_p008-001621252" preload="none" src="LDOCE6_exa_bre_d_p008-001621252.mp3"></audio><audio id="LDOCE6_exa_bre_2_ldoce5_exa020270" preload="none" src="LDOCE6_exa_bre_2_ldoce5_exa020270.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-000631024" preload="none" src="LDOCE6_exa_bre_e_p008-000631024.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001621263" preload="none" src="LDOCE6_exa_bre_2_p008-001621263.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-000631051" preload="none" src="LDOCE6_exa_bre_a_p008-000631051.mp3"></audio><audio id="LDOCE6_exa_bre_a_ldoce5_exa001546" preload="none" src="LDOCE6_exa_bre_a_ldoce5_exa001546.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-000631047" preload="none" src="LDOCE6_exa_bre_f_p008-000631047.mp3"></audio>`;
