var custom_content = `<meta charset='utf-8'/><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_thanks_1"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_thanks_1').scrollIntoView();"> <i>interjection</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_thanks_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_1"></a><span class="entry"><span class="entryhead"><span class="hwd">thanks</span><span class="hyphenation frequent">thanks</span><span class="homnum frequent">1</span><span class="proncodes"><span class="neutral"> /</span><span class="pron">θæŋks</span><span class="neutral">/</span></span><span class="level tooltip"> ●●●</span> <span class="freq">S1</span><span class="pos"> interjection</span><span class="registerlab"> informal</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_1_brelasdethanks').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_0_thanks1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>"Do you want another cup of coffee?" "Oh, <span class="nodeword">thanks</span>."</li><li>Could you hold the door for me? <span class="nodeword">Thanks</span>.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">ways of politely saying no when someone offers you something</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">no thanks/no thank you</span></span><div class="content"><span class="def"> use this as a polite way of saying no when someone offers you something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"More coffee?" "No thanks."</span><span class="example"><span class="neutral">· </span>If someone offers you a cigarette, just say no thanks.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thanks all the same</span></span><div class="content"><span class="def"> use this when you do not want something which someone has offered you, but you are grateful that they asked</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Do you want to come in our car?" "I've already got a lift, thanks all the same."</span><span class="example"><span class="neutral">· </span>"Can you come for dinner on Saturday?" "Thanks all the same, but I'm afraid I'm busy on Saturday."</span></div></span></span><span class="section last"><span class="secheading">what you say when you thank someone</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thank you</span></span><div class="content"><span class="def"> say this when you want to thank someone politely</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Would you like to come to dinner with us on Sunday?" "Thank you, I'd love to."</span><span class="thespropform">thank you for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Thank you for a splendid evening. I really enjoyed myself.</span><span class="example"><span class="neutral">· </span>Thank you for letting me stay.</span><span class="thespropform">thank you very much</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>It's lovely. Thank you very much.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thanks</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to thank someone for something they have just done or given you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Here's your coffee." "Thanks, Mom."</span><span class="thespropform">thanks for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Thanks for coming. Hope we see you again next year.</span><span class="thespropform">thanks to somebody</span><span class="gloss"><span class="neutral"> (=</span>used in speeches<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Thanks to everyone for all the cards and flowers. They really cheered me up.</span><span class="thespropform">thanks a lot/a million</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Here, let me help you." "Thanks a lot. That's great."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">ta/cheers</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">British informal spoken</span><span class="def"> say this when you want to thank someone for something they have just done or given you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Here's the book you wanted." "Ta."</span><span class="example"><span class="neutral">· </span>"I've made you a cup of tea." "Cheers."</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">that's very kind of you/good of you</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal spoken</span><span class="def"> say this when someone has generously offered to do something for you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>"Here, you can have my seat." "Thank you, that's very kind of you."</span><span class="example"><span class="neutral">· </span>"Would you like me to carry your shopping?" "That's very good of you!"</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I appreciate it</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you want to thank someone who has done a lot to help you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Thanks for helping out on a Sunday - I appreciate it.</span><span class="thespropform">I really appreciate it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I couldn't have managed without your support and encouragement. I really appreciate it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">you shouldn't have</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> say this when you want to thank someone who has given you something, especially something expensive</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>What a beautiful ring! Oh Mike, you shouldn't have!</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">you've saved my life</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to thank someone who has got you out of a difficult situation, or solved a problem for you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You've saved my life, Jim! Thank goodness you were here.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">I'd like to thank</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken</span><span class="def"> use this to thank someone in a formal speech</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I would like to thank everyone who helped at the school fair.</span><span class="thespropform">I'd like to thank somebody for (doing) something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'd like to thank Betty and Jim for organizing this wonderful party.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">many thanks</span></span><div class="content"><span class="def"> use this to thank someone, especially when writing a letter</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We received the pictures on Wednesday. Many thanks.</span><span class="thespropform">many thanks for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Many thanks for your letter of the other day.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">Thanks a lot</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001629003').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">Thanks a lot</span> for the drink.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">Thanks very much</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001629006').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">Thanks very much</span> for your help.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">Many thanks</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001827764').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">Many thanks</span> for the lovely flowers.</span></div></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">express your thanks/gratitude/appreciation</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>say thank you to someone, in a speech<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>On behalf of the team, I’d like to express our appreciation for all your efforts.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">extend ... thanks</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001657893').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’d like to <span class="colloinexa">extend</span> my <span class="colloinexa">thanks</span> to all the catering staff.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">I’m fine (thanks/thank you)</span></span><div class="content"><span class="registerlab"> spoken</span><span class="collgloss"><span class="neutral"> (=</span>used when telling someone that you do not want any more when they offer you something<span class="neutral">)</span></span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001255673').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘More coffee?’ ‘No, <span class="colloinexa">I’m fine</span>, thanks.’</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">grateful thanks</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001333968').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Our <span class="colloinexa">grateful thanks</span> go to all who participated.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a letter of thanks/introduction/complaint etc</span></span><div class="content"><span class="example"><span class="neutral">· </span>I finally got a letter of apology from the company.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">No thanks</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000243386').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘Do you want any more?’ ‘<span class="colloinexa">No thanks</span>.’</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">sincere thanks/thank you/gratitude</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-000687279').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I would like to say a sincere thank you to everyone who has helped and supported me.</span></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">no thanks</span></span><div class="content"><ul class="exas"><li><span class="nodeword">Thanks</span>, but - no thanks!</li><li>Carrie said we could do another screen but I said no <span class="nodeword">thanks</span>.</li><li>He offers me a free go too but I just stick my nose in the air and say no <span class="nodeword">thanks</span>.</li><li>I offer to buy him a fibreglass canoe but he says no <span class="nodeword">thanks</span>, he already has one.</li><li>I threw the last one's sketches across his office and told him: shoddy, unexciting, no <span class="nodeword">thanks</span>.</li><li>No great compliments, no <span class="nodeword">thanks</span>.</li><li>She didn't think that there was anything broken ... but no <span class="nodeword">thanks</span> to Johnny, she thought bitterly.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">thanks a lot</span></span><div class="content"><ul class="exas"><li>"I forgot to bring your money." "Well, <span class="nodeword">thanks</span> a lot!"</li><li>Gee, <span class="nodeword">thanks</span> a lot for saving me from Madonna in the morning.</li><li>Hey, <span class="nodeword">thanks</span> a lot, Wyatt.</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">a chorus of thanks/disapproval/protest etc</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">debt of gratitude/thanks</span></span><div class="content"><ul class="exas"><li>In addition to that great teacher of prayer, there were others to whom I owe a <span class="nodeword">debt</span> of gratitude.</li><li>Some say it is because of Baba Firdaus and his holy life, others say because Amanullah Khan paid his <span class="nodeword">debt</span> of gratitude.</li><li>The present writer owes him a particularly deep <span class="nodeword">debt</span> of gratitude.</li><li>Their varied tasks in the Library have all contributed to its success and we owe them a very real <span class="nodeword">debt</span> of gratitude.</li><li>We all owe him a great <span class="nodeword">debt</span> of gratitude, and we wish him well.</li><li>We all owe Solly a deep <span class="nodeword">debt</span> of gratitude for his services to the cause of jazz for so many years.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">many thanks</span></span><div class="content"><ul class="exas"><li><span class="nodeword">Many</span> thanks for your letter of the other day.</li><li>We received the pictures on Wednesday. <span class="nodeword">Many</span> thanks.</li><li>Finally very <span class="nodeword">many</span> thanks are again due to everyone who contributed to this edition.</li><li>Finally, <span class="nodeword">many</span> thanks to the parents and all the other folk who helped us by being part of the duty rota.</li><li>I stood up swiftly, telling him <span class="nodeword">many</span> thanks for bothering.</li><li>In anticipation, <span class="nodeword">many</span> thanks for your assistance.</li><li>Letter from the Chairman Very <span class="nodeword">many</span> thanks to all of you who returned the questionnaire.</li><li>Once again, <span class="nodeword">many</span> thanks for your assistance in this matter.</li><li>Thanks to Sam Wright for the transcription and <span class="nodeword">many</span> thanks for reading.</li><li>Very <span class="nodeword">many</span> thanks for your help.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">propose a vote of thanks (to somebody)</span></span><div class="content"><ul class="exas"><li>Chairman Gerald Davies proposed a <span class="nodeword">vote</span> of thanks.</li><li>I did listen to him proposing a <span class="nodeword">vote</span> of thanks occasionally, and I was always glad when he sat down.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">a word of warning/caution/advice/thanks etc</span></span><div class="content"><ul class="exas"><li>And a <span class="nodeword">word</span> of advice just you be careful of that old goat, Moreton.</li><li>And a <span class="nodeword">word</span> of warning - when you arrive in Hye-on-Wye beware of the second-hand bookshops.</li><li>But business advisors in the city have sounded a <span class="nodeword">word</span> of caution.</li><li>He would hand her a sheaf of uncounted bank notes, which she received without a <span class="nodeword">word</span> of thanks.</li><li>However, a <span class="nodeword">word</span> of caution is in order.</li><li>Now a <span class="nodeword">word</span> of warning about the stitch patterns in the pattern book.</li><li>Take a <span class="nodeword">word</span> of advice, Mr Watt.</li><li>You won't mind a <span class="nodeword">word</span> of advice?</li></ul></div></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_1_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">used to tell someone that you are grateful for something they have given you or done for you</span><span class="syn"> <span class="synopp">SYN</span>  thank you</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-000593080"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000593080').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘Pass the salt, please ... thanks.’</span><span class="gramexa"><span class="propformprep">thanks for</span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001629003"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001629003').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">Thanks a lot</span> for the drink.</span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001629006"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001629006').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">Thanks very much</span> for your help.</span><span class="example" id="anchor_LDOCE6_exa_bre_2_p008-001827764"><a onclick="document.getElementById('LDOCE6_exa_bre_2_p008-001827764').play();"><img src="LDOCE6_img_spkr_g.png"/></a> <span class="colloinexa">Many thanks</span> for the lovely flowers.</span></span><span class="gramexa"><span class="propform">thanks for doing something</span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-000593083"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-000593083').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’d love to go to the party. Thanks for asking me.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_1_s2"></a><span class="sense newline"><span class="sensenum">2</span><span class="def">used as a polite way of accepting something that someone has offered you</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_a_p008-000593096"><a onclick="document.getElementById('LDOCE6_exa_bre_a_p008-000593096').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘Do you want another cup of coffee?’ ‘Oh, thanks.’</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_1_s3"></a><span class="sense newline"><span class="sensenum">3</span><span class="registerlab">spoken</span><span class="def"> used when politely answering someone’s question</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_0_p008-001629014"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-001629014').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘Hi, Bill, how are you?’ ‘Fine, thanks.’</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_1_s4"></a><span class="sense newline"><span class="sensenum">4</span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_1_s5"></a><span class="lexunit">no thanks</span><span class="def"> used to say politely that you do not want something</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-000593107"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-000593107').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘How about some cake?’ ‘Oh, no thanks, I’m on a diet.’</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_1_s6"></a><span class="sense newline"><span class="sensenum">5</span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_1_s7"></a><span class="lexunit">thanks a lot</span><span class="variant"><span class="neutral"> (</span><span class="linkword">also</span><span class="lexvar"> thanks a bunch</span><span class="geo"> American English</span><span class="registerlab"> spoken</span><span class="neutral">)</span></span><span class="def"> used when really you are annoyed about something and you do not mean ‘thank you’ at all</span></span></span></div><a id="6504937708254a64972bf4d4ccdfcc9d_LDOCE6_thanks_2"></a><div><div class="chwd"><a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_thanks_1').scrollIntoView();"> <i>interjection</i></a> | <a onclick="document.getElementById('6504937708254a64972bf4d4ccdfcc9d_LDOCE6_thanks_2').scrollIntoView();"> <i>noun</i></a></div><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_2"></a><span class="entry"><span class="entryhead"><span class="hwd">thanks</span><span class="hyphenation frequent">thanks</span><span class="homnum frequent">2</span><span class="level tooltip"> ●●○</span> <span class="freq">W3</span><span class="pos"> noun</span><span class="gram"> [plural]</span> <a onclick="document.getElementById('LDOCE6_hwd_bre_1_brelasdethanks').play();"><img src="LDOCE6_img_spkr_r.png"/></a> <a onclick="document.getElementById('LDOCE6_hwd_ame_0_thanks1').play();"><img src="LDOCE6_img_spkr_b.png"/></a><span class="buttons"><span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word Origin</span><div class="at-link"><span class="entry"><span class="popheader popetym">WORD ORIGIN</span><span class="hyphenation">thanks</span><span class="homnum">2</span><div class="etymology"><b>Origin:</b></div><span class="etymsense"><span class="d"></span><span class="etymcentury">1300-1400</span> Plural of <span class="etymorigin">thank</span> <span class="etymtran"> ‘gratefulness’</span> <span class="etymcentury">(11-17 centuries)</span>, from <span class="etymlang">Old English</span> <span class="etymorigin">thanc</span> <span class="etymtran"> ‘thought, gratefulness’</span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Examples</span><div class="at-link"><span class="entry"><span class="popheader popexa">EXAMPLES FROM OTHER DICTIONARIES</span><ul class="exas"><li>He won't get any <span class="nodeword">thanks</span> from them for being so honest.</li><li>He wrote me a short letter of <span class="nodeword">thanks</span>.</li><li>I'd like to propose a vote of <span class="nodeword">thanks</span> to Sandra for organizing the whole evening.</li><li>Please accept this bottle of champagne as our <span class="nodeword">thanks</span> for organizing the seminar.</li><li>She got up and left without a word of <span class="nodeword">thanks</span>.</li><li>She received a personal letter of <span class="nodeword">thanks</span> from the president.</li></ul></span><span class="entry"><span class="popheader popexa">EXAMPLES FROM THE CORPUS</span><ul class="exas"><li>And Ley and EconoMax supermarkets are seeing profits rise <span class="nodeword">thanks</span> to maquila food coupons.</li><li>I expressed my <span class="nodeword">thanks</span> and sat down.</li><li>In it she expressed her <span class="nodeword">thanks</span> and gratitude to him for changing her whole dying experience.</li><li>Nobody commented; after brief <span class="nodeword">thanks</span> and farewells, Jack departed and the two friends went indoors.</li><li>Thereafter the Bambino became an object of pilgrimage and there are <span class="nodeword">thanks</span> offerings all around.</li></ul></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Thesaurus</span><div class="at-link"><span class="entry"><span class="popheader popthes">Longman Language Activator</span><span class="section"><span class="secheading">dressed, arranged, decorated etc in a beautiful way</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">because</span></span><div class="content"><span class="def"> use this when you are explaining the reason why something happens or why you do something </span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She's in a bad mood because her father won't let her go to the party tonight.</span><span class="example"><span class="neutral">· </span>"This photograph doesn't look like you." "That's because it isn't me - it's my sister".</span><span class="example"><span class="neutral">· </span>Because you've done such a good job, I'm giving everyone a 10% bonus.</span><span class="thespropform">just because</span><span class="gloss"><span class="neutral"> (=</span>used when you think an explanation is not a good enough reason for something<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>You mean you dumped him just because he forgot your birthday?</span><span class="thespropform">simply because</span><span class="gloss"><span class="neutral"> (=</span>used when there is a very simple reason for something<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We're not going on holiday this year, simply because we can't afford it.</span><span class="thespropform">because of something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I had to move because of my job.</span><span class="example"><span class="neutral">· </span>Because of the increase in street crime, many old people are afraid to leave their homes.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">since</span></span><div class="content"><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">as</span><span class="neutral"> </span><span class="registerlab">British</span></span><span class="def"> use this to give the reason why someone decides to do something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We had planned to play tennis but since it was raining we decided to go swimming instead.</span><span class="example"><span class="neutral">· </span>Since you're going to be in the area anyway, you can pick up the order for me.</span><span class="example"><span class="neutral">· </span>As he wasn't well, I offered to do the shopping.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">due to/owing to</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> used especially in official statements to explain what causes a particular problem</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Our flight was delayed due to poor weather conditions.</span><span class="example"><span class="neutral">· </span>Owing to circumstances beyond our control, we regret to inform customers that this store will close early.</span><span class="example"><span class="neutral">· </span>In the end I was unable to attend the conference, owing to financial difficulties.</span><span class="thespropform">be due to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The accident was due to a concrete block thrown from a bridge.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thanks to</span></span><div class="content"><span class="def"> use this to explain that something has been possible because of someone's actions or because something is very good, very effective etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Today thanks to the Internet, you can do all your Christmas shopping from home.</span><span class="example"><span class="neutral">· </span>The play was a great success thanks to the effort and commitment of everyone involved.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">as a result of</span></span><div class="content"><span class="def"> use this when you are explaining what made something happen, especially something unpleasant</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Many people are now homeless as a result of the civil war.</span><span class="thespropform">as a direct result of</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Mr Logan died as a direct result of the injuries he received in the accident.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">the reason ... is</span></span><div class="content"><span class="def"> use this when you are explaining something carefully, especially when you have been asked to explain why something happened</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The reason we didn't consider her for the job was that she didn't have enough experience.</span><span class="example"><span class="neutral">· </span>The reason we are here this evening is to say thank you to Brian for all his hard work.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">through</span></span><div class="content"><span class="def"> use this when you are explaining why someone or something has succeeded or failed</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We succeeded through sheer hard work.</span><span class="example"><span class="neutral">· </span>Hundreds of working days have been lost this year through illness.</span><span class="example"><span class="neutral">· </span>The Community Association collapsed through lack of support.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">out of</span></span><div class="content"><span class="def"> use this when someone does something because of a particular feeling</span><span class="thespropform"><span class="neutral">: </span>out of interest/curiosity/desperation etc</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She opened the letter, just out of curiosity.</span><span class="example"><span class="neutral">· </span>I came to you out of desperation - you've got to help me.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">on account of</span></span><div class="content"><span class="def"> use this when you want to give the reason why something is necessary, impossible, or true</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We had to move to London on account of my job.</span><span class="example"><span class="neutral">· </span>They're called the Black Hills on account of their color.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">seeing as</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">spoken informal</span><span class="def"> use this to give a reason for what you are suggesting or deciding</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Seeing as it's your birthday, why don't we go out for a meal?</span><span class="example"><span class="neutral">· </span>I'd better do it myself, seeing as no one else wants to do it.</span></div></span></span><span class="section"><span class="secheading">something that you say or do to thank someone</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thanks</span></span><div class="content"><span class="def"> what you say or do to thank someone</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He won't get any thanks from them for being so honest.</span><span class="thespropform">thanks for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Please accept this bottle of champagne as our thanks for organizing the seminar.</span><span class="thespropform">letter/message etc of thanks</span><span class="gloss"><span class="neutral"> (=</span>expressing thanks<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He wrote me a short letter of thanks.</span><span class="thespropform">without a word of thanks</span><span class="gloss"><span class="neutral"> (=</span>without saying thank you<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>She got up and left without a word of thanks.</span><span class="thespropform">a vote of thanks</span><span class="gloss"><span class="neutral"> (=</span>a formal, public expression of thanks, especially at a meeting<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I'd like to propose a vote of thanks to Sandra for organizing the whole evening.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thank-you letter/note</span></span><div class="content"><span class="def"> a letter that you send to someone to thank them, for example when they have given you a present or when you have stayed at their house</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We spent three days after the wedding writing thank-you letters for all the presents we'd had.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">acknowledgements</span></span><div class="content"><span class="def"> a note at the end or beginning of a book, article etc, in which the writer thanks all the people who helped him or her to produce it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In the acknowledgements the authors thanked everyone who'd contributed to the book.</span><span class="example"><span class="neutral">· </span>Her book was based on her ex-husband's letters and yet his name did not even appear in the acknowledgements.</span></div></span></span><span class="section last"><span class="secheading">to say thank you to someone</span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">thank</span></span><div class="content"><span class="def"> to tell someone that you are pleased and grateful for something they have given you or done for you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I spent three hours helping her and she didn't even thank me.</span><span class="thespropform">thank somebody for (doing) something</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We must write and thank Cathy for the present.</span><span class="example"><span class="neutral">· </span>The Governor publicly thanked the people of Arizona for supporting him during his campaign.</span><span class="thespropform">thank somebody profusely</span><span class="gloss"><span class="neutral"> (=</span>thank someone a lot in a very obvious way<span class="neutral">)</span></span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>A relieved Mr Maxwell thanked his lawyer profusely.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">say thank you</span></span><div class="content"><span class="def"> to thank someone for what they have done</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I wanted to come round and say thank you in person.</span><span class="thespropform">say thank you to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>This little gift is our way of saying thank you to everyone who worked so hard.</span><span class="thespropform">say thank you for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>Make sure you say thank you for your birthday presents.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">show your appreciation</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> to show someone that you are grateful for something they have done, by giving them something or by doing something special for them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He found a special way of showing his appreciation -- dinner at the Ritz.</span><span class="thespropform">show your appreciation for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>We have decided to pay a special bonus to everyone on the staff to show our appreciation for their hard work during the year.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">express your thanks/gratitude</span></span><div class="content"><span class="neutral"> </span><span class="registerlab">formal</span><span class="def"> to say how grateful you are to someone for something they have done - use this in a speech or in a letter</span><span class="thespropform"><span class="neutral">: </span>express your thanks/gratitude for</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>I should like to express my thanks for all that you have done.</span><span class="thespropform">express your thanks/gratitude to</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He made a short speech expressing his gratitude to all those who had sent him letters of support.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">acknowledge</span></span><div class="content"><span class="def"> to tell people publicly that someone has done something to help you and that you are grateful for it</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>In his speech he acknowledged the help his parents gave him at the start of his career.</span><span class="example"><span class="neutral">· </span>I did a lot of work on that book. It annoys me that the editor never acknowledged it.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">recognize</span></span><div class="content"><span class="variant"> <span class="varitype">also</span><span class="neutral"> </span><span class="lexvar">recognise</span><span class="neutral"> </span><span class="registerlab">British</span></span><span class="def"> if a country or a large organization recognizes something important that someone has done for them, they officially and publicly thank them, by giving them something or doing something special for them</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>The government recognized his bravery in the battle by awarding him the Military Cross.</span><span class="example"><span class="neutral">· </span>Her contribution to horticulture was recognized when a new rose was named after her.</span></div></span><span class="exponent"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="exp display">in recognition of something</span></span><div class="content"><span class="def"> if you are given something in recognition of something you have done, it is publicly given to you as a way of thanking you</span><span class="neutral">: </span><span class="example"><span class="neutral">· </span>He was awarded a knighthood in recognition of his work for charity.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Collocations</span><div class="at-link"><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE ENTRY</span><span class="tail"><span class="section"><span class="secheading">verbs</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">express your thanks</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>say that you are grateful<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I would like to express my thanks to you for all your support.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">offer/extend your thanks to somebody</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>thank someone publicly<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>We would also like to extend our heartfelt thanks to the medical staff at Broadgreen Hospital.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">give thanks</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>thank God<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Let us give thanks for the beauty of the world.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">thanks go to somebody</span></span><div class="content"><span class="example"><span class="neutral">· </span>Above all, our thanks go to Barbara Lambourne.</span></div></span></span><span class="section last"><span class="secheading">adjectives</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">grateful thanks</span></span><div class="content"><span class="example"><span class="neutral">· </span>Our grateful thanks go to everyone who took part in the event.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">sincere/heartfelt thanks</span></span><div class="content"><span class="example"><span class="neutral">· </span>Please accept my most sincere thanks for all your help.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">special thanks</span></span><div class="content"><span class="example"><span class="neutral">· </span>Special thanks are due to John Bryden for his advice.</span></div></span></span></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM OTHER ENTRIES</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">express your thanks/gratitude/appreciation</span></span><div class="content"><span class="collgloss"><span class="neutral"> (=</span>say thank you to someone, in a speech<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>On behalf of the team, I’d like to express our appreciation for all your efforts.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">extend ... thanks</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001657893').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I’d like to <span class="colloinexa">extend</span> my <span class="colloinexa">thanks</span> to all the catering staff.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">I’m fine (thanks/thank you)</span></span><div class="content"><span class="registerlab"> spoken</span><span class="collgloss"><span class="neutral"> (=</span>used when telling someone that you do not want any more when they offer you something<span class="neutral">)</span></span><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_4_p008-001255673').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘More coffee?’ ‘No, <span class="colloinexa">I’m fine</span>, thanks.’</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">grateful thanks</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_e_p008-001333968').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Our <span class="colloinexa">grateful thanks</span> go to all who participated.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">a letter of thanks/introduction/complaint etc</span></span><div class="content"><span class="example"><span class="neutral">· </span>I finally got a letter of apology from the company.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">No thanks</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000243386').play();"><img src="LDOCE6_img_spkr_g.png"/></a> ‘Do you want any more?’ ‘<span class="colloinexa">No thanks</span>.’</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">sincere thanks/thank you/gratitude</span></span><div class="content"><span class="example"><a onclick="document.getElementById('LDOCE6_exa_bre_0_p008-000687279').play();"><img src="LDOCE6_img_spkr_g.png"/></a> I would like to say a sincere thank you to everyone who has helped and supported me.</span></div></span></span><span class="entry"><span class="popheader popcollo">COLLOCATIONS FROM THE CORPUS</span><span class="section"><span class="secheading">ADJECTIVE</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">fine</span></span><div class="content"><span class="example"><span class="neutral">· </span>I can make him out just <span class="colloinexa">fine, thanks</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">grateful</span></span><div class="content"><span class="example"><span class="neutral">· </span>To the people who had already heard of her illness and sent her cards and good wishes Molly sends her <span class="colloinexa">grateful thanks</span>.</span><span class="example"><span class="neutral">· </span>The Bishop tried hard to express his <span class="colloinexa">grateful thanks</span>.</span><span class="example"><span class="neutral">· </span>Our <span class="colloinexa">grateful thanks</span> to Unwins and Suttons seeds for allowing us to use their original seed catalogues.</span><span class="example"><span class="neutral">· </span>I extend to them our <span class="colloinexa">grateful thanks</span>.</span><span class="example"><span class="neutral">· </span>Three Cheers for all of your, and <span class="colloinexa">grateful thanks</span> for your response to our call for new members during last year.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">sincere</span></span><div class="content"><span class="example"><span class="neutral">· </span>To all these - whether their names are recorded or not - I offer <span class="colloinexa">sincere thanks</span>.</span><span class="example"><span class="neutral">· </span>Please accept my most <span class="colloinexa">sincere thanks</span> for your interest.</span><span class="example"><span class="neutral">· </span>Central Council extends its <span class="colloinexa">sincere thanks</span> to them all.</span><span class="example"><span class="neutral">· </span>The Society would like to express their <span class="colloinexa">sincere thanks</span> to Mr Wheeler for the gifts of these photos and information.</span><span class="example"><span class="neutral">· </span>The Society wish to extend their <span class="colloinexa">sincere thanks</span> to Mr Bayes for information and photos.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">special</span></span><div class="content"><span class="example"><span class="neutral">· </span>A <span class="colloinexa">special thanks</span> to our prayer support group who meet regularly at John and Rosemary Turner's.</span><span class="example"><span class="neutral">· </span>Elton, 45, had <span class="colloinexa">special</span> words of <span class="colloinexa">thanks</span> for Liz and Larry.</span><span class="example"><span class="neutral">· </span>A <span class="colloinexa">special</span> vote of <span class="colloinexa">thanks</span> was given on behalf of Adult Education in Kingston.</span><span class="example"><span class="neutral">· </span>It looks really <span class="colloinexa">special thanks</span> to a superb print in shades of blue, apple and black on white.</span><span class="example"><span class="neutral">· </span>And a <span class="colloinexa">special thanks</span> to all the families of our staff, who know provide an awful lot of support and encouragement.</span><span class="example"><span class="neutral">· </span><span class="colloinexa">Special thanks</span> Special thanks from Link to office services manager, for her considerable help with this Focus.</span><span class="example"><span class="neutral">· </span>A <span class="colloinexa">special</span> word of <span class="colloinexa">thanks</span> to Lucy Jackson, who although away had arranged for her daughter to make an enormous cake.</span></div></span></span><span class="section last"><span class="secheading">VERB</span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">express</span></span><div class="content"><span class="example"><span class="neutral">· </span>The Bishop tried hard to <span class="colloinexa">express</span> his grateful <span class="colloinexa">thanks</span>.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">expressed</span> my <span class="colloinexa">thanks</span> and sat down.</span><span class="example"><span class="neutral">· </span>The committee <span class="colloinexa">express</span> their <span class="colloinexa">thanks</span> for the work he has done in a voluntary capacity over the past months.</span><span class="example"><span class="neutral">· </span>Perhaps, he thought with sudden abandon, he should give Edith Mallory a ring this very moment and <span class="colloinexa">express</span> his <span class="colloinexa">thanks</span>.</span><span class="example"><span class="neutral">· </span>But Steffi fans will like to <span class="colloinexa">express</span> their <span class="colloinexa">thanks</span> and wish her well.</span><span class="example"><span class="neutral">· </span>Perkins says he included the song as a way of <span class="colloinexa">expressing thanks</span> to McCartney.</span><span class="example"><span class="neutral">· </span>The Society would like to <span class="colloinexa">express</span> their sincere <span class="colloinexa">thanks</span> to Mr Wheeler for the gifts of these photos and information.</span><span class="example"><span class="neutral">· </span>As she rises from her seat to return to work, she <span class="colloinexa">expresses</span> her <span class="colloinexa">thanks</span>.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">extend</span></span><div class="content"><span class="example"><span class="neutral">· </span>Central Council <span class="colloinexa">extends</span> its sincere <span class="colloinexa">thanks</span> to them all.</span><span class="example"><span class="neutral">· </span>We would also like to <span class="colloinexa">extend</span> our heartfelt <span class="colloinexa">thanks</span> to the medical staff at Broadgreen Hospital.</span><span class="example"><span class="neutral">· </span>Central Council welcomes the new members and <span class="colloinexa">extends</span> its <span class="colloinexa">thanks</span> to all those whose membership has ended.</span><span class="example"><span class="neutral">· </span>The Society wish to <span class="colloinexa">extend</span> their sincere <span class="colloinexa">thanks</span> to Mr Bayes for information and photos.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">give</span></span><div class="content"><span class="example"><span class="neutral">· </span>However modern incinerators <span class="colloinexa">give</span> off less dioxins <span class="colloinexa">thanks</span> to tougher design standards.</span><span class="example"><span class="neutral">· </span>We should appreciate their help and <span class="colloinexa">give thanks</span> for it but never waste our strength worrying about its fairness.</span><span class="example"><span class="neutral">· </span>The ringing stopped, and <span class="colloinexa">gave</span> a silent <span class="colloinexa">thanks</span> to St Bernadette of Lourdes for the miracle.</span><span class="example"><span class="neutral">· </span>We <span class="colloinexa">gave thanks</span> and praise-bes throughout the day and were taught to begin and end our day in communion with the Lord.</span><span class="example"><span class="neutral">· </span>I wondered for a moment whether I was dreaming, and then silently <span class="colloinexa">gave thanks</span>.</span><span class="example"><span class="neutral">· </span>People came over to praise him and were <span class="colloinexa">given</span> his amused <span class="colloinexa">thanks</span>.</span><span class="example"><span class="neutral">· </span>We recall <span class="colloinexa">giving</span> silent <span class="colloinexa">thanks</span> that no one had ten bob for the juke box.</span><span class="example"><span class="neutral">· </span>Above all we <span class="colloinexa">give thanks</span> to thee for that thou art mighty.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">offer</span></span><div class="content"><span class="example"><span class="neutral">· </span>To all these - whether their names are recorded or not - I <span class="colloinexa">offer</span> sincere <span class="colloinexa">thanks</span>.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">offered</span> my <span class="colloinexa">thanks</span> to Oliver, and even suggested some debt restructuring by way of gratitude, but he just shrugged it off.</span><span class="example"><span class="neutral">· </span>Ashley <span class="colloinexa">offered</span> up a fervent <span class="colloinexa">thanks</span>.</span><span class="example"><span class="neutral">· </span>Instead he went to St-Denis to <span class="colloinexa">offer thanks</span>, and from there to St-Germain-des-Prés.</span><span class="example"><span class="neutral">· </span>This had dropped him at a garage in Cromcruach and had then mysteriously vanished before he could <span class="colloinexa">offer</span> his <span class="colloinexa">thanks</span>.</span><span class="example"><span class="neutral">· </span>I <span class="colloinexa">offered</span> my <span class="colloinexa">thanks</span> to the interviewer for seeing me and shook his/her hand at the end. 15.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">receive</span></span><div class="content"><span class="example"><span class="neutral">· </span>If any teacher has not <span class="colloinexa">received</span> a card of <span class="colloinexa">thanks</span> to take to her class, please let me know.</span></div></span><span class="collocate"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="colloc collo">send</span></span><div class="content"><span class="example"><span class="neutral">· </span>To the people who had already heard of her illness and <span class="colloinexa">sent</span> her cards and good wishes Molly sends her grateful <span class="colloinexa">thanks</span>.</span><span class="example"><span class="neutral">· </span>They also <span class="colloinexa">send thanks</span> to Mrs Smith and her friends who raised almost £400 at a lunch and bridge party.</span><span class="example"><span class="neutral">· </span>The Legion <span class="colloinexa">sends thanks</span> for your past support and asks for helpers with this year's Poppy Appeal.</span><span class="example"><span class="neutral">· </span>She <span class="colloinexa">sends</span> a message of <span class="colloinexa">thanks</span> to all those who made her birthday so happy and memorable.</span></div></span></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Phrases</span><div class="at-link"><span class="entry"><span class="popheader popphrase">PHRASES FROM THE ENTRY</span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">thanks to somebody/something</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">no thanks to somebody/something</span></span><div class="content"><ul class="exas"><li>Everyone got out safely, no <span class="nodeword">thanks</span> to the smoke alarm - its batteries were dead.</li><li>Glorious because they came away with a victory over Rosslyn Park ... but no <span class="nodeword">thanks</span> to the quagmire at the Kingsholm ground.</li><li>She didn't think that there was anything broken ... but no <span class="nodeword">thanks</span> to Johnny, she thought bitterly.</li></ul></div></span></span><span class="entry"><span class="popheader popphrase">PHRASES FROM OTHER ENTRIES</span><span class="phrase"><span>► </span><span class="phrasetext"><span class="text">a chorus of thanks/disapproval/protest etc</span></span></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">debt of gratitude/thanks</span></span><div class="content"><ul class="exas"><li>In addition to that great teacher of prayer, there were others to whom I owe a <span class="nodeword">debt</span> of gratitude.</li><li>Some say it is because of Baba Firdaus and his holy life, others say because Amanullah Khan paid his <span class="nodeword">debt</span> of gratitude.</li><li>The present writer owes him a particularly deep <span class="nodeword">debt</span> of gratitude.</li><li>Their varied tasks in the Library have all contributed to its success and we owe them a very real <span class="nodeword">debt</span> of gratitude.</li><li>We all owe him a great <span class="nodeword">debt</span> of gratitude, and we wish him well.</li><li>We all owe Solly a deep <span class="nodeword">debt</span> of gratitude for his services to the cause of jazz for so many years.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">many thanks</span></span><div class="content"><ul class="exas"><li><span class="nodeword">Many</span> thanks for your letter of the other day.</li><li>We received the pictures on Wednesday. <span class="nodeword">Many</span> thanks.</li><li>Finally very <span class="nodeword">many</span> thanks are again due to everyone who contributed to this edition.</li><li>Finally, <span class="nodeword">many</span> thanks to the parents and all the other folk who helped us by being part of the duty rota.</li><li>I stood up swiftly, telling him <span class="nodeword">many</span> thanks for bothering.</li><li>In anticipation, <span class="nodeword">many</span> thanks for your assistance.</li><li>Letter from the Chairman Very <span class="nodeword">many</span> thanks to all of you who returned the questionnaire.</li><li>Once again, <span class="nodeword">many</span> thanks for your assistance in this matter.</li><li>Thanks to Sam Wright for the transcription and <span class="nodeword">many</span> thanks for reading.</li><li>Very <span class="nodeword">many</span> thanks for your help.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">propose a vote of thanks (to somebody)</span></span><div class="content"><ul class="exas"><li>Chairman Gerald Davies proposed a <span class="nodeword">vote</span> of thanks.</li><li>I did listen to him proposing a <span class="nodeword">vote</span> of thanks occasionally, and I was always glad when he sat down.</li></ul></div></span><span class="phrase"><span class="expandable" onclick="d8018d6852bc49e3b3e655364cf1439c.toggle(this)"><span class="arrow">►</span> <span class="phrasetext">a word of warning/caution/advice/thanks etc</span></span><div class="content"><ul class="exas"><li>And a <span class="nodeword">word</span> of advice just you be careful of that old goat, Moreton.</li><li>And a <span class="nodeword">word</span> of warning - when you arrive in Hye-on-Wye beware of the second-hand bookshops.</li><li>But business advisors in the city have sounded a <span class="nodeword">word</span> of caution.</li><li>He would hand her a sheaf of uncounted bank notes, which she received without a <span class="nodeword">word</span> of thanks.</li><li>However, a <span class="nodeword">word</span> of caution is in order.</li><li>Now a <span class="nodeword">word</span> of warning about the stitch patterns in the pattern book.</li><li>Take a <span class="nodeword">word</span> of advice, Mr Watt.</li><li>You won't mind a <span class="nodeword">word</span> of advice?</li></ul></div></span></span></div> <span class="popup-button" onclick="d8018d6852bc49e3b3e655364cf1439c.showAtLink(this)">Word family</span><div class="at-link"><span class="entry"><span class="popheader popwf">WORD FAMILY</span><span class="wf"><span class="group"><span class="pos">noun</span><span class="w"><span class="wfwd">thanks</span></span><span class="w"><span class="wfwd">thankfulness</span></span></span><span class="group"><span class="pos">adjective</span><span class="w"><span class="wfwd">thankful</span></span><span class="w"><span class="wfwd">thankless</span></span></span><span class="group"><span class="pos">verb</span><span class="w"><span class="wfwd">thank</span></span></span><span class="group"><span class="pos">adverb</span><span class="w"><span class="wfwd">thankfully</span></span></span></span></span></div> </span></span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_2_s1"></a><span class="sense newline"><span class="sensenum">1</span><span class="def">the things you say or do to show that you are grateful to someone</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-000593117"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-000593117').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Joe got up and left without <span class="colloinexa">a word of thanks</span>.</span><span class="gramexa"><span class="propformprep">thanks to</span><span class="example" id="anchor_LDOCE6_exa_bre_7_p008-001629032"><a onclick="document.getElementById('LDOCE6_exa_bre_7_p008-001629032').play();"><img src="LDOCE6_img_spkr_g.png"/></a> My thanks to all of you for your help.</span></span></span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_2_s2"></a><span class="sense newline"><span class="sensenum">2</span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_2_s3"></a><span class="lexunit">thanks to somebody/something</span><span class="registerlab"> informal</span><span class="def"> because of someone or something</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_6_p008-000593123"><a onclick="document.getElementById('LDOCE6_exa_bre_6_p008-000593123').play();"><img src="LDOCE6_img_spkr_g.png"/></a> We’ve reached our goal of $50,000, thanks to the generosity of the public.</span><span class="example" id="anchor_LDOCE6_exa_bre_8_p008-000593136"><a onclick="document.getElementById('LDOCE6_exa_bre_8_p008-000593136').play();"><img src="LDOCE6_img_spkr_g.png"/></a> Some ski resorts opened early, thanks to a late-October snowstorm.</span><span class="hint"><span class="warning"> ► </span>Don’t say ‘thank to someone/something’.</span></span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_2_s4"></a><span class="sense newline"><span class="sensenum">3</span><a id="6504937708254a64972bf4d4ccdfcc9d_thanks_2_s5"></a><span class="lexunit"> no thanks to somebody/something</span><span class="registerlab"> spoken</span><span class="def"> an expression meaning ‘in spite of’, used when someone should have helped you but did not</span><span class="neutral">: </span><span class="example" id="anchor_LDOCE6_exa_bre_f_p008-000593142"><a onclick="document.getElementById('LDOCE6_exa_bre_f_p008-000593142').play();"><img src="LDOCE6_img_spkr_g.png"/></a> It was no thanks to you that we managed to win the game.</span></span><span class="tail"><span class="crossref"><span class="neutral"> →</span><a> <span class="refhwd">vote of thanks</span></a></span><span class="collobox"><span class="heading">COLLOCATIONS</span><span class="section"><span class="secheading">verbs</span><span class="collocate"><span class="colloc collo">express your thanks</span><span class="collgloss"><span class="neutral"> (=</span>say that you are grateful<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>I would like to express my thanks to you for all your support.</span></span><span class="collocate"><span class="colloc collo">offer/extend your thanks to somebody</span><span class="collgloss"><span class="neutral"> (=</span>thank someone publicly<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>We would also like to extend our heartfelt thanks to the medical staff at Broadgreen Hospital.</span></span><span class="collocate"><span class="colloc collo">give thanks</span><span class="collgloss"><span class="neutral"> (=</span>thank God<span class="neutral">)</span></span><span class="example"><span class="neutral">· </span>Let us give thanks for the beauty of the world.</span></span><span class="collocate"><span class="colloc collo">thanks go to somebody</span><span class="example"><span class="neutral">· </span>Above all, our thanks go to Barbara Lambourne.</span></span></span><span class="section last"><span class="secheading">adjectives</span><span class="collocate"><span class="colloc collo">grateful thanks</span><span class="example"><span class="neutral">· </span>Our grateful thanks go to everyone who took part in the event.</span></span><span class="collocate"><span class="colloc collo">sincere/heartfelt thanks</span><span class="example"><span class="neutral">· </span>Please accept my most sincere thanks for all your help.</span></span><span class="collocate"><span class="colloc collo">special thanks</span><span class="example"><span class="neutral">· </span>Special thanks are due to John Bryden for his advice.</span></span></span></span></span></span></div><audio id="LDOCE6_hwd_bre_1_brelasdethanks" preload="none" src="LDOCE6_hwd_bre_1_brelasdethanks.mp3"></audio><audio id="LDOCE6_hwd_ame_0_thanks1" preload="none" src="LDOCE6_hwd_ame_0_thanks1.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001629003" preload="none" src="LDOCE6_exa_bre_7_p008-001629003.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001629006" preload="none" src="LDOCE6_exa_bre_7_p008-001629006.mp3"></audio><audio id="LDOCE6_exa_bre_2_p008-001827764" preload="none" src="LDOCE6_exa_bre_2_p008-001827764.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001657893" preload="none" src="LDOCE6_exa_bre_4_p008-001657893.mp3"></audio><audio id="LDOCE6_exa_bre_4_p008-001255673" preload="none" src="LDOCE6_exa_bre_4_p008-001255673.mp3"></audio><audio id="LDOCE6_exa_bre_e_p008-001333968" preload="none" src="LDOCE6_exa_bre_e_p008-001333968.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-000243386" preload="none" src="LDOCE6_exa_bre_f_p008-000243386.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-000687279" preload="none" src="LDOCE6_exa_bre_0_p008-000687279.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-000593080" preload="none" src="LDOCE6_exa_bre_a_p008-000593080.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-000593083" preload="none" src="LDOCE6_exa_bre_0_p008-000593083.mp3"></audio><audio id="LDOCE6_exa_bre_a_p008-000593096" preload="none" src="LDOCE6_exa_bre_a_p008-000593096.mp3"></audio><audio id="LDOCE6_exa_bre_0_p008-001629014" preload="none" src="LDOCE6_exa_bre_0_p008-001629014.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-000593107" preload="none" src="LDOCE6_exa_bre_7_p008-000593107.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-000593117" preload="none" src="LDOCE6_exa_bre_7_p008-000593117.mp3"></audio><audio id="LDOCE6_exa_bre_7_p008-001629032" preload="none" src="LDOCE6_exa_bre_7_p008-001629032.mp3"></audio><audio id="LDOCE6_exa_bre_6_p008-000593123" preload="none" src="LDOCE6_exa_bre_6_p008-000593123.mp3"></audio><audio id="LDOCE6_exa_bre_8_p008-000593136" preload="none" src="LDOCE6_exa_bre_8_p008-000593136.mp3"></audio><audio id="LDOCE6_exa_bre_f_p008-000593142" preload="none" src="LDOCE6_exa_bre_f_p008-000593142.mp3"></audio>`;
